insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (460,'汕头城管','n182Svuk4qnXRoAn.jpg','<p><span style=\"text-decoration: line-through;\"><em><strong><span style=\"text-decoration: line-through; font-size: 20px; font-family: 宋体, SimSun;\">疏散客流减少了</span></strong></em><span style=\"text-decoration: line-through; background-color: rgb(192, 0, 0);\"><em><strong><span style=\"text-decoration: line-through; font-size: 20px; font-family: 宋体, SimSun; color: rgb(118, 146, 60); background-color: rgb(192, 0, 0);\">地方</span></strong></em><em><strong><span style=\"color: rgb(118, 146, 60); text-decoration: line-through; font-family: 宋体, SimSun; font-size: 16px; background-color: rgb(192, 0, 0);\"></span></strong></em></span></span><span style=\"font-family: 微软雅黑;\"><br/><img src=\"/uploads/20160517/1463454639439397.jpg\" title=\"1463454639439397.jpg\" alt=\"a1.jpg\"/></span><br/></p>','2016-05-01 11:10:19',0,NULL,2,NULL,4,9,'',0,1,0,'2016-05-17 11:12:01','2016-05-17 15:35:11');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (461,'【动态】比拼业务技能，赛出城管风采——市城管局举行“业务大比拼、岗位大练兵”技能实操比赛','ZlUsm3FTYZy1knoy.jpg','<p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160517/1463492116107902.jpg\" title=\"1463492116107902.jpg\" alt=\"IMG_1(204).jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　5月13日上午，市城管局在市人民广场举行“业务大比拼、岗位大练兵”技能实操比赛，市园林处、市环卫局、市城照中心等3个单位共52名职工，分5个项目展开了技能比拼。市总工会副主席王述宗、市城管局局长许创生等有关领导现场观摩指导。</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\"><br/></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　比赛开始前，市城管局副调研员任振威主持了技能实操比赛启动仪式，明确此次技能实操比赛的总体要求和具体内容，并向各参赛单位和选手提出了希望。随后，参加当天技能实操比赛的3个单位迅速投入到比赛中，各项工作有序进行。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160517/1463492207861668.jpg\" title=\"1463492207861668.jpg\" alt=\"IMG_2(176).jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　<strong>多线开花，园林人展娴熟技艺</strong></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　此次比赛中，市园林处共派出9支代表队，分别进行了割灌机操作、宽带绿篱剪操作和高空压条等3个项目的比赛，以及现场盆景制作演示。作为传统繁育花木果树技术，高空压条在园林培育方面发挥着非常重要的作用，在这次比赛中，园林参赛队现场对植株进行高空压条，充分考验了职工对枝条的选取、环割范围的控制以及包土的处理等程序的熟练程度；割灌机、宽带绿篱剪等机械操作的比拼同样精彩，作为园林管养的主要机械，参赛队伍从机油配比、安全防护、操作稳定性和修剪标准等四个方面进行了竞赛，在展现园林人高水准作业的同时，也提高了职工对园林管养技术的掌握。最终，高空压条、机械操作均决出团体名次，并进行颁奖。此外，人民广场、中山公园还派出技艺精湛的园艺专家，为在场人员现场演示了罗汉松盆景和山水盆景的制作。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160517/1463492238125523.jpg\" title=\"1463492238125523.jpg\" alt=\"IMG_5(30).jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　<strong>精准拆装，城照人露矫健身手</strong></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　灯具是城市照明基础设施，对灯具的拆、装是城照职工的必备技能之一，市城照中心管养着全市3万多盏灯，对灯具的管养要在保证质量的情况下追求速度，才能提高管养效率，为市民群众的出行提供保障。因此，市城照中心选取了“高压钠灯灯具拆、装”作为此次技能实操比赛的项目，参赛选手在紧张的比赛节奏中，争分夺秒，对全新完好、亮灯正常的灯具进行拆卸和重新组装，并亮灯调试。最终，第一名选手仅用了2分29秒便完成了灯具的拆、装，比正常规定时间整整快了8分钟，充分展现了城照中心职工扎实的基础和过硬的技术。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160517/1463492262110557.jpg\" title=\"1463492262110557.jpg\" alt=\"IMG_4(77).jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　<strong>一气呵成，环卫人显精细水平</strong></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">&nbsp;</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　洒水作业是环卫日常作业内容，为城市道路降尘和清洁起到了重要作用。市环卫直属车队派出5名队员，轮流在指定路段进行洒水实操作业，从吸水栓装水、洒水车前喷水、左右冲洒、交叉对喷冲洗以及后洒水等环节，检验参赛队员对各流程和细节的把握和掌控，最终根据技术规范和操作准则，评出比赛名次。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160517/1463492351406521.jpg\" title=\"1463492351406521.jpg\" alt=\"IMG_3(119).jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　此次技能实操比赛是市城管局“业务大比拼、岗位大练兵”技能竞赛活动的一项重要内容，也是开展“两学一做”学习教育的一个亮点动作市园林处、环卫局、维修中心、城照中心和城绿中心等5个单位根据职责，分板块、分项目，共选出15个项目进行比赛和演示，通过参赛队伍和队员之间互相比拼，进一步强化一线职工在日常维修、养护等管养作业中的技术要求和要领，加强职工之间的技术交流，进一步提升一线人员日常作业技术规范和质量标准。下来，市维修中心、城绿中心还将针对各自项目组织技能实操比赛，以“两学一做”为引领，在全系统营造学习、比拼的浓烈氛围。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在市总工会的大力支持下，将从各单位参赛队伍中评选出“岗位能手”称号，在抓好理论知识学习、开展好技能实操比赛，给予城管人信心的肯定，激发城管系统干部职工干事创业、奋发进取的工作热情和创造力，建设作风硬、业务精、能力强的干部职工队伍，不断提高城管队伍形象，朝“地更绿、水更清、灯更亮、路更净，市容更整洁，夜景更璀璨，城市更美丽”的目标迈进！</span><br/></p><p><br/></p>','2016-05-16 21:21:36',0,NULL,2,NULL,98,9,'',0,0,0,'2016-05-17 21:47:26','2016-05-18 23:09:40');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (462,'【法规】《城镇排水与污水处理条例》','jrougU4SUxu8of3p.jpg','<p><span style=\"font-family: 微软雅黑;\"></span><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">中华人民共和国国务院令</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">第641号</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《城镇排水与污水处理条例》已经2013年9月18日国务院第24次常务会议通过，现予公布，自2014年1月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　　　　　　　　　　　　　　　　　　总　理 　李克强</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　　　　　　　　　　　　　　 　　　　2013年10月2日</span></p><p><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><strong>城镇排水与污水处理条例</strong></span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章　总　　则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为了加强对城镇排水与污水处理的管理，保障城镇排水与污水处理设施安全运行，防治城镇水污染和内涝灾害，保障公民生命、财产安全和公共安全，保护环境，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　城镇排水与污水处理的规划，城镇排水与污水处理设施的建设、维护与保护，向城镇排水设施排水与污水处理，以及城镇内涝防治，适用本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条　县级以上人民政府应当加强对城镇排水与污水处理工作的领导，并将城镇排水与污水处理工作纳入国民经济和社会发展规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条　城镇排水与污水处理应当遵循尊重自然、统筹规划、配套建设、保障安全、综合利用的原则。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条　国务院住房城乡建设主管部门指导监督全国城镇排水与污水处理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上地方人民政府城镇排水与污水处理主管部门（以下称城镇排水主管部门）负责本行政区域内城镇排水与污水处理的监督管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上人民政府其他有关部门依照本条例和其他有关法律、法规的规定，在各自的职责范围内负责城镇排水与污水处理监督管理的相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条　国家鼓励采取特许经营、政府购买服务等多种形式，吸引社会资金参与投资、建设和运营城镇排水与污水处理设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上人民政府鼓励、支持城镇排水与污水处理科学技术研究，推广应用先进适用的技术、工艺、设备和材料，促进污水的再生利用和污泥、雨水的资源化利用，提高城镇排水与污水处理能力。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章　规划与建设</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　国务院住房城乡建设主管部门会同国务院有关部门，编制全国的城镇排水与污水处理规划，明确全国城镇排水与污水处理的中长期发展目标、发展战略、布局、任务以及保障措施等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门会同有关部门，根据当地经济社会发展水平以及地理、气候特征，编制本行政区域的城镇排水与污水处理规划，明确排水与污水处理目标与标准，排水量与排水模式，污水处理与再生利用、污泥处理处置要求，排涝措施，城镇排水与污水处理设施的规模、布局、建设时序和建设用地以及保障措施等；易发生内涝的城市、镇，还应当编制城镇内涝防治专项规划，并纳入本行政区域的城镇排水与污水处理规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条　城镇排水与污水处理规划的编制，应当依据国民经济和社会发展规划、城乡规划、土地利用总体规划、水污染防治规划和防洪规划，并与城镇开发建设、道路、绿地、水系等专项规划相衔接。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇内涝防治专项规划的编制，应当根据城镇人口与规模、降雨规律、暴雨内涝风险等因素，合理确定内涝防治目标和要求，充分利用自然生态系统，提高雨水滞渗、调蓄和排放能力。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条　城镇排水主管部门应当将编制的城镇排水与污水处理规划报本级人民政府批准后组织实施，并报上一级人民政府城镇排水主管部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水与污水处理规划一经批准公布，应当严格执行；因经济社会发展确需修改的，应当按照原审批程序报送审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条　县级以上地方人民政府应当根据城镇排水与污水处理规划的要求，加大对城镇排水与污水处理设施建设和维护的投入。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条　城乡规划和城镇排水与污水处理规划确定的城镇排水与污水处理设施建设用地，不得擅自改变用途。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条　县级以上地方人民政府应当按照先规划后建设的原则，依据城镇排水与污水处理规划，合理确定城镇排水与污水处理设施建设标准，统筹安排管网、泵站、污水处理厂以及污泥处理处置、再生水利用、雨水调蓄和排放等排水与污水处理设施建设和改造。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇新区的开发和建设，应当按照城镇排水与污水处理规划确定的建设时序，优先安排排水与污水处理设施建设；未建或者已建但未达到国家有关标准的，应当按照年度改造计划进行改造，提高城镇排水与污水处理能力。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条　县级以上地方人民政府应当按照城镇排涝要求，结合城镇用地性质和条件，加强雨水管网、泵站以及雨水调蓄、超标雨水径流排放等设施建设和改造。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　新建、改建、扩建市政基础设施工程应当配套建设雨水收集利用设施，增加绿地、砂石地面、可渗透路面和自然地面对雨水的滞渗能力，利用建筑物、停车场、广场、道路等建设雨水收集利用设施，削减雨水径流，提高城镇内涝防治能力。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　新区建设与旧城区改建，应当按照城镇排水与污水处理规划确定的雨水径流控制要求建设相关设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条　城镇排水与污水处理规划范围内的城镇排水与污水处理设施建设项目以及需要与城镇排水与污水处理设施相连接的新建、改建、扩建建设工程，城乡规划主管部门在依法核发建设用地规划许可证时，应当征求城镇排水主管部门的意见。城镇排水主管部门应当就排水设计方案是否符合城镇排水与污水处理规划和相关标准提出意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设单位应当按照排水设计方案建设连接管网等设施；未建设连接管网等设施的，不得投入使用。城镇排水主管部门或者其委托的专门机构应当加强指导和监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　城镇排水与污水处理设施建设工程竣工后，建设单位应当依法组织竣工验收。竣工验收合格的，方可交付使用，并自竣工验收合格之日起15日内，将竣工验收报告及相关资料报城镇排水主管部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条　城镇排水与污水处理设施竣工验收合格后，由城镇排水主管部门通过招标投标、委托等方式确定符合条件的设施维护运营单位负责管理。特许经营合同、委托运营合同涉及污染物削减和污水处理运营服务费的，城镇排水主管部门应当征求环境保护主管部门、价格主管部门的意见。国家鼓励实施城镇污水处理特许经营制度。具体办法由国务院住房城乡建设主管部门会同国务院有关部门制定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水与污水处理设施维护运营单位应当具备下列条件：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）有法人资格；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）有与从事城镇排水与污水处理设施维护运营活动相适应的资金和设备；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）有完善的运行管理和安全管理制度；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）技术负责人和关键岗位人员经专业培训并考核合格；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）有相应的良好业绩和维护运营经验；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）法律、法规规定的其他条件。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章　排　　水</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条　县级以上地方人民政府应当根据当地降雨规律和暴雨内涝风险情况，结合气象、水文资料，建立排水设施地理信息系统，加强雨水排放管理，提高城镇内涝防治水平。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上地方人民政府应当组织有关部门、单位采取相应的预防治理措施，建立城镇内涝防治预警、会商、联动机制，发挥河道行洪能力和水库、洼淀、湖泊调蓄洪水的功能，加强对城镇排水设施的管理和河道防护、整治，因地制宜地采取定期清淤疏浚等措施，确保雨水排放畅通，共同做好城镇内涝防治工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条　城镇排水主管部门应当按照城镇内涝防治专项规划的要求，确定雨水收集利用设施建设标准，明确雨水的排水分区和排水出路，合理控制雨水径流。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条　除干旱地区外，新区建设应当实行雨水、污水分流；对实行雨水、污水合流的地区，应当按照城镇排水与污水处理规划要求，进行雨水、污水分流改造。雨水、污水分流改造可以结合旧城区改建和道路建设同时进行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在雨水、污水分流地区，新区建设和旧城区改建不得将雨水管网、污水管网相互混接。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在有条件的地区，应当逐步推进初期雨水收集与处理，合理确定截流倍数，通过设置初期雨水贮存池、建设截流干管等方式，加强对初期雨水的排放调控和污染防治。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条　城镇排水设施覆盖范围内的排水单位和个人，应当按照国家有关规定将污水排入城镇排水设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在雨水、污水分流地区，不得将污水排入雨水管网。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条　从事工业、建筑、餐饮、医疗等活动的企业事业单位、个体工商户（以下称排水户）向城镇排水设施排放污水的，应当向城镇排水主管部门申请领取污水排入排水管网许可证。城镇排水主管部门应当按照国家有关标准，重点对影响城镇排水与污水处理设施安全运行的事项进行审查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水户应当按照污水排入排水管网许可证的要求排放污水。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　排水户申请领取污水排入排水管网许可证应当具备下列条件：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）排放口的设置符合城镇排水与污水处理规划的要求；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）按照国家有关规定建设相应的预处理设施和水质、水量检测设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）排放的污水符合国家或者地方规定的有关排放标准；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）法律、法规规定的其他条件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　符合前款规定条件的，由城镇排水主管部门核发污水排入排水管网许可证；具体办法由国务院住房城乡建设主管部门制定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条　城镇排水主管部门应当加强对排放口设置以及预处理设施和水质、水量检测设施建设的指导和监督；对不符合规划要求或者国家有关规定的，应当要求排水户采取措施，限期整改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　城镇排水主管部门委托的排水监测机构，应当对排水户排放污水的水质和水量进行监测，并建立排水监测档案。排水户应当接受监测，如实提供有关资料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　列入重点排污单位名录的排水户安装的水污染物排放自动监测设备，应当与环境保护主管部门的监控设备联网。环境保护主管部门应当将监测数据与城镇排水主管部门共享。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条　因城镇排水设施维护或者检修可能对排水造成影响的，城镇排水设施维护运营单位应当提前24小时通知相关排水户；可能对排水造成严重影响的，应当事先向城镇排水主管部门报告，采取应急处理措施，并向社会公告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条　设置于机动车道路上的窨井，应当按照国家有关规定进行建设，保证其承载力和稳定性等符合相关要求。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水管网窨井盖应当具备防坠落和防盗窃功能，满足结构强度要求。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条　城镇排水主管部门应当按照国家有关规定建立城镇排涝风险评估制度和灾害后评估制度，在汛前对城镇排水设施进行全面检查，对发现的问题，责成有关单位限期处理，并加强城镇广场、立交桥下、地下构筑物、棚户区等易涝点的治理，强化排涝措施，增加必要的强制排水设施和装备。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水设施维护运营单位应当按照防汛要求，对城镇排水设施进行全面检查、维护、清疏，确保设施安全运行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在汛期，有管辖权的人民政府防汛指挥机构应当加强对易涝点的巡查，发现险情，立即采取措施。有关单位和个人在汛期应当服从有管辖权的人民政府防汛指挥机构的统一调度指挥或者监督。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章　污水处理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条　城镇排水主管部门应当与城镇污水处理设施维护运营单位签订维护运营合同，明确双方权利义务。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇污水处理设施维护运营单位应当依照法律、法规和有关规定以及维护运营合同进行维护运营，定期向社会公开有关维护运营信息，并接受相关部门和社会公众的监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条　城镇污水处理设施维护运营单位应当保证出水水质符合国家和地方规定的排放标准，不得排放不达标污水。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇污水处理设施维护运营单位应当按照国家有关规定检测进出水水质，向城镇排水主管部门、环境保护主管部门报送污水处理水质和水量、主要污染物削减量等信息，并按照有关规定和维护运营合同，向城镇排水主管部门报送生产运营成本等信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇污水处理设施维护运营单位应当按照国家有关规定向价格主管部门提交相关成本信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门核定城镇污水处理运营成本，应当考虑主要污染物削减情况。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条　城镇污水处理设施维护运营单位或者污泥处理处置单位应当安全处理处置污泥，保证处理处置后的污泥符合国家有关标准，对产生的污泥以及处理处置后的污泥去向、用途、用量等进行跟踪、记录，并向城镇排水主管部门、环境保护主管部门报告。任何单位和个人不得擅自倾倒、堆放、丢弃、遗撒污泥。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条　城镇污水处理设施维护运营单位不得擅自停运城镇污水处理设施，因检修等原因需要停运或者部分停运城镇污水处理设施的，应当在90个工作日前向城镇排水主管部门、环境保护主管部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇污水处理设施维护运营单位在出现进水水质和水量发生重大变化可能导致出水水质超标，或者发生影响城镇污水处理设施安全运行的突发情况时，应当立即采取应急处理措施，并向城镇排水主管部门、环境保护主管部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门或者环境保护主管部门接到报告后，应当及时核查处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条　排水单位和个人应当按照国家有关规定缴纳污水处理费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　向城镇污水处理设施排放污水、缴纳污水处理费的，不再缴纳排污费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水监测机构接受城镇排水主管部门委托从事有关监测活动，不得向城镇污水处理设施维护运营单位和排水户收取任何费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条　污水处理费应当纳入地方财政预算管理，专项用于城镇污水处理设施的建设、运行和污泥处理处置，不得挪作他用。污水处理费的收费标准不应低于城镇污水处理设施正常运营的成本。因特殊原因，收取的污水处理费不足以支付城镇污水处理设施正常运营的成本的，地方人民政府给予补贴。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　污水处理费的收取、使用情况应当向社会公开。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条　县级以上地方人民政府环境保护主管部门应当依法对城镇污水处理设施的出水水质和水量进行监督检查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门应当对城镇污水处理设施运营情况进行监督和考核，并将监督考核情况向社会公布。有关单位和个人应当予以配合。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇污水处理设施维护运营单位应当为进出水在线监测系统的安全运行提供保障条件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条　城镇排水主管部门应当根据城镇污水处理设施维护运营单位履行维护运营合同的情况以及环境保护主管部门对城镇污水处理设施出水水质和水量的监督检查结果，核定城镇污水处理设施运营服务费。地方人民政府有关部门应当及时、足额拨付城镇污水处理设施运营服务费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条　城镇排水主管部门在监督考核中，发现城镇污水处理设施维护运营单位存在未依照法律、法规和有关规定以及维护运营合同进行维护运营，擅自停运或者部分停运城镇污水处理设施，或者其他无法安全运行等情形的，应当要求城镇污水处理设施维护运营单位采取措施，限期整改；逾期不整改的，或者整改后仍无法安全运行的，城镇排水主管部门可以终止维护运营合同。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门终止与城镇污水处理设施维护运营单位签订的维护运营合同的，应当采取有效措施保障城镇污水处理设施的安全运行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条　国家鼓励城镇污水处理再生利用，工业生产、城市绿化、道路清扫、车辆冲洗、建筑施工以及生态景观等，应当优先使用再生水。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上地方人民政府应当根据当地水资源和水环境状况，合理确定再生水利用的规模，制定促进再生水利用的保障措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　再生水纳入水资源统一配置，县级以上地方人民政府水行政主管部门应当依法加强指导。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章　设施维护与保护</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条　城镇排水与污水处理设施维护运营单位应当建立健全安全生产管理制度，加强对窨井盖等城镇排水与污水处理设施的日常巡查、维修和养护，保障设施安全运行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　从事管网维护、应急排水、井下及有限空间作业的，设施维护运营单位应当安排专门人员进行现场安全管理，设置醒目警示标志，采取有效措施避免人员坠落、车辆陷落，并及时复原窨井盖，确保操作规程的遵守和安全措施的落实。相关特种作业人员，应当按照国家有关规定取得相应的资格证书。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条　县级以上地方人民政府应当根据实际情况，依法组织编制城镇排水与污水处理应急预案，统筹安排应对突发事件以及城镇排涝所必需的物资。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水与污水处理设施维护运营单位应当制定本单位的应急预案，配备必要的抢险装备、器材，并定期组织演练。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条　排水户因发生事故或者其他突发事件，排放的污水可能危及城镇排水与污水处理设施安全运行的，应当立即采取措施消除危害，并及时向城镇排水主管部门和环境保护主管部门等有关部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水与污水处理安全事故或者突发事件发生后，设施维护运营单位应当立即启动本单位应急预案，采取防护措施、组织抢修，并及时向城镇排水主管部门和有关部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条　城镇排水主管部门应当会同有关部门，按照国家有关规定划定城镇排水与污水处理设施保护范围，并向社会公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在保护范围内，有关单位从事爆破、钻探、打桩、顶进、挖掘、取土等可能影响城镇排水与污水处理设施安全的活动的，应当与设施维护运营单位等共同制定设施保护方案，并采取相应的安全防护措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条　禁止从事下列危及城镇排水与污水处理设施安全的活动：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）损毁、盗窃城镇排水与污水处理设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）穿凿、堵塞城镇排水与污水处理设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）向城镇排水与污水处理设施排放、倾倒剧毒、易燃易爆、腐蚀性废液和废渣；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）向城镇排水与污水处理设施倾倒垃圾、渣土、施工泥浆等废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）建设占压城镇排水与污水处理设施的建筑物、构筑物或者其他设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）其他危及城镇排水与污水处理设施安全的活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条　新建、改建、扩建建设工程，不得影响城镇排水与污水处理设施安全。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设工程开工前，建设单位应当查明工程建设范围内地下城镇排水与污水处理设施的相关情况。城镇排水主管部门及其他相关部门和单位应当及时提供相关资料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设工程施工范围内有排水管网等城镇排水与污水处理设施的，建设单位应当与施工单位、设施维护运营单位共同制定设施保护方案，并采取相应的安全保护措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因工程建设需要拆除、改动城镇排水与污水处理设施的，建设单位应当制定拆除、改动方案，报城镇排水主管部门审核，并承担重建、改建和采取临时措施的费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条　县级以上人民政府城镇排水主管部门应当会同有关部门，加强对城镇排水与污水处理设施运行维护和保护情况的监督检查，并将检查情况及结果向社会公开。实施监督检查时，有权采取下列措施：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）进入现场进行检查、监测；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）查阅、复制有关文件和资料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）要求被监督检查的单位和个人就有关问题作出说明。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　被监督检查的单位和个人应当予以配合，不得妨碍和阻挠依法进行的监督检查活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条　审计机关应当加强对城镇排水与污水处理设施建设、运营、维护和保护等资金筹集、管理和使用情况的监督，并公布审计结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章　法律责任</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条　违反本条例规定，县级以上地方人民政府及其城镇排水主管部门和其他有关部门，不依法作出行政许可或者办理批准文件的，发现违法行为或者接到对违法行为的举报不予查处的，或者有其他未依照本条例履行职责的行为的，对直接负责的主管人员和其他直接责任人员依法给予处分；直接负责的主管人员和其他直接责任人员的行为构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例规定，核发污水排入排水管网许可证、排污许可证后不实施监督检查的，对核发许可证的部门及其工作人员依照前款规定处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条　违反本条例规定，城镇排水主管部门对不符合法定条件的排水户核发污水排入排水管网许可证的，或者对符合法定条件的排水户不予核发污水排入排水管网许可证的，对直接负责的主管人员和其他直接责任人员依法给予处分；直接负责的主管人员和其他直接责任人员的行为构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条　违反本条例规定，在雨水、污水分流地区，建设单位、施工单位将雨水管网、污水管网相互混接的，由城镇排水主管部门责令改正，处5万元以上10万元以下的罚款；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条　违反本条例规定，城镇排水与污水处理设施覆盖范围内的排水单位和个人，未按照国家有关规定将污水排入城镇排水设施，或者在雨水、污水分流地区将污水排入雨水管网的，由城镇排水主管部门责令改正，给予警告；逾期不改正或者造成严重后果的，对单位处10万元以上20万元以下罚款，对个人处2万元以上10万元以下罚款；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条　违反本条例规定，排水户未取得污水排入排水管网许可证向城镇排水设施排放污水的，由城镇排水主管部门责令停止违法行为，限期采取治理措施，补办污水排入排水管网许可证，可以处50万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例规定，排水户不按照污水排入排水管网许可证的要求排放污水的，由城镇排水主管部门责令停止违法行为，限期改正，可以处5万元以下罚款；造成严重后果的，吊销污水排入排水管网许可证，并处5万元以上50万元以下罚款，可以向社会予以通报；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条　违反本条例规定，因城镇排水设施维护或者检修可能对排水造成影响或者严重影响，城镇排水设施维护运营单位未提前通知相关排水户的，或者未事先向城镇排水主管部门报告，采取应急处理措施的，或者未按照防汛要求对城镇排水设施进行全面检查、维护、清疏，影响汛期排水畅通的，由城镇排水主管部门责令改正，给予警告；逾期不改正或者造成严重后果的，处10万元以上20万元以下罚款；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条　违反本条例规定，城镇污水处理设施维护运营单位未按照国家有关规定检测进出水水质的，或者未报送污水处理水质和水量、主要污染物削减量等信息和生产运营成本等信息的，由城镇排水主管部门责令改正，可以处5万元以下罚款；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例规定，城镇污水处理设施维护运营单位擅自停运城镇污水处理设施，未按照规定事先报告或者采取应急处理措施的，由城镇排水主管部门责令改正，给予警告；逾期不改正或者造成严重后果的，处10万元以上50万元以下罚款；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条　违反本条例规定，城镇污水处理设施维护运营单位或者污泥处理处置单位对产生的污泥以及处理处置后的污泥的去向、用途、用量等未进行跟踪、记录的，或者处理处置后的污泥不符合国家有关标准的，由城镇排水主管部门责令限期采取治理措施，给予警告；造成严重后果的，处10万元以上20万元以下罚款；逾期不采取治理措施的，城镇排水主管部门可以指定有治理能力的单位代为治理，所需费用由当事人承担；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例规定，擅自倾倒、堆放、丢弃、遗撒污泥的，由城镇排水主管部门责令停止违法行为，限期采取治理措施，给予警告；造成严重后果的，对单位处10万元以上50万元以下罚款，对个人处2万元以上10万元以下罚款；逾期不采取治理措施的，城镇排水主管部门可以指定有治理能力的单位代为治理，所需费用由当事人承担；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条　违反本条例规定，排水单位或者个人不缴纳污水处理费的，由城镇排水主管部门责令限期缴纳，逾期拒不缴纳的，处应缴纳污水处理费数额1倍以上3倍以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条　违反本条例规定，城镇排水与污水处理设施维护运营单位有下列情形之一的，由城镇排水主管部门责令改正，给予警告；逾期不改正或者造成严重后果的，处10万元以上50万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）未按照国家有关规定履行日常巡查、维修和养护责任，保障设施安全运行的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）未及时采取防护措施、组织事故抢修的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）因巡查、维护不到位，导致窨井盖丢失、损毁，造成人员伤亡和财产损失的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条　违反本条例规定，从事危及城镇排水与污水处理设施安全的活动的，由城镇排水主管部门责令停止违法行为，限期恢复原状或者采取其他补救措施，给予警告；逾期不采取补救措施或者造成严重后果的，对单位处10万元以上30万元以下罚款，对个人处2万元以上10万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条　违反本条例规定，有关单位未与施工单位、设施维护运营单位等共同制定设施保护方案，并采取相应的安全防护措施的，由城镇排水主管部门责令改正，处2万元以上5万元以下罚款；造成严重后果的，处5万元以上10万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例规定，擅自拆除、改动城镇排水与污水处理设施的，由城镇排水主管部门责令改正，恢复原状或者采取其他补救措施，处5万元以上10万元以下罚款；造成严重后果的，处10万元以上30万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七章　附　　则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条　依照《中华人民共和国水污染防治法》的规定，排水户需要取得排污许可证的，由环境保护主管部门核发；违反《中华人民共和国水污染防治法》的规定排放污水的，由环境保护主管部门处罚。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条　本条例自2014年1月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 13:10:59',0,NULL,2,NULL,17,11,'',0,0,0,'2016-05-18 13:17:50','2016-05-18 13:17:50');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (463,'【法规】《广东省城乡垃圾生活垃圾处理条例》','WTZB8EAI8qBNQiCe.png','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">广东省第十二届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公 &nbsp; &nbsp;告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第40号）</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《广东省城乡生活垃圾处理条例》已由广东省第十二届人民代表大会常务委员会第二十次会议于2015年9月25日通过，现予公布，自2016年1月1日起施行。</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">广东省人民代表大会常务委员会</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年9月25日<br/><br/></span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">广东省城乡生活垃圾处理条例</span></strong></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2015年9月25日广东省第十二届人民代表大会常务委员会第二十次会议通过）<br/><br/></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 &nbsp;总则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 &nbsp;为了规范城乡生活垃圾处理，控制污染，保护环境，根据《中华人民共和国固体废物污染环境防治法》和《城市市容和环境卫生管理条例》等有关法律、行政法规，结合本省实际，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 &nbsp;本条例适用于本省行政区域内城乡生活垃圾的清扫、分类、收集、运输、处置等活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例所称生活垃圾，是指在日常生活中或者为日常生活提供服务的活动中产生的固体废物以及法律、行政法规规定视为生活垃圾的固体废物。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 &nbsp;城乡生活垃圾处理遵循城乡统筹、属地管理、全民参与、分类处理的原则，实行减量化、资源化、无害化控制和管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 &nbsp;各级人民政府应当将城乡生活垃圾处理工作纳入国民经济与社会发展规划，制定城乡生活垃圾处理目标，落实城乡生活垃圾清扫、分类、收集、运输、处置的保障措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　乡镇人民政府、街道办事处负责本辖区内城乡生活垃圾的处理工作，指导村民委员会、居民委员会组织生活垃圾清扫、分类和收集转运、处置工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 &nbsp;省人民政府住房城乡建设主管部门指导全省城乡生活垃圾的处理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府环境卫生主管部门负责本行政区域内城乡生活垃圾的处理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上人民政府有关部门按照职责分工协同做好城乡生活垃圾的处理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 &nbsp;单位和个人应当尽量减少生活垃圾产生，按规定分类投放生活垃圾，缴纳生活垃圾处理费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 &nbsp;各级人民政府应当加强生活垃圾处理的宣传教育，增强公众生活垃圾减量、分类意识，倡导绿色健康的生活方式，鼓励公众参与生活垃圾处理的监督活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　教育主管部门应当把生活垃圾源头减量、分类、回收利用和无害化处理等知识作为学校教育和社会实践内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　媒体应当开展生活垃圾分类处理的公益宣传。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章 &nbsp;分类与投放</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 城乡生活垃圾应当分类，并投放到指定的收集点或者收集容器内。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡生活垃圾分为以下四类：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）可回收物，是指适宜回收和可循环再利用的物品，如纸制品、塑料制品、玻璃制品、纺织品和金属等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）有机易腐垃圾，是指餐饮垃圾、家庭厨余垃圾和废弃的蔬菜、瓜果、花木等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）有害垃圾，是指对人体健康、自然环境造成直接或者潜在危害的物质，如废弃的充电电池、纽扣电池、灯管、医药用品、杀虫剂、油漆、日用化学品、水银产品以及废弃的农药、化肥残余及包装物等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）其他垃圾，是指前三项以外的生活垃圾，如惰性垃圾，不可降解的一次性用品、普通无汞电池、烟蒂、纸巾、家庭装修废弃物、废弃家具等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　地级以上市人民政府可以根据本地实际情况，制定城乡生活垃圾分类的具体办法，引导城乡居民分类投放垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 &nbsp;生活垃圾分类管理实行管理责任人制度：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）住宅小区、街巷等实行物业管理的，由物业管理单位负责；单位自行管理的，由自管单位负责；没有物业管理或者单位自行管理的，由居民委员会负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（二）农村地区由村民委员会负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（三）机关、部队、企事业单位、社会团体及其他组织的办公场所，由本单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（四）建设工程的施工现场，由建设单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（五）集贸市场、商场、展览展销、餐饮服务、商铺等经营场所，由经营管理单位负责；没有经营管理单位的，由经营单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）道路、公路、铁路沿线、桥梁、隧道、人行过街通道（桥）、机场、港口、码头、火车站、长途客运站、公交场站、轨道交通车站、公园、旅游景区、河流与湖泊水面等　　公共场所和公共建筑，由所有权人或者其他实际管理人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）不能确定生活垃圾分类管理责任人的，由所在地乡镇人民政府、街道办事处落实责任人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 &nbsp;生活垃圾分类管理责任人的工作责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）建立生活垃圾日常分类管理制度，记录产生的生活垃圾种类和去向，并接受环境卫生主管部门的监督检查；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（二）开展生活垃圾分类知识宣传，指导、监督单位和个人开展生活垃圾分类；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（三）根据生活垃圾产生量和分类方法，按照标准和分类标志设置生活垃圾分类收集点和收集容器，并保持生活垃圾分类收集容器正常使用；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　（四）明确生活垃圾的投放时间、地点；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）制止混合已分类的生活垃圾；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）督促检查垃圾分类，把垃圾交由相关单位处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 &nbsp;任何单位和个人不得随意倾倒、抛撒、焚烧或者堆放垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 &nbsp;可回收物应当交由再生资源回收企业处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府应当制定并公布可回收目录，合理布局再生资源回收网络，制定低价值可回收物回收利用优惠政策，鼓励企业参与低价值可回收物的回收利用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 第十三条 &nbsp;各级人民政府应当加强对餐饮垃圾的控制和管理，提高餐饮垃圾资源化利用和无害化处理水平。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　环境卫生主管部门应当制定餐饮垃圾产生、收集、运输、处置等过程的联单制度或者信息化监管措施，对餐饮垃圾收集、运输、处置设施运行管理情况进行实时监督和定期检查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　餐饮垃圾产生单位应当落实餐饮垃圾源头减量分类工作责任，餐饮垃圾应当交给有经营许可证的单位收运处理，不得直接排入公共水域、厕所、市政管道或者混入其他生活垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 &nbsp;有害垃圾由具有经营许可证的专业企业处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 &nbsp;居民家庭装修废弃物和废弃沙发、衣柜、床等大件家具应当预约环境卫生作业单位或者再生资源回收站处理，不得投放到垃圾收集点或者收集容器内。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励电器电子产品生产者自行或者委托销售者、维修机构、售后服务机构、废弃电器电子产品回收经营者回收废弃电器电子产品。回收的废弃电器电子产品应当由有废弃电器电子产品处理资格的企业处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 &nbsp;各级人民政府应当建立涵盖生产、流通、消费等领域的各类废弃物源头减量机制，鼓励单位和个人减少生活垃圾的产生，促进资源节约和循环利用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章 &nbsp;清扫、收集、运输与处置</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 &nbsp;市、县（区）、乡镇、街道、村（居）应当建立生活垃圾清扫制度，明确清扫区域、标准要求、作业规范。道路两侧、山边，河流（涌）、湖泊、水库及沿岸应当纳入清扫范围。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　机关、团体、企事业等单位应当按照城市人民政府市容环境卫生主管部门划分的卫生责任区域负责清扫生活垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 城乡生活垃圾由市、县（区）人民政府组织收运，乡镇、街道、村（居）应当按照市、县（区）人民政府的要求做好生活垃圾收运工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 第十九条 收集、运输生活垃圾的单位应当根据生活垃圾分类、收集量、作业时间等因素，做好收集和运输工作：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按时收集生活垃圾；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）将生活垃圾运输至符合规定的转运或者处置设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）生活垃圾应当实行密闭化运输，在运输过程中不得丢弃、扬撒、遗漏垃圾以及滴漏污水；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）垃圾运输线路应当避开水源保护区；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）不得混合收运已分类的生活垃圾；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）不得擅自将境外和省外生活垃圾转移至本省处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 &nbsp;城乡生活垃圾应当分类处置，充分回收利用，不能回收利用的采取无害化焚烧、生化技术、卫生填埋等方式进行处置。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励发展生活垃圾焚烧发电方式，以焚烧发电为依托，结合先进技术和综合处理方式，建设集约化生活垃圾处理环境园。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 &nbsp;生活垃圾收集、运输、处置单位应当严格执行各项工程技术规范、操作规程和污染控制标准，及时处理生活垃圾处理过程中产生的废水、废气、废渣等，建立污染物　　排放监测制度和措施，定期向所在地环境卫生主管部门和环境保护主管部门报告监测结果，并按照规定向社会公开监测结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 &nbsp;有机易腐垃圾的处置应当采用生化处理为主的综合处理方式。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励集贸市场、超市、食堂、餐饮单位以及有条件的居住区安装符合标准的有机易腐垃圾处理装置，就地处理有机易腐垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 &nbsp;从事餐饮垃圾处置的单位在处置过程中应当采取有效的污染控制措施，按照生活垃圾处置标准，实施无害化处置。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　禁止将餐饮垃圾及其加工物用于原料生产、食品加工，禁止使用未经无害化处理的餐饮垃圾饲养畜禽。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 &nbsp;农村生活垃圾按照以下方式处理：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）可回收垃圾交由再生资源回收企业回收；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）有机易腐垃圾应当按照农业废弃物资源化的要求，采用生化处理等技术就地处理，直接还田、堆肥或者生产沼气；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）低价值可回收物、有害垃圾应当建立收集点，专项回收，集中处理；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）惰性垃圾实行就地深埋；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）其他类型的垃圾由市、县（区）统筹处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 &nbsp;城乡结合部或者人口密集的农村的生活垃圾，纳入城市生活垃圾分类收运处理系统。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　偏远地区或者人口分散的农村的生活垃圾在充分回收、合理利用基础上，因地制宜就近处理；不能就近处理的，应当纳入城市生活垃圾分类收运处理系统。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 &nbsp;鼓励通过市场化方式，选择承担生活垃圾清扫、收集、运输和处置工作的单位。从事清扫、收集、运输、处置的单位应当按照合同约定履行工作职责，并执行环卫作业标准和规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">第四章 &nbsp;建设与保障</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 &nbsp;省人民政府应当依据省城乡生活垃圾处理目标,结合生活垃圾产生和处理情况，组织编制全省城乡生活垃圾处理专项规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府环境卫生主管部门应当依据省城乡生活垃圾处理专项规划，组织编制本行政区域城乡生活垃圾处理专项规划，报本级人民政府批准后组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 &nbsp;市、县（区）城乡生活垃圾处理专项规划报送审批前，组织编制机关应当向社会公示专项规划草案，公示时间不得少于三十日，并采取论证会、听证会等方式征求专家和公众的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经批准的专项规划，应当在同级人民政府网站和当地主要新闻媒体公布。专项规划确需修改的，按照原审批程序办理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 &nbsp;市、县（区）人民政府应当将生活垃圾处理设施的建设用地、建设计划纳入土地利用年度计划、城乡规划年度实施计划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府城乡规划部门应当把城乡生活垃圾处理专项规划中确定的生活垃圾处理设施用地，纳入控制性详细规划和城市黄线保护范围，并依法向社会公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经规划确定的生活垃圾处理设施建设用地，未经法定程序，不得改变用途。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 &nbsp;市、县（区）环境卫生主管部门和生活垃圾分类管理责任人应当按照相关规划、国家和省的有关技术标准和要求设置生活垃圾收集点。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　自然村应当按照村庄规划以及国家和省的有关技术标准和要求设置围蔽的生活垃圾收集点，并定期清运、清洁、消毒，防止污染环境。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 &nbsp;市、县（区）环境卫生主管部门、乡镇、街道应当按照国家、省相关标准规范建设生活垃圾转运站。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">站内渗滤液应当统一收集并进行预处理后，运往垃圾渗滤液处理厂、生活污水处理厂，或者通过市政污水管道输送至生活污水处理厂处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条 &nbsp;市、县（区）人民政府应当按照城乡生活垃圾处理目标和专项规划，同步推进城镇和农村生活垃圾处理设施建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生活垃圾处理设施建设应当按照减量化、资源化、无害化的技术路线开展，做到因地制宜、技术可行、规模适度。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生活垃圾处理设施建设应当符合国家、省的有关标准和规范。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 &nbsp;新建、改建、扩建建设工程应当按照标准配套建设生活垃圾分类、收集、转运设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　配套生活垃圾处理设施应当与主体工程同时设计、同时施工、同时交付使用；建设工程分期建设的，配套生活垃圾处理设施应当与首期工程同时交付使用；配套设施建设费用纳入建设工程投资。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条 &nbsp;任何单位和个人不得擅自关闭、闲置、拆除生活垃圾集中转运、处置设施。确需关闭、闲置、拆除的，必须报市、县（区）人民政府环境卫生主管部门和环境保护主管部门核准，按先建后拆的原则，重建、补建或者提供替代设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条 &nbsp;生活垃圾临时堆放点和简易填埋场，应结合垃圾堆放、填埋规模、场址地质构造和周边环境条件制定综合治理方案并限期治理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　填埋库容已满的生活垃圾卫生填埋场，应当按规定进行封场、跟踪检测污染物排放和地下水、大气、垃圾堆体沉降指标，并采取相应处置措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条 &nbsp;市、县（区）人民政府应当根据生活垃圾处理资金需求落实生活垃圾处理经费，并纳入本级政府财政预算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府应当从征收的城市维护建设税、城市基础设施配套费中安排一定的资金，用于生活垃圾收集、运输、处置设施的建设和垃圾处理收费不足时的运营成本补充。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条 &nbsp;城市的单位和个人，应当按照市、县（区）人民政府确定的收费标准缴纳城市生活垃圾处理费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　农村地区的生活垃圾处理费，通过政府补贴、社会捐赠、村民委员会筹措等方式筹集。市、县（区）人民政府负责对农村生活垃圾处理的经费保障。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生活垃圾处理费应当专项用于生活垃圾的清扫、收集、运输和处置，不得挪作他用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 &nbsp;各级人民政府应当积极探索生活垃圾分类新模式，城镇地区可在收取的垃圾处理费中划出一定比例，作为开展源头分类减量和低价值回收物回收的激励资金。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 &nbsp;市、县（区）人民政府应当按照地域统筹、设施共享的原则，建立生活垃圾处理生态补偿机制，科学合理设置补偿原则和补偿标准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 &nbsp;生活垃圾处理生态补偿费主要用于生活垃圾处理设施周边地区环境美化、环境整治，市政配套设施建设和维护，公共服务设施建设和维护，经济发展扶持和补偿等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章 &nbsp;监督管理</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 &nbsp;省人民政府住房城乡建设主管部门应当建立健全城乡生活垃圾监督管理制度，对城乡生活垃圾处理情况进行监督检查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府环境卫生主管部门应当结合生活垃圾管理责任目标和任务要求，对本行政区域内生活垃圾清扫、收集、运输、处置单位执行本条例的情况进行监督检查。根据需要，可以向生活垃圾处理设施现场派驻监督员。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条 &nbsp;省住房城乡建设主管部门应当根据国家技术标准，制定生活垃圾收集、运输和处置设施运营规范，建立生活垃圾处理运营单位信用体系和失信惩戒机制、黑名单制度，对生活垃圾处理设施运营状况和处理效果的监管开展年度评价，并公开评价结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条 &nbsp;市、县（区）人民政府环境卫生主管部门可以委托专业机构、行业协会等对生活垃圾处理数量、服务质量以及运营情况进行监管和评价。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条 &nbsp;市、县（区）人民政府环境卫生主管部门应当逐步建立生活垃圾清扫、分类、收集、运输、处置全过程信息管理系统，并与省城乡生活垃圾管理信息系统实时联网。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　信息管理系统应当包含环境卫生作业全过程监管，生活垃圾收集、运输、处置设施运营在线监控，以及处置设施污染物排放在线监控等内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条 &nbsp;市、县（区）人民政府环境保护主管部门应当对生活垃圾集中转运、处置设施的污染物排放情况进行监测，并实时发布监测信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县（区）人民政府环境卫生主管部门应当加强对本辖区内生活垃圾收集、运输、处置各环节的监督管理和环境监测，及时督促整改不符合规定的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条 &nbsp;各级人民政府应当建立生活垃圾处理信息公开制度，依法向社会公布生活垃圾清扫、分类、收集、运输、处置状况和生活垃圾处理费收支及使用信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生活垃圾处置单位应当向社会公开主要污染物的名称、排放方式、排放浓度和总量、超标排放情况，以及生活垃圾处置设施的运行情况、主要污染物排放数据、环境监测数据等，接受政府监管和社会监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条 &nbsp;鼓励单位和个人举报生活垃圾收集、运输、处置设施的建设、运营和污染排放中的违法行为，监管单位应当依法受理和查处，并公布查处结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">监管单位对举报的违法行为进行查实并依法处理后，可以根据举报人的贡献大小，给予相应奖励。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条 &nbsp;环境卫生行业协会应当加强行业自律，开展企业信用评价、行业技术指导和技术培训。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章 &nbsp;法律责任</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条 &nbsp;市、县（区）人民政府违反本条例规定，未履行生活垃圾处理职责的，由上级人民政府或者监察机关追究主要负责人行政责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条 &nbsp;市、县（区）人民政府环境卫生主管部门违反本条例规定未履行监督管理职责，有下列情形之一的，由本级人民政府、上级主管部门或者监察机关依法予以处理：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）垃圾处理设施建设项目未纳入专项规划即开展项目建设的；＼</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违反规定批准关闭、闲置或者拆除垃圾处理设施的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）挪用垃圾处理专项经费的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）因监管不力造成影响公众健康和重大环境安全事故发生的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条 &nbsp;县级以上人民政府有关主管部门、执法部门及其工作人员未履行或者未正确履行生活垃圾管理职责的，由本级人民政府、上级主管部门或者监察机关责令改正并通报批评；情节严重的，对负有责任的主管人员和其他责任人员，依法给予处分；涉嫌犯罪的，依法移送司法机关追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条 &nbsp;违反本条例第十条规定，生活垃圾分类管理责任人未履行工作责任的，由市、县（区）人民政府环境卫生主管部门责令限期改正；逾期不改正的，处一千元以上一万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条 &nbsp;违反本条例第八条、第十一条规定，未将生活垃圾分类投放到指定的收集点或者收集容器内，随意倾倒、抛撒、焚烧或者堆放的，由市、县（区）人民政府环境卫生主管部门责令停止违法行为，并对单位处五千元以上五万元以下的罚款，对个人处二百元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第十五条第一款规定，将家庭装修废弃物或者废弃沙发、衣柜、床等大件家具投放到垃圾收集点或者收集容器内的，处二百元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条 &nbsp;违反本条例第十三条第三款、第十九条第一项、第二项、第四项、第二十三条第二款规定，由相关职能部门责令限期改正，可以并处五千元以上五万元以下的罚款；涉嫌犯罪的，依法移送司法机关追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条 &nbsp;违反本条例第十九条第三项规定，在运输过程中沿途丢弃、扬撒、遗漏生活垃圾以及滴漏污水的，由市、县（区）人民政府环境卫生主管部门责令限期改正，并处一万元以上五万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第十九条第五项规定，擅自混合收集、运输已分类的生活垃圾的，由市、县（区）人民政府环境卫生主管部门责令限期改正，并处一千元以上五千元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第十九条第六项规定，擅自收集、运输处理境外和省外垃圾的，由市、县（区）人民政府环境卫生主管部门处五万元以上十万元以下的罚款，情节严重的吊销经营许可证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条 &nbsp;违反本条例第二十一条规定，收集、运输、处置单位未按照有关规定和技术标准处理生活垃圾，或者未按照各项工程技术规范、操作规程处理废水、废气、废渣等的，由市、县（区）人民政府环境卫生主管部门责令限期整改，并处三万元以上十万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条 &nbsp;违反本条例第三十三条规定，新建、改建、扩建建设项目的配套生活垃圾分类、收集、转运设施未达到规划设计要求，或者未与主体工程同时交付使用的，由市、县（区）规划部门责令限期改正，可以并处三万元以上十万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条 &nbsp;违反本条例第三十四条规定，擅自关闭、闲置、拆除生活垃圾集中转运、处置设施的单位，由市、县（区）环境卫生主管部门责令停止违法行为，限期改正，并处一万元以上十万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条 &nbsp;违反本条例第三十七条第一款规定，未按规定缴纳垃圾处理费的，由市、县（区）人民政府环境卫生主管部门责令限期缴纳。逾期不缴纳的，对单位处应当缴纳的垃圾处理费三倍以下不超过五万元的罚款；对个人处应当缴纳的生活垃圾处理费三倍以上不超过一千元的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十条 &nbsp;违反本条例规定，妨碍、阻挠生活垃圾管理监督检查工作的，妨碍环卫工人正常保洁作业的，或者围堵生活垃圾收集、处置设施和运输车辆，阻碍生活垃圾处理设施建设和正常运行的，由公安机关依照《中华人民共和国治安管理处罚法》处理；涉嫌犯罪的，依法移送司法机关追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七章 &nbsp;附 则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十一条 &nbsp;本条例下列用语的含义：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　餐饮垃圾，是指食品生产经营单位、机关、学校、企事业等单位食堂产生的食物残余、食品加工废料、过期食品和废弃食用油脂。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　惰性垃圾，是指生活垃圾中对环境无害的无机组分，包括混凝土、土渣、煤渣、灰渣、砖瓦、碎石块、废砂浆、泥浆等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十二条 &nbsp;本条例自2016年1月1日起施行。2001年10月8日广东省人大常委会颁布的《广东省城市垃圾管理条例》同时废止。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 13:18:26',0,NULL,2,NULL,15,11,'',0,0,0,'2016-05-18 13:24:57','2016-05-18 13:24:57');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (464,'【法规】《汕头市市政设施管理条例》','HexJkL59Mn5EtiHh.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市第十一届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公 告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第 23 号）</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头市市政设施管理条例》已由汕头市第十一届人民代表大会常务委员会第二十三次会议于2006年8月23日通过，2006年9月28日广东省第十届人民代表大会常务委员会第二十七次会议批准，现予公布，自2006年12月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民代表大会常务委员会</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2006年10月18日</span></p><p><br/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市市政设施管理条例</span></strong></p><p><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2006年8月23日汕头市第十一届人民代表大会</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">常务委员会第二十三次会议通过&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2006年9月28日广东省第十届人民代表大会</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">常务委员会第二十七次会议批准）</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 总 则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 为加强城市市政设施管理，保障市政设施完好，充分发挥市政设施的功能，促进城市经济和社会发展，根据有关法律、法规的规定，结合本市实际，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 本条例适用于金平区、龙湖区、濠江区所辖行政区域，澄海区、潮阳区、潮南区的城区，南澳县的县城，中心镇人民政府所在地等城市化管理地区的市政设施管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公路、电力、邮政、电信、广播电视、道路交通安全、消防等社会公共设施的管理，依照有关法律、法规的规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 市政设施管理实行统一规划、配套建设、协调发展，统一领导与分级管理相结合的原则。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 市市政设施管理行政主管部门负责本市的市政设施管理工作，组织实施本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区（县）市政设施管理行政主管部门负责本辖区承担的市政设施管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中心镇人民政府负责本辖区的市政设施管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　规划、国土、建设、公安、人防、水利、环保、交通、信息、电力、公路、海事等部门按照各自的职责权限，协同市政设施管理行政主管部门实施本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 任何单位和个人都有依法使用市政设施的权利和保护市政设施的义务，对违反本条例的行为有权制止或者举报。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章 综合管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 市、区（县）人民政府应当根据社会经济发展状况和城市总体规划，组织编制市政设施发展规划并负责实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施管理行政主管部门应当制定市政设施年度养护、维修、应急处置计划，报同级人民政府批准后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施的养护、维修、应急处置经费应当纳入各级财政年度预算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　依附于市政设施的城市公交、供水、燃气、供电、通信、消防、广告等各种设施的建设计划，应当以近期建设规划为依据，与市政设施各专业规划和年度建设计划相协调，并与城市道路建设同步实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 市政设施管理行政主管部门办理行政许可事项时，应当公开行政许可的事项、依据、条件、数量、需要提交的全部材料目录、申请书示范文本和受理、审查、决定的程序，并在法定期限内作出许可或者不许可的书面决定；不许可的，应当书面向申请人说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 市政设施管理行政主管部门、城市管理行政执法部门或者其他行政管理部门的工作人员实施执法检查或者行政处罚时，应当有两人以上同时进行，并向当事人出示执法证件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 市政设施的管理、养护、维修责任人，按照下列规定确定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）政府投资（含单位或者个人捐资）建设的市政设施，由市政设施管理行政主管部门负责；已经出让经营权的，在经营期内由经营单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）单位或者个人投资建设的市政设施，由产权人或者使用管理人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）正在开发建设或者尚未移交的市政设施，由开发建设单位负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　按照前款规定不能确定责任人的，由市政设施管理行政主管部门会同有关单位研究后报同级人民政府确定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 市政设施的管理、养护、维修责任人应当加强对市政设施的管理、养护和维修，定期组织对市政设施进行检测和普查，并建立健全完整的档案管理制度。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施管理行政主管部门应当加强对市政设施的监督检查，发现问题应当及时养护、维修，或者通知有关责任人养护、维修。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因养护、维修确需对城市道路、桥涵、河道进行交通管制的，市政设施管理行政主管部门应当会同公安机关交通管理部门或者海事管理机构等部门联合向社会发布封路、封桥、封航信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 从事市政设施养护、维修工作的作业服务单位，应当具备相应资质，严格执行有关技术规范和标准，确保养护、维修工程的质量。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　地下市政设施的建设单位或者使用单位应当建立健全安全责任制度，采取可行措施，防止危害人身安全和健康的情况发生。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施管理行政主管部门应当对养护、维修工程的质量进行监督检查，保障市政设施安全、完好。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 市政设施以及设置在市政设施上的检查井、沟井盖等各类附属设施，应当符合有关技术规范和标准；出现破损、移位或者丢失时，有关产权人或者使用管理人应当及时修复、正位或者补缺。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 市政设施工程竣工，建设单位收到市政设施工程竣工报告后，应当组织设计、施工、工程监理等有关单位进行竣工验收。市政设施工程经竣工验收合格备案后，方可交付使用；验收不合格或者未经竣工验收合格备案的，不得交付使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施实行工程质量保修制度，保修期为一年，自交付使用之日起计算，保修期内出现工程质量问题的，由有关责任单位保修。法律、法规对市政设施建设工程质量保修期限另有规定的，按照法律、法规的规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 未经市政设施管理行政主管部门同意和城市规划行政管理部门批准，任何单位和个人不得利用市政设施设置各类管线、杆线或者广告架、灯箱等设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 毗邻市政设施的建设工程，建设施工单位在施工时应当采取必要的保护措施，保证市政设施不受损坏。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">因意外事故造成市政设施损坏的，当事人应当立即向市政设施管理行政主管部门报告，及时采取必要的保护措施。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章 城市道路管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 在城市道路设施管理范围内，禁止下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一） 擅自在道路上建设建筑物、构筑物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二） 擅自改变道路结构；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三） 利用桥梁、涵洞（箱涵）等设施进行牵引、吊装；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四） 撞击损坏桥梁、涵洞（箱涵）等设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五） 在桥梁上架设压力在4公斤／平方厘米(0.4兆帕)以上的燃气管道、10千伏以上的架空高压电力线和其他易燃易爆管线；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六） 在桥梁或者非指定的道路上试刹车，或者在人行天桥上驾驶摩托车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七） 碾压道路边石；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八） 直接在道路上搅拌水泥沙浆、混凝土和其他拌合物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九） 其他侵占、损害城市道路设施的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 履带车、铁轮车或者超重、超高、超长、超宽车辆需要在城市道路行驶的，应当遵守限重、限高、限长、限宽和限速规定，事先征得市政设施管理行政主管部门和公安机关交通管理部门的同意，采取必要的安全防范措施，按照公安机关交通管理部门指定的时间、路线和速度行驶。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 桥梁安全防护应当遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）市政设施管理行政主管部门应当根据桥梁的具体技术特点、结构安全条件和河道的通航条件等情况，会同海事管理机构确定桥梁的安全控制范围并向社会公布；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在桥梁安全控制范围内从事河道疏浚、挖掘、打桩、地下管道顶进、爆破等作业的，在向海事管理机构提出施工许可前，应当经市政设施管理行政主管部门同意，并与桥梁产权人签订保护协议，采取保护措施后，方可施工；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）需要依附桥涵设置各种设施的，应经原桥涵设计部门、桥涵管养部门进行技术安全论证提出意见，并经市政设施管理行政主管部门批准后，方可进行施工。在桥涵扩建、改建、维修时，应当无条件及时拆除、迁移。需跨越桥涵进行施工作业的，须经市政设施管理行政主管部门同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 任何单位和个人不得擅自占用或者挖掘城市道路。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因特殊情况需要占用或者挖掘城市道路的，应当向市政设施管理行政主管部门提出申请，经批准后方可占用；因工程建设等需要挖掘城市道路的，应当持城市规划行政管理部门批准签发的《建设工程规划许可证》和有关设计文件，向市政设施管理行政主管部门申请办理有关批准手续；占用或者挖掘城市道路影响交通安全的，市政设施管理行政主管部门在批准前，应当征得公安机关交通管理部门同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 新建、扩建、改建的城市道路交付使用后五年内、大修的城市道路竣工后三年内不得挖掘；因特殊情况需要挖掘的，应当经规划行政管理部门和市政设施管理行政主管部门同意，报同级人民政府批准；影响交通安全的，市政设施管理行政主管部门审核时，应当征得公安机关交通管理部门同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因紧急抢险、抢修确需挖掘城市道路的，可以先行破路，同时向市政设施管理行政主管部门和公安机关交通管理部门报告，并在挖掘城市道路之日起二十四小时内，补办城市道路挖掘批准手续。逾期不办理批准手续的，按违法挖掘城市道路处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　经批准临时占用、挖掘城市道路的单位和个人，应当遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按批准的时间、范围和要求占用、挖掘，不准擅自变更，不准出租、转让，需要移动位置、扩大面积、延长时间的，应当提前十五日办理变更批准手续；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在占用、挖掘现场醒目处悬挂许可证或者标明许可证批准文号，设置明显的警示标志和安全防护设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）需要对部分车辆限制行驶或临时交通管制的，应当事先报请公安机关交通管理部门批准，并提前五日向社会公告，但紧急情况除外；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）挖掘道路时应当精心组织、文明施工，在主要道路上进行横穿道路施工的，应当在夜间进行，白天应当恢复交通；条件不具备时，应当分车道施工，保障道路单向畅通；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）挖掘道路不得压占检查井、消防栓、雨水口和边沟，挖掘或修复路面，应当按规定的技术要求进行施工；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）挖掘道路遇到测量标志、地下管线、文物保护标志等设施时，应当立即采取保护措施，不得移位、损坏；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）挖掘道路有关工程完成后，应当及时按有关技术要求回填夯实，恢复道路原状；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）占用、挖掘结束后应当及时清理占用现场，拆除临时设施，清除道路上的障碍物，消除安全隐患，并通知市政设施管理行政主管部门和公安机关交通管理部门检查验收；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）因特殊情况批准部门需中止占用道路时，占用单位和个人应当按规定腾退；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）横穿道路施工、铺设地下管线应当优先采用非开挖施工等先进技术。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 经批准占用或者挖掘城市道路的，应当依法向市政设施管理行政主管部门缴纳城市道路占用费或者城市道路挖掘修复费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市道路占用费按照物价行政主管部门核定的标准收取；城市道路挖掘修复费按照省有关规定的标准收取。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经批准临时占用城市道路，属下列情况之一的，可免收城市道路占用费：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）市政工程建设和养护维修项目；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）与城市道路同步建设的供水、燃气、热力、供电、通信、消防等新建工程项目；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）公安交通岗亭、公共汽车站亭牌、环卫设施等非营业性的社会公益事业设施建设和维修作业；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）环境污染治理项目。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章 城市排水设施管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 城市排水设施应当保持完好、畅通，机电设备运转安全正常。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市政设施管理行政主管部门应当定期组织对本辖区内的城市沟渠进行清淤疏浚，保证城市排水设施的畅通和安全。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市排水设施堵塞或者损坏的，其产权单位或者养护单位在发现或者接到报告后，应当及时疏通或者抢修。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水设施跨越厂区、院落的，有关单位应当按照市政设施管理行政主管部门的规定负责养护和疏通。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 在城市排水设施管理范围内，禁止下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）堵塞、破坏、填埋、盗窃城市排水设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）向城市排水设施排放腐蚀性物质、剧毒物质、易燃易爆物质、废气或者废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）将水泥沙浆、混凝土和其他拌合物排入雨水、污水管内；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）搭建各类妨碍排水设施正常使用和安全的建筑物、构筑物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）在沟渠及其保护区范围内采掘沙石土、开荒种地或者堆放物料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）擅自接驳、占压排水设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）擅自将雨水、污水管混接排水；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）其他侵占、损害城市排水设施的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 因工程建设需要拆除、迁移、改动城市排水设施的，应当经市政设施管理行政主管部门批准并组织实施，所需费用由申请人承担。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 排水设施发生故障进行抢修或者维护作业时，沿线有关排水户应当服从市政设施管理行政主管部门的统一调度，采取限制排放量、调整排放时间等措施，确保故障及时清除。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章 城市道路照明设施管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 新建、改建的城市道路照明设施必须符合有关的设计安装规程规定，并积极采用新光源、新技术、新设备及科学的节能方式。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 在城市道路照明设施管理范围内，禁止下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）擅自在城市道路照明设施周围堆放杂物、挖坑取土、搭建建筑物和构筑物，妨碍照明设施正常维护和安全运行；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）擅自接用城市道路照明设施电源；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）擅自占用城市道路照明设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）损坏城市道路照明设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）盗窃城市道路照明设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）其他侵占、损害城市道路照明设施的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 因工程建设需要拆除、迁移、改动城市道路照明设施的，应当经市政设施管理行政主管部门批准并组织实施，所需费用由申请人承担。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 市政设施管理行政主管部门应当建立健全检查、考核制度，及时督促有关单位更换和修复破损的照明设施，使亮灯率不低于百分之九十五。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章 法律责任</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条　本市实施城市管理相对集中行政处罚权的区域，由城市管理行政执法部门依法实施行政处罚和行政强制措施；其他区域由市政设施管理行政主管部门或者有关行政管理部门依法实施行政处罚和行政强制措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　法律、法规对违反本条例行为的法律责任另有规定的，按照法律、法规的规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 违反本条例第十三条规定，设置在市政设施上的检查井、沟井盖等各类附属设施出现破损、移位或者丢失，有关产权人或者使用管理人未及时修复、正位或者补缺的，责令限期改正；逾期未改正的，代为修复、正位或者补缺，所需费用由违法行为人承担，并可处五百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条 违反本条例第十五条规定，擅自利用市政设施建设各类管线、杆线或者设置广告架、灯箱等设施的，责令限期改正；逾期未改正的，强制停止建设或者强制拆除，所需费用由违法行为人承担，并可处五百元以上五千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条 违反本条例第十六条规定，建设施工单位在毗邻市政设施的建设工程施工时没有采取必要的安全防范措施的，责令限期改正；逾期未改正的，处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条 违反本条例第十七条规定，有下列情形之一的，责令限期改正；逾期未改正的，按照以下规定处理：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）违反第（一）项规定，擅自在道路上建设建筑物、构筑物的，强制停止建设或者强制拆除，所需费用由违法行为人承担，并可处三百元以上三千元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违反第（二）项至第（五）项规定,擅自改变道路结构，或者利用桥梁、涵洞（箱涵）等设施进行牵引、吊装，撞击损坏桥梁、涵洞（箱涵）等设施，或者在桥梁上架设压力在4公斤／平方厘米(0.4兆帕)以上的燃气管道、10千伏以上的架空高压电力线和其他易燃易爆管线的，处五百元以上二千元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）违反第（六）项至第（九）项规定，在桥梁或者非指定的道路上试刹车，或者在人行天桥上驾驶摩托车，或者碾压道路边石，或者直接在道路上搅拌水泥沙浆、混凝土和其他拌合物，或者从事其他侵占、损害城市道路设施行为的，处二百元以上五百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条 违反本条例第十八条规定，履带车、铁轮车或者超重、超高、超长、超宽车辆擅自在城市道路行驶的，责令停止行驶，并可处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 违反本条例第十九条第二（项）规定，擅自在桥梁施工控制范围内从事河道疏浚、挖掘、打桩、地下管道顶进、爆破等作业的，责令限期改正，并可处一万元以上二万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 违反本条例第二十条第一款、第二十一条第二款规定，擅自占用或者挖掘道路，或者挖掘道路不办理批准手续的，责令限期改正，并可处一千元以上五千元以下罚款。违反第二十二条第（一）项的，责令限期改正，并可处五百元以上三千元以下罚款；违反第二十二条第（二）至第（十）项规定之一的，责令限期改正，并可处一千元以上五千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 违反本条例第二十三条规定，未依法缴纳城市道路占用费或者城市道路挖掘修复费的，责令限期缴纳；逾期不缴纳的，可按日加收千分之二的滞纳金。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 违反本条例第二十五条规定，实施侵占、损害城市排水设施的行为的，责令限期改正，并可处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条 违反本条例第二十九条规定，实施侵占、损害城市道路照明设施的行为的，责令限期改正，并可处二百元以上一千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条 违反本条例第二十六条、第三十条规定，未经市政设施管理行政主管部门批准，擅自拆除、迁移、改动城市排水设施或者城市道路照明设施的，责令限期恢复原状或者采取其他补救措施，并可处五百元以上三千元以下罚款；逾期未恢复原状或者未采取其他补救措施的，代为恢复原状或者采取其他补救措施，所需费用由违法行为人承担。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条 违反本条例规定或者因意外事故等原因造成市政设施损坏的，应当予以修复或者按修复工程造价给予赔偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条　因市政设施管理、养护、维修失职造成人身伤害、财产损失、环境污染的，有关责任单位应当承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条 市政设施管理行政主管部门、城市管理行政执法部门或者其他行政管理部门及其工作人员有下列情形之一的，给予行政处分；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）对应当受理的事项或者举报不受理的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）对应当制止或者处罚的违法行为不制止、不处罚的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）包庇、纵容违法行为人的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）办理行政许可事项、实施执法检查或者行政处罚不符合法定程序的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）利用职权收受他人财物或者谋取其他利益的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）其他滥用职权、徇私舞弊或者不履行职责的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条 当事人对市政设施管理行政主管部门、城市管理行政执法部门或者其他行政管理部门作出的具体行政行为不服的，可以依法申请行政复议或者提起行政诉讼。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条 阻碍市政设施管理行政主管部门、城市管理行政执法部门或者其他行政管理部门的管理人员、执法人员执行公务，违反《中华人民共和国治安管理处罚法》的，由公安机关依法处罚；构成犯罪的，依法追究刑事责任。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七章 附则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条 本条例下述用语的含义为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）“市政设施”，包括城市道路、城市排水设施和城市道路照明设施及其他设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）“城市道路”，是指城市供车辆、行人通行的具备一定技术条件的道路、桥梁及其附属设施，包括机动车道、非机动车道、人行步道、地下通道、隔离带、绿化带、路肩、路坡、广场、公共停车站场、桥梁（含立交桥、人行天桥、高架桥）、涵洞（箱涵）、隧道等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）“城市排水设施”，是指接纳、输送城市雨水和污水的市政管网、沟渠、出水口、下水道检查井、雨水井、沟井盖、泵站、启闭器等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）“城市道路照明设施”，是指用于城市道路（含里巷、住宅小区、桥梁、隧道、广场、公共停车站场）、不售票的公园和公共绿地等处的路灯配电室、变压器、配电箱、灯杆、灯具、地上地下管线、工作井以及照明附属设备等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）“中心镇”，是指根据省规定标准和程序核准为中心镇的建制镇；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六） “以上”、“以下”，均包括本数。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条 高新区、保税区、风景名胜区和中心镇以外其他建制镇人民政府所在地的市政设施的管理，参照本条例执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条 本条例自2006年 12 月1日起施行。</span></p>','2016-05-18 13:25:09',0,NULL,2,NULL,35,11,'',0,0,0,'2016-05-18 13:33:41','2016-05-18 13:33:41');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (465,'【法规】《汕头经济特区城市绿化条例》','EAnGDTxQJSED45IG.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>汕头市第十二届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公　告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第28号）</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头经济特区城市绿化条例》已由汕头市第十二届人民代表大会常务委员会第三十七次会议于2011年10月31日通过，现予公布，自2012年1月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民代表大会常务委员会</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2011年10月31日</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><br/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头经济特区城市绿化条例</span></strong></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2011年10月31日汕头市第十二届人民代表大会常务委员会第三十七次会议通过）</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章　总　则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为发展城市绿化事业，保护和改善生态环境，建设宜居城市，促进生态文明建设，根据有关法律、行政法规的基本原则，结合汕头经济特区（以下简称特区）实际，制定本条例。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　本条例适用于城市、镇规划区范围内绿化的规划、建设、保护及其相关的管理活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">法律、行政法规对森林、古树名木、公园、自然保护区、风景名胜区有规定的，适用其规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条　城市绿化工作应当遵循生态、景观、文化统一协调和节约资源的原则，坚持以人为本、生态优先、合理保护、科学布局，实现社会效益、环境效益和经济效益相统一。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条　市、区（县）人民政府应当将城市绿化纳入本级国民经济和社会发展规划，确定本行政区域绿化目标，实行绿化目标责任制，并保障城市公共绿地建设和养护的资金投入。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条　市、区（县）城市绿化行政主管部门负责本行政区域内城市绿化工作。镇人民政府和街道办事处按照职责做好本辖区内的城市绿化管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">特区范围内实施城市管理相对集中行政处罚权的区域，由城市管理行政执法部门依法实施行政处罚和行政强制措施；其他区域由城市绿化行政主管部门依法实施行政处罚和行政强制措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">发展和改革、城乡规划、国土资源、财政、价格、环境保护、住房和城乡建设、农（林）业、水务、交通运输、公安、工商等行政管理部门，依照各自职责协同做好城市绿化相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条　单位和有劳动能力的适龄公民，应当积极参加全民义务植树活动，履行绿化城市和保护绿化的义务。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">鼓励单位和个人通过投资、捐资、认种、认养等形式，参与城市绿化建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">鼓励开展绿化科学研究，推广应用绿化先进技术，倡导低碳环保绿色理念，优化植物配置，保护生物多样性，促进绿化科技成果的转化运用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　任何单位和个人都有权制止、投诉和举报损害城市绿化、破坏生态环境的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条　各级人民政府对在城市绿化工作中做出显著成绩的单位和个人应当给予表彰、奖励。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章　规划与建设</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条　城市总体规划应当安排与城市性质、规模和发展需要相适应的绿化用地面积。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条　市城市绿化行政主管部门应当会同市城乡规划行政管理部门，依据城市总体规划编制城市绿地系统规划，经市人民政府批准后实施，并在批准后三十日内报市人民代表大会常务委员会备案。经批准的城市绿地系统规划确需变更的，必须按照原批准程序重新审批和备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市绿地系统规划应当符合环境保护功能、国家有关标准和本土特点，明确城市绿化目标、规划布局、各类绿地的面积和控制原则，重点加强道路和铁路两侧、海边、江（河）边、湖边及城（镇）区周边绿化带的建设，合理设置公园绿地、生产绿地、防护绿地、附属绿地和其他绿地，并满足防灾避险功能。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条　市城市绿化行政主管部门应当会同有关部门，依据城市绿地系统规划编制城市绿地详细规划，报市人民政府批准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条　城市绿地系统规划和城市绿地详细规划应当向社会公布，接受公众监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条　各区（县）人民政府应当根据城市绿地系统规划、城市绿地详细规划和本辖区区域规划编制本区（县）城市绿地规划，并报市城市绿化行政主管部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">镇的城市绿地规划，由镇人民政府组织编制，纳入区（县）城市绿地规划，报区（县）人民政府批准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条　市城乡规划行政管理部门应当会同市城市绿化行政主管部门，根据城市绿地系统规划，对已建成的城市绿地和规划预留的城市绿地划定城市各类绿地范围的控制线（以下简称绿线）。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市各类绿地实行绿线控制，依法划定的绿线应当在三十日内向社会公布，接受公众监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">绿线内的用地不得改作他用，不得进行与绿化不相关的建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　绿线不得擅自调整。因城乡建设确需调整的，城乡规划行政管理部门应当征求城市绿化行政主管部门的意见，并按照规划审批权限报原审批机关批准。调整绿线不得减少规划绿地的总量，因调整绿线减少的规划绿地面积，应当予以补充落实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市绿化行政主管部门应当建立绿线档案。城乡规划行政管理部门应当在绿线调整后十日内将调整结果告知城市绿化行政主管部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条　公园绿地周边新建建设项目，应当与绿地的景观相协调，并不得影响植物的正常生长。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城乡规划行政管理部门在编制控制性详细规划时，应当会同同级城市绿化行政主管部门在公园绿地周边划定一定范围的控制区，控制区内建筑物、构筑物的高度、布局应当与公园绿地景观相协调。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条　城市绿化的苗圃、花圃、草圃、盆景基地等生产绿地的规划，应当适应城市园林绿化建设和发展的需要，其用地面积不得低于城市建成区面积的百分之二。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条　鼓励发展垂直绿化、屋顶绿化、桥梁绿化和绿荫停车场等多种绿化形式。屋顶绿化、绿荫停车场的面积可按百分之二十五的比例折算为建设项目的绿地面积，但折算面积的总和不得超过该建设项目绿地面积的百分之五。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">鼓励单位和个人在办公区域、工矿区域、居住小区和私家庭院进行绿化，美化环境，但不得危及公共安全和损害他人的合法权益。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条　绿地的建设指标应当达到或者超过《国家园林城市标准》的各项指标。城市建成区绿化覆盖率不得低于百分之四十五，绿地率不得低于百分之三十八，人均公园绿地面积不得低于十二平方米。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条　建设项目必须配套绿化用地，绿化用地面积占建设工程总用地面积的比例，应当符合下列标准：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）医院、休（疗）养院（所）等医疗卫生单位不得低于百分之四十五，属于旧城改造区的不得低于百分之四十；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）高等院校不得低于百分之四十，其他学校、机关团体等单位不得低于百分之三十五；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）经环境保护行政管理部门鉴定属于有毒有害的重污染单位和危险品仓库不得低于百分之四十，并根据国家标准设置宽度不少于五十米的防护林带；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）宾馆、商业、商住、体育场（馆）、文化娱乐场所等大型公共建筑设施，应当进行环境设计，建筑面积在二万平方米以上的，不得低于百分之四十；建筑面积在二万平方米以下的，不得低于百分之三十；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）新建居住小区（含商住综合小区）不得低于百分之三十五，属于旧城改造项目的不得低于百分之三十；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）工业小区、工业企业、交通运输站场及其仓库设施不得低于百分之二十；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）城市干道不得低于百分之二十五；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）其他建设工程项目不得低于百分之三十。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条　确因条件限制，建设项目的绿化用地面积达不到本条例第二十条规定标准的，经城市绿化行政主管部门会同城乡规划行政管理部门审核，报同级人民政府批准，由建设单位承担补偿责任，按照所缺少的绿化用地面积向城市绿化行政主管部门交纳绿化补偿费，由城市绿化行政主管部门按照规划专项用于易地绿化建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　城市绿化工程建设应当符合国家、省和特区有关标准和规范。从事绿化工程设计、施工、监理的单位，应当具备相应的资质。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条　建设项目应当按规定的绿地率要求配套绿化建设资金，并在工程项目建设投资中统一安排，其比例应当占工程项目土建投资的百分之一至百分之五。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">市城市绿化行政主管部门应当会同有关部门，制定建设项目配套绿化建设资金标准及其管理办法，报市人民政府批准后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　具备绿化条件的建设用地和建设项目，半年内不能开工建设的，土地使用权人和建设单位应当按照城市绿化行政主管部门的要求及期限，进行简易绿化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市内应当绿化而没有绿化的裸露空地，由所在地人民政府组织有关部门明确绿化责任，限期绿化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条　建设项目的配套绿化工程应当与主体工程同时设计、同时施工、同时验收、同步交付使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设项目配套绿化工程的设计方案由城市绿化行政主管部门审查同意后，方可办理建设工程报建手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设项目配套绿化工程竣工验收时，应当有城市绿化行政主管部门参加。建设单位应当在办妥竣工验收备案手续三十日内，将配套绿化工程的有关建设资料移交城市绿化行政主管部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章　管理和保护</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条　城市绿地的管理和保护，按照下列规定分工负责：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）政府投资建设的公园、广场绿地等城市公园绿地、城市道路附属绿地、防护绿地及其附属设施，由市、区（县）城市绿化行政主管部门按照职责权限或者其委托的城市绿化专业管理机构负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）单位和个人投资建设的城市绿地，由产权人或者使用管理权人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）居住区的城市绿地，实施物业管理服务的，由物业管理单位负责；未实施物业管理服务的，由所属街道办事处负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）生产绿地，由其经营单位和个人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）铁路、公路沿线两侧、江河两岸、水库周围等绿地，由各有关主管部门或者单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）建设工程范围内保留的绿地，施工期间由建设单位负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　权属不明的绿地，由所在区（县）城市绿化行政主管部门或者其委托的单位负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市绿化行政主管部门应当对各管理责任单位和个人的绿地管理和保护进行检查、监督和指导。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条　城市树木和城市绿地的认种、认养不得改变其产权关系。认种、认养的单位和个人　　不得以任何理由在其认种、认养的绿地内建设建（构）筑物，不得改变绿地的性质和功能。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　捐资、认种、认养的单位或者个人可以享有绿地、树木一定期限的冠名权。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市树木的所有权及其收益按照国家、省的有关规定确定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条　城市绿地建设和养护责任单位应当按照城市绿化建设和养护管理技术规范进行建设和养护管理。树木、灌木、地被植物受损或者死亡的，由养护责任人及时修护、补种或者更换。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条　任何单位和个人不得擅自改变城市绿地系统规划用地的性质或者破坏城市绿地系统规划用地的地形、地貌、水体和植被。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条　任何单位或者个人不得擅自占用城市绿地。已占用的必须限期归还，并恢复城市绿地的使用功能。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因城市基础设施建设确需占用城市绿地的，应当征得城市绿化行政主管部门同意，按照规定程序报城乡规划行政管理部门审批，并由城乡规划行政管理部门按照调整城市规划的原则，补偿同等面积和质量的绿地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市绿化行政主管部门和城乡规划行政管理部门应当向社会公布依法占用城市绿地的程序与结果，接受公众监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条　因城市建设或者其他特殊情况需要临时占用城市绿地的，应当向城市绿化行政主管部门提出申请。城市绿化行政主管部门应当自受理申请之日起五日内作出决定。经批准临时占用城市绿化行政主管部门管理的城市绿地的，占用者应当缴纳临时占用绿地费，并到城乡规划、国土行政管理部门办理手续。因紧急抢险救灾，可先予临时占用，并在占用后二日内按规定补办手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　临时占用城市绿地的期限一般不超过两年。确因建设需要延长的，应当办理延期手续，延期最长不超过一年。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　批准占用绿地的审批机关、批准时间、占用期限和范围应当在现场公示，接受公众监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　占用期满后，占用者应当及时清场退地，并自行恢复绿化；逾期不恢复绿化的，按照恢复绿地实际费用缴纳恢复绿化补偿费，由城市绿化行政主管部门组织恢复绿地。临时占用城市绿地造成相关设施损坏的，占用者应当承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条　申请临时占用城市绿地的，按照下列规定审批：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）同一建设项目占用城市绿地七千平方米以上的，由市城市绿化行政主管部门受理并报省建设行政管理部门审核后，由省人民政府批准；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）同一建设项目占用城市绿地一千五百平方米以上七千平方米以下的，由市城市绿化行政主管部门审核，报市人民政府批准；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）同一建设项目占用城市绿地一千平方米以上一千五百平方米以下的，由市城市绿化行政主管部门审批；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）同一建设项目占用城市绿地一千平方米以下的，属市级管理的城市绿地，由市城市绿化行政主管部门审批；属区（县）级管理的城市绿地，由所在区（县）城市绿化行政主管部门审批，报市城市绿化行政主管部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条　任何单位和个人不得损坏城市树木花草和绿化设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有下列情形之一，确需修剪、迁移或者砍伐城市树木的，应当先征求树木权属单位或者个人的意见，并向城市绿化行政主管部门提出申请。有关法律法规另有规定的，从其规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）城市建设需要的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）电力、市政、交通、广播电视、供水、燃气和通信等部门或者单位因施工、维护城市基础设施安全需要的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）严重影响相邻建筑物采光、通风、通行和居民生活安全的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）发生检疫性或新传入的危险性病虫害的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）其他符合法定条件的情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">因紧急抢险救灾确需修剪、迁移或者砍伐城市树木的，可以先行实施修剪、迁移或者砍伐，并及时报告城市绿化行政主管部门和树木权属单位或者个人，在险情排除后二日内按照本条例第三十四条的规定补办审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条　申请迁移、砍伐城市树木的，按照下列规定审批：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）迁移、砍伐二百株以上树木的，由市城市绿化行政主管部门审核，报市人民政府批准；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）迁移、砍伐二十株以上二百株以下或者胸径八十厘米以上树木的，由市城市绿化行政主管部门审批；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）迁移、砍伐二十株以下或者胸径八十厘米以下树木的，属市级管理的树木，由市城市绿化行政主管部门审批；属区（县）级管理的树木，由所在区（县）城市绿化行政主管部门审批，并报市城市绿化行政主管部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　按照前款规定申请迁移、砍伐城市树木的，申请材料应当包括树木权属单位或者个人的书面意见或者双方签订的协议书；申请迁移、砍伐十株以上或者胸径八十厘米以上城市树木的，申请材料还应当包括当地相关居民的意见和绿化专家评审论证结论。经批准迁移、砍伐城市树木，应当给树木权属单位或者个人合理补偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请修剪城市树木的，由城市绿化行政主管部门审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条　城市绿化行政主管部门审批前，应当将迁移、砍伐原因和株数在现场公示，接受公众监督；必要时，应当组织专家进行论证或召开听证会，听取社会公众意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市绿化行政主管部门应当在受理修剪、迁移或者砍伐树木申请之日起七日内作出许可决定；不予许可的，应当书面说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条　经依法批准修剪、迁移或者砍伐城市树木的，可以由城市绿化专业管理机构或者具有城市园林绿化资质的企业实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因城市基础设施建设、改造、维护等影响城市绿化的，城市基础设施的使用或者管理单位必须在设计和施工前制定保护措施，经城市绿化行政主管部门同意后方可进行施工。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因生产、建设、交通事故造成树木、绿地及其绿化设施损坏的，责任人应承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条　建设单位和施工单位应当对施工现场内的城市绿地、城市树木以及绿化设施等进行保护，不得损坏。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条　禁止下列损坏城市绿化及其设施的行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）践踏、损坏树木花草；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在树木上悬挂或者张贴广告、标语；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）在树木或绿化设施上涂、写、刻、画和悬挂重物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）在城市绿地内设置摊点或者进行经营活动；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）在城市绿地内采石取土、乱搭建；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）在城市绿地内倾倒垃圾、有害废渣废水、油类或者堆放、焚烧物料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）破坏树木支架、绿化带栏杆、绿篱、花基、坐椅、庭园灯、园林小品、景石、公益指示标志、水景设施和绿化供排水等设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）损坏绿化的娱乐活动；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）其他损坏城市绿化或者绿化设施的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条　除紧急抢险救灾外，修剪、迁移或者砍伐城市树木以及临时占用城市绿地的，施工单位应当在现场设立公示牌，向社会公示，接受公众监督。公示期自施工之日起至完工之日止；公示牌应当注明批准机关、批准项目、批准期限、施工单位、施工负责人及监督电话等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条　城市绿化行政主管部门应当建立绿化植物疫病疫情监测预报网络，编制疫害事件应急预案，禁止使用带有检疫性或危险性有害生物的植物进行绿化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条　市、区（县）城市绿化行政主管部门或者其委托的城市绿化专业管理机构应当按照管理权限，组织对辖区内的古树名木进行调查鉴定、定级登记、建立档案、设置标志、划定保护范围。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条　禁止擅自迁移、砍伐或者买卖古树名木。因公益性市政建设确需迁移古树名木的，由省建设行政管理部门审核，报省人民政府批准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经批准迁移古树名木的，属单位负责养护管理的，应当制定技术方案，并承担所需费用，由城市绿化专业管理机构执行；属个人负责养护管理的，由城市绿化行政主管部门组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条　绿化补偿费、临时占用绿地费和恢复绿化补偿费按照价格行政管理部门核定的标准收取。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、区（县）城市绿化行政主管部门按照规定收取的绿化补偿费、临时占用绿地费和恢复绿化补偿费，纳入本级财政管理，实行收支两条线，列入各级城市绿化专项资金，专款专用，由财政部门监督使用，并向社会公示，接受公众监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章　法律责任</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条　违反本条例第二十条、第二十一条规定，建设项目绿化用地面积占建设工程总用地面积的比例达不到规定或者批准标准的，责令停工，限期整改；逾期仍达不到规定或者批准标准的，按照相差面积处以所在区域基准地价五至十倍的罚款。未缴纳绿化补偿费的，责令补缴绿化补偿费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条　违反本条例第二十二条规定，不具备相应资质，擅自从事城市绿化工程设计、施工、监理的，责令停止，没收违法所得，并分别对项目建设单位、设计单位、施工单位处以一万元以上三万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条　违反本条例第二十九条规定，擅自改变规划绿地性质的，责令停止侵害、恢复原状，并按照每平方米处以五百元以上一千元以下罚款；不能恢复绿化用地，或者造成绿化功能损失的，处以应缴绿化补偿费二倍的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条　违反本条例第三十条规定，擅自占用城市绿地的，责令限期退出、恢复原状，并按照每平方米处以三百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十一条规定，临时占用城市绿地不缴纳临时占用绿地费的，责令限期缴纳；超过临时占用期限的，责令限期归还，并按照所占面积处以临时占用绿地费二倍的罚款。逾期不缴纳恢复绿化补偿费的，责令限期缴纳；损坏绿化相关设施的，责令限期赔偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条　违反本条例第三十三条、第三十四条第三款规定，擅自修剪树木的，按照下列规定处以罚款：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）树枝直径为本株树木胸径百分之三十以下的，按照树枝直径大小处以罚款：树枝四厘米以下的，按照每枝八十元至一百元处以罚款；树枝四厘米以上八厘米以下的，按照每枝一百二十元至二百五十元处以罚款；树枝八厘米以上的，按照每枝五百元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）树根直径在四厘米以下的，未造成树木倒伏危险的，按照每根二百元至五百元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）超出上述范围的，按照修剪前树木原价值的三倍处以罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条　违反本条例第三十三条、第三十四条第一、二款规定，擅自迁移、砍伐城市树木或者损坏城市树木花草的，按照下列规定处以罚款，但罚款总额最高不得超过树木砍伐或者迁移前价值的五倍：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）迁移或者砍伐胸径十厘米以下乔木的，按照砍伐或者迁移前价值的三倍处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）迁移或者砍伐胸径十厘米以上二十厘米以下乔木的，按照每株二千元至六千元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）迁移或者砍伐胸径二十厘米以上乔木的，按照每株六千元至一万元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）迁移或者砍伐灌木的，按照每丛一百元至六百元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）损坏单排绿篱的，按照每米三百元至五百元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）损坏草坪的，按照每平方米一百元处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）损坏混栽花坛和花草的，按照每平方米二百元处以罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条　违反本条例第三十八条规定，实施损坏城市绿化及其设施的行为的，予以警告，责令停止侵害，恢复原状，并按照下列规定予以罚款；造成损失的，应当赔偿损失：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）违反第（一）项规定的，处以一百元以上五百元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违反第（二）、（三）、（五）、（六）项规定的，处以五百元以上一千元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）违反第（四）项规定的，处以一千元以上五千元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）违反第（七）项规定的，按照设施造价或者破坏实际价值的二倍处以罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）违反第（八）项规定的，对组织者处以二千元以上五千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条　违反本条例第四十二条规定，擅自迁移、砍伐或者买卖古树名木的，按照下列规定予以处罚：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）擅自迁移、砍伐古树名木的,处以四万元以上六万元以下罚款;损害古树名木致死的,处以六万元以上十万元以下罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）擅自买卖古树名木的，没收违法所得，并按照古树名木价值的五倍处以罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条　当事人对行政处罚决定不服的，可以依法申请行政复议或者提起行政诉讼。逾期不申请行政复议，也不提起行政诉讼，又不履行行政处罚决定的，由作出行政处罚决定的部门申请人民法院强制执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条　城市绿化行政主管部门、有关行政管理部门及其工作人员违反本条例规定，有下列行为之一的，对直接负责的主管人员和其他直接责任人员依法给予行政处分：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）未按照规定的条件、程序和期限实施行政许可、行政审批的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）未按照规定划定和管理本市城市绿线的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）发现违法行为不及时查处，造成后果的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）违法实施行政处罚的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）索取、收受他人财物，或者谋取其他利益的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）法律、法规规定的其他违法行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条　违反本条例规定，触犯治安管理法律法规的，由公安机关依法处理；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条　违反本条例规定，按照规划、建设、环境保护等法律、法规的规定应当给予行政处罚的，由各有关部门依法给予处罚。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章　附　则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条　本条例所称城市绿地，是指已建成和在建的绿地以及规划确定的绿地，包括：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）公园绿地，是指向公众开放，以游憩为主要功能，兼具生态、美化、防灾等作用的绿地，包括综合性公园、社区公园、专类公园、带状公园和街旁绿地等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）生产绿地，是指为城市绿化提供苗木、花草、种子的圃地；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）防护绿地，是指城市中具有卫生、隔离和安全防护功能的绿地，包括卫生隔离带、道路防护绿地、城市高压走廊绿带、防风林、城市组团隔离带等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）附属绿地，是指城市建设用地中绿地之外各类用地中的附属绿化用地，包括居住绿地（城市居住用地内社区公园以外的绿地）、公共设施用地内的绿地、工业用地内的绿地、仓储用地内的绿地、道路广场绿地（包括城市干道绿地）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）其他绿地，是指位于城市建设用地以外，对城市生态环境质量、居民休闲生活、城市景观和生物多样性保护有直接影响的绿地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例所称绿化覆盖率，是指在一定区域范围内绿化覆盖面积占区域总面积的比例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例所称绿地率，是指在一定区域范围内园林绿地面积占区域总面积的比例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例所称古树名木，是指百年以上树龄的树木，稀有珍贵的树木，具有历史价值或者重要纪念意义的树木。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条　本条例所称的“以上”包括本数，“以下”不包括本数。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例规定按照“米”、“平方米”计算罚款或者赔偿责任的，不足一米的按一米计算；不足一平方米的按一平方米计算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条　城市、镇规划区外的村庄可以根据当地实际情况科学布局绿化用地，改善生态环境，提高绿化水平。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条　本条例自2012年1月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 13:34:45',0,NULL,2,NULL,22,11,'',0,0,0,'2016-05-18 14:32:26','2016-05-18 14:33:04');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (466,'【法规】《汕头市城市市容环境卫生管理条例》','YkG9Wvndme0RTyvz.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市第十一届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公 告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第 17 号）</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头市城市市容环境卫生管理条例》已由汕头市第十一届人民代表大会常务委员会第十六次会议于2005年8月26日通过，2005年9月23日广东省第十届人民代表大会常务委员会第二十次会议批准，现予公布，自2005年12月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民代表大会常务委员会</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2005年9月30日</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><br/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市市容环境卫生管理条例</span></strong></p><p><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2005年8月26日汕头市第十一届人民代表大会</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">常务委员会第十六次会议通过</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2005年9月23日广东省第十届人民代表大会常</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">务委员会第二十次会议批准）</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 总　则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为加强城市市容环境卫生管理，维护清洁、优美、文明、和谐的城市环境，保障市民身体健康，根据有关法律、法规的规定，结合本市实际，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　本条例适用于金平区、龙湖区、濠江区所辖行政区域，澄海区、潮阳区、潮南区的城区，南澳县的县城，中心镇人民政府所在地以及高新区、保税区、独立工业（科技）园区、风景旅游区等城市化管理地区。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 城市市容环境卫生管理工作遵循统一领导、分级管理、部门协调、公众参与、社会监督的原则。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 市市容环境卫生行政主管部门负责本市的市容环境卫生管理工作，组织实施本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区（县）市容环境卫生行政主管部门负责本辖区的市容环境卫生管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　街道办事处、镇人民政府负责本辖区的市容环境卫生管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本市实施城市管理相对集中行政处罚权的区域，由城市管理行政执法部门依法实施行政处罚和行政强制措施；其他区域由市容环境卫生行政主管部门或者有关部门依法实施行政处罚和行政强制措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　规划、国土、环保、建设、公安、交通、工商、卫生、房管等部门按照各自职责，协同实施本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 各级人民政府应当加强对市容环境卫生工作的领导、组织、协调和监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">市、区（县）人民政府应当将城市市容环境卫生事业纳入国民经济和社会发展计划，完善市容环境卫生设施，提供市容环境卫生公共服务，保障市容环境卫生事业建设和发展所需的资金。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 市市容环境卫生行政主管部门应当根据本市市容环境卫生事业发展的需要，会同有关部门编制市容环境卫生事业发展规划，经市人民政府批准纳入城市总体规划后组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 市容环境卫生行政主管部门和文化、广播电视、新闻出版、教育、卫生等部门以及机场、车站、码头、旅游景点等公共场所的产权人或者经营管理者，应当加强市容环境卫生的宣传教育，增强市民的市容环境卫生意识。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　广播、电视、报刊和户外广告应当有市容环境卫生方面的公益性宣传内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 任何单位和个人都应当维护市容环境卫生，爱护公共设施，尊重市容环境卫生工作人员的劳动，对违反本条例的行为有权制止或者举报。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市容环境卫生行政主管部门和城市管理行政执法部门可以聘请市容环境卫生义务监督员。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市容环境卫生行政主管部门和城市管理行政执法部门应当建立市容环境卫生举报制度，对举报应当及时调查处理，并为举报人保密；举报人要求答复的，应当自受理之日起五个工作日内答复举报人。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 各级人民政府对在市容环境卫生工作中成绩显著的单位和个人应当予以表彰和奖励。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章　市容管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 市市容环境卫生行政主管部门应当会同有关部门，根据国家城市容貌标准，结合本市实际，制订本市的城市容貌标准，经市人民政府批准后公布实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建筑物、构筑物和其他设施、公共场所的容貌，必须符合城市容貌标准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 建筑物、构筑物和其他设施应当保持整洁、完好、美观，并与周围环境相协调。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在建筑物外墙上安装的空调器、排水管、排气扇、排油烟管、遮阳棚等设施，应当保持安全、整洁、完好、美观。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市主要道路两侧临街建筑物、构筑物和其他设施的产权人或者使用管理人应当按照城市容貌标准，定期对建筑物、构筑物和其他设施的外部进行清洗或者粉刷、修饰。违反规定的，责令限期改正；逾期未改正的，由主管部门组织市容环境卫生作业服务单位代为清洗或者粉刷、修饰，所需费用由违法行为人承担。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 城市主要道路两侧临街建筑物的门前、阳台、窗户、屋顶、遮阳物、外墙应当保持整洁、美观，不得在门前、阳台外、窗外、屋顶、外墙等处堆放、架设、吊挂有碍市容观瞻的物品。违反规定的，责令改正；拒不改正的，处五十元以上二百元以下罚款。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　禁止在城市道路及其他公共场所的护栏、路牌、电杆、路灯杆、树木、绿篱等设施上吊挂、设置、晾晒物品。违反规定的，责令限期清理、拆除；逾期不清理、不拆除的，强制清理、拆除，并可处二十元以上五十元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 城市主要道路两侧临街的新建建筑物应当按照规划要求选用透景、半透景的围墙、栅栏或者绿篱、花坛（池）、草坪等作为分界。违反规定的，责令限期改建或者拆除。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　透景、半透景围墙、栅栏内外或者绿篱、花坛（池）、草坪等应当保持整洁、美观。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 城市主要道路两侧及其他公共场所周围商场、门店的经营者不得超出经营场所的门、窗、外墙经营。违反规定的，责令改正；拒不改正的，处二百元以上一千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　任何单位和个人不得擅自占用城市道路及其他公共场所堆放物品、设摊经营、兜售商品。违反规定的，责令改正；拒不改正的，处二百元以上一千元以下罚款，并可暂扣物品和有关工具，要求当事人到指定地点接受处理。当事人接受处理的，暂扣的物品和工具应当及时发还。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经批准临时占用城市道路及其他公共场所堆放物品、设摊经营的，应当在规定的时间和范围内进行，并保持周围市容环境卫生整洁。违反规定的，责令改正；拒不改正的，处五十元以上五百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 任何单位和个人不得擅自占用城市道路及其他公共场所举行促销、展览、文化、体育、节庆等活动。违反规定的，责令限期改正；逾期未改正的，处一千元以上五千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经批准临时占用城市道路及其他公共场所举行促销、展览、文化、体育、节庆等活动的，应当在规定的时间和范围内进行，并保持周围市容环境卫生整洁，及时清除产生的废弃物和临时搭建的设施。违反规定的，责令改正；拒不改正的，由主管部门组织市容环境卫生作业服务单位代为清理、拆除，所需费用由违法行为人承担，并可处五百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 在公共场所利用条幅、彩旗、气球、充气式装置、实物造型等载体形式设置标语、宣传品的，应当经主管部门批准并遵守以下规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按照批准的范围、地点、数量、规格、内容和期限设置；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）保持整洁美观、字迹清晰，无破损、无残缺，到期及时拆除。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反规定的，责令限期清理、拆除；逾期不清理、不拆除的，强制清理、拆除，并可处一百元以上一千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　任何单位和个人不得擅自在城市道路及其他公共场所散发、悬挂经营性宣传物品。违反规定的，责令改正；拒不改正的，处五十元以上二百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 任何单位和个人不得在城市道路两侧和其他公共场所搭建影响市容环境卫生的临时建筑物、构筑物或者其他设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因建设等特殊需要，经批准在城市道路两侧和其他公共场所设置候车亭、岗亭、报刊亭、电话交接箱、箱式变电站、有线电视端子箱或者其他设施的，设置单位应当保持设置物的完好和整洁。出现破损或者丢失的，产权人或者使用管理人应当及时维修、更换、清洗或者补设。违反规定的，责令限期改正；逾期未改正的，强制拆除，并可处三百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 未经批准不得在城市道路上空及新建住宅楼宇之间设置架空管线。违反规定的，责令限期改正；逾期未改正的，处五百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 经批准对城市道路扩建、改建，以及开挖道路建设杆线、管网的，应当设置明显的警示标志，保持周围市容环境卫生整洁，及时清理余泥、污物等建筑垃圾，修复路面和拆除临时设施。违反规定的，责令限期改正；逾期未改正的，由主管部门组织市容环境卫生作业服务单位代为清理、修复或拆除，所需费用由违法行为人承担，并可处五百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 在城市道路行驶的车辆应当保持车容整洁。利用车辆张贴、绘制、设置广告或者宣传品的，应当保持整洁、完好；出现陈旧、污损的，应当及时清洗、修复、更换。违反规定的，责令限期改正；逾期未改正的，处五十元以上二百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　车辆运输砂石、水泥、煤炭、渣土、泥浆、垃圾、粪便等散体物料、液体、废弃物，应当采取密闭、覆盖或者其他有效措施，不得泄漏、散落或者飞扬。违反规定的，责令清除；拒不清除的，由主管部门组织市容环境卫生作业服务单位代为清除，所需费用由违法行为人承担，并可处五百元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　设置户外广告，应当遵守以下规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按照批准的地点、规格和期限等要求设置；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）保持安全牢固、完好整洁，无空置，无破损、污迹和严重褪色等；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）保持霓虹灯、电子显示屏（牌）、灯箱体形式的户外广告显示完整，不残损。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反前款第（一）项规定的，责令限期拆除；逾期未拆除的，强制拆除，并可处五百元以上五千元以下罚款；违反前款第（二）、（三）项规定的，责令限期改正，并可处五百元以上五千元以下罚款；霓虹灯、电子显示屏（牌）、灯箱等断亮、残损的，在修复前应当停止使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条　机关、团体、部队、院校、企事业单位和其他组织及个体工商户的非广告牌匾、标识、标语、电子显示屏（牌）、灯箱、画廊、橱窗、告示栏、宣传栏（牌）、实物造型，应当按照规范设置。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　非广告牌匾、标识、电子显示屏（牌）、灯箱、画廊、橱窗、告示栏、宣传栏（牌）、实物造型应当牢固安全、整洁美观，照明和显亮设施功能完好，不残损。画面污损、字体残缺、灯光显示不完整等影响市容的，应当及时维修或者更换。违反规定的，责令停止使用，限期修复；逾期未修复的，强制拆除，并可处二百元以上一千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　禁止擅自在建筑物、构筑物以及其他设施上张贴、涂写、刻画、喷涂宣传品和广告。违反规定的，责令限期清洗，对个人处五十元以上五百元以下罚款，对单位处五百元以上三千元以下罚款，并可以对宣传品和广告中标明的电讯号码依法进行处理，有关电讯单位应当依法协助执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 区（县）人民政府应当组织有关部门在街巷和住宅小区等处选择适当地点设置公共信息栏，为发布信息者提供方便，并负责管理和保洁。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 城市主要道路两侧及公共场所、景区景点的建筑物、构筑物和其他设施，应当按照景观灯光规划要求设置景观灯光设施。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市景观灯光设施的产权人或者使用管理人，应当保持景观灯光设施的完好、整洁，并按照规定开启、关闭。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章　环境卫生管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 市市容环境卫生行政主管部门应当会同有关部门，根据国家环境卫生质量标准，结合本市实际，制订本市的环境卫生质量标准，经市人民政府批准后公布实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市的环境卫生应当符合城市环境卫生质量标准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 城市环境卫生实行责任区制度。有关单位和个人应当按照本条例的规定，做好责任区内环境卫生的管理或者保洁工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 责任区及其责任人按照下列规定确定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）实行物业管理的住宅小区，由物业管理单位负责；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）铁路、公路、高速公路由产权人或者使用管理人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）机场、车站、码头和公园、影剧院、体育场（馆）、展览馆、图书馆、医院、宾馆、大型商场等公共场所，由产权人或者使用管理人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）城市道路两侧临街单位、门店、摊档门前的卫生保洁由该单位、门店、摊档的产权人或者使用管理人负责；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）机关、团体、部队、学校和有关企事业单位的门前卫生保洁，由本单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）集贸市场由产权人或者使用管理人负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）建筑工程施工场地由施工单位负责；待建地块由业主负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）高新区、保税区、独立工业（科技）园区、风景旅游区内的公共区域由其管理单位负责；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）海湾、江河、湖泊、沟渠、池塘等水域由其管理单位负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　按照前款规定责任不清的地区，由所在地的区（县）市容环境卫生行政主管部门确定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　行政辖区接壤地区责任不清的，或者对责任人的确定存在争议的，由市市容环境卫生行政主管部门确定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 城市环境卫生责任区的责任要求是：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）保持环境卫生整洁，无暴露垃圾、粪便、污水，无污迹，无杂草，无蚊蝇孳生地；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）按照规定设置环境卫生设施，并保持整洁、完好。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　责任人对责任区内违反环境卫生管理规定的行为有权制止，并有权要求市容环境卫生行政主管部门或者城市管理行政执法部门查处。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　责任人不履行责任的，责令限期改正；逾期未改正的，处五十元以上五百元以下罚款，并可建议其上级主管部门对直接负责的主管人员给予行政处分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 市容环境卫生行政主管部门应当将环境卫生责任区的具体范围和责任要求书面告知责任人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市容环境卫生行政主管部门或者城市管理行政执法部门应当加强对责任区内环境卫生的监督，并定期组织检查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条 城市主次干道、人行道、地下通道、广场、高架桥、立交桥、人行天桥等城市公共区域的环境卫生，由各区（县）市容环境卫生行政主管部门负责；内街小巷和未实行物业管理的住宅小区，由所在地街道或镇人民政府负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 禁止下列影响环境卫生的行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）随地吐痰、便溺；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）乱扔瓜果皮核、纸屑、烟蒂、饮料罐、口香糖等废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）从建筑物、构筑物、车辆上向外泼水、抛掷废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）乱倒生活垃圾、污水、粪便、乱扔动物尸体等废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）在露天场地或者公共垃圾收集容器内焚烧树叶、垃圾或者其他废弃物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）将排水管道的污水直接排向路面。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反前款规定的，责令限期纠正违法行为，采取补救措施，并可处五十元以上五百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条 空调器的冷却水应当接入排水系统，不得凌空排放。违反规定的，责令限期改正，拒不改正的，处五十元以上二百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条 禁止占用城市道路从事机动车辆清洗、维修等经营活动，违反规定的，责令改正；拒不改正的，处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　从事机动车辆清洗、维修等经营活动的单位和个人，应当保持经营场地周围环境卫生整洁，采取措施防止废水、废油外流或者废弃物向外散落。违反规定的，责令限期改正；逾期未改正的，处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条　从事废品收购和废弃物接纳作业的，应当保持经营场所整洁，经营者应当采取围挡、遮盖等措施，防止废弃物向外散落污染周围环境。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条　城市绿地应当保持整洁、美观，养护单位或者作业单位应当及时清除绿地内的枝叶、杂草、渣土等垃圾杂物。违反规定的，责令限期改正；逾期未改正的，处二百元以上一千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 建筑工程施工单位应当遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）在建设工地设置符合规定要求的围蔽设施，并在批准的占地范围内封闭作业；所有拆建施工，必须设置遮挡尘土的设施。经批准占用道路堆放建筑材料的，应按批准范围堆放整齐，占用期满必须立即撤除、清场；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）施工产生的泥水，必须经过沉淀，水导入下水道，泥沙另外收集处理；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）建筑工程竣工后，应当及时平整建设工地，清除建筑材料和建筑垃圾并拆除施工临时设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反前款规定的，责令限期改正；逾期未改正的，对未设置围蔽设施，或者在围蔽设施外堆放建筑材料和建筑垃圾的，处一千元以上三千元以下罚款；对向建设工地外直接排放污水的，处五百元以上一千元以下罚款；对竣工后未及时清除建筑垃圾或者未拆除临时施工设施的，可以由主管部门组织市容环境卫生作业服务单位代为清除，所需费用由违法行为人承担，并可处一千元以上三千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 禁止在中心城区饲养家禽家畜；因教学、科研以及其他特殊用途需要饲养的，须经市容环境卫生行政主管部门批准，并应当实行圈养，不得影响环境卫生。违反规定的，责令限期处理；逾期不处理的，予以没收，并可按照家畜每头二百元、家禽每只二十元处以罚款，但罚款总额最高不得超过三万元。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　禁止饲养家禽家畜中心城区的范围由市人民政府划定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 市容环境卫生行政主管部门应当按照便民的原则，规定生活垃圾投放的时间、地点和方式。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">生活垃圾应当逐步实行分类投放、收集、运输和处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 居民和单位应当按照规定的时间、地点和方式投放生活垃圾。实行生活垃圾分类投放的单位和地区的居民，应当积极配合分类收集。违反规定投放生活垃圾的，责令限期改正，对居民处以每次五十元罚款，但罚款总额最高不得超过五百元；对单位处以每立方米五百元罚款，但罚款总额最高不得超过五千元。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条 生活垃圾应当定时、定点收集、运送，并做到日产日清。违反规定的，责令限期改正。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">大件生活垃圾应当单独收集和处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在收集、运送生活垃圾的过程中不得翻捡垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生活垃圾的收集、运送应当逐步实行容器化、密闭化和机械化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条 单位和餐饮业经营者产生的餐厨垃圾，应当单独收集、自行处理，或者委托环境卫生作业服务单位收集、处理，不得直接排入下水道。违反规定的，责令改正；拒不改正的，处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条　集贸市场内的环境卫生保洁工作，由市场产权人或者使用管理人负责，并应当遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）设置与废弃物产生量相适用的废弃物容器，并做到废弃物日产日清、无满冒外溢；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）按照规划要求设置公共厕所，并保持公厕内卫生清洁；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）对市场内进行全天保洁，保持市场整洁有序；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）责成产生废弃物的经营者自备废弃物容器。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反前款规定的，责令限期改正；逾期不改正的，处五十元以上五百元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条 化粪池、储粪池应当定期疏通。粪便外溢时，本条例第二十九条规定的责任人应当先及时清除、疏通，再分清责任，并由责任者承担清除、疏通费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条 处置、受纳建筑垃圾应当向市容环境卫生行政主管部门申请核准</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条 任何单位和个人不得任意倾倒、堆放建筑垃圾。违反规定的，责令限期改正，给予警告，并对单位处五千元以上五万元以下罚款，对个人处二百元以下罚款。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章　环境卫生设施管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条 市容环境卫生行政主管部门应当会同有关部门组织编制城市环境卫生设施专项规划，经市人民政府批准后纳入城市总体规划。城市环境卫生设施专项规划由市、区（县）市容环境卫生行政主管部门按照职责分工组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条 机场、车站、码头和住宅小区、工业区、公园、影剧院、体育场（馆）、展览馆、图书馆、学校、医院、宾馆、大型商场、旅游景点等公共场所，建设单位应当根据城市环境卫生设施专项规划，配套建设、设置符合标准的公共厕所、垃圾转运站、垃圾容器等环境卫生设施。违反规定的，责令限期改正。对未按规定和标准设置垃圾收集容器的，处五十元以上五百元以下罚款；对未按规定和标准配套建设公共厕所、垃圾转运站等环境卫生设施的，处三千元以上三万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条　配套建设的公共厕所、垃圾转运站等环境卫生设施，应当与主体工程同时设计、同时施工、同时投入使用，设计方案应当征求市容环境卫生行政主管部门的意见。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　配套建设的公共厕所、垃圾转运站等环境卫生设施，经验收合格后方可投入使用。市容环境卫生行政主管部门应当参加验收。环境卫生设施未经验收或者验收不合格即投入使用的，责令限期改正。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条 城市道路两侧临街的单位、门店、摊档应当设置实用、美观的垃圾容器。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　环境卫生设施的产权人或者使用管理人，应当做好环境卫生设施的维修、保养工作，保持其整洁、完好。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公共厕所应当设有明显标志，并由专人负责保洁。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条 禁止任何单位和个人占用、损毁、迁移、拆除、封闭环境卫生设施。违反规定的，责令恢复原状、赔偿损失或者采取其他补救措施，并可处二百元以上二千元以下罚款；情节严重的，处二千元以上二万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因建设等特殊原因确需迁移、拆除、封闭环境卫生设施的，建设单位或者其他有关单位应当事先提出拆迁或者补建方案，报市容环境卫生行政主管部门核准后实施；拆迁或者补建费用由建设单位或者其他有关单位承担。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章　作业服务管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条 鼓励单位和个人兴办市容环境卫生作业服务企业，逐步实行市容环境卫生作业服务的专业化、社会化、市场化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条 从事城市生活垃圾经营性清扫、收集、运输、处理等服务的，必须具备国家规定的条件并经市容环境卫生行政主管部门批准后方可从事经营。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　未经批准从事城市生活垃圾经营性清扫、收集、运输、处理等服务的，责令其停止经营行为，没收清运工具，并可处以五千元以上二万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市垃圾运输车辆由市市容环境卫生行政主管部门核发准运证后，方可运输城市垃圾。违反规定未领取准运证运输城市垃圾的，对车主处以每车次二千元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条 市容环境卫生作业服务项目可以根据实际需要和作业服务企业的资质条件，通过委托、招标的方式确定承包人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">作业服务企业承接的作业服务项目不得转包。违反规定的，发包单位可以终止作业服务项目的承包。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条 单位和个人从事市容环境卫生作业服务，应当遵循市容环境卫生作业服务规范，并符合城市容貌标准和城市环境卫生质量标准。违反规定的，责令改正。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　道路和公共场所的清扫、保洁，应当在规定的时间进行，垃圾应当及时清除、收运，不得将垃圾扫入道路两侧的花坛、下水道、路旁空地和绿化带。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市道路和公共场所的清扫作业，应当逐步实行机械化。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条　从事城市垃圾清扫、收集、运输服务的单位和个人，应当将城市垃圾运往垃圾转运站、处理场（厂）和建筑垃圾消纳场，不得任意倾倒。违反规定任意倾倒城市垃圾的，责令清运，按照倾倒垃圾的数量处以每立方米五百元罚款，但罚款总额最高不得超过五万元。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　垃圾转运站、处理场（厂）和建筑垃圾消纳场应当按照有关规定和作业规范处理垃圾。市容环境卫生行政主管部门应当进行监督检查。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章 其他规定</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条　市容环境卫生行政主管部门办理本条例规定的行政许可事项时，应当公开行政许可的条件和受理、审查、决定的程序，并在法定期限内作出准予许可或者不予许可的书面决定；不予许可的，应当书面向申请人说明理由。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条 市容环境卫生行政主管部门、城市管理行政执法部门或者其他行政管理部门的工作人员实施执法检查或者行政处罚时，应当有两人以上同时进行，并向当事人出示执法证件。违反规定的，应当予以批评教育并责令改正；情节严重的，给予行政处分。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十条 市容环境卫生行政主管部门、城市管理行政执法部门或者有关部门及其工作人员有下列情形之一的，给予行政处分；构成犯罪的，依法追究刑事责任：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）对应当受理的事项或者举报不受理的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）对应当制止或者处罚的违法行为不制止、不处罚的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）包庇、纵容违法行为人的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）违法办理行政许可事项的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）利用职权收受他人财物或者谋取其他利益的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）其他滥用职权、徇私舞弊或者不履行职责的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十一条　当事人对市容环境卫生行政主管部门、城市管理行政执法部门或者其他行政管理部门的具体行政行为不服的，可以依法申请行政复议或者提起行政诉讼。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十二条　拒绝、阻碍市容环境卫生工作人员执行职务，违反治安管理法律法规的，由公安部门依法予以处罚；构成犯罪的，依法追究刑事责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十三条 本条例所称的“以上”、“以下”均包括本数。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例规定按照立方米计算罚款的，不足一立方米按照一立方米计算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十四条 本条例所称的中心镇，是指根据省规定标准和程序核准为中心镇的建制镇。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十五条 中心镇以外的其他建制镇人民政府所在地的市容和环境卫生管理，可以参照本条例执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十六条 本条例自2005年12月1日起施行。</span></p>','2016-05-18 14:33:08',0,NULL,2,NULL,36,11,'',0,0,0,'2016-05-18 14:40:10','2016-05-18 14:40:10');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (467,'【法规】《汕头经济特区城乡规划条例》','nRYGv0l84fJSJFgn.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>汕头市第十三届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公　告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第20号）</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头经济特区城乡规划条例》已由汕头市第十三届人民代表大会常务委员会第二十七次会议于2014年10月29日通过，现予公布，自2014年12月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民代表大会常务委员会</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2014年10月29日</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头经济特区城乡规划条例</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2014年10月29日汕头市第十三届人民代表大会</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">常务委员会第二十七次会议通过））</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 &nbsp;总 &nbsp; 则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 &nbsp;为了科学制定和管理城乡规划，协调城乡空间布局，改善人居环境，促进城乡经济社会全面协调可持续发展，根据《中华人民共和国城乡规划法》等法律、行政法规的基本原则，结合汕头经济特区（以下简称特区）实际，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 &nbsp;特区范围内制定、修改、实施和监督检查城乡规划，以及在规划区内进行建设活动，应当遵守本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　规划区的具体范围由市、县、镇人民政府在组织编制的城市总体规划、镇总体规划、村庄规划中划定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 &nbsp;制定和实施城乡规划，应当遵循城乡统筹、合理布局、节约土地、集约发展和先规划后建设的原则，坚持以人为本、精致建构，优化空间布局结构，保护自然资源和历史文化遗产，彰显滨海山水特色和潮汕传统风貌，提升区域性中心城市功能。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 &nbsp;依法批准的城乡规划应当及时公布，未经法定程序不得修改。未经公布的城乡规划，不得作为城乡规划管理和建设的依据，但法律、法规另有规定的除外。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 &nbsp;市人民政府统一领导、组织、协调特区城乡规划工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　特区实行规划委员会制度。城镇体系规划，城市、镇、特定地区的总体规划、近期建设规划、分区规划、专项规划、控制性详细规划和重要地块的修建性详细规划，以及城乡规划管理技术规范等重大事项，应当经规划委员会审议。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　规划委员会委员应当体现广泛性，由人民政府及其职能部门代表、专家和公众代表组成。委员总数应当为单数，其中专家和公众代表委员人数应当超过委员总数的二分之一。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　规划委员会的具体议事规则由本级人民政府另行规定，并报上一级人民政府备案。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 &nbsp;市城乡规划主管部门负责特区城乡规划管理工作，组织实施本条例，并具体负责市区的城乡规划管理工作，其所属的派出机构和城乡规划监督机构，按照规定职责或者市城乡规划主管部门的委托实施规划管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　南澳县城乡规划主管部门负责南澳县行政区域内的城乡规划管理工作，接受市城乡规划行政主管部门的业务指导和监督管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区、镇人民政府（街道办事处）以及其他有关行政管理部门按照各自职责，做好城乡规划管理的相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 市人民政府可以根据特区城乡规划管理的实际，对市和区（包括有关产业园区）在城乡规划的制定、修改和实施等方面的管理体制作出特别规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 &nbsp;本条例规定由城乡规划主管部门查处的城乡规划违法行为，在实行城市管理相对集中行政处罚权制度的区域，由城市管理行政执法部门负责查处。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 制定、修改和实施城乡规划，应当依据国民经济和社会发展规划，与土地利用总体规划相衔接，并体现新型城镇化规划、主体功能区规划、海洋功能区规划、环境保护和生态建设规划的要求，合理确定建设规模和建设时序，优先发展公共服务设施和基础设施，符合区域人口发展、国防建设、防灾减灾、环境保护和公共卫生、公共安全的需要。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">各类专项规划，涉及土地利用和空间布局的，应当符合城乡规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 &nbsp;制定、修改、实施和监督检查城乡规划，应当建立健全公众参与制度，听取公众意见，依法公开城乡规划编制修改、规划许可和违法建设查处等信息，保障公民、法人和其他组织的知情权、参与权和监督权。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 &nbsp;任何单位和个人应当遵守城乡规划，服从规划管理，并有权就涉及其利害关系的建设活动是否符合规划要求向城乡规划主管部门查询。城乡规划主管部门应当为社会公众查询提供便利。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　任何单位和个人有权向城乡规划主管部门或者其他有关部门举报或者控告违反城乡规划的行为。城乡规划主管部门或者其他有关部门应当及时受理并组织核查、处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 &nbsp;市城乡规划主管部门应当组织开展规划研究，规范地理信息和城乡规划档案的收集和管理，加强各类城乡规划数据库的建设和管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 &nbsp;各级人民政府应当将城乡规划的编制和测绘、研究、信息系统建设等管理经费纳入本级财政年度预算。村庄规划编制和管理经费应当在县级以上人民政府预算中安排。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 &nbsp;市人民政府根据本条例制定特区的城乡规划管理技术规范（以下简称规划技术规范），作为制定、修改和实施城乡规划的技术依据，向社会公布后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章 &nbsp;城乡规划的制定和修改</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 &nbsp;制定和修改城乡规划，组织编制机关应当采取座谈会、论证会、听证会或者其他方式，充分听取专家、公众的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划涉及区域统筹与城乡统筹、城市发展目标与空间布局、资源与环境保护、历史文化遗产保护、交通规划和工程地质环境影响等重大专题事项的，应当组织相关领域的专家进行研究论证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　专家和公众在规定期限内提出的意见，有法律、法规和技术规范依据并与征求意见的规划事项直接相关的，应当予以采纳，并在报送审议、审批的材料中附送意见采纳情况及理由。　</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 &nbsp;编制和修改城乡规划，组织编制机关应当在上报审批前将规划草案进行公示，并在批准后予以公布。城乡规划上报审批前公示的内容应当包括规划编制的依据、规划文本的主要内容和主要图纸等。城乡规划批准后公布的内容应当包括规划批准文件、规划文本的主要内容和主要图纸等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划公开公示的信息可在项目现场、展示厅或者政府网站发布。公示公布的时间不得少于三十日。依照国家有关规定需要批准的，未经批准不得发布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 &nbsp;编制和修改城乡规划，应当采取政府采购与直接委托相结合的方式，委托具有规划编制资质的单位承担具体编制工作，并可根据实际需要，委托其他具有规划编制资质的单位对规划草案进行第三方技术审查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　编制和修改城乡规划以及对规划草案进行第三方技术审查，应当使用具有规划测绘资质的单位测制的地形图。地形图的测绘应当采取政府采购与直接委托相结合的方式，委托具有规划测绘资质的单位承担，地形图的有关技术标准应当符合城乡规划要求。承担城乡规划编制和修改以及规划草案第三方技术审查的单位，应当按照有关法律、法规、标准和规范进行编制、修改和审查，不得弄虚作假，提供虚假审查报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城乡规划主管部门应当建立规划编制单位服务质量的诚信管理体系，制定委托城乡规划编制、测绘单位的具体办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 &nbsp;市人民政府组织编制城市总体规划，应当先经市人民代表大会常务委员会审议，并对审议意见进行研究处理，反馈处理情况，按照法定程序报上级人民政府审批。市人民政府可以根据特区城乡规划管理的实际完善城市总体规划，经市人民代表大会常务委员会审议后，报告上级人民政府。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 &nbsp;除市人民政府另有规定外，城市的交通运输、水利、电力、通信、给排水、燃气、环境卫生、绿化、消防、人民防空、住房保障、医疗、教育、文化、体育等各类专项规划，涉及土地利用和空间布局的，由相关行政主管部门会同市城乡规划主管部门组织编制，报市人民政府审批；依法应当报上级机关审批的，按有关规定执行。各类专项规划的内容应当相互衔接、符合总体规划，并纳入控制性详细规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城乡规划主管部门应当会同有关部门，依据总体规划组织编制城市地下空间开发利用规划，报市人民政府审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 城市的分区规划由市城乡规划主管部门组织编制，报市人民政府审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市的控制性详细规划由市城乡规划主管部门组织编制，经市人民政府审批后，报市人民代表大会常务委员会和省人民政府备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 &nbsp;制定或者修改城乡规划，可以围绕整体城市形态和特色、自然和历史文化保护、新区开发、社区环境、交通等内容开展城市设计。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市设计由城乡规划主管部门会同相关单位组织编制，对建筑、公共空间的形态、布局和景观控制提出规划管理要求。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　总体规划层次的城市设计，应当按照总体规划的专项规划的审批程序执行；控制性详细规划层次的城市设计，可以按照控制性详细规划的审批程序执行，或者纳入控制性详细规划一并审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 &nbsp;城市中心、历史风貌保护区、城市出入口、主要交通节点等城市重要地块的修建性详细规划由市城乡规划主管部门组织编制，报市人民政府审批。经批准的修建性详细规划可以作为出具用地规划条件的依据。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市重要地块以外需要编制修建性详细规划的，应当在出具用地规划条件时予以明确，由建设单位组织编制，报城乡规划主管部门审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 &nbsp;城市总体规划确定独立建设的镇，其总体规划、近期建设规划、专项规划、控制性详细规划以及村庄规划，由镇人民政府组织编制；修建性详细规划由镇人民政府、建设单位或者个人组织编制。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　镇的总体规划、近期建设规划、专项规划和控制性详细规划经区人民政府和市城乡规划主管部门审查同意后，报市人民政府审批；镇人民政府组织编制的修建性详细规划经区人民政府审查同意后，报市城乡规划主管部门审批。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　镇的总体规划和近期建设规划在报送区人民政府和市城乡规划主管部门审查前，应当先经镇人民代表大会审议。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 &nbsp;城市总体规划、镇总体规划确定保留的村庄，其村庄规划由镇人民政府（街道办事处）组织编制。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　村庄规划经村民会议或者村民代表大会讨论同意以及区人民政府审查同意，由市城乡规划主管部门审批，报市人民政府备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 &nbsp;城乡规划不得擅自修改；确需修改的，应当依照法定程序进行，并按照法律、法规和本条例规定的权限、程序报批和备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　修改城乡规划涉及上层次规划的强制性内容的，应当先修改上层次规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 &nbsp;控制性详细规划的修改分为修编、调整和完善三个层次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">修编、调整和完善控制性详细规划的，组织编制机关应当对修改的必要性进行论证并将有关事项予以公示，采取论证会、听证会或者其他方式听取利害关系人、专家、公众的意见。修编控制性详细规划的，组织编制机关应当形成专题报告，经市人民政府同意后，方可编制修编方案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　修编或者调整城市的控制性详细规划的，修编方案或者调整方案应当经市规划委员会审议通过后方可报市人民政府审批；但仅涉及修改单条支路走向、宽度或者单个地块建筑高度、建筑密度等内容的，直接报市人民政府审批。完善城市的控制性详细规划的，由市城乡规划主管部门审批后报市人民政府备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对控制性详细规划中的非居住、商业服务业设施用地的容积率进行修改，或者将非居住、商业服务业设施用地调整为其它非居住、商业服务业设施用地的，市人民政府可以根据特区城乡规划管理实际简化控制性详细规划的修改程序。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 &nbsp;选址意见书、建设用地规划许可证、建设工程规划许可证或者乡村建设规划许可证核发后，因依法修改城乡规划给被许可人的合法权益造成损失的，应当依法给予补偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 &nbsp;南澳县城乡规划的编制、修改、审批和备案程序按照国家、省和特区的有关规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　南澳县城乡规划的编制和修改，应当依据和遵守市的城市总体规划和近期建设规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章 城乡规划的实施</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一节 &nbsp;一般规定</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 &nbsp;特区依法实行建设项目选址意见书、建设用地规划许可证、建设工程规划许可证、建设工程规划条件核实证明、乡村建设规划许可证、临时建设用地规划许可证和临时建设工程规划许可证等城乡规划管理制度。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在城市、镇规划区内使用集体所有土地进行村民住宅建设的，市人民政府可以根据特区城乡规划管理的实际，决定按照乡村建设规划许可制度进行管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 &nbsp;建设用地和建设工程应当符合城乡规划，依法取得规划许可。规划许可规定的主体工程配套建设项目，应当与主体工程同步设计、同步建设、同时交付使用，不得改作他用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设单位或者个人应当按照规划条件和规划许可的内容进行建设，不得擅自变更，确需变更的，应当经城乡规划主管部门批准。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当及时将依法变更后的规划条件通报土地管理部门并公告。建设单位或者个人应当及时将依法变更后的规划条件报土地管理部门备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 &nbsp;开发利用地下空间应当符合地下空间开发利用规划，依法取得规划许可。与地面建设工程一并开发利用地下空间的，应当与地面建设工程一并办理规划许可；独立开发利用地下空间的，单独办理规划许可。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　任何单位和个人未经批准，不得擅自开挖建筑底层地面，不得擅自改变经许可确定的地下空间的使用功能、高度、层数和面积。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条 &nbsp;建设单位或者个人依法取得的规划许可证及其配套文件，受法律保护。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为了公共利益的需要，城乡规划主管部门可以依法变更或者撤回已作出的规划许可。因变更或者撤回规划许可给被许可人合法权益造成损失的，应当依法给予补偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划部门出具城乡规划许可证件时，必须注明证件的时限和自行失效的情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 &nbsp;城乡规划主管部门实施城乡规划许可，应当在其办公场所或者本机关公众信息网站公布下列事项：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）许可事项名称、条件、程序、办理期限、需要提交的全部材料目录；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）有关法律、法规依据；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）提出申请的方式；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）申请书示范文本。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　依法需要经过公示、论证、听证、专家评审、征求有关部门意见、有关部门审批等必经程序的，所需时间不计算在前款规定的办理期限内，但城乡规划主管部门应当书面告知申请人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条　城乡规划主管部门作出许可或者审批决定前，应当将许可或者审批内容、申请人和利害关系人享有的权利等事项在其办公场所、本机关公众信息网站、建设项目现场等场所公示征求意见，公示时间不得少于十日。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请人、利害关系人对许可或者审批事项提出异议的，城乡规划主管部门应当及时处理并回复处理结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条　规划许可或者审批事项批准后十五日内，城乡规划主管部门应当在本机关公众信息网站进行公告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条 &nbsp;城乡规划主管部门作出行政许可或者审批决定前，可以委托具有相应资质的单位对申请材料进行技术审查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条 &nbsp;建设单位或者个人向城乡规划主管部门申请规划许可，应当如实提交有关材料、反映真实情况，对其申报材料的真实性、准确性和合法性负责，并承担相应的法律责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 &nbsp;城乡规划确定的铁路、公路、港口、机场、道路、绿地、输配电设施及输电线路走廊、通信设施、广播电视设施、管道设施、供水设施、供气设施、河道、水库、水源地、自然保护区、防汛通道、消防通道、消防站、电站、垃圾填埋场及焚烧厂、污水处理厂、环卫设施等基础设施的用地，以及学校、公园、文化、体育、医院、福利院等公共服务设施的用地和其他依法需要保护的用地，禁止擅自改变用途。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 沿海道路的临海一侧应当作为重点地段进行规划管理。除规划确定的港口、码头、船坞、自然保护区之外，沿海道路的临海一侧只能建设道路基础设施、市政公用设施、旅游设施和公园、绿地，并控制建筑高度和建设容量，对公众开放。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 &nbsp;新建、改建和扩建的城市道路、公共建筑、公共交通设施、特殊设施、居住小区和居住建筑，应当规划建设无障碍设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 &nbsp;新建、改建和扩建建（构）筑物和设施，不得占用城市道路、河道、广场、公共绿地、高压走廊，不得压占城市地下管线或者依附防汛墙。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条 &nbsp;地上、地下建（构）筑物和设施（含裙楼等附属工程）的建设，应当按照建筑退缩道路红线和用地红线的有关规定后退。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条 &nbsp;城乡规划应当妥善处理新区开发与旧区改建的关系。旧区改建应当保护文化遗产和历史风貌，结合旧城更新规划，有计划地对危房集中、基础设施落后等地段进行改建。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市旧区的具体范围由市人民政府根据城市规划划定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二节 &nbsp;建设项目规划选址</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条 &nbsp;依法需要有关部门批准或者核准的建设项目，以划拨方式提供国有土地使用权的，建设单位在报送批准或者核准前，应当向城乡规划主管部门申请核发建设项目选址意见书；但属于国家和省批准或者核准的建设项目，由城乡规划主管部门出具选址初步意见后向省城乡规划主管部门申请核发建设项目选址意见书。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设项目有下列情形之一的，其选址意见应当经规划委员会审议通过；申请核发建设项目选址意见书或者出具选址初步意见时，应当提供由乙级以上规划编制资质单位编制的建设项目选址评估报告：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）属于国家和省批准、核准项目投资的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）选址地尚未编制控制性详细规划的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）对环境和安全有重大影响的。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条 &nbsp;建, 设项目选址意见书有效期为二年，自建设项目选址意见书核发之日起计算。有效期内建设项目未获得有关部门批准或者核准，需要延期的，应当在有效期届满三十日前申请不超过一年的延长期；未获得延期批准或者延长期逾期建设项目仍未获得有关部门批准、核准的，选址意见书自行失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设项目所依据的批准、核准文件被依法撤销、撤回、吊销，或者土地使用权被依法收回的，相应的项目选址意见书失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三节 &nbsp;建设用地规划</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条 &nbsp;制订土地储备年度计划和土地供应年度计划应当与城乡规划相衔接，并征求城乡规划主管部门的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条 &nbsp;在城市、镇规划区内以划拨、出让方式取得国有土地使用权的建设项目，以及使用集体建设用地的建设项目，建设单位或者个人应当向城乡规划主管部门申请核发建设用地规划许可证。使用集体建设用地的建设项目，申请核发建设用地规划许可证时应当提供村（居）民委员会出具的书面同意意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条　在城市、镇规划区内国有土地使用权的划拨、出让以及使用集体建设用地，应当以控制性详细规划为依据，由城乡规划主管部门提出规划条件；尚未编制控制性详细规划的地块，除本条例第四十九条规定的情形外，城乡规划主管部门不得办理建设用地规划许可手续，土地管理部门不得办理建设用地审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设用地的规划条件应当明确用地的位置、面积、使用性质、允许建设的范围、容积率、绿地率、建筑高度、建筑密度、基础设施和公共服务设施配套规定、地下空间开发利用要求等内容。属于依法需要明确保障性住房建设要求的居住用地，规划条件应当明确保障性住房建设要求。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条 &nbsp;在城市、镇规划区内尚未编制控制性详细规划的地块，有下列情形之一的，城乡规划主管部门可以根据总体规划或者分区规划等规划和规划技术规范，提出或者修改规划条件进行公示，经规划委员会审议通过并报本级人民政府批准后，作为国有土地使用权划拨、出让以及建设用地规划许可的依据：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）因国家、省或者特区重点建设项目需要使用土地的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）因基础设施、公益性公共设施建设项目需要使用土地的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）小于二公顷的政府储备土地；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）建设项目的用地小于二公顷，且建设项目周边片区已经建成、配套公共服务设施能够满足需要的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）仅涉及修改单条支路走向、宽度或者单个地块建筑高度、建筑密度等内容的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）修改非居住、商业服务业设施用地容积率的，或者将非居住、商业服务业设施用地调整为其它非居住、商业服务业设施用地的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条 &nbsp;在城市、镇规划区内以出让方式提供国有土地使用权的，城乡规划主管部门应当在出让前提出出让地块的规划条件及附图，规划条件及附图应当作为国有土地使用权出让合同的组成部分。未确定规划条件及附图的地块，不得出让国有土地使用权；规划条件及附图未纳入国有土地使用权出让合同的，该国有土地使用权出让合同无效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门不得在建设用地规划许可证中，擅自改变作为国有土地使用权出让合同组成部分的规划条件及附图。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条 &nbsp;城乡规划主管部门作出建设用地规划许可，应当自受理申请之日起五个工作日内，将有关事项公示十日征求意见，必要时可以采取听证会、论证会或者其他方式听取各方意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当自受理申请之日起二十个工作日内依法进行审查，符合条件的，核发建设用地规划许可证；不符合条件的，不予核发并书面说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条 &nbsp;建设用地规划许可证应当附规划条件、建设用地红线图等配套文件，作为建设用地规划许可证的组成部分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设用地规划许可证及其配套文件是修建性详细规划、建设工程设计方案的依据。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条 &nbsp;当事人依法转让或者人民法院、仲裁机构裁决转让国有土地使用权的，受让方应当执行原出让合同中规定的规划条件，并向城乡规划主管部门申请换发建设用地规划许可证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　转让国有土地使用权涉及改变建设用地规划条件的，转让前应当征求城乡规划主管部门的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条 &nbsp;当事人依法申请或者人民法院、仲裁机构裁决分割国有土地使用权的，分割前应当征求城乡规划主管部门的意见，由城乡规划主管部门明确分割后各地块的规划条件；原规划条件或者经审定的修建性详细规划、建设工程设计方案总平面图确定的道路、广场、公共绿地、市政公用设施、公共服务设施等建设项目，应当在分割国有土地使用权的有关法律文书、用地批准文件或者转让合同中明确实施责任人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　国有土地使用权分割后，当事人应当向城乡规划主管部门申请核发建设用地规划许可证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条 &nbsp;在城市、（镇）规划区内，利用已取得使用权但没有规划条件或者规划条件不明确的国有土地进行建设的，应当向城乡规划主管部门申请规划条件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条 &nbsp;建设单位或者个人应当按照建设用地规划许可确定的规划条件进行建设，确需调整规划条件的，应当经城乡规划主管部门批准。调整的规划条件应当符合原规划条件所依据的城乡规划；确需突破原规划条件所依据的城乡规划的，应当先修改原规划条件所依据的城乡规划后方可批准调整。因调整规划条件给利害关系人合法权益造成损失的，应当依法给予补偿。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条 &nbsp;建设用地出租、抵押期间，承租人和抵押人应当按照建设用地规划许可证确定的规划条件使用土地；确需变更的，土地使用权人应当向城乡规划主管部门提出申请，经批准后方可办理相关用地手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条 &nbsp;在城市、镇规划区内以划拨方式取得国有土地使用权的建设项目，其建设用地规划许可证的有效期为一年，自建设用地规划许可证核发之日起计算。建设单位或者个人应当在有效期内向土地管理部门申请用地。有效期内建设用地未取得土地使用权属文件，需要延期的，应当在有效期届满三十日前申请不超过一年的延长期；未获得延期批准或者延长期逾期建设用地仍未取得土地使用权属文件的，建设用地规划许可证自行失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四节 建设工程规划</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条 &nbsp;在城市、镇规划区内进行建（构）筑物、道路、桥梁和管线等工程建设（含新建、扩建、改建）的，建设单位或者个人应当向城乡规划主管部门申请核发建设工程规划许可证，并按照建设工程规划许可证的规定进行建设，但依照本条第二款规定属于无需核发建设工程规划许可证的工程除外。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　无需核发建设工程规划许可证的工程类型，由市城乡规划主管部门会同有关部门制定，经市规划委员会审议通过，并报市人民政府批准后公布实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　无需核发建设工程规划许可证的工程，应当按照城市容貌标准和相关主管部门的要求进行建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十条 &nbsp;城乡规划主管部门作出建设工程规划许可，应当自受理申请之日起五个工作日内，将有关事项公示十日征求意见，必要时可以采取听证会、论证会或者其他方式听取各方意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当自受理建设工程规划许可申请之日起二十个工作日内，对申请人提交的建设工程设计方案总平面图进行审查，必要时可以组织专家评审，符合规划条件和规划技术规范的，予以审定，不符合的，不予审定并书面说明理由。申请人应当按照审定的建设工程设计方案总平面图组织编制建设工程设计方案报送城乡规划主管部门，城乡规划主管部门应当自受理建设工程设计方案之日起二十个工作日内进行审查，符合规划条件和规划技术规范的，申请人依法缴纳有关费用后，核发建设工程规划许可证；不符合的，不予核发并书面说明理由。城乡规划主管部门应当依法将经审定的建设工程设计方案总平面图予以公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设工程依法还应当经其他行政管理部门或者单位审查或者出具意见的，按照有关规定办理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十一条 &nbsp;规划条件要求编制修建性详细规划的，建设单位或者个人应当组织编制修建性详细规划，报城乡规划主管部门审查同意后，自审查同意之日起一年内向城乡规划主管部门申请核发建设工程规划许可证，逾期不申请的，修建性详细规划的审查意见自行失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当自受理修建性详细规划审查申请之日起五个工作日内，将有关事项公示十日征求意见，必要时可以采取听证会、论证会或者其他方式听取各方意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当自受理修建性详细规划审查申请之日起二十个工作日内，对申请人提交的修建性详细规划进行审查，符合规划条件和技术规范的，批准修建性详细规划，不符合的，不予批准并书面说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十二条 &nbsp;建设单位或者个人在建设工程放线之前，应当在建设工程施工现场显著地点设置建设工程规划许可公告牌，直至建设工程规划条件核实完成。公告牌应当载明以下内容：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)建设工程规划许可证或者乡村建设规划许可证证载内容;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)建设项目名称、建设规模及主要指标;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)建设单位及其负责人;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(四)建设工程总平面图;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(五)投诉举报受理单位和受理途径;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(六)城乡规划主管部门要求的其他内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在建设工程竣工规划核实前，建设单位或者个人应当保持公告牌的完整。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十三条 &nbsp;供水、供电单位为建设工程提供供水、供电服务前，应当查验建设工程是否取得建设工程规划许可证，未取得建设工程规划许可证的，不得提供供水、供电服务。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十四条 &nbsp;建设工程分期建设的，建设单位或者个人应当组织编制分期建设总平面图，合理划分分期建设地块和分期建设计划，并明确配套公共服务设施的建设时序，经城乡规划主管部门审核同意，可以分期核发建设工程规划许可证。分期建设的建设工程应当符合经审定的修建性详细规划、建设工程设计方案总平面图，同一建设周期的建设内容应当包括相应的配套设施和绿地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十五条 &nbsp;建设单位或者个人应当按照建设工程规划许可的规定进行建设，不得擅自变更，确需变更建设工程规划许可的，应当经城乡规划主管部门批准。城乡规划主管部门应当自受理变更申请之日起五个工作日内，将有关事项公示十日征求意见，必要时可以采取听证会、论证会或者其他方式听取各方意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设工程已预（销）售或者抵押的，建设单位或者个人申请变更建设工程规划许可前，应当先行停止预（销）售，并提供全部预（销）售单元买受人和抵押权人的书面同意意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因变更建设工程规划许可给利害关系人合法权益造成损失的，建设单位或者个人应当给予补偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十六条 &nbsp;居民住宅建设工程的建设单位或者个人要求对经审定的修建性详细规划或者建设工程设计方案进行修改的，不得超出规划设计条件提高容积率和建筑密度，不得降低绿地率，不得改变配套设施的功能和位置。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十七条 &nbsp;建设工程规划许可证的有效期为一年，自核发之日起计算。建设单位或者个人在有效期内未能依法取得建设工程施工许可文件，需要延期的，应当在有效期届满三十日前申请不超过六个月的延长期，但延长次数不得超过一次。逾期未办理延期手续或者未获延期批准，或者延长期逾期仍未依法取得建设工程施工许可文件的，建设工程规划许可证自行失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十八条 &nbsp;建设单位或者个人应当委托有相应资质的单位编制建设工程设计方案；建设工程应当由具有相应规划测绘资质的单位放线，并经城乡规划主管部门验线合格后，方可施工建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　承担建设工程设计方案编制或者建设工程测绘等业务的单位应当遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按照有关法律、法规、标准和规范以及规划许可内容进行编制；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）确保建设工程设计方案文字标明的技术经济指标或者相关数据与设计图纸所示相一致；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）按照有关法律、法规、标准和规范进行测绘；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）不得弄虚作假，出具虚假设计方案或者测绘成果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十九条 &nbsp;建设工程竣工并经具有相应测绘资质的单位出具竣工测绘图纸和报告后，建设单位或者个人应当向城乡规划主管部门申请规划条件核实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　隐蔽性建设工程的规划条件核实工作应当在覆土前进行。建设工程规划许可证规定应当拆除的原有建（构）筑物和其他因施工需要临时搭建的设施，建设单位或者个人应当在规划条件核实前予以拆除，并清理场地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十条 &nbsp;建设单位或者个人对建设工程进行局部建筑设计修改，不涉及经城乡规划主管部门审定的有关内容的，不需要修改建设工程规划许可，可以在工程竣工后直接申请规划条件核实。但是，建设工程已预（销）售或者抵押的，申请规划条件核实前应当先行停止预（销）售，并提供全部预（销）售单元买受人和抵押权人的书面同意意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十一条 &nbsp;建设工程竣工后，城乡规划主管部门应当查验竣工测绘图纸和报告，并进行现场核查，符合建设工程规划许可内容的，核发建设工程规划条件核实证明，作为建设单位或者个人办理建设工程竣工验收备案和权属登记的一项依据；不符合建设工程规划许可内容的，出具未通过规划条件核实的书面处理意见。建设工程未取得建设工程规划条件核实证明的，建设单位或者个人不得组织验收和投入使用，有关部门不得办理工程质量竣工验收备案、权属登记等手续，供水、供电、燃气等单位不得办理相关开户接入手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例施行前的建设工程权属登记、违法建设处理等遗留问题，由市人民政府制定计划，逐步解决。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十二条 &nbsp;房屋使用人应当按照建设工程规划许可证或者房地产权证书载明的用途使用房屋，确需改变房屋用途的，应当向城乡规划主管部门申请办理变更手续。有关行政管理部门实施行政管理涉及房屋用途的，应当查验建设工程规划核准文件或者房屋权属文件记载的房屋用途并保持一致。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五节 &nbsp;临时建设规划</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十三条 &nbsp;在城市、镇规划区内需要临时使用土地或者临时改变土地使用性质的，建设单位或者个人应当向城乡规划主管部门申请核发临时建设用地规划许可证；需要进行临时建设的，应当申请核发临时建设工程规划许可证，并按照临时建设工程规划许可证的规定进行建设。未取得临时建设用地规划许可证的，土地管理部门不得办理临时用地审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门核发临时建设工程规划许可证前，建设单位或者个人应当按临时工程造价百分之二十的标准缴纳自行拆除保证金，依法按期自行拆除并清理现场的，发还保证金；逾期不自行拆除并清理现场的，保证金不予发还，用于强制拆除。临时建设自行拆除保证金的具体管理制度，由市城市管理行政执法部门会同城乡规划、财政、价格部门制定，报市人民政府批准后执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十四条 &nbsp;临时建设用地规划许可证应当对临时用地的使用性质、位置、允许建设范围、建筑高度、建筑用途，以及临时建设用地规划许可证的有效期、自行失效情形等作出明确规定。临时建设用地规划许可证的有效期不得超过二年，期满需要延期的，应当在期满三十日前申请不超过二年的延长期，但延长次数不得超过二次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例施行前已取得临时建设用地规划许可证并办理过延长期的，应当在期满三十日前申请不超过二年的延长期，但延长次数不得超过一次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十五条 &nbsp;有下列情形之一的，临时建设用地规划许可证自行失效：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）临时建设用地规划许可证有效期届满且未取得延期批准的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违法使用临时用地或者利用临时用地从事违法行为的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）因实施城乡规划、执行土地利用年度计划和土地储备年度计划等城乡建设的需要的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）因采取应急处置措施应对自然灾害、事故灾难、公共卫生事件和社会安全事件的需要的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）法律、法规规定的其他情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十六条 &nbsp;临时建设工程规划许可证应当对临时建筑的用途、位置、建筑面积、平面、立面、高度以及临时建设工程规划许可证的有效期、自行失效情形等作出明确规定。临时建设工程规划许可证的有效期不得超过二年，并应当与临时建设用地规划许可证的有效期一致，期满需要延期的，应当在期满三十日前申请不超过二年的延长期，但延长次数不得超过二次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例施行前已取得临时建设工程规划许可证并办理过延长期的，应当在期满三十日前申请不超过二年的延长期，并缴纳自行拆除保证金，但延长次数不得超过一次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十七条 &nbsp;临时建筑应当符合下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）不得超过两层且高度不得超过十米，但确因公共利益需要的除外；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）符合消防技术标准和管理规定；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）按照临时建设工程规划许可证规定的用途使用，不得改变用途；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）不得办理权属登记以及转让、抵押、交换、赠与。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十八条 &nbsp;有下列情形之一的，临时建设工程规划许可证自行失效，建设单位或者个人应当无条件自行拆除临时建（构）筑物和设施并清理现场，恢复土地原状；不具有土地使用权的，应当退回土地：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）临时用地规划许可证失效的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）临时建设工程规划许可证有效期届满且未取得延期批准的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）不按临时建设工程规划许可进行临时建设的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）违法使用临时建筑或者利用临时建筑从事违法行为的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）法律、法规规定的其他情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十九条 &nbsp;有下列情形之一的，城乡规划主管部门不得批准临时建设：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）压占道路红线、河道蓝线或者机场、铁路、公路建设控制范围内土地的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）占用绿地、广场、公共停车场(库)、文物保护范围或者其他公共活动场地的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）占用输配电线路走廊、压占地下管线或者影响近期管线铺设的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）影响近期建设规划或者控制性详细规划实施的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）妨碍城镇交通安全的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）市人民政府规定的其他情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六节 &nbsp;村庄建设规划</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十条 &nbsp;村庄建设应当遵循先规划后建设的原则，鼓励适度集中建设村民住宅。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　各级人民政府和有关管理部门应当加强对村庄建设的指导，对村（居）民采取集中建设的方式进行村民住宅建设的，可以通过资金扶持、开发强度奖励等方式给予鼓励。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门应当将村民住宅的建设要求纳入规划技术规范，城乡规划主管部门、住房和城乡建设管理部门应当免费向村（居）民提供具有潮汕特点和乡村特色的住宅设计图件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十一条 &nbsp;在村庄规划确定的建设用地范围内使用集体所有土地进行乡镇企业、乡村公共设施、公益事业和其他工程建设的，建设单位或者个人应当向镇人民政府（街道办事处）提出申请，由镇人民政府（街道办事处）报城乡规划主管部门核发乡村建设规划许可证，并按照乡村建设规划许可证的规定进行建设。乡村建设规划许可证按照下列程序核发：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）镇人民政府（街道办事处）应当自受理申请之日起十个工作日内提出审查意见报城乡规划主管部门，城乡规划主管部门应当自收到审查意见之日起五个工作日内进行审查，符合条件的，依据村庄规划核定建设用地位置、允许建设的范围、基础标高、建筑高度等规划条件；不符合条件的，不予批准并书面说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）建设单位或者个人依据城乡规划主管部门核定的规划条件组织编制建设工程设计方案，报城乡规划主管部门审查，城乡规划主管部门应当自收到建设工程设计方案之日起十五个工作日内进行审查，符合条件的，核发乡村建设规划许可证；不符合条件的，不予批准并书面说明理由。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　乡村建设规划许可证应当载明建设项目的位置、建设规模和主要功能等内容，并附规划设计图纸。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十二条 &nbsp;在村庄规划确定的建设用地范围内使用集体所有土地进行农村村民住宅建设的，或者在根据本条例第二十九条第二款规定实行乡村建设规划许可制度的城市、镇规划区内使用集体所有土地进行村民住宅建设的，建设单位或者个人应当向镇人民政府（街道办事处）提出申请，由镇人民政府（街道办事处）报城乡规划主管部门核发乡村建设规划许可证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　村民住宅建设的具体管理办法由市人民政府另行制定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">村民住宅应当按照乡村建设规划许可证的规定进行建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十三条 &nbsp;取得乡村建设规划许可证的建设工程开工前，建设单位或者个人应当向城乡规划主管部门申请验线。城乡规划主管部门应当自受理申请之日起十个工作日内进行验线。未经验线，建设工程不得开工。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　乡村建设项目竣工后，建设单位或者个人应当就建设工程是否符合乡村建设规划许可的内容，向城乡规划主管部门申请规划条件核实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十四条 &nbsp;城乡规划主管部门可以委托镇人民政府（街道办事处）实施乡村建设规划许可、规划验线和规划条件核实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章 监督检查</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十五条　各级人民政府应当向本级人民代表大会常务委员会或者镇人民代表大会报告城乡规划的实施情况，并接受监, 督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十六条 &nbsp;市人民政府应当建立城乡规划督察制度，加强对区、县人民政府实施城乡规划情况的监督检查，定期对督察的情况进行通报。 &nbsp;,</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十七条 &nbsp;市、区（县）人民政府应当加强对城乡规划监督检查工作的统筹协调，明确相关部门和单位在城乡规划监督检查中的职责，建立城乡规划行政执法联动机制和查处城乡规划违法行为的信息共享机制，提高城乡规划管理的效能。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十八条 &nbsp;城乡规划主管部门和城市管理行政执法部门应当建立健全城乡规划管理信息共享和工作配合机制。城乡规划主管部门核发建设工程规划许可证、建设工程规划条件核实证明、乡村建设规划许可证和临时建设工程规划许可证的，应当自核发之日起三个工作日内将核发证件情况和有关图件、资料等通报城市管理行政执法部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　国土资源、建设、房管、公安、工商、质量技术监督、公证、档案、供水、供电、供气等部门和单位，应当及时、无偿向城乡规划主管部门和城市管理行政执法部门提供涉及城乡规划管理的信息资料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十九条 &nbsp;城乡规划主管部门、城市管理行政执法部门应当加强城乡规划的日常巡查工作，及时查处违反城乡规划管理的行为，查处情况应当在部门网站公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十条 &nbsp;城乡规划主管部门或者城市管理行政执法部门实施监督检查时，有关行政管理部门和镇人民政府（街道办事处）应当予以配合。居（村）民委员会、物业服务企业发现其管理区域内有城乡规划违法行为的，应当予以制止，并及时向城乡规划主管部门或者城市管理行政执法部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门在实施城乡规划行政管理中，发现违反城乡规划从事建设工程设计、施工、监理、测绘等业务的，应当书面告知建设、测绘等行政管理部门依法进行处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十一条 &nbsp; 城乡规划主管部门和城市管理行政执法部门应当公布举报方式，接受公民、法人和其他组织对城乡规划违法行为的举报。属于本部门职责范围的，应当及时进行核实、处理，并答复举报人；不属于本部门职责范围的，应当及时告知举报人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十二条 &nbsp;城乡规划主管部门和城市管理行政执法部门的工作人员实施监督检查时，应当出示执法证件，并有权采取下列措施：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）要求建设单位或者个人提供与监督检查事项相关的文件、资料，并进行查阅或者复制；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）要求建设单位或者个人就监督检查事项涉及的问题做出解释或者说明；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）根据需要进入建设工程施工现场了解与监督检查事项相关的情况，进行现场勘测或者采集音像资料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门和城市管理行政执法部门及其工作人员应当为被检查的建设单位或者个人保守技术秘密和商业秘密。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十三条 &nbsp;建设单位或者个人应当配合城乡规划主管部门或者城市管理行政执法部门、镇人民政府及其工作人员的监督检查，如实提供与监督检查事项有关的文件、资料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划违法行为人与房屋所有权人、房屋使用人不是同一主体的，房屋所有权人、房屋使用人对城乡规划主管部门或者城市管理行政执法部门、镇人民政府的依法查处行为应当予以配合。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章 法律责任</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十四条 &nbsp;城乡规划主管部门、城市管理行政执法部门和其他行政管理部门及其工作人员在城乡规划管理中有下列情形之一的，对直接负责的主管人员和其他直接责任人员依法给予处分；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）违法实施行政许可和行政审批、监督检查、行政处罚或者行政强制的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）不依法履行监督检查职责，或者发现城乡规划违法行为不予查处的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）对应当受理的事项或者投诉举报不受理的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）包庇、纵容城乡规划违法行为人的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）泄露被监督检查的建设单位或者个人的技术秘密或者商业秘密的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）利用职务上的便利谋取利益的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）其他玩忽职守、滥用职权、徇私舞弊的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十五条 &nbsp;承担城乡规划编制和修改以及规划草案第三方技术审查的单位违反本条例第十七条规定，不按照有关法律、法规、标准和规范进行编制、修改和审查的，或者弄虚作假，提供虚假审查报告的，由城乡规划主管部门责令限期改正；逾期不改正的，处合同约定的服务费二倍以上四倍以下的罚款；情节严重的，责令停业整顿；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十六条 &nbsp;建设单位或者个人违反本条例第五十九条规定，未取得建设工程规划许可证或者未按照建设工程规划许可证的规定进行建设的，由城乡规划主管部门责令停止建设；尚可采取改正措施消除对规划实施的影响的，责令限期改正，并处建设工程造价百分之五以上百分之十以下的罚款；无法采取改正措施消除影响的，责令限期拆除并清理现场，不能拆除的，没收实物或者违法收入，可以并处建设工程造价百分之五以上百分之十以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有下列行为之一的，可以认定为前款规定的尚可采取改正措施消除对规划实施的影响的情形：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）取得建设工程规划许可证，但未按照建设工程规划许可证的规定进行建设的在建工程违法建设，在限期内采取局部拆除等整改措施，能够使建设工程符合建设工程规划许可证要求的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）未取得建设工程规划许可证即开工建设，但已取得城乡规划主管部门的建设工程设计方案审查文件，且建设内容符合或者采取局部拆除等整改措施后符合审查文件要求的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有下列行为之一的，应当认定为本条第一款规定的无法采取改正措施消除影响的情形：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）超过建设工程规划许可证确定的建筑面积（计算容积率部分）或者建筑高度且超出合理误差范围的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违反建筑间距、建筑退让道路红线、建筑退让用地边界等城乡规划管理技术规定或者控制性详细规划确定的强制性内容的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）侵占现状及规划确定的道路、消防通道、广场、公共绿地、河湖水面、地下工程、轨道交通设施、通讯设施或者压占城市管线、永久性测量标志等公共设施、公共场所用地的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）占用各级文物保护单位、历史建筑保护范围用地进行建设的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）擅自在建筑物楼顶、退层平台、住宅底层院内以及配建的停车场地进行建设的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）在已完成规划条件核实的建设工程用地范围内擅自新建、搭建建筑物、构筑物的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）其他无法采取改正措施消除影响的情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有下列行为之一的，可以认定为本条第一款规定的不能拆除的情形：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）拆除可能影响相邻建筑安全、损害无过错利害关系人合法权益或者对社会公共利益造成重大损害的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违法建筑与合法建筑整体施工并在建筑结构上具有整体性，拆除违法建筑将严重影响合法建筑结构安全的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）法律、法规规定的其他情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条第一款规定的违法收入，按照该建设工程的销售平均单价或者市场评估单价与违法建设面积的乘积确定；建设工程造价按照有违法建设情形的单项工程造价确定，其中房屋建筑工程按照单体建筑物工程造价确定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十七条 &nbsp;建设单位或者个人违反本条例第六十二条规定，未按规定在建设项目工程施工现场显著地点设置建设工程规划许可公告牌的，由城乡规划主管部门责令限期改正；逾期不改正的，处二万元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十八条 &nbsp;承担建设工程设计方案编制或者建设工程测绘等业务的单位违反本条例第六十八条第二款规定的，由城乡规划主管部门责令限期改正；逾期不改正的，处合同约定的服务费一倍以上二倍以下的罚款；情节严重的，责令停业整顿；造成损失的，依法承担赔偿责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十九条 &nbsp;建设单位或者个人违反本条例第七十一条第二款规定，对未取得规划条件核实证明的建设工程组织验收或者将其投入使用的，由城乡规划主管部门责令限期改正；逾期不改正的，按照以下规定处罚：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）组织验收的，处二万元以上五万元以下的罚款；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）投入使用的，没收违法收入，并处五万元以上十万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百条 &nbsp;房屋使用人违反本条例第七十二条规定，擅自改变房屋用途的，由城乡规划主管部门责令限期改正，并按改变用途部分的建筑面积处每平方米一百元以上二百元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零一条 &nbsp;建设单位或者个人违反本条例第七十三条、第七十八条规定，有下列行为之一的，由城乡规划主管部门责令停止建设，限期拆除并清理现场，可以并处临时建设工程造价一倍的罚款：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）未取得临时建设工程规划许可证或者未按照临时建设工程规划许可证的规定进行建设的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）临时建设工程规划许可证失效后未按规定自行拆除临时建（构）筑物和设施并清理现场，恢复土地原状的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　临时建筑使用人违反本条例第七十八条规定，违法使用临时建筑或者利用临时建筑从事违法行为的，由城乡规划主管部门责令限期改正，没收违法收入，并处一万元以上三万元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零二条 &nbsp;建设单位或者个人违反本条例第八十一条、第八十二条规定，未取得乡村建设规划许可证或者未按照乡村建设规划许可证的规定进行建设，在镇辖区范围内的，由镇人民政府责令停止建设、限期改正，逾期不改正的，依法强制拆除，城乡规划主管部门应当予以配合；在街道辖区范围内的，由城乡规划主管部门责令停止建设、限期改正，逾期不改正的，依法强制拆除，街道办事处应当予以配合。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零三条 &nbsp;建设单位或者个人违反本条例第九十三条规定，不配合监督检查的，由城乡规划主管部门或者镇人民政府责令限期改正，并可处五千元以上二万元以下的罚款；逾期不改正且情节严重，致使不能实施监督检查的，可以责令停止建设或者限期拆除并清理现场，暂扣相应的规划许可证件。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零四条 &nbsp;城乡规划主管部门作出责令停止建设的决定后，建设单位或者个人不停止建设的，城乡规划主管部门可以采取查封施工现场等措施，并应当立即向本级人民政府报告。本级人民政府应当自收到报告之日起五个工作日内书面通知供水、供电单位暂停向违法建设工程供水、供电，供水、供电单位应当自接到通知之日起五个工作日内暂停向违法建设工程供水、供电。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门作出责令限期拆除并清理现场的决定后，建设单位或者个人逾期不拆除并清理现场的，由本级人民政府责成的部门依法强制拆除并清理现场，强制拆除和清理现场的费用由违法行为人承担。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零五条　对无法确定建设单位或者个人的违法建设项目，城乡规划主管部门应当在报纸等公共媒体和违法建设项目现场予以公告，告知建设单位或者个人依法接受处理，公告期限不少于三十日。公告期限届满仍无法确定建设单位或者个人的，市、县人民政府可以责成有关部门予以拆除或者没收。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　无法确定建设单位或者个人的违法建设项目，不及时拆除影响安全、交通等的，可以在向公证机关办理证据保全手续后直接予以拆除。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章 &nbsp;附 &nbsp; 则</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零六条 &nbsp;本条例对城乡规划管理相关事项未作规定的，按照有关法律、法规的规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零七条 &nbsp;本条例有关用语的含义为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）完善城市总体规划是指，在不改变规划期限，不对规划确定的用地规模、用地发展方向和总体布局等进行重大变更，以及不变更规划区范围、规划强制性内容、基础设施、公共服务设施、自然与历史文化资源保护、防灾减灾等内容的前提下，对规划的部分内容进行补充完善。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）修编控制性详细规划是指，对控制性详细规划中的土地用途用地性质、容积率、城市主次干道红线、绿线、蓝线、黄线、紫线以及教育、文化、体育、卫生等公共设施用地和防灾减灾设施用地等内容进行修改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）调整控制性详细规划是指，对控制性详细规划中的支路红线、建筑密度、建筑高度、绿地率、地下空间利用、基础设施和公共服务设施配套规定等内容进行修改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）完善控制性详细规划是指，对控制性详细规划中不涉及本款第（二）项和第（三）项的其他内容进行修改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例规定的建设工程造价，按照市住房和城乡建设部门测算的年度建筑造价指标执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例中涉及数字的以上、以下均包含本数。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零八条 &nbsp;市人民政府可以根据本条例制定相关配套实施规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例的具体应用问题由市人民政府解释。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一百零九条 &nbsp;本条例自2014年12月1日起施行。1997年8月26日广东省汕头市第九届人民代表大会常务委员会第三十六次会议通过的《汕头经济特区城市规划条例 》同时废止。</span></p>','2016-05-18 14:40:41',0,NULL,2,NULL,40,11,'',0,0,0,'2016-05-18 14:56:08','2016-05-18 15:08:04');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (468,'【法规】《汕头经济特区道路交通安全条例》','vgscCgQnjtgduft3.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市第十三届人民代表大会常务委员会</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">公 &nbsp; &nbsp; 告</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（第14号）</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头经济特区道路交通安全条例》已由汕头市第十三届人民代表大会常务委员会第二十一次会议于2013年12月25日通过，现予公布，自2014年3月1日起施行。　　　　　<br/><br/></span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民代表大会常务委员会<br/>2013年12月25日</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/><strong>汕头经济特区道路交通安全条例</strong></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（2013年12月25日汕头市第十三届人民代表大会常务委员会第二十一次会议通过）</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 &nbsp;总 则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 &nbsp;为了加强道路交通安全管理，保障道路交通安全、有序、畅通，根据《中华人民共和国道路交通安全法》和有关法律、行政法规的基本原则，结合汕头经济特区(以下简称特区)实际，制定本条例。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 &nbsp;特区范围内的车辆驾驶人、车辆所有人、行人、乘车人以及其他与道路交通活动有关的单位和个人，应当遵守本条例。本条例未规定的，依照相关法律、法规的规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 &nbsp;道路交通安全工作应当坚持以人为本、高效便民、依法管理、安全第一、预防为主的方针，遵循政府统一领导、部门严格监管、单位主动负责、公众积极参与的原则。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 &nbsp;市、区（县）人民政府统一领导、组织、协调本行政区域的道路交通安全管理工作，建立健全规划、建设、管理一体化的道路交通综合协调机制，科学制定道路交通安全管理规划并组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、区（县）人民政府应当将道路交通安全隐患治理、道路交通安全宣传教育以及道路智能交通建设、道路交通安全设施建设纳入道路交通安全管理规划，所需经费由财政予以保障。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、区（县）人民政府应当建立道路交通安全宣传教育机制，通过开展道路交通安全宣传教育，增强公民的道路交通安全意识，提高遵守道路交通法律法规的自觉性。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 &nbsp;市人民政府公安部门是特区道路交通安全管理工作的主管机关，各级公安机关交通管理部门负责本行政区域内的道路交通安全管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划、住房和城乡建设、交通运输、发展和改革、财政、国土资源、质量技术监督、工商行政管理、环境保护、安全生产监督、城市管理、教育、公路、市政、园林等部门应当按照各自的职责，负责做好道路交通安全的相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 &nbsp;鼓励单位和个人在公安机关交通管理部门和志愿服务管理机构的组织下，进行道路交通安全志愿服务，协助维护道路交通秩序，宣传道路交通安全法律、法规。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 &nbsp;对在道路交通安全工作中作出突出贡献的单位和个人，予以表彰和奖励。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章　道路交通安全责任</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 维护道路交通安全、有序、畅通是全社会的责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 &nbsp;市、区（县）人民政府应当将道路交通安全工作纳入考核评价内容，定期评估分析道路交通安全状况，组织开展道路交通安全隐患治理和道路交通安全宣传教育活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 &nbsp;公安部门每年应当制定道路交通安全宣传教育计划，报本级人民政府批准后组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 &nbsp;公安机关交通管理部门应当定期发布道路交通安全管理信息、道路交通安全违法信息，提供道路交通安全咨询和其他依法应当公开的信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 &nbsp;公安机关交通管理部门应当建立道路通行状况实时监测机制，及时发现道路拥堵情况，发布路况信息引导交通出行，疏解道路交通拥堵。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门应当研究分析道路交通拥堵发生的原因，及时向市、区（县）人民政府或者相关部门提出疏解道路交通拥堵的意见和建议，制定改进方案并组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 &nbsp;在交通繁忙路段和路口以及容易引发道路交通拥堵的车站、码头、旅游景点和商场、医院、学校等周边道路，公安机关交通管理部门应当派员进行交通疏导；相关单位应当配合公安机关交通管理部门疏导车辆和行人。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 &nbsp;交通运输行政管理部门应当加强对机动车驾驶员培训机构的监管，组织、指导、监督公共汽车客运企业和道路客（货物）运输企业落实道路交通安全措施和责任，定期通报其交通事故和道路交通安全违法行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 质量技术监督、工商行政管理部门应当依据各自职责，及时查处非法生产、拼装车辆以及销售不符合安全技术标准的车辆成品、配件等行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 &nbsp;城乡规划行政管理部门应当根据城市总体规划，在各层次规划中科学合理确定道路路网和停车场，确保道路交通规划和建设规范化、制度化和可持续发展。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 道路管养部门应当加强道路及其附属设施建设并及时维修、养护，保持道路及其附属设施完好。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 &nbsp;教育行政管理部门应当将道路交通安全教育纳入法制教育的内容，督促学校、幼儿园加强对学生和学龄前儿童的道路交通安全教育，落实道路交通安全责任。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中小学校每学期应当开设交通安全课，聘请交通民警、具备相关专业知识的人士讲授交通安全知识，开展多种形式的道路交通安全学习活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 &nbsp;安全生产监督管理部门应当组织开展道路交通安全专项督查，督促落实道路交通安全责任制。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 &nbsp;环境保护行政管理部门应当加强对车辆尾气污染、废电池等车辆拆解物污染的监督管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 &nbsp;新闻、出版、广播、电视等有关单位有义务进行道路交通安全法律、法规等公益宣传，发布道路交通安全信息。公安机关交通管理部门决定实行道路交通管制的，广播、电视、报纸、互联网站、移动通信等单位应当及时配合做好信息发布工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 交通运输企业应当落实交通安全管理责任制，加强对运输车辆的安全检查，及时排除安全隐患；对行驶中的运输车辆应当进行定位监控，发现车辆超速、超载、驾驶人疲劳驾驶等影响道路交通安全的情形，应当及时提醒纠正。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 &nbsp;机关、企业事业单位、社会团体以及其他组织，应当建立落实道路交通安全责任制，对本单位的人员经常进行道路交通安全宣传教育，提高本单位人员的道路交通安全意识，加强本单位的车辆安全管理，维护道路交通秩序。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　市人民政府应当设立道路交通事故社会救助基金，用于垫付机动车道路交通事故中受害人人身伤亡的丧葬费用、部分或者全部抢救费用。社会救助基金管理办法由市人民政府具体制定。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章 车 辆</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 &nbsp;市人民政府可以根据特区机动车保有量、道路交通流量、道路设施承载能力等情况，对机动车号牌的发放实行总量调控。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　机动车号牌发放办法由市公安部门会同有关部门制定，报市人民政府批准后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 &nbsp;三轮汽车、摩托车、轻便摩托车、燃油助力车等车型在金平、龙湖、濠江区不予注册登记，市人民政府批准的执法机关和用于市政服务的摩托车除外。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 &nbsp;澄海、潮阳、潮南区、南澳县办理注册登记的摩托车、轻便摩托车实行实名制。具有本区（县）户籍或居住证的成年人只能申请注册登记一辆摩托车或轻便摩托车。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 &nbsp;非本市号牌及本条例施行后在澄海、潮阳、潮南区、南澳县注册登记的摩托车、轻便摩托车不得进入城市中心区域行驶。城市中心区域的具体范围，由市人民政府划定并公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对本条例施行前注册登记的摩托车、轻便摩托车，市人民政府可以根据道路通行条件、交通流量的实际情况，划定区域、路段、时段，采取限制或者禁止通行的措施。限制或者禁止通行的区域、路段、时段应当提前十五个工作日向社会公告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 电动自行车应当注册登记后方可上道路行驶。电动自行车的注册登记实行实名制。具有本市户籍或居住证的成年人只能申请登记一辆电动自行车。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门可以根据道路和交通流量的情况,规定限制、禁止电动自行车通行的区域、路段、时段。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　电动自行车销售、登记注册和通行管理的具体规定由市人民政府另行制定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 三轮汽车、燃油助力车、人力三轮车、板车、畜力车等车型不得在城市中心区域道路上行驶，用于市政服务的人力三轮车、保洁车除外。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 &nbsp;残疾人机动轮椅车应当注册登记后方可上路行驶。每个下肢残疾人只能申请登记一辆残疾人机动轮椅车，并限于自用。残疾人机动轮椅车注册登记的具体办法，由市公安机关交通管理部门会同市残疾人联合会等部门制定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　禁止残疾人机动轮椅车从事营运活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条 用于接送义务教育阶段学生的校车，应当按照国家有关校车安全管理法规的要求，申请校车使用许可，领取校车标牌。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　取得校车标牌的车辆应当配备统一的校车标志灯和停车指示标志。校车未运载学生上道路行驶的，不得使用校车标牌、校车标志灯和停车指示标志。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　校车应当保持安全性能良好，校车驾驶人应当取得法定的校车驾驶资格。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 &nbsp;运输砂石、水泥、煤炭、渣土、泥浆等散体物料、废弃物等大（中）型货运车辆在城市中心区域范围内实行备案制度。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　从事上述项目运输的车辆应当实行车厢全密闭化，安装使用行驶记录仪或者卫星定位系统，并按照公安机关交通管理部门指定的时间、路线行驶。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条 过境车辆需经限制、禁止通行的区域或者路段的，应当按照公安机关交通管理部门指定的路线行驶。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条 &nbsp;机动车应当符合国家、省和特区的机动车污染物排放标准，不符合机动车污染物排放标准的，公安机关交通管理部门不予注册登记、核发检验合格标志，不予办理相关变更登记、转移登记。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条 &nbsp;机动车应当按照规定安装、使用车辆号牌、临时号牌。禁止下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）在机动车上安（改）装、使用超标准或者影响交通安全的光电、动力、音响等设备；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在机动车辆号牌、临时号牌上喷涂、粘贴、遮挡影响交通技术监控信息接收的材料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）在机动车上安装、使用影响交通技术监控设备正常使用的装置。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条 &nbsp;禁止单位和个人从事下列非法经营活动：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）生产、销售擅自安装动力装置的自行车、人力三轮车等非机动车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）生产、销售擅自更换发动机的残疾人机动轮椅车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）生产、销售拼装的机动车或者生产、销售擅自改装的机动车。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章　道路通行条件</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 &nbsp;市人民政府应当建立交通影响评价制度，对规划和建设项目实施后可能造成的交通影响进行分析、预测和评估，提出预防或者减轻不良交通影响的交通设计、交通管理方案和措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　新建、改建、扩建的大型建设项目应当依法进行交通影响评价。交通影响评价制度的具体规定由公安部门组织城乡规划、住房和城乡建设、城市管理、交通运输、发展和改革、财政、环境保护等行政管理部门另行制定，并报市人民政府批准后公布实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 &nbsp;道路交通信号灯、交通标志、标线和交通监控设备等道路交通安全设施，应当与道路建设主体工程同时规划、同时设计、同时建设、同时验收。道路建设主体工程与道路交通安全设施未经验收或者验收不合格的，不得投入使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　道路交通安全设施的设置和改建，应当事先征求公安机关交通管理部门的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 公安机关交通管理部门和道路管理部门应当根据道路通行的实际情况，依据职责分工，合理设置车行道、人行道，及时增设、调整、更新交通信号灯、交通标志、交通标线等交通安全设施，提高道路通行效率。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 供电部门因供电设施检修、依法限电等原因，需要中断交通信号灯和交通监控设施供电的，应当按照法律法规的有关规定提前通知公安机关交通管理部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十二条 &nbsp;新建、改建、扩建道路需在道路上开设路口、开辟通道、设置台阶、坡道的，应当符合安全、畅通的要求，并按照规定报相关管理部门审批。相关管理部门在审批前，应当征求公安机关交通管理部门的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　新建、改建、扩建主干道距离交叉路口一百米、次干道八十米、支路五十米范围内不得开设出入口，上述道路与交叉路口的距离以路口的转弯曲线与路段直线的切点起算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　快速路不得设置沿街铺面。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十三条 &nbsp;在已建成使用的道路上申请开设路口，进行道路改造等作业，或者在道路上悬挂、架设管线、广告等设施，应当征得公安机关交通管理部门的同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十四条 &nbsp;挖掘道路、占用道路施工，或者跨越、穿越道路架设、增设管线设施的，应当经相关道路管理部门批准；影响道路交通安全、畅通的，还应当征得公安机关交通管理部门的同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十五条 施工单位进行道路作业，应当错开交通高峰时段，在批准的区域和时间内进行施工，并采取相应的安全防护设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　需要实施交通管制的，应当经公安机关交通管理部门批准，提前向社会发布公告。依法需先予抢修的应急抢修项目，应在抢修的同时向公安机关交通管理部门报告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十六条 &nbsp;管理养护单位应当加强对道路两侧及中心隔离带、交通渠化岛上的植物，以及管线、照明、广告等设施的管理, 对影响道路交通安全的，应当及时排除妨碍。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门发现植物或者设施影响道路交通安全的，应当向管理养护单位提出整改意见，管理养护单位应当及时排除妨碍。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　交通渠化岛、道路两侧及中心隔离带距离路口三十米范围内种植的灌木高度不得高于路面一米。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十七条 &nbsp;城市停车场（库）的规划和建设应当符合道路通行条件，适应车辆停放需求。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　新建、改建、扩建的公共建筑、商业街区、居住区、大（中）型建筑等建设项目，应当按照有关规定配套设计、建设停车场（库）。建筑物配套建设的停车场（库）不得擅自停止使用或者改作他用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励单位和个人投资建设停车场。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十八条 &nbsp;公安机关交通管理部门会同城乡规划、城市管理、价格、交通运输、公安消防等部门在不影响道路交通安全、畅通的前提下，结合道路周边停车需求在次干道及以下等级的道路上设置道路停车泊位。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十九条 &nbsp;停放机动车，应当遵守以下规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）在停车场或者交通标志、标线规定的道路停车泊位内停放，不得在车行道、人行道和其他妨碍交通的地点停放；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在道路停车泊位内，应当按顺行方向或者按照停车泊位标示的方向停放，车身不得超出停车泊位；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）借道进出停车场或者道路停车泊位，不得妨碍其他车辆或者行人通行;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）在实行收费的道路停车泊位停放机动车的，按照有关规定缴纳停车费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十条 &nbsp;禁止非法占用道路、道路停车泊位从事经营活动或者在道路、道路停车泊位设置有碍通行、停车的障碍物。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十一条 &nbsp;因紧急情况或者举办大型活动，公安机关交通管理部门可以在道路范围内确定临时停车区，或者暂停道路停车泊位的使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十二条 设置和调整交通枢纽、客运车辆停靠点、出租小汽车停靠点，交通运输管理部门在批准前，应当征得公安机关交通管理部门同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十三条 任何单位和个人不得擅自设置、移动、占用、损毁交通信号灯、交通标志、交通标线、交通监控设施、交通护栏等道路交通安全设施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十四条 &nbsp;公安机关交通管理部门发现已投入使用的道路、停车场、道路配套设施存在交通安全隐患的，应当向有关单位提出明确的防范交通事故、消除隐患的建议，并督促有关单位及时进行整改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门应当在交通事故多发路段设立警示牌，提醒驾驶人谨慎驾驶。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章 &nbsp;道路通行规定</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十五条 &nbsp;有下列情形之一的，机动车驾驶人应当减速行驶，或者按规定让行：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）行经人行横道；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）通过没有交通信号灯的路口或右转专用车道；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）经过泥泞或者积水道路；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）遇有公共汽车驶入或驶出公共汽车站点；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）遇有中小学生、幼儿园学龄前儿童上、下校车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）遇有执行紧急任务的警车、消防车、救护车、工程救险车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）遇有前方交叉路口交通阻塞，或者遇有停车排队等候、缓慢行驶；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）法律、法规规定应当减速、让行的其他情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十六条 &nbsp;驾驶机动车，不得有下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）在道路上追逐竞驶、非法赛车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）故意在道路上停驶或者以其他方式阻碍交通；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）不按路口导向线标示线路行驶、转向，或者进入导向车道后变更车道或者不按规定方向行驶，占用或者跨越导流线行驶；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）不按交通信号规定通行；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）违反禁令标志、禁止标线指示通行；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）不按规定超车、让行，或者逆向行驶；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）行经交叉路口不按规定行车或停车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）不按规定使用灯光；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）驾驶摩托车推、拉其他车辆；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）驾驶摩托车不戴安全头盔；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十一）在机动车道上购买兜售者的物品。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十七条 服用国家管制的精神药品或者麻醉药品不得驾驶机动车。机动车驾驶人有服用国家管制的精神药品或者麻醉药品嫌疑的，应当接受测试、检验。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十八条 &nbsp;驾驶非机动车应当在非机动车道内行驶；没有划分非机动车道的，应当在距道路右侧边缘线一点五米的范围内行驶，并遵守下列规定：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）驾驶电动自行车应戴安全头盔；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）与相邻的非机动车保持安全距离，在行经人行道或人行横道时避让行人；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）不得在机动车之间穿插通行、逆向行驶；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）不得在车行道上停车滞留；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）不得驾驶擅自安装、改装动力装置的非机动车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）不得驾驶制动器失灵或者安全设施不全的非机动车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）不得违反规定载人、载物；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）时速不超过十五公里；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）酒后不得驾驶安装动力装置的非机动车；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）不得安装棚架、边斗等附加装置或者使用伞具等妨碍交通安全的器具；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五十九条 &nbsp;行人不得有下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）不按照交通信号指示通行；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）跨越、穿越、倚坐道路隔离设施；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）扒车、追车、强行拦车等妨碍道路交通安全的行为；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）在车行道上乞讨、兜售、散发物品，或者在车行道上坐卧、停留、嘻闹、拉客；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）在道路上使用滑板、旱冰鞋等工具滑行；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）在道路上牵引、驱赶牲畜；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）通过铁路道口未按照管理人员指挥通行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十条 &nbsp;乘车人不得有下列行为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）乘坐摩托车不戴安全头盔、侧坐、倒坐或者使用伞具等妨碍交通安全的器具；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）乘坐汽车将身体或者携带物品任何部分伸出车外或超出车身范围。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六章 执法与监督</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十一条 &nbsp;公安机关交通管理部门应当建立交通警察路面巡查制度，安排警力上路巡逻，并在道路交通高峰时段增派警力，及时纠正查处道路交通安全违法行为，维护道路交通秩序，疏导交通，保障交通畅通。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十二条 公安机关交通管理部门可以聘用交通协管员。交通协管员应当接受培训并佩戴统一标志，方可在公安机关交通管理部门的组织指挥下协助维护道路交通秩序。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十三条 &nbsp;非机动车驾驶人或者行人违反道路交通安全法律、法规关于道路通行规定的，按照以下方式之一接受处理：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按规定接受罚款处罚；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）按照交通警察的要求劝导交通；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）参加交通安全学习，观看交通安全视频录像。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　按照前款规定协助劝导交通的时间不得少于一小时；观看交通安全视频录像不少于两小时。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　选择劝导交通或者参加交通安全学习，观看交通安全视频录像的，可以不予罚款处罚。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十四条 &nbsp;机动车驾驶人违反道路交通安全法律、法规被处暂扣一个月以上机动车驾驶证处罚的，除依法接受罚款外，应当到公安机关交通管理部门指定的地点接受三小时的道路交通安全教育。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　机动车驾驶人被处暂扣一个月以上机动车驾驶证处罚的，可以申请参加交通劝导。参加交通劝导一个小时折抵暂扣机动车驾驶证一天，但最长不得超过被处暂扣机动车驾驶证期限的二分之一。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十五条 接受安全教育和劝导交通应当由违法行为人完成。由他人代替或者有其他弄虚作假行为的，安全教育和参加劝导交通的时间重新计算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十六条 下列依法需要公告的事项，公安机关交通管理部门应当在交警公众信息平台发布：<br/>　　（一）车辆被扣留，逾期不接受处理的；<br/>　　（二）通知机动车所有人办理车辆注销登记的；<br/>　　（三）机动车逾期未办理注销登记，其登记证书、号牌、行驶证作废的；<br/>　　（四）机动车驾驶证停止使用或者作废的；<br/>　　（五）其他需要公告的事项。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十七条 &nbsp;公安机关交通管理部门应当建立举报道路交通肇事逃逸奖励制度。对于举报属实的，给予举报人物质奖励，并为举报人保密。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十八条 任何单位和个人都有权对公安机关交通管理部门及其交通警察不履行法定职责以及其他违法违纪的行为进行检举、控告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六十九条 &nbsp;公安机关交通管理部门应当建立健全投诉、举报制度，公布投诉、举报电话、信箱或者电子邮箱，自觉接受社会和公民的监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门应当自接到投诉、举报之日起五日内决定是否受理，并告知投诉人、举报人；对决定受理的，应当及时组织调查并将处理结果告知投诉人、举报人。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七章　法律责任</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十条 违反本条例第二十八条、第二十九条的规定，驾驶摩托车、轻便摩托车、电动自行车在限制、禁止通行的区域或者路段通行的，由公安机关交通管理部门扣留车辆，处二百元罚款。一个记分周期内有三次以上行为的，除罚款外，并处暂扣机动车驾驶证一个月。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十一条 违反本条例第二十九条的规定，驾驶未经注册登记的电动自行车上道路行驶，由公安机关交通管理部门扣留车辆，处三百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十二条 违反本条例第三十条的规定，驾驶三轮汽车、燃油助力车、人力三轮车、板车、畜力车在城市中心区域行驶的，由公安机关交通管理部门扣留车辆，处二百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十三条 违反本条例第三十一条第一款的规定，驾驶未经注册登记的残疾人机动轮椅车上路行驶的，由公安机关交通管理部门扣留车辆，处二百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十一条第二款的规定，驾驶残疾人机动轮椅车营运的，由公安机关交通管理部门扣留车辆，处以一千元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十四条 违反本条例第三十二条第一款规定，使用未取得校车标牌的车辆提供校车服务，或者使用未取得校车驾驶资格的人员驾驶校车的，由公安机关交通管理部门扣留该机动车，处一万元以上二万元以下的罚款，有违法所得的予以没收。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十二条第二款规定，驾驶校车运载学生，不按照规定放置校车标牌、开启校车标志灯，或者未运载学生，使用校车标牌、校车标志灯和停车指示标志的，由公安机关交通管理部门责令改正，可以处二百元罚款。 &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十二条第三款规定，机动车驾驶人未取得校车驾驶资格驾驶校车的，由公安机关交通管理部门处一千元以上三千元以下的罚款，情节严重的，可以并处吊销机动车驾驶证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十五条 &nbsp;违反本条例第三十三条第二款的规定，运输车辆车厢不实行全密闭化，撒漏渣土、抛洒造成污染的，由城市管理部门责令改正，限期清除污染，处以五百元以上三千元以下罚款。撒漏、倾倒建筑垃圾造成大面积污染的，处以三千元以上一万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十三条第二款的规定，运输车辆未按指定的时间、路线行驶以及运输车辆逆向行驶、违反交通信号、超过车辆的载质量等行为的，除依法给予罚款处罚外，公安机关交通管理部门对运输车辆半年内不予核发备案证;再次发生的，一年内不予核发备案证;第三次发生的，两年内不予核发备案证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十六条 违反本条例第三十四条的规定，过境汽车未按照公安机关交通管理部门指定的路线行驶，由公安机关交通管理部门责令改正，处二百元罚款。一个记分周期内第二次违反的，处一千元罚款；一个记分周期内三次以上违反的，除罚款外，并处暂扣机动车驾驶证一个月。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十七条 有下列情形之一的，公安机关交通管理部门应当定期将交通违法行为人和运输企业的道路交通安全违法行为信息通知信用征信机构录入个人或者企业信用征信系统：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）驾驶人违反道路交通安全法律、法规，被处拘留、吊销或者暂扣机动车驾驶证三个月以上的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）发生道路交通事故致人重伤或者死亡且负有同等以上事故责任的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）一年内运输企业车辆平均违法率超过百分之三十，或者发生交通事故被追究刑事责任率超过百分之十的；驾驶人醉酒驾驶运输车辆的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十八条 违反本条例第三十六条第（一）项的规定，在机动车上安（改）装、使用超标准或者影响交通安全设备的，由公安机关交通管理部门处警告或者一千元罚款，强制拆除非法装置并予以收缴。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第三十六条第（二）项、第（三）项的规定，在机动车号牌上喷涂、粘贴、遮挡或者以其他方式妨碍交通监管的，由公安机关交通管理部门责令改正，处三千元罚款，强制拆除非法装置并予以收缴。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七十九条 违反本条例第三十七条的规定，从事非法经营活动，由质量技术监督管理部门或者工商行政管理部门依照各自职责，没收非法生产、销售的车辆成品及配件，违反第（一）、（二）项的，可以并处二千元以上一万元以下罚款，违反第（三）项的，可以并处非法产品价值三倍以上五倍以下罚款；有营业执照的，吊销营业执照，没有营业执照的，予以查封。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十条 违反本条例第四十四条的规定，擅自挖掘道路、占用道路施工或者跨越、穿越道路架设、增设管线设施的，由公安机关交通管理部门责令停止施工，恢复交通，并移交有关道路管理部门依法处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十一条 &nbsp;违反本条例第四十九条的规定，未按规定停放机动车，或者在道路停车泊位停放车辆不按有关规定缴纳停车费的，由公安机关交通管理部门责令改正，处二百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十二条 违反本条例第五十条的规定，非法占有道路或停车泊位的，由公安机关交通管理部门责令改正，处三百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十三条 违反本条例第五十三条的规定，擅自设置、移动、占用、损毁交通标志标牌、交通标线、交通护栏等道路交通安全设施，由公安机关交通管理部门责令限期恢复原状；逾期未恢复的，处五百元以上二千元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十四条 违反本条例第五十五条的规定，驾驶机动车未减速行驶或按规定让行的，由公安机关交通管理部门责令改正，处二百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十五条 违反本条例第五十六条第（一）项的规定，在道路上追逐竞驶、非法赛车的，由公安机关交通管理部门处二千元罚款，并处吊销机动车驾驶证，五年内不得重新取得机动车驾驶证，构成犯罪的，依法追究刑事责任；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　违反本条例第五十六条第（二）项至第（十一）项的规定，未按规定驾驶机动车，由公安机关交通管理部门责令改正，处二百元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十六条 &nbsp;违反本条例第五十七条的规定，机动车驾驶人服用国家管制的精神药品或者麻醉药品的，公安机关交通管理部门应当将其驾驶的机动车移至不妨碍交通的地点停放并处二千元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十七条 违反本条例第五十八条第（一）项的规定，驾驶电动自行车未戴安全头盔的，由公安机关交通管理部门责令改正，并要求其协助劝导交通一小时或者观看交通安全视频录像两小时；违反第（二）项至第（九）项的规定，未按规定驾驶非机动车的，责令改正，处警告或者一百元罚款。驾驶擅自安装动力装置的非机动车上路行驶的，强制拆除非法安装的动力装置并予以收缴。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十八条 行人违反本条例第五十九条的规定，由公安机关交通管理部门责令改正，处警告或者二十元罚款。但有下列行为之一的，处一百元罚款：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）翻越机动车道隔离设施的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）在机动车道内兜售物品或者散发广告的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八十九条 乘车人违反本条例第六十条的规定，由公安机关交通管理部门责令改正，处警告或者五十元罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十条 公安机关交通管理部门扣留机动车、非机动车，应当当场出具凭证，并告知当事人在规定期限内到公安机关交通管理部门接受处理。 &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公安机关交通管理部门对被扣留的车辆应当妥善保管，不得使用。 &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　逾期不接受处理，并且经公告三个月仍不来接受处理的，对扣留的车辆依法处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十一条　公安机关交通管理部门及其交通警察有下列行为的，对直接负责的主管人员和其他直接责任人员给予相应的行政处分；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）利用职务便利收受他人财物或者谋取其他利益的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）违法扣留车辆、机动车行驶证、驾驶证、车辆号牌的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）为不符合污染物排放标准的机动车办理相关登记或核发检验合格标志的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）违反规定消除道路交通安全违法行为记录的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）统计交通违法、交通事故及伤亡数据时弄虚作假的； &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）干扰依法进行的道路交通安全执法活动的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）不执行罚款决定与罚款收缴分离制度或者不按规定将依法收取的费用、收缴的罚款全部上缴国库的； &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）滥用职权、玩忽职守、徇私舞弊以及其他不依法履行法定职责的行为。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十二条 &nbsp;有关行政管理部门及其工作人员违反本条例规定，不履行法定职责或者滥用职权，有下列行为之一的，依法追究直接负责的主管人员和其他直接责任人员的行政责任；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）未按照本条例规定落实道路交通安全责任的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）对未按照有关规定同时规划、设计、建设、验收道路交通安全设施的道路建设主体工程予以审批的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）对未按照有关规定配套设计、建设停车场（库）的建设项目予以审批的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）未征得公安机关交通管理部门同意，挖掘道路、占用道路施工，或者跨越、穿越道路架设、增设管线设施影响道路交通安全、畅通的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）其他不履行法定职责或者滥用职权的行为。&nbsp;</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八章 &nbsp;附 则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十三条 &nbsp;本条例所称电动自行车，是指符合国家技术标准，以蓄电池作为辅助能源，具有两个车轮，能实现人力骑行、电动或电助动功能的特种自行车。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十四条 本条例所称的快速路，是指设有中央分隔带，具有四条以上的机动车道，全部或部分采用立体交叉与控制出入，设计车速为60km/h至100km/h的道路。 &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十五条 &nbsp;高速公路的交通安全管理工作，按照国家、省有关高速公路的相关规定进行管理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　拖拉机登记和检验、拖拉机驾驶证申领和使用等按照国家、省有关规定办理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十六条 &nbsp;市人民政府可以根据本条例制定实施办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条例规定市人民政府、相关部门以及单位制定具体管理办法的，应当自条例实施之日起一年内制定并组织实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九十七条 &nbsp;本条例自2014年3月1日起施行，《汕头经济特区摩托车管理规定》同时废止。</span></p>','2016-05-18 15:02:38',0,NULL,2,NULL,45,11,'',0,0,0,'2016-05-18 15:07:50','2016-05-18 15:07:50');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (469,'【规章】《城镇污水排入排水管网许可管理办法》','im3pvFKyTixKvlAj.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">中华人民共和国住房和城乡建设部令</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">第21号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《城镇污水排入排水管网许可管理办法》已经第20次部常务会议审议通过，现予发布，自2015年3月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">住房城乡建设部部长　陈政高</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年1月22日</span></p><p><br/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城镇污水排入排水管网许可管理办法</span></strong></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章　总则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为了加强对污水排入城镇排水管网的管理，保障城镇排水与污水处理设施安全运行，防治城镇水污染，根据《中华人民共和国行政许可法》、《城镇排水与污水处理条例》等法律法规，制定本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　在中华人民共和国境内申请污水排入排水管网许可（以下称排水许可），对从事工业、建筑、餐饮、医疗等活动的企业事业单位、个体工商户（以下称排水户）向城镇排水设施排放污水的活动实施监督管理，适用本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条　国务院住房城乡建设主管部门负责全国排水许可工作的指导监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　省、自治区人民政府住房城乡建设主管部门负责本行政区域内排水许可工作的指导监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　直辖市、市、县人民政府城镇排水与污水处理主管部门（以下简称城镇排水主管部门）负责本行政区域内排水许可证书的颁发和监督管理。城镇排水主管部门可以委托专门机构承担排水许可审核管理的具体工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条　城镇排水设施覆盖范围内的排水户应当按照国家有关规定，将污水排入城镇排水设施。排水户向城镇排水设施排放污水，应当按照本办法的规定，申请领取排水许可证。未取得排水许可证，排水户不得向城镇排水设施排放污水。城镇居民排放生活污水不需要申请领取排水许可证。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在雨水、污水分流排放的地区，不得将污水排入雨水管网。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条　城镇排水主管部门会同环境保护主管部门依法确定并向社会公布列入重点排污单位名录的排水户。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章　许可申请与审查</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条　排水户向所在地城镇排水主管部门申请领取排水许可证。城镇排水主管部门应当自受理申请之日起20日内作出决定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　集中管理的建筑或者单位内有多个排水户的，可以由产权单位或者其委托的物业服务企业统一申请领取排水许可证，并由领证单位对排水户的排水行为负责。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　各类施工作业需要排水的，由建设单位申请领取排水许可证。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　申请领取排水许可证，应当如实提交下列材料：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）排水许可申请表；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）排水户内部排水管网、专用检测井、污水排放口位置和口径的图纸及说明等材料；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）按规定建设污水预处理设施的有关材料；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）排水隐蔽工程竣工报告；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）排水许可申请受理之日前一个月内由具有计量认证资质的水质检测机构出具的排水水质、水量检测报告；拟排放污水的排水户提交水质、水量预测报告；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）列入重点排污单位名录的排水户应当提供已安装的主要水污染物排放自动监测设备有关材料；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）法律、法规规定的其他材料。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条　符合以下条件的，由城镇排水主管部门核发排水许可证：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）污水排放口的设置符合城镇排水与污水处理规划的要求；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）排放污水的水质符合国家或者地方的污水排入城镇下水道水质标准等有关标准；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）按照国家有关规定建设相应的预处理设施；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）按照国家有关规定在排放口设置便于采样和水量计量的专用检测井和计量设备；列入重点排污单位名录的排水户已安装主要水污染物排放自动监测设备；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）法律、法规规定的其他条件。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　施工作业需排水的，建设单位应当已修建预处理设施，且排水符合本条第一款第二项规定的标准。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条　排水许可证的有效期为5年。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　因施工作业需要向城镇排水设施排水的，排水许可证的有效期，由城镇排水主管部门根据排水状况确定，但不得超过施工期限。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条　排水许可证有效期满需要继续排放污水的，排水户应当在有效期届满30日前，向城镇排水主管部门提出申请。城镇排水主管部门应当在有效期届满前作出是否准予延续的决定。准予延续的，有效期延续5年。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水户在排水许可证有效期内，严格按照许可内容排放污水，且未发生违反本办法规定行为的，有效期届满30日前，排水户可提出延期申请，经原许可机关同意，可不再进行审查，排水许可证有效期延期5年。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条　在排水许可证的有效期内，排水口数量和位置、排水量、污染物项目或者浓度等排水许可内容变更的，排水户应当按照本办法规定，重新申请领取排水许可证。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水户名称、法定代表人等其他事项变更的，排水户应当在工商登记变更后30日内向城镇排水主管部门申请办理变更。</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章　管理和监督</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条　排水户应当按照排水许可证确定的排水类别、总量、时限、排放口位置和数量、排放的污染物项目和浓度等要求排放污水。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条　排水户不得有下列危及城镇排水设施安全的行为：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）向城镇排水设施排放、倾倒剧毒、易燃易爆物质、腐蚀性废液和废渣、有害气体和烹饪油烟等；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）堵塞城镇排水设施或者向城镇排水设施内排放、倾倒垃圾、渣土、施工泥浆、油脂、污泥等易堵塞物；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）擅自拆卸、移动和穿凿城镇排水设施；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）擅自向城镇排水设施加压排放污水。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条　排水户因发生事故或者其他突发事件，排放的污水可能危及城镇排水与污水处理设施安全运行的，应当立即停止排放，采取措施消除危害，并按规定及时向城镇排水主管部门等有关部门报告。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　城镇排水主管部门应当加强对排水户的排放口设置、连接管网、预处理设施和水质、水量监测设施建设和运行的指导和监督。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条　城镇排水主管部门应当将排水许可材料按户整理归档，对排水户档案实行信息化管理。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条　城镇排水主管部门委托的具有计量认证资质的排水监测机构应当定期对排水户排放污水的水质、水量进行监测，建立排水监测档案。排水户应当接受监测，如实提供有关资料。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　列入重点排污单位名录的排水户，应当依法安装并保证水污染物排放自动监测设备正常运行。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　列入重点排污单位名录的排水户安装的水污染物排放自动监测设备，应当与环境保护主管部门的监控设备联网。环境保护主管部门应当将监测数据与城镇排水主管部门实时共享。对未与环境保护主管部门的监控设备联网，城镇排水主管部门已进行自动监测的，可以将监测数据与环境保护主管部门共享。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条　城镇排水主管部门应当依照法律法规和本办法的规定，对排水户排放污水的情况实施监督检查。实施监督检查时，有权采取下列措施：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）进入现场开展检查、监测；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）要求被监督检查的排水户出示排水许可证；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）查阅、复制有关文件和材料；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）要求被监督检查的单位和个人就有关问题做出说明；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）依法采取禁止排水户向城镇排水设施排放污水等措施，纠正违反有关法律、法规和本办法规定的行为。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　被监督检查的单位和个人应当予以配合，不得妨碍和阻挠依法进行的监督检查活动。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条　城镇排水主管部门委托的专门机构，可以开展排水许可审查、档案管理、监督指导排水户排水行为等工作，并协助城镇排水主管部门对排水许可实施监督管理。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条　有下列情形之一的，许可机关或者其上级行政机关，根据利害关系人的请求或者依据职权，可以撤销排水许可：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）城镇排水主管部门工作人员滥用职权、玩忽职守作出准予排水许可决定的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）超越法定职权作出准予排水许可决定的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）违反法定程序作出准予排水许可决定的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）对不符合许可条件的申请人作出准予排水许可决定的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）依法可以撤销排水许可的其他情形。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水户以欺骗、贿赂等不正当手段取得排水许可的，应当予以撤销。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条　有下列情形之一的，城镇排水主管部门应当依法办理排水许可的注销手续：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）排水户依法终止的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）排水许可依法被撤销、撤回，或者排水许可证被吊销的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）排水许可证有效期满且未延续许可的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）法律、法规规定的应当注销排水许可的其他情形。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　城镇排水主管部门应当按照国家有关规定将监督检查的情况向社会公开。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门及其委托的专门机构、排水监测机构的工作人员对知悉的被监督检查单位和个人的技术和商业秘密负有保密义务。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条　城镇排水主管部门实施排水许可不得收费。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇排水主管部门实施排水许可所需经费，应当列入城镇排水主管部门的预算，由本级财政予以保障，按照批准的预算予以核拨。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/>　　第四章　法律责任</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　城镇排水主管部门有下列情形之一的，由其上级行政机关或者监察机关责令改正，对直接负责的主管人员和其他直接责任人员依法给予处分；构成犯罪的，依法追究刑事责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）对不符合本规定条件的申请人准予排水许可的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）对符合本规定条件的申请人不予核发排水许可证或者不在法定期限内作出准予许可决定的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）利用职务上的便利，收受他人财物或者谋取其他利益的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）泄露被监督检查单位和个人的技术或者商业秘密的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）不依法履行监督管理职责或者监督不力，造成严重后果的。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条　违反本办法规定，在城镇排水与污水处理设施覆盖范围内，未按照国家有关规定将污水排入城镇排水设施，或者在雨水、污水分流地区将污水排入雨水管网的，由城镇排水主管部门责令改正，给予警告；逾期不改正或者造成严重后果的，对单位处10万元以上20万元以下罚款；对个人处2万元以上10万元以下罚款，造成损失的，依法承担赔偿责任。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条　违反本办法规定，排水户未取得排水许可，向城镇排水设施排放污水的，由城镇排水主管部门责令停止违法行为，限期采取治理措施，补办排水许可证，可以处50万元以下罚款；对列入重点排污单位名录的排水户，可以处30万元以上50万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条　排水户未按照排水许可证的要求，向城镇排水设施排放污水的，由城镇排水主管部门责令停止违法行为，限期改正，可以处5万元以下罚款；造成严重后果的，吊销排水许可证，并处5万元以上50万元以下罚款，对列入重点排污单位名录的排水户，处30万元以上50万元以下罚款，并将有关情况通知同级环境保护主管部门，可以向社会予以通报；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条　排水户名称、法定代表人等其他事项变更，未按本办法规定及时向城镇排水主管部门申请办理变更的，由城镇排水主管部门责令改正，可以处3万元以下罚款。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条　排水户以欺骗、贿赂等不正当手段取得排水许可的，可以处3万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条　违反本办法规定，排水户因发生事故或者其他突发事件，排放的污水可能危及城镇排水与污水处理设施安全运行，没有立即停止排放，未采取措施消除危害，或者并未按规定及时向城镇排水主管部门等有关部门报告的，城镇排水主管部门可以处3万元以下罚款。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条　违反本办法规定，从事危及城镇排水设施安全的活动的，由城镇排水主管部门责令停止违法行为，限期恢复原状或者采取其他补救措施，并给予警告；逾期不采取补救措施或者造成严重后果的，对单位处10万元以上30万元以下罚款，对个人处2万元以上10万元以下罚款；造成损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条　排水户违反本办法规定，拒不接受水质、水量监测或者妨碍、阻挠城镇排水主管部门依法监督检查的，由城镇排水主管部门给予警告；情节严重的，处3万元以下罚款。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/>　　第五章　附则</span><br/></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条　排水许可证由国务院住房城乡建设主管部门制定格式，由省、自治区人民政府住房城乡建设主管部门和直辖市人民政府城镇排水主管部门组织印制。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排水许可申请表由国务院住房城乡建设主管部门制定推荐格式，直辖市、市、县人民政府城镇排水主管部门可参照印制。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条　本办法自2015年3月1日起施行。《城市排水许可管理办法》（建设部令第152号）同时废止。</span><br/></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 15:08:25',0,NULL,2,NULL,24,11,'',0,0,0,'2016-05-18 15:18:00','2016-05-18 15:18:00');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (470,'【规章】《城市道路照明设施管理规定》','HU64A0Iu3BcS7KpD.jpg','<p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　颁布部门：建设部</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　文 号：第21号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　颁布时间：1992-11-30</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　生效时间：1993-1-1<br/><br/></span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">城市道路照明设施管理规定</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 总 则&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 为加强城市道路照明设施管理，保证城市道路照明设施完好，促进城市现代化建设，制定本规定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 凡在城市规划区内从事城市道路照明设施的规划、建设、维护和管理，使用城市道路照明设施的单位和个人，必须遵守本规定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 本规定所称城市道路照明设施，是指用于城市道路(含里巷、住宅小区、桥梁、隧道、广场、公共停车场)、不售票的公园和绿地等处的路灯配电室、变压器、配电箱、灯杆、地上地下管线、灯具、工作井以及照明附属设备等。&nbsp;</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 国务院城市建设行政主管部门主管全国城市道路照明设施工作。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　县级以上地方人民政府城市建设行政主管部门负责本行政区域城市道路设施工作。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市人民政府城市建设行政主管部门可以设置城市道路照明设施管理机构，也可以委托有关机构，负责本城市规划区内道路照明设施的日常管理工作。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 城市道路照明设施是国家财产，任何单位和个人都有权对违反本规定的行为进行制止、检举和控告。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二章 照明设施的规划和建设&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 城市道路照明设施规划、建设和改造计划应当纳入城市道路建设、改造规划和年度建设计划，并与其同步实施。城市建设行政主管部门负责制定市道路照明设施规划和建设计划，报同级人民政府批准后由城市道路照明设施管理机构负责具体实施。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 需要改造的城市道路照明设施，由城市道路照明设施机构负责编制改造规划，报城市建设行政主管部门批准后由城市道路照明设施管理机构负责具体实施。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 城市新建和改建的城市道路照明设施必须符合有关设计安装规程规定，并积极采用新光源、新技术、新设备。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 城市道路照明设施的新建、改建工程必须符合国家有关标准规范，并经验收合格后交付使用。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 厂(矿)或者其他单位投资建设的城市道路照明设施，需移交城市道路照明设施管理机构的，应当报城市建设行政主管部门审核同意，并应当具备下列条件:&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)符合道路照明安装及施工质量标准；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)提供必要的维修条件；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)交纳l年至3年的运行维护费用。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 对符合上述条件的城市道路照明设施，由城市建设行政主管部门组织验收，合格后方可办理资产移交手续。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 城市道路照明设施的改建和维护，应当按照现有资金渠道安排计划。住宅小区和旧城改造中的城市道路照明设施建设应当纳入城市建设综合开发计划。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 城市道路照明设施中的灯杆，可以分为专用杆和合用杆。对道路两侧符合城市道路照明设施条件的电力杆和无轨电车杆在不影响其功能和交通的前提下应当予以利用。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三章 照明的维护和管理&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 城市道路照明设施的维护和管理应当坚持安全第一，认真执行各项规章制度，保证城市道路照明设施完好、运行正常。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 城市建设行政主管部门必须对道路照明设施管理机构建立严格的检查和考核制度，及时督促更换和修复破损的照明设施，使亮灯率不低于95%。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 各地根据其具体情况可以采用以下节能方式:&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)根据道路的行人、车辆流量等因素实行分时照明；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)对气体放电灯采用无功补偿；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)采用先进的停电、送电控制方式；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(四)推广和采用高光效光源、逐步取代低光效光源；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(五)采用节能型的镇流器和控制电器；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(六)采用高效率的照明灯具，并定期对照明灯具进行清扫，提高照明效果；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(七)其他行之有效的节能措施。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 城市建设行政主管部门应当在道路照明设施管理机构内设置城建监督人员，负责城市道路照明设施的安全运行与维护的监督检查。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 任何单位和个人在进行可能触及、迁移、拆除城市道路照明设施或者影响其安全运行的地上、地下施工时，应当经城市建设行政主管部门审核同意后，由城市道路照明设施管理机构负责迁移或拆除工作，费用由申报单位承担。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 城市道路照明设施附近的树木距带电物体的安全距离不得小于1.0米。因自然生长而不符合安全距离标准影响照明效果的树木，由城市道路照明设施管理机构与城市园林绿化管理部门协商后剪修；因不可抗力致使树木严重危及城市道路照明设施安全运行的，城市道路照明设施管理机构应当采取紧急措施进行剪修，并同时通知城市园林绿化管理部门。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 任何单位和个人在损坏道路照明设施后，应当保护事故现场，防止事故扩大，并立即通知城市道路照明设施管理机构及有关单位。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四章 奖励和处罚&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 城市建设行政主管部门对在城市道路照明设施管理工作中做出显著成绩的单位和个人应当给予表彰和奖励。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 违反本规定，并有下列行为之一的，由城市建设行政主管部门或者授权管理机构责令限期拆除，赔偿经济损失、没收违章用电设备，并可处以罚款。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)擅自拆除、迁移、改动城市道路照明设施的。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)在城市道路设施附近堆放杂物、挖坑取土、兴建建筑物及有碍城市道路照明设施正常维护和安全运行活动的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)擅自在城市道路照明灯杆上架设通讯线(缆)或者安置其他设施的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(四)私自接用路灯电源的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(五)偷盗城市道路照明设施的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(六)故意打、砸城市道路照明设施的；&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(七)不听劝阻和制止，非法占用城市道路照明设施的。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 违反本规定，构成治安管理处罚的，由公安机关依照《中华人民共和国治安管理处罚条例》予以处罚；构成犯罪的由司法机关依法追究刑事责任。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 当事人对行政处罚不服的，可以在接到处罚通知之日起十五日内，向作出处罚决定机关的上一级机关申请复议，对复议决定不服的，可以在接到复议决定之日起十五日内向人民法院起诉；当事人可以直接向人民法院起诉。逾期不申请复议，也不向人民法院起诉，又不履行处罚决定的，由作出处罚决定的机关申请人民法院强制执行。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 城市建设行政主管部门及城市道路照明设施机构的工作人员玩忽职守、滥用职权、徇私舞弊的，由其所在单位或者上级主管部门给予行政处分；构成犯罪的，由司法机关依法追究刑事责任。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五章 附 则&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 各省、自治区、直辖市人民政府建设行政主管部门可以根据本规定制定实施细则。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 未设镇建制的矿区可以参照本规定执行。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 本规定由建设部负责解释。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 本规定自一九九三年一月一日起施行。&nbsp;</span></p>','2016-05-18 15:19:49',0,NULL,2,NULL,23,11,'',0,0,0,'2016-05-18 15:27:05','2016-05-18 15:27:05');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (471,'【规章】《广东省户外广告管理规定》','DFetqkGotiDCqLuc.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为了规范户外广告管理秩序，维护经营者和消费者的合法权益，根据《中华人民共和国广告法》、《广告管理条例》和有关法律、法规，结合本省实际，制定本规定。&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　在本省行政区域内设置和发布户外广告的有关活动，适用本规定。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本规定所称户外广告，是指利用户外场所、空间、设施等发布的广告。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本规定所称户外广告设施，是指利用建筑物、构筑物、场地等设置的，向户外空间发布广告的霓虹灯、展示牌、电子显示装置、灯箱、实物造型等设施。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条　工商行政管理部门是户外广告的监督管理机关，负责户外广告的登记管理工作。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、县人民政府应当按照精简、高效、便民原则，指定三个以下部门负责本行政区域户外广告设置专项规划的组织编制，以及户外广告设施的设置审批和监督管理工作。其他相关部门依据各自职责，配合做好户外广告的监督管理工作。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条　市、县人民政府指定的部门应当结合城乡规划和交通运输发展规划的要求，组织编制本行政区域户外广告设置专项规划，并报本级人民政府审批。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　举办文化、旅游、体育、会展、庆典、公益、促销等活动临时设置的户外广告设施，以及利用交通工具、移动设施设置的户外广告设施，不纳入户外广告设置专项规划。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条　编制户外广告设置专项规划，应当征求相关部门和行业协会的意见，并采取座谈会、论证会等方式征求专家和公众的意见。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　户外广告设置专项规划草案应当向社会公示，公示时间不得少于30日。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条　经批准的户外广告设置专项规划应当在政府网站、新闻媒体或者专门场所公告，并在政府网站长期公布，便于公众查询和监督。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经公布的户外广告设置专项规划，不得随意更改；确需更改的，应当经原批准机关批准。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　户外广告设置专项规划应当明确户外广告设施的设置地点、位置、形式、规格等具体要求。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条　设置户外广告设施应当遵守以下规定：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）符合户外广告设置专项规划的范围和要求。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）使用安全、环保、清洁材料；符合国家建筑物和构筑物结构荷载、防雷、防风、抗震、消防、电气安全以及环境保护的要求；使用光源性装置的，避免对周边环境造成光污染。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）符合其他安全技术规范和标准。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条　有下列情形之一的，不得设置户外广告设施：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）利用或者遮挡指路牌、交通信号灯、电子监控等交通安全设施、交通标志及其支架的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）霓虹灯、LED等光源性广告设施与交通信号灯、电子监控设施距离过近，户外广告设施的形式与交通安全设施、交通标志相近，影响交通安全设施、交通标志正常使用的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）影响道路交通安全的区域；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）破坏建筑物、构筑物结构安全，影响市政公共设施、无障碍设施使用的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）影响消防安全设施使用，妨碍消防车通行以及影响逃生、灭火救援和消防登高扑救的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）利用立交、高架道路桥梁、危房、违章建筑的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）利用行道树、绿化带，侵占、损毁绿地，占用机动车道、人行道路面或者阻碍机动车、行人通行的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）国家机关、学校、医院、名胜风景点、文物保护单位的建筑控制地带，但自设性户外广告除外；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）户外广告设置专项规划禁止设置户外广告的其他情形。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条　设置户外广告设施涉及两个以上主管部门实施许可或者审批的，市、县人民政府应当指定一个主管部门受理申请并转告有关部门分别提出意见后统一办理。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条　设置非临时性户外广告设施，许可或者审批机关在作出许可或者审批决定前，应当在政府网站和专门场所公示15日，或者通过书面征求意见等方式征求利害关系人的意见。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对利害关系人提出的异议，许可或者审批机关应当及时处理，并回复处理结果。必要时可以采取听证会或者论证会等方式听取各方意见。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条　户外广告设施设置者应当定期对户外广告设施进行安全检查，保障户外广告设施安全；对检查不合格的，应当立即进行维修或者拆除。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　遇到台风、暴雨等自然灾害，户外广告设施设置者应当采取相应的安全防范措施。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条　户外广告发布者和户外广告设施设置者应当加强日常维护管理，保证户外广告及其设施整洁、完好、美观。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对画面污损、严重褪色、字体残缺、照明或者电子显示出现断亮或者残损的，应当及时更新维护。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条　户外广告设施设置者应当在户外广告设施上公示户外广告设施日常维护责任人及其联系电话，接受社会监督。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　户外广告设施达到设计使用年限的，户外广告设施设置者应当予以拆除或者更新。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条　举办文化、旅游、体育、会展、庆典、公益、促销等活动临时设置的户外广告设施，户外广告设施设置者应当在有效期届满之日起3日内予以拆除。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条　利用国家和集体所有的建筑物、构筑物、场地等设置的户外广告设施，应当通过招投标、拍卖或者其他公平竞争方式出让其使用权。招标或者拍卖应当委托具有相应资质的招标代理机构、拍卖机构进行。因使用权出让而取得的收益，按照国家和省的有关规定进行管理。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条　利用私人所有的建筑物、构筑物、场地等设置的户外广告设施，其所有权人或者用益物权人可以自由选择其使用权的出让方式，并依法享有因使用权出让而取得的收益。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条　发布户外广告，应当按照国家规定办理户外广告登记。法规、规章另有规定的除外。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条　《户外广告登记证》有效期届满需要延续的，户外广告发布者应当在有效期届满30日前向原登记的工商行政管理部门申请延续。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　工商行政管理部门应当自受理延续申请之日起7个工作日内作出是否准予延续的决定；逾期未作决定的，视为准予延续。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条　违反本规定第九条规定设置户外广告设施的，由市、县人民政府指定的部门依法处理。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条　违反本规定第十二条、第十三条规定的，由市、县人民政府指定的部门责令户外广告设施设置者或者户外广告发布者限期改正；逾期未改正的，处以5000元以上30000元以下罚款。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条　违反本规定第十四条规定的，由市、县人民政府指定的部门责令户外广告设施设置者限期改正；逾期未改正的，处以500元以上2000元以下罚款。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条　违反本规定第十五条、第十六条规定的，由市、县人民政府指定的部门责令户外广告设施设置者限期拆除或者更新；逾期未拆除或者更新的，处以5000元以上30000元以下罚款。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条　工商行政管理部门应当加强对户外广告的监督管理，依法查处违法户外广告。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条　因公共利益需要确需拆除合法设置的户外广告设施的，作出拆除决定的主管部门应当提前30日书面通知户外广告设施设置者限期拆除，对其经济损失予以补偿，并将拆除决定抄送有关部门。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　政府及其有关主管部门无正当理由强制拆除合法设置的户外广告设施，给当事人的合法权益造成损害的，应当依照国家赔偿法的规定给予赔偿。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条　因户外广告设施倒塌、坠落而造成他人人身伤害或者财产损失的，户外广告设施设置者应当依法承担民事责任；构成犯罪的，依法追究刑事责任。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条　市、县人民政府有下列行为之一的，由上级人民政府责令改正，通报批评；对直接负责的主管人员和其他直接责任人员依法给予处分：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）未按照本规定第三条第二款规定指定三个以下部门负责本行政区域户外广告设置专项规划的组织编制，以及户外广告设施的设置审批和监督管理工作的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）未按照规定审批、公布户外广告设置专项规划的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）未按照本规定第十条规定实施户外广告设施设置许可或者审批的统一办理的。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有关部门未按照规定组织编制、修改户外广告设置专项规划的，由本级人民政府责令改正，通报批评；对直接负责的主管人员和其他直接责任人员依法给予处分。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条　有关部门及其工作人员违反户外广告设置专项规划许可或者批准设置户外广告设施，或者在户外广告管理中滥用职权、玩忽职守、徇私舞弊的，由任免机关或者监察部门依法给予处分；构成犯罪的，依法追究刑事责任。&nbsp;</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条　本规定自2013年6月1日起施行。广东省人民政府1991年11月21日发布的《广东省户外广告管理规定》（粤府〔1991〕137号）同时废止。</span><br/></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 15:27:18',0,NULL,2,NULL,33,11,'',0,0,0,'2016-05-18 15:40:41','2016-05-18 15:40:41');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (472,'【规章】《汕头经济特区城乡生活垃圾管理规定》','GOs1dyHmtlxHnFMj.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民政府令</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">第166号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头经济特区城乡生活垃圾管理规定》已经2015年9月9日汕头市人民政府第十三届71次常务会议审议通过，现予公布，自2015年11月1日起施行。</span></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">市长 &nbsp;郑人豪</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年9月13日</span></p><p><br/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头经济特区城乡生活垃圾管理规定<br/><br/></span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一章 &nbsp;总则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 &nbsp;为加强城乡生活垃圾管理，改善城乡环境，保障公众健康和生态安全，推动经济社会可持续发展，根据《汕头市城市市容环境卫生管理条例》等有关规定，结合汕头经济特区（以下简称特区）实际，制定本规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 &nbsp;特区范围内金平区、龙湖区和濠江区的生活垃圾投放、清扫、收集、运输、处理以及相关管理活动，适用本规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 &nbsp;城乡生活垃圾管理遵循减量化、资源化、无害化和“谁产生谁付费’’的原则，逐步实行生活垃圾分类投放、分类收集、分类运输和分类处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 &nbsp;各级人民政府应当把生活垃圾管理及设施建设所需资金纳入本级政府预算。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 &nbsp;市市容环境卫生行政主管部门负责特区范围内的市容环境卫生管理工作，组织实施本规定；具体事务性工作由其所属的市环卫管理机构负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区市容环境卫生行政主管部门负责本行政区域生活垃圾投放、清扫、收集、运输、处理的组织、管理及监督工作；具体事务性工作由区环卫管理机构负责。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　发展和改革、住房和城乡建设、城乡规划、环境保护、交通运输、公路、工商、财政、国土资源、公安、社会保障、水务、海洋与渔业、农业、商务、卫生、教育、质量技术监督、食品药品监管、房产管理、城管执法等行政管理部门，应当按照各自职责，协同实施本规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　镇人民政府（街道办事处）负责本辖区生活垃圾的日常管理工作，指导村民（社区居民）委员会落实本辖区生活垃圾收集、转运工作，动员辖区内单位和个人参与生活垃圾减量、分类投放工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 &nbsp;公共媒体应当开展城乡生活垃圾管理的公益性宣传。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　学校应当结合教学活动和社会实践开展生活垃圾减量、分类投放、无害化处理等相关知识的教育。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 &nbsp;任何单位和个人应当依法履行生活垃圾产生者的责任，减少生活垃圾产生，分类投放生活垃圾，有权对违反生活垃圾管理的行为进行检举和控告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<br/>　　第二章 &nbsp;规划和建设</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 &nbsp;市、区人民政府应当把生活垃圾管理目标纳入国民经济与社会发展年度规划。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 &nbsp;市市容环境卫生行政主管部门应当会同市城乡规划行政管理部门组织编制特区城乡生活垃圾处理专项规划，报市人民政府批准，并向社会公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 &nbsp;新建、改建、扩建生活垃圾处理设施，应当符合特区城乡生活垃圾处理专项规划、所在区域的控制性详细规划以及生活垃圾处理、环境保护等技术规范。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 &nbsp;城市建设和改造应当优先配套建设生活垃圾处理设施，与建设项目主体工程同时规划、同时施工、同时投入使用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 &nbsp;建设项目配套建设的垃圾转运站等生活垃圾处理设施设计方案应当征求市市容环境卫生行政主管部门的意见。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设生活垃圾处理设施应当依法进行环境影响评价。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　配套建设的生活垃圾处理设施经验收合格后方可投入使用。市市容环境卫生行政主管部门应当参加验收。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 &nbsp;禁止任何单位和个人占用、损毁、迁移、拆除、封闭生活垃圾处理设施；确有必要迁移、拆除或者封闭的，建设单位或者其他有关单位应当事先提出拆迁或者补建方案，报市市容环境卫生行政主管部门核准后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<br/>　　第三章 &nbsp;清扫、收集、运输和处理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 &nbsp;任何产生生活垃圾的国家机关、企事业单位、个体经营者、社会团体、本市常住人口，应当缴纳城镇生活垃圾处理费。城镇生活垃圾处理费可以采取和供水统一收费、代扣代缴等方式缴纳。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城镇生活垃圾处理费缴纳后，任何单位和个人不得向缴费对象重复收取与生活垃圾处理相关的其他费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 &nbsp;城镇生活垃圾处理费的收费标准应当根据生活垃圾清扫、收集、运输、处理的成本，兼顾当地居民和单位的承受能力确定。具体的收费标准、收费办法及资金使用管理等制度由市发展和改革、财政、市容环境卫生等行政管理部门依照各自职责权限，会同相关部门另行制订。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 &nbsp;城乡生活垃圾投放、清扫、收集、运输、处理的方式由市市容环境卫生行政主管部门确定，并向社会公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　任何单位和个人应当按照规定的时间、地点和方式处置生活垃圾。在实行生活垃圾分类投放、收集的区域内，应当将生活垃圾分别投入相应的垃圾容器。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 &nbsp;市市容环境卫生行政主管部门应当制订特区生活垃圾作业规范和检查考核办法、生活垃圾排放处理申报制度，并向社会公布。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 &nbsp;特区实行生活垃圾分类、收集管理责任人制度。责任区及其责任人的确定原则由市市容环境卫生行政主管部门按照《汕头市城市市容环境卫生管理条例》第二十八条和第二十九条执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 &nbsp;生活垃圾分类、收集管理责任人的工作责任：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）建立生活垃圾日常管理制度，记录产生的生活垃圾种类和去向，并提供给环卫管理机构；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）根据生活垃圾产生量和处理要求，按照标准和规范设置生活垃圾收集容器，并及时清收生活垃圾，保持生活垃圾收集容器的清洁卫生和正常使用；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）制止翻拣和不按规定的时间、地点投放生活垃圾的行为；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）开展生活垃圾分类投放、收集、处理知识宣传，指导、监督单位和个人开展生活垃圾分类工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 &nbsp;从事生活垃圾清扫、收集、运输、处理的作业服务单位应当取得生活垃圾经营性清扫、收集、运输、处理服务相应资质。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　任何单位或者个人不得委托未取得生活垃圾服务资质的单位从事生活垃圾经营性清扫、收集、运输、处理活动。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 &nbsp;特区推行市容环境卫生作业服务的专业化、社会化、市场化。区人民政府应当指定或者授权负责环境卫生管理工作的行政主管部门作为政府采购服务的招标人（下称招标人），通过公开招标等方式确定承担生活垃圾清扫、收集、运输和处理工作的单位（下称中标人）。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 &nbsp;通过公开招标等方式实行市容环境卫生作业服务市场化的，招标人应当与中标入签订经营协议，约定服务区域、经营期限、服务标准、违约责任等内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 &nbsp;中标人应当按照经营协议履行义务，完成中标项目。中标人不得向他人转让中标项目。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中标人不得擅自停业、歇业。确需停业、歇业的，应当提前六个月向区市容环境卫生行政主管部门书面报告，经同意后方可停业或者歇业，区市容环境卫生行政主管部门应当提前落实生活垃圾的清扫、收集、运输、处置措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 &nbsp;招标人应当对中标人的经营服务活动进行监督、检查、考核，考核结果作为拨付中标人作业经费的依据。中标人应当接受并配合招标人的监督、检查、考核。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 &nbsp;中标人在经营服务期间因管理不善导致出现安全事故、擅自停业歇业、经检查招标人发现有违法违规行为二次以上且警告无效，并在社会上造成不良影响的，招标人有权采取临时接管措施，并可以解除合同。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中标人因违约被解除合同的，自合同解除之日起三年内不得参与特区范围内的环卫保洁政府采购项目的招投标。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 &nbsp;由村民（社区居民）委员会组织保洁人员采用上门收集或者村（居）民自行定点投放生活垃圾的，区市容环境卫生行政主管部门、区环卫管理机构、镇人民政府（街道办事处）、村民（社区居民）委员会应当共同做好生活垃圾收集、运输工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 &nbsp;生活垃圾应当运送到市容环境卫生行政主管部门指定的垃圾处理场（厂）进行处理。已分类收集的生活垃圾应当进行分类处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">禁止在生活垃圾收集、处理场所投放医疗垃圾、有毒有害工业垃圾。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十八条 &nbsp;生活垃圾运输车辆应当进行定时清洁与消毒，采取密闭方式运输，运输途中不得泄漏、洒落，不得污染城市路面、水面，防止造成二次污染。垃圾运输线路应当避开水源保护区。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市、区市容环境卫生行政主管部门和环卫管理机构应当加强对生活垃圾运输车辆的监督管理，公安、交通运输、城管执法等行政管理部门应当按照职责分工负责监督检查生活垃圾运输车辆。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十九条 &nbsp;生活垃圾处理单位应当保持生活垃圾处理设施设备正常运行，按照规定处理生活垃圾，并定期向所在地的市容环境卫生行政主管部门和环境保护行政管理部门提交监测报告，向社会公开监测结果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十条 &nbsp;生活垃圾简易填埋场和临时堆放点，应当结合垃圾堆体规模、场址地质构造和周边环境条件制定综合治理方案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　填埋库容已满的生活垃圾卫生填埋场，应当按照规定进行封场、跟踪检测污染物排放和地下水、大气、垃圾堆体沉降指标，并采取相应处理措施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/>　　第四章 &nbsp;监督管理</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十一条 &nbsp;市、区人民政府应当建立和完善生活垃圾管理的综合考核制度，并纳入政府考核指标。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十二条 &nbsp;市市容环境卫生行政主管部门、环卫管理机构应当加强对区市容环境卫生行政主管部门、环卫管理机构的监督、检查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十三条 &nbsp;市容环境卫生行政主管部门应当会同有关生活垃圾转运、处理单位和部门制定生活垃圾管理应急预案，建立生活垃圾应急处理机制。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十四条 &nbsp;市环卫管理机构应当在每年的第一季度向社会公布上一年度生活垃圾投放、收集、运输、处置的监管信息。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/>　　第五章 &nbsp;法律责任</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十五条 &nbsp;违反本规定规定的行为，法律、法规、规章已有处罚规定的，从其规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十六条 &nbsp;违反本规定第十三条规定，擅自占用、损毁、迁移、拆除、封闭生活垃圾处理设施的，由市容环境卫生行政主管部门责令恢复原状、赔偿损失或者采取其他补救措施，并可以处二百元以上二千元以下罚款；情节严重的，处五千元以上二万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十七条 &nbsp;违反本规定第二十条规定，未取得生活垃圾服务资质而从事生活垃圾经营性清扫、收集、运输、处理作业的，由市容环境卫生行政主管部门责令停止违法行为，并处五千元以上二万元以下罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十八条 &nbsp;行政机关及其工作人员，未按照本规定履行生活垃圾监督管理职责，或者滥用职权、玩忽职守、徇私舞弊的，构成犯罪的，依法追究刑事责任；尚不构成犯罪的，依法给予行政处分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/>　　第六章 &nbsp;附 &nbsp;则</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三十九条 &nbsp;澄海区、潮阳区、潮南区和南澳县的生活垃圾投放、清扫、收集、运输、处理以及相关管理活动，参照本规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十条 &nbsp;本规定所称生活垃圾，是指日常生活中或者为日常生活提供服务的活动中产生的固体废物，以及法律法规规定视为生活垃圾的固体废物。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四十一条 &nbsp;本规定自2015年11月1日起施行。</span></p>','2016-05-18 15:41:38',0,NULL,2,NULL,23,11,'',0,0,0,'2016-05-18 15:49:16','2016-05-18 15:49:16');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (473,'【规章】《汕头经济特区农村村民住宅建设管理办法》','47Z3WjqCaS37gZwm.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 为规范农村村民住宅建设，根据《中华人民共和国城乡规划法》、《中华人民共和国土地管理法》、《中华人民共和国建筑法》等法律、法规的基本原则，结合汕头经济特区（以下简称特区）实际，制定本办法。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 在特区范围内使用集体所有土地建设农村村民住宅及其监督管理等活动，适用本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 城乡规划、国土资源、住房和城乡建设等行政管理部门按照各自职责负责农村村民住宅建设的规划、用地和建筑管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　发展和改革、环境保护、公安消防、城市综合管理、城市管理行政执法、房产等行政管理部门以及镇人民政府（街道办事处）按照各自职责，做好农村村民住宅建设管理的相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 农村村民住宅建设遵循以下原则：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）符合城乡规划、土地利用规划和相关技术规范，实行先规划后建设；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）节约、集约利用土地，适度集中布局；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）建筑设计应当安全、适用、经济、美观，体现村居地域特色，鼓励采用农村村民住宅通用设计图。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　编制和修改村庄规划，应当按照适度集中布局的原则规划农村村民住宅建设用地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本条第一款第三项规定的农村村民住宅通用设计图，由市城乡规划行政管理部门会同市住房和城乡建设行政管理部门编制，并免费向村（居）民提供。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 农村村民住宅建设应当优先利用空闲地、原有宅基地和未利用地，确需占用农用地的，应当依法办理农用地转用审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 在城市（镇）规划区内进行农村村民住宅建设的，应当按照适度集中布局的原则实行规划管理和用地管理，并由村（居）民委员会或者集体经济组织集中建设；但使用原有宅基地（含本办法施行前已分散布局的宅基地，下同）进行农村村民住宅建设的除外。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在城市（镇）规划区外进行农村村民住宅建设的，或者使用原有宅基地进行农村村民住宅建设的，鼓励交由村（居）民委员会或者集体经济组织集中建设，也可以由村（居）民自建。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 在城市（镇）规划区内由村（居）民委员会或者集体经济组织集中建设农村村民住宅的，不得建设低层联体式或者单户独院式住宅。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励村（居）民委员会或者集体经济组织集中建设农村村民住宅小区。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 城乡规划对农村村民住宅建设规划技术标准有规定的区域，按照城乡规划执行；尚未编制城乡规划或者已经编制城乡规划但对农村村民住宅建设规划技术标准未作规定的区域，按照下列规划技术指标执行：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）村（居）民委员会或者集体经济组织集中建设农村村民住宅的，其规划技术指标为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.容积率：中心城区和澄海区、潮阳区、潮南区的区政府所在地以及南澳县县城不大于4.0，其他地区不大于3.5；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.建筑密度：不大于40%；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3.绿地率：中心城区和澄海区、潮阳区、潮南区的区政府所在地以及南澳县县城不小于25%，其他地区不小于20%；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4.停车率：中心城区和澄海区、潮阳区、潮南区的区政府所在地以及南澳县县城不小于总建筑面积的15%，其他地区不小于12%；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5.其他规划技术指标按照城乡规划有关技术规定执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）村（居）民自建农村村民住宅的，其规划技术指标为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.建筑层数和层高：中心城区和澄海区、潮阳区、潮南区的区政府所在地以及南澳县县城建筑层数不得超过6层，其他地区不得超过4层。建筑层高首层不超过4米，其他层高不超过3.5米。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.建筑主朝向间距宜不小于建筑高度的0.7倍且不小于6米，次朝向间距宜不小于4米，且应当满足消防安全的要求。对因用地指标或者建设现状等客观条件限制无法满足上述间距要求的，至少应当满足消防安全的要求，并征得相邻村（居）民同意。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3.临村民住宅区小区路及组团路的住宅退让道路红线应当不少于2米，临40米以下城市道路的住宅退让道路红线应当不少于4米，临40米以上城市道路的住宅退让道路红线应当不少于6米。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 农村村民住宅建设的审批管理遵循重心下移、简化程序、方便群众的原则。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 农村村民住宅建设涉及审批部门应当按照职责做好相关审核工作，其中：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）城乡规划行政管理部门负责审核：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.是否符合城乡规划；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.建筑面积、建筑层数、建筑间距、建筑高度等技术指标是否符合要求；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3.依法应当审核的其他内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）国土资源行政管理部门负责审核：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.是否符合宅基地管理相关规定；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.是否符合土地利用总体规划；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3.是否已办理用地手续；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4.依法应当审核的其他内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）住房和城乡建设行政管理部门负责审核：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.自行委托设计的，设计单位是否符合资质要求；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.是否需要申领施工许可证；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3.依法应当审核的其他内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 村（居）民自建农村村民住宅的，由城乡规划、国土资源、住房和城乡建设等行政管理部门委托镇人民政府（街道办事处）办理审批手续，并向委托的行政管理部门备案；暂时不具备委托条件的，由城乡规划、国土资源、住房和城乡建设等行政管理部门报本级人民政府批准后，可以继续由城乡规划、国土资源、住房和城乡建设等行政管理部门办理审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 村（居）民自建农村村民住宅且尚未实行委托审批的，以及村（居）民委员会或者集体经济组织集中建设农村村民住宅的，实行牵头部门统一收件和答复，相关审批部门提前介入、集中联审。集中联审可以采取会议研究、书面征求意见等形式，采取书面征求意见形式的，参与联审有关行政管理部门应当在十五个工作日内答复牵头部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款规定的牵头部门，在金平区、龙湖区为市城乡规划行政管理部门，在其他区（县）由区（县）人民政府指定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 村（居）民自建农村村民住宅的，按照以下程序申请办理建设审批手续：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）向所属村（居）民委员会递交申请材料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）村（居）民委员会应当将申请人现居住情况及申请建房等情况公示十个工作日以上，无异议或者异议不成立的，经村（居）民委员会签署意见后，报送镇人民政府（街道办事处）。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）镇人民政府（街道办事处）受城乡规划、国土资源、住房和城乡建设等行政管理部门委托办理审批手续的，应当自受理申请之日起二十个工作日内对申请材料进行审核，符合条件的，办理核发乡村建设规划许可证等审批手续；不符合条件的，书面通知村（居）民委员会，由其书面告知申请人并说明理由。尚未实行委托审批的，按照以下程序办理：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1.镇人民政府（街道办事处）自受理申请之日起五个工作日内进行审核，符合条件的，提出审核意见报送牵头部门；不符合条件的，书面通知村（居）民委员会，由其书面告知申请人并说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2.牵头部门应当自受理申请之日起二十个工作日内，组织城乡规划、国土资源、住房和城乡建设等行政管理部门进行联审，联审意见认为符合条件的，核发乡村建设规划许可证，需要办理土地、施工等审批手续的，由国土资源、住房和城乡建设等行政管理部门根据联审意见办理；不符合条件的，书面通知镇人民政府（街道办事处），由其组织村（居）民委员会书面告知申请人并说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 村（居）民委员会或者集体经济组织集中建设农村村民住宅的，按照以下程序申请办理建设审批手续：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）提出集中建设方案和分配方案并公示十个工作日以上，无异议或者异议不成立的，经村（居）民会议或者村（居）民代表会议讨论同意，并由村（居）民委员会签署意见后，报送镇人民政府（街道办事处）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）镇人民政府（街道办事处）应当自受理申请之日起五个工作日内进行审核，符合条件的，提出审核意见报送牵头部门；不符合条件的，书面告知申请人并说明理由；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）牵头部门应当自受理申请之日起二十个工作日内，组织城乡规划、国土资源、住房和城乡建设、发展和改革、环境保护、公安消防等行政管理部门进行联审，联审意见认为符合条件且不需办理建设工程基本建设审批手续（以下简称基建审批手续）的，办理规划许可等审批手续；不符合条件的，书面告知申请人并说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　按照前款第三项规定联审后，认为符合条件但依法需要办理基建审批手续的，由城乡规划行政管理部门出具规划审批意见，参与联审有关行政管理部门将办理基建审批手续依法需向本部门提供的材料书面告知牵头部门，牵头部门将规划审批意见和办理基建审批手续依法需要提供的材料一并书面通知镇人民政府（街道办事处），由其组织申请人向有关行政管理部门办理基建审批手续，相关部门应当按照联审意见依法办理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 申请人在办理农村村民住宅建设审批手续时，应当向镇人民政府（街道办事处）提供以下材料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）农村村民住宅建设申请表；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）户口簿及家庭成年成员的身份证复印件；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）村（居）民委员会签署的书面同意意见，属村（居）民委员会或者集体经济组织集中建设农村村民住宅的，还需提供集中建设方案和分配方案；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）宅基地使用权证明材料或者国土资源行政管理部门的审核意见，属改建、扩建或者翻建原住宅的，还需提供原住宅的权属证明材料；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）拟建住宅可能影响邻里等相关利害关系人合法权益的，需提供相关利害关系人的书面同意意见，书面同意意见应当经公证或者村（居）民委员会见证；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）住宅设计图，建筑层数在二层以上的，应当由具备资质的建筑设计单位或者个人设计或者选用通用设计图；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）参与联审有关行政管理部门要求提供的其他材料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 农村村民住宅未依法办理规划许可或者其他建设审批手续的，不得开工建设。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 建设单位或者个人应当自取得规划许可证件之日起一年内依法施工建设，需要延期的，应当在期满三十日前申请不超过六个月的延长期，但延长次数不得超过一次。逾期未依法施工建设的，规划许可证件自行失效。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 规划许可证件应当明确建设用地位置、建设规模、规划用地界线及面积、使用功能、建筑高度、建筑间距要求等内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设单位或者个人应当按照规划许可证件的规定进行建设，不得擅自变更；确需变更的，应当依法向城乡规划行政管理部门申请办理变更手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 农村村民住宅开工建设前，建设单位或者个人应当向城乡规划行政管理部门申请验线。城乡规划管理部门应当自受理申请之日起十个工作日内进行验线。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　农村村民住宅建设竣工后，建设单位或者个人应当就建设工程是否符合规划许可证件的内容，向城乡规划行政管理部门申请核实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划行政管理部门可以委托镇人民政府（街道办事处）实施规划验线和核实。城乡规划行政管理部门已经委托镇人民政府（街道办事处）办理规划审批手续的，应当一并委托其实施规划验线和核实。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 农村村民住宅建设工程未经城乡规划行政管理部门核实合格的，建设单位或者个人不得组织验收和投入使用，有关部门不得办理工程质量竣工验收备案、权属登记等手续，供水、供电、燃气等单位不得办理相关开户接入手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 村（居）民委员会或者集体经济组织集中建设的农村村民住宅竣工验收合格后，应当按照经村（居）民会议或者村（居）民代表会议讨论通过的分配方案分配住房，分户办理权属登记手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 农村村民住宅建设规划档案由城乡规划行政管理部门管理，并按规定移送城建档案管理机构。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 相关行政管理部门和镇人民政府（街道办事处）应当将农村村民住宅建设的申请条件、申报审批程序、审批工作时限等向社会公开。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　村（居）民委员会应当将村（居）民建房申请和审批情况在村（居）务公开栏上进行公示。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十四条 相关行政管理部门和镇人民政府（街道办事处）应当向村民宣传住宅小区规划、建房技术标准、质量安全、减灾防灾、生态环境保护和配套设施建设等相关规定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　鼓励社会各方面技术力量支持农村村民住宅建设，有条件的地区可组建农村村民住宅建设技术服务机构，为农村村民住宅建设服务。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十五条 城乡规划行政管理部门、城市管理行政执法部门和镇人民政府（街道办事处）应当加强对本辖区内农村村民住宅建设情况的监督检查，有关单位和个人应当配合监督检查，如实提供与监督检查事项有关的文件、资料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　村（居）民委员会发现本辖区内的农村村民住宅建设存在违法情形的，应当立即制止并报告镇人民政府（街道办事处）处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十六条 未依法取得乡村建设规划许可证或者未按照乡村建设规划许可证的规定进行农村村民住宅建设，在镇辖区范围内的，由镇人民政府责令停止建设、限期改正，逾期不改正的，依法强制拆除，城乡规划行政管理部门应当予以配合；在街道辖区范围内的，由城乡规划行政管理部门责令停止建设、限期改正，逾期不改正的，依法强制拆除，街道办事处应当予以配合。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款规定由城乡规划行政管理部门查处的城乡规划违法行为，在实行城市管理相对集中行政处罚权制度的区域，由城市管理行政执法部门负责查处。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十七条 本办法自2014年12月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 15:50:12',0,NULL,2,NULL,80,11,'',0,0,0,'2016-05-18 15:53:34','2016-05-18 15:53:34');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (474,'【规章】《汕头经济特区房屋临时变更用途规划管理办法》','klvsbhiaOdh8Xpc5.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 为规范房屋用途管理，根据《中华人民共和国城乡规划法》、《广东省城乡规划条例》等法律、法规的基本原则，结合汕头经济特区（以下简称特区）实际，制定本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 特区范围内房屋临时变更用途的监督管理，适用本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款所称的房屋临时变更用途，是指对国有土地上依法建设并投入使用的房屋，因社会经济发展、产业布局调整、城市区域功能调整等原因，在保留原主体结构基础上临时改变其建设工程规划许可确定的用途。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 市城乡规划主管部门负责组织实施本办法，并直接负责市区房屋临时变更用途的监督管理工作；但区人民政府设置城乡规划主管部门的，由区城乡规划主管部门负责本行政区域内房屋临时变更用途的监督管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　南澳县城乡规划主管部门负责本行政区域内房屋临时变更用途的监督管理工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　国土资源、土地储备、房屋征收、房产、环境保护、公安、公安消防、城市综合管理、城市管理行政执法、文化、交通、住房和城乡建设、工商、食品药品监督管理等行政管理部门按照各自职责，做好房屋临时变更用途管理的相关工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 任何单位或者个人不得擅自变更房屋用途，确需临时变更的，应当向城乡规划主管部门申请办理规划审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 房屋临时变更用途的期限不得超过五年。需要继续变更使用的，应当在期限届满六十日前重新提出申请。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 申请临时变更房屋用途的，应当符合下列条件：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）符合城乡规划管理要求；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）符合建筑物使用的实际需要；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）符合建筑安全、城市景观、交通、消防、环境保护、物业管理等方面的管理规定和技术规范；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）没有本办法第七条规定的不予批准的情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条 申请临时变更用途的房屋有下列情形之一的，不予批准：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）属于农贸市场、邮政电信、医疗、教育、文化、体育、市政、人防、停车库（位）等规划配套服务设施的； &nbsp;&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）属于公共活动空间的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）属于文物古迹、历史建筑、纪念性建筑、标志性建筑、具有地方特色和传统风格的建筑物，变更用途不符合保护要求的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）已纳入房屋征收范围、城市近期建设改造范围、土地储备计划范围的，或者属于危房的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）未经共有产权人、相邻建筑物产权人或者周边其他利害关系人同意的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）被依法查封的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）物业管理规约明确不得变更用途的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）局部变更房屋用途时，变更部分不能满足独立使用要求的，或者影响剩余部分使用的；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）自竣工验收合格之日起未满五年的，但临时变更用途符合所在区域控制性详细规划的除外；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）其他依法不得变更用途的情形。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条 房屋临时变更用途的，应当由房屋产权人向城乡规划主管部门申请，并提供下列材料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）申请书，说明拟临时变更用途的房屋地点、变更用途的理由和原因、具体使用用途、变更期限等内容；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）申请人身份证明；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）房屋权属证明和房屋安全鉴定结论；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）具有相应建筑设计资质的单位出具的关于建筑安全、消防、交通等方面的设计说明和技术论证报告；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）房屋改造设计方案及相应图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）房屋的共有产权人、相邻建筑物产权人或者周边其他利害关系人的意见；将住宅变更为经营性用房的，还应当提供本住宅楼内所有业主的意见；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）承诺书，承诺房屋临时变更用途期间，因城市建设需要收储或者征收的，按房屋原用途和土地原用途给予补偿；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）其他必要的证明材料。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 房屋临时变更用途按照以下程序办理规划审批手续：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）城乡规划主管部门自受理申请之日起五个工作日内在本部门门户网站、行政服务中心办公场所、房屋现场，将申请人、房屋原用途、拟临时变更用途、房屋用途变更有关图纸等内容公示三十日以上；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）公示无异议或者异议已经协调解决的，城乡规划主管部门应当在十五个工作日内进行审查，并根据具体情况征求国土资源、土地储备、房屋征收、房产、环境保护、公安、公安消防、城市综合管理、文化、交通、住房和城乡建设、工商、食品药品监督管理等行政管理部门的意见，必要时组织专家论证；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）城乡规划主管部门结合征求意见和专家论证的情况，认为申请材料需要修改完善的，提出修改完善意见，告知申请人按照修改完善意见和其他行政管理部门的意见对申请材料进行修改完善，并自受理修改完善后的申请材料之日起十五个工作日内作出决定；不需修改完善的，应当自征求意见或者专家论证结束之日起十五个工作日内作出决定；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）城乡规划主管部门审查同意临时变更房屋用途的，应当出具准予临时变更房屋用途决定书；需要补交地价款的，应当先函告国土资源行政管理部门，在申请人补交地价款后出具准予临时变更房屋用途决定书。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城乡规划主管部门在审查中认为房屋临时变更用途申请不符合本办法规定条件的，应当作出不予批准决定，书面告知申请人并说明理由。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 经批准临时变更房屋用途但需要补交地价款的，申请人应当向国土资源行政管理部门申请办理临时改变土地用途手续并补交地价款。补交地价款的标准为：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）属宗地临时改变用途的，补交地价款=（新土地用途宗地地价－原土地用途宗地地价）×临时改变土地用途年限×5%；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）属分摊土地临时改变用途的，补交地价款=（新土地用途平均楼面地价－原土地用途平均楼面地价）×房屋建筑面积×临时改变土地用途年限×5%。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款规定的宗地地价和平均楼面地价，根据基准地价及修正体系计算后确定。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 准予临时变更房屋用途决定书应当载明房屋临时变更用途期间，因城市建设需要收储或者征收的，按房屋原用途和土地原用途给予补偿。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 城乡规划主管部门批准临时变更用途的房屋涉及外立面装修的，应当在准予临时变更房屋用途决定书中予以确认。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 城乡规划主管部门批准房屋临时变更用途的，应当在本部门门户网站公告三十日以上；房屋位于实行城市管理相对集中行政处罚权制度的区域内的，应当抄送城市管理行政执法部门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 房屋经城乡规划主管部门批准临时变更用途后，使用该房屋从事有关活动依法需要取得其他行政审批的，应当向有关行政管理部门办理相应审批手续。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 本办法实施前未经城乡规划主管部门批准擅自变更房屋用途的，应当自本办法实施之日起六个月内补办相关手续，变更期限自批准之日起计算；逾期未补办手续的，按擅自变更房屋用途处理。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 未经批准擅自变更房屋用途的，由城乡规划主管部门责令限期改正，并按改变用途部分的建筑面积处每平方米一百元以上二百元以下的罚款。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款规定由城乡规划主管部门实施的行政处罚，在实行城市管理相对集中行政处罚权制度的区域，由城市管理行政执法部门负责实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 本办法自2014年9月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 15:54:06',0,NULL,2,NULL,71,11,'',0,0,0,'2016-05-18 16:00:07','2016-05-18 16:00:07');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (475,'【规范性文件】印发《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作监督管理的暂行办法》的通知','lK1ggJerwPqK2dRi.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>印发《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作监督管理的暂行办法》的通知</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕城综管〔2015〕232号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">各有关单位：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经市人民政府同意，现将《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作监督管理的暂行办法》印发给你们，请认真贯彻执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市环境卫生管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年12月7日</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城管局、环卫局关于金平、龙湖区环卫作</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">业市场化运作监督管理的暂行办法</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条　为加强我市金平、龙湖区市容环境卫生服务作业管理，提高日常监管水平，确保作业质量和市场化运作的顺利进行，维护环卫工人合法权益，根据《广东省城乡生活垃圾处理条例》、《汕头市城市市容环境卫生管理条例》等规定，结合本市实际，制订本办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条　金平、龙湖区范围内使用财政预算经费实行市场化运作的市容环境卫生服务作业项目的监管工作适用于本办法。其他市容环境卫生服务作业项目的监管工作参照执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条　市容环境卫生服务作业日常监管工作实行属地管理、分级负责，遵循公平、公正、公开的原则。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条　市城市综合管理局是全市市容环境卫生服务作业市场化运作监管的行政主管部门，市环境卫生管理局具体负责市容环境卫生服务作业市场化监管的协调、指导、监督工作，同时组织金平、龙湖区的市级市容环境卫生服务作业市场化考核的日常作业质量检查和月度考核工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区城管局、区城管办、区环卫局、镇人民政府和街道办事处按各区政府确定的职责做好责任范围内区级市容环境卫生服务作业市场化考核的月度考核、日常作业质量检查和协调指导工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市财政、住建、交通、公路、房管、社保、交警等部门按照各自责任协助做好市容环境卫生服务作业市场化运作的监管工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条　采购人在市容环境卫生服务作业项目招标前，应当根据国家、省、市的相关要求，明确作业范围、作业标准及作业质量后，编制项目经费预算及招标文件，报经区人民政府批准后实施。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条　市容环境卫生服务作业项目招标应当遵循政府采购的相关规定，投标人不得以低于成本的报价竞标和恶性竞争。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第七条　采购人与中标的作业服务单位（以下简称作业服务单位）签订市容环境卫生服务作业合同（以下简称合同）时，应当使用广东省环境卫生协会制定的项目合同范本。合同期限一般为三年，最长不超过五年。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　合同自签订之日起五个工作日内，由采购人同时将合同送市财政局、市城市综合管理局、市环境卫生管理局各一份进行备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第八条　市容环境卫生服务作业市场化运作的监管及质量评分考核工作实行市、区分级负责、分别组织考核。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市环境卫生管理局组织市级考核，负责检查区级考核监管部门履行月度考核、日常作业质量检查和协调指导职能情况，检（抽）查作业服务单位的履约作业情况。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　区级考核监管部门要按照合同约定和相关作业标准，严格督促作业服务单位做好市容环境卫生服务工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条　市、区考核监管部门应当根据法律、法规、作业规范和《汕头市城市环境卫生质量标准》以及市容环境卫生服务作业合同的约定，制订具体的环卫作业监管制度和作业质量考核标准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条　市容环境卫生服务作业市场化考核评分每月综合汇总一次，月评分数采用百分制，满分为100分，其中市级考核占40分，区级考核占60分，由区城管局或区城管办于当月考核结束后10个工作日内汇总并送采购人核定当月的包干经费。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条　市环境卫生管理局可以独立开展市级市容环境卫生服务作业市场化考核或者联合其他部门考核，也可以委托第三方机构进行考核，并将考核结果通报区级考核监管部门和作业服务单位。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">日常检（抽）查每月不少于4次，自然月份的中、下旬组织月度考核。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条　区级市容环境卫生服务作业市场化考核应当按照日抽查、周督查、月考核的方式进行检查，重点检查作业质量、环卫工人到岗情况、环卫设备配套使用情况等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条　市、区开展市容环境卫生服务作业市场化考核时，考核人员不得少于2人，检查时应当记录作业基本情况，填写作业质量检查考核表，对检查发现的问题应当拍照取证，告知作业服务单位进行整改，并及时跟踪查看整改效果。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条　对群众投诉、媒体曝光等情况，市、区考核监管部门应当立即通知作业服务单位到现场处理，并跟踪落实情况，确保整改工作的落实。事后应当对投诉热点问题加大督查力度，并做好记录备查。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对群众投诉、媒体曝光的存在问题未能按照市、区考核监管部门的要求落实整改，或经2次书面督办未予完成整改的，市、区考核监管部门根据问题所在区的《作业评分标准》予以扣分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条　采购人应当每月对市、区的月度考核结果进行核算确认，并按照合同约定兑现奖罚，付还相关包干费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条　作业服务单位应当按照国家、省、市的有关环境卫生质量标准和合同约定，保证每日作业时间，根据实际需要合理调配人员及工具、机械，在每月25日前应当将次月环卫作业排班计划提交区考核监管部门备查并按排班计划执行，作业计划需临时调整的，调整当天应当应立即告知。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条　作业服务单位应当完善内部监管制度，按要求配备自查巡检工具和专职巡检人员，记录巡检情况，发现问题及时落实整改。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条　作业服务单位应当按照国家、省及市的有关法律法规和标准，加强安全生产管理，并保障环卫工人工资、保险等合法权益。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条　作业服务单位应当按照工作实际，制订环卫作业突发事件应急预案，组织抢险突击队伍，相应配备工具和机械，完成上级或区考核监管部门下达的抢险突击任务。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条　市、区财政部门按照比例分担的原则，分别承担市、区两级考核监管部门的监管经费，在市和区投入市场化招标结余资金中列支，专项用于监管工作的开支或委托第三方评估考核监管的费用。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条　本办法自2016年1月1日起施行，有效期两年。有效期届满，经评估认为需要继续施行的，根据评估情况重新修订。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 16:14:01',0,NULL,2,NULL,97,11,'',0,0,0,'2016-05-18 16:24:04','2016-05-18 16:33:04');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (476,'【规范性文件】印发《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作日常卫生质量检查考核的暂行办法》的通知','VokoDHL76HEWeYuM.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">印发《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作日常卫生质量检查考核的暂行办法》的通知</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕城综管〔2015〕233号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">各有关单位：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经市人民政府同意，现将《汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作日常卫生质量检查考核的暂行办法》印发给你们，请认真贯彻执行。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市环境卫生管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年12月7日</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城管局、环卫局关于金平、龙湖区环卫作业市场化运作日常卫生质量检查考核的暂行办法</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为切实加强金平、龙湖区市容环境卫生服务作业市场化运作的管理，完善考核监管机制，推动工作落实，提升环境卫生作业质量水平，根据市政府的工作部署以及《广东省城乡生活垃圾处理条例》、《汕头市城市市容环境卫生管理条例》、《汕头市城市环境卫生质量标准》等有关法规规章和标准规范的规定，结合金平、龙湖区环卫作业市场化招标文件的约定，特制订本检查考核办法。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　一、指导思想</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为加强对市容环境卫生服务作业市场化运作项目的监督、管理，促进我市环境卫生质量的稳定和提高，营造良好的城市卫生环境，更好地服务市民群众。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　二、组织机构</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　成立金平、龙湖区市容环境卫生服务作业市场化运作质量检查考核领导小组，由市环境卫生管理局（以下简称市环卫局）主要领导任组长，市环卫局分管业务工作的领导和市城市综合管理局（以下简称市城管局）市容科主要负责人为副组长，市城管局、环卫局有关科室及金平、龙湖区城管局、环卫局主要负责人为成员，负责对金平、龙湖区市容环境卫生服务作业市场化运作项目的质量检查考核工作进行监督、协调、指导，根据实际及时修订检查考核内容及标准，审定考核结果等工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　领导小组下设考核办公室（以下简称考核办，设在市环卫局业务管理科），办公室主任由市环卫局分管业务工作的领导兼任。考核办负责市容环境卫生服务作业市场化运作项目的质量检查考核工作，牵头组织各相关部门及区环卫局、有关方面人员代表对市场化运作项目的日常作业质量进行检查、监督和月度考核评分工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市公路局参与列入市容环境卫生服务作业市场化运作项目中国省道公路的月度考核工作。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　考核办主要职责：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1、负责组织市容环境卫生服务作业市场化运作项目的月度考核和日常作业质量检查考核；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、负责检查情况的收集、整理、评分、反馈；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3、负责日常环卫作业的指导、协调、监督；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4、负责群众投诉、媒体曝光事项整改情况的督办等。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　三、检查考核</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　以金平、龙湖区市容环境卫生服务作业市场化运作项目招标文件约定的作业任务量、作业范围、作业质量要求及其他相关约定作为检查考核内容。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　四、环卫作业质量要求及考核评分标准</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1、汕头市城市市容环境卫生管理条例；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、汕头市城市环境卫生质量标准；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3、汕头市金平、龙湖区市容环境卫生服务作业市场化运作项目招标文件明确的评分标准。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　五、检查考核方式和计分办法</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）检查考核方式</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　考核办通过日常检查与月度考核相结合、单项与综合、随机抽查与通知检查、明查与突击检查等多种形式，全面了解掌握作业服务单位执行市容环境卫生服务作业合同文件和卫生作业质量情况。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1、日常检查：考核办开展日常巡查工作，发现存在问题当场拍照并通知作业服务单位赴现场确认并落实整改；发现严重问题除拍照外，还应要求其现场签名确认，并书面告知，同时形成检查记录，月度进行评分。每月日常检查次数应不少于4次。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、月度考核：每月中、下旬由考核办根据日常检查了解掌握的情况，对每项作业内容按照一定的比例随机抽查，对照金平、龙湖区环卫作业项目评分标准进行考核评分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3、公众监督：对群众投诉、媒体曝光的问题，经核实属于作业服务单位责任的，通知作业服务单位一同到现场处理并按规定进行扣分，拖延处理或造成不良影响的，加倍扣分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）计分办法</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1、作业服务单位应接受市、区两级市容环境卫生服务作业市场化考核，市、区考核月度评估分值按4：6折算，满分为一百分。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、市级市容环境卫生服务作业市场化考核每月综合考评满分为100分，其中日常检查占40％，月度考核占50％，公众监督占10％，市级考核月度得分为三项得分之和，然后按40％计入市、区考核当月得分。即：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市级考核月度分值﹦（日常检查分值＋月度检查分值＋公众监督分值）×40％</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　六、有关要求</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1、各作业服务单位必须严格按照法律、法规和标准、规范以及招标文件、政府采购合同的约定，高质量、高水平落实责任范围内的环境卫生作业和日常管理工作，并接受市、区考核监管部门的检查和监督。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、各作业服务单位要建立健全环卫作业质量自查自评工作机制，健全内部日常作业巡查和监管工作，整理完善自查资料档案，做到制度健全、工作落实、资料齐全，促进环卫作业质量的不断提高。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3、日常检查和月度考核各项资料应齐全，考核人员应客观、公平、公正，每次考核参加人员应不少于二人同时在场。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4、考核达标情况报市财政局、市城管局、市环卫局各一份进行备案。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5、对群众投诉、媒体曝光的环境卫生问题以及重大节日、活动和上级交办的环卫工作要认真制订工作措施，落实整改工作，确保发现的环境卫生问题得以及时、有效地解决。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6、积极开展环境卫生宣传活动，加强环卫从业人员的职业道德教育、责任意识教育和职业技能培训，弘扬环卫行业无私奉献精神，形成良好的环境卫生工作氛围，共同为提高城市环境卫生质量而努力。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　七、本办法自2016年1月1日起施行，有效期两年。有效期届满，经评估认为需要继续施行的，根据评估情况重新修订。</span></p>','2016-05-18 16:25:05',0,NULL,2,NULL,131,11,'',0,0,0,'2016-05-18 16:32:45','2016-05-18 16:32:45');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (477,'【便民指南】市城管局办事问答','zfiDk8AnFfQq6dP7.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我的××店刚开业，想要做店庆占用门店前人行步道摆设充气拱门、花篮等烘托喜庆氛围，请问需要什么手续？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、平面图纸;标明商铺所在路段、人行道从商铺到机动车道之间距离，商铺门面长度，用A4纸打印商铺正面照片详细标明商铺所在路段、人行道从商铺到机动车道之间距离商铺门面长度，用A4纸打印商铺正面照片。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：我公司因工程建设需要占用人行步道对施工场地范围进行围护，请问要怎么申请？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供《建设工程规划许可证》和《建设工程施工许可证》（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、提供经规划部门批准的施工平面图，并详细标明现有人行道的宽度，以及申请占用人行道的宽度和长度。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：我公司承接了一个建筑外墙翻新工程，需要占用城市道路搭设围护或脚手架，请问须提供什么申请材料？ &nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人（业主或业主单位）或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供房产证及复印件；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、提供施工单位资质；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">6、提供施工合同；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">7、提供《建设工程规划许可证》（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">8、提供施工图纸，并详细标明现有人行道的宽度，以及申请占用人行道的宽度和长度。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问: 我店面在装修，需要占道搭设竹架翻新店面招牌，请问需要什么手续？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供房产证及复印件；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、搭设竹架翻新招牌施工图纸。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：请问申请占用城市道路要收费吗？有什么依据？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：根据《汕头市市政设施管理条例》第二十条、第二十三条以及《广东省收费许可证》粤费汕行字*014—1、—2号的规定：营业性占用：1元/平方米•天；基建或其他占用：0.5元/平方米•天。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我店门前步道年久失修，想自费修复步道，请问要如何申请？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸（包括明确步道结构形式、侧石材质、高度，步道砖材质，盲人通道砖，残疾人坡道，树穴等）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因供水用水需要申请驳接自来水管，请问须提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、市自来水总公司开具的《挖掘路面申请书》。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位埋设通讯管线需要开挖步道，请问须提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、涉及管线施工的，应提供有市政设计资质的设计单位的施工图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、属非开挖式施工（顶管、拖管），应有准确的地下管线物探资料（原件）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因工程建设需要申请埋设地下排污管，请问应提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、提供市规划部门批准签发的《建设工程规划许可证》（原件及复印件）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：我单位因工程建设需要申请在道路新设出入口，请问要提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、提供市规划部门批准签发的《建设工程规划许可证》（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">6、涉及市政、路灯等市政工程的，申请人须提供与相关单位签订的合同或协议副本。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：我家下水道管堵塞，申请挖路清通，请问要提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：市民可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位、当地街道社区有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、应提供市政维修管养部门现场勘查意见（原件）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：申请挖掘道路要收费吗？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：根据《汕头市市政设施管理条例》第二十条、第二十一条、第二十三条，《广东省城市道路挖掘修复收费标准》，《广东省行政收费许可证》粤费汕行字 *014号等规定，一般情况：人行道：188元/㎡；水泥混凝土路面：516元/㎡；沥青混凝土路面：1161元/㎡。特殊情况：人行道：564元/㎡；水泥混凝土路面：1204元/㎡；沥青混凝土路面：2709元/㎡。新建或改造的城市道路3年内不得开挖，5年内申请开挖的城市道路按特殊情况标准收费。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因工程建设需要申请迁移步道树，请问要提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请报告；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供市规划部门批准签发的《建设工程规划许可证》（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、申请人与城市绿化管养部门签订的迁移树木协议。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问：我家旁边步道树树枝插进窗户，我要如何申请修剪树木？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：市民可通过12345热线电话或直接向市城管局提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请报告。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问: 我单位要在中心城区临时占用公共绿地开展公益宣传活动，请问要提供什么手续？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、申请报告；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、临时占用绿地申请人与城市绿地管养部门签订的协议(原件)。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因工程建设需要申请临时占用绿地，请问要提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、申请报告；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供市规划部门批准签发的《建设工程规划许可证》（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、施工图资料</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">6、绿地恢复承诺（原件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">7、临时占用绿地费缴纳协议（原件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">8、利害关系人的意见或听证意见（原件）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因工程建设需要申请在道路分车绿带开口，要提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、提供市规划部门批准签发的《建设工程规划许可证》，公安交通管理部门的意见书（原件及复印件）。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位因需要在自有场地设置一块广告牌，须提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、户外广告设施设置场地的所有权、使用权证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">6、承担施工单位的营业执照、资质证书和施工合同（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">户外广告设施必须由有施工资质的单位施工，涉及钢结构的应具备钢结构工程施工叁级或叁级以上资质；涉及地基与基础工程的应具备地基与基础工程施工叁级或叁级以上资质。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位要在本大厦外墙立面设置一块广告牌，需要什么手续？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、施工设计图纸；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、户外广告设施设置场地、建（构）筑物、设施的所有权、使用权证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">6、承担施工单位的营业执照、资质证书和施工合同（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">7、提供市规划部门签发的户外广告设施规划许可证（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">8、涉及户外电子显示屏广告设施安装的应具备城市及道路照明工程施工叁级或叁级以上资质。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">问:我单位在本大厦外墙立面设置广告牌许可已到期限，需要申请延期，须提供什么材料？</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">答：可通过网址http://sp.st.gov.cn/提交申请材料或直接向市城管局办事窗口提交申请材料，并提供以下资料：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">1、《汕头市城市综合管理局行政许可事项申请表》；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2、申请人或委托代理人身份证明资料（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">3、工商营业执照或单位有效证明（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">4、提供户外广告设施许可证（原件及复印件）；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">5、安全检测报告书（提供原件）。</span></p><p><br/></p>','2016-05-18 16:45:15',0,NULL,2,NULL,483,7,'',0,0,0,'2016-05-18 16:53:21','2016-05-18 16:53:21');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (478,'【城管知多少】市城管局机关组织架构','uSa81uvCyD7gbR3X.jpg','<p></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463562107135020.jpg\" title=\"1463562107135020.jpg\" alt=\"1(32)(1).jpg\"/></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">一、办公室</span></strong><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责局机关行政事务、日常工作的协调工作；承办重要会议的组织及协调工作；负责文秘、收发、信息、统计、安全、保密、信访、通讯报道、政务公开、民兵、接待和后勤保障工作；负责计算机网络管理、资产管理工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">二、人事科（与直属机关党委办合署）</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责局党组日常事务和局机关及所属单位党建工作；负责局机关及所属单位的机构编制、考核任免、调配、劳动工资、社会保险工作；负责专业技术人员职称的审核、申报及继续教育工作；负责老干部管理工作；负责局及所属单位的工会、共青团、妇女和计划生育工作；负责局及所属单位的精神文明建设、思想政治教育和培训工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">三、计划财务科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责组织制定局财务管理制度；负责编制和执行部门预算、决算工作，拟定局机关及所属单位项目支出经费预算计划；负责局所属单位财务管理工作的指导、检查和监督以及内部审计工作；负责审核局所属单位固定资产的购置、处置；承办有关规费的收缴工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">四、法制宣传科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责相关地方性法规、规章草案的起草、会办和拟制立法建议；负责对全市城管执法人员的资格审查、业务培训和执法证件的管理；负责对本部门重大行政执法行为进行事前合法性审查；负责对市、区城市管理行政执法工作规范化的监督、指导；承办城市管理方面行政复议、行政诉讼的应诉和听证工作；负责法制宣传教育、报道和普法工作；负责各级人大代表、政协委员有关城市管理方面议案、提案的办理工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">五、市政设施管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">编制市政设施发展规划和科技进步规划；协调指导、检查监督市政设施日常管理工作；负责城市地下综合管沟管理工作；承办市政工程经济遗留问题的处理工作；参与市政工程的竣工验收工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">六、市容环境管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责编制城乡环境卫生事业发展规划及拟订城市容貌标准；负责对城乡市容环境工作进行指导协调、检查督促；负责对中心城区城市景观照明建设和规范化管理的协调工作；负责对中心城区户外广告设施设置的监督管理工作；负责拟订市容环境卫生行业管理制度，指导市容环境卫生行业协会；参与环境卫生设施工程设计方案的审查和竣工验收工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">七、园林管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责编制城市园林绿化发展规划、建设计划；负责城市绿化工程项目设计方案、工程建设项目附属绿化工程设计方案的审查；参与城市绿化工程的竣工验收和“星级园林式单位”的组织评选工作；指导协调、检查监督城市园林绿化日常管理工作；组织协调市区义务植树活动工作；指导市园林绿化科学技术研究工作，指导市园林绿化行业协会和市园林学会。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">八、城管综合科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责对城市管理责任单位的城市管理工作进行督办、考评；负责全市性城市管理重大整治活动的组织、协调工作；承办市城管委和市城管办的日常工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">九、监察室</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责局机关及所属单位的纪检和监察工作，抓好党风廉政建设和对党员的民主评议及纪律教育，承办反映党员干部违纪违规问题的群众来信来访和查处工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">十、城乡垃圾管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">负责城乡垃圾管理工作；拟定生活垃圾源头分类和减量化、资源化、无害化处理及生活垃圾处理设施运营监管、建筑垃圾管理方面的政策、制度和管理办法，并牵头组织实施和监督执行；负责市级环卫设施项目的规划、建设和竣工验收工作；负责城乡垃圾无害化处理率的相关统计工作；指导、协调、检查督促生活垃圾、建筑垃圾的管理运营及设施建设工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">十一、行政许可管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">承担本局行政许可事项的审批、办理工作；承担涉及城市管理的行政审批制度改革工作；承担政务公开和行政服务窗口的日常管理等工作。</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">十二、污水处理管理科</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">指导、协调、监督城镇污水处理建设及管理工作；负责编制城镇污水处理、中水回用的发展规划和年度计划并组织实施；负责市污水处理信息管理工作；指导城镇污水处理费征收工作；负责中心城区污水处理项目的建设以及污水处理费管理工作；指导中心城区污水处理厂的运营考核工作。</span></p><p><br/></p>','2016-05-18 16:55:50',0,NULL,2,NULL,615,7,'',0,0,0,'2016-05-18 17:02:25','2016-05-18 17:02:25');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (479,'【逛公园】中山公园','3F8IKEcJHbmhlouj.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463563309957887.jpg\" title=\"1463563309957887.jpg\" alt=\"201511684324b1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　汕头市中山公园位于市区月眉河畔，是汕头市新八景之一&quot;月苑莺声&quot;。一九二六年奠基兴建，1928年8月28日建成开放，是粤东地区建园历史最早、规模最大、具有深厚历史和人文内涵的综合性公园，总面积309亩。公园四周环水，月眉河环绕园区，中山桥、月眉桥和迎春桥三座桥梁将公园与市区陆地连在一起，园区的玉鉴湖面积近百亩，风光绮丽，名闻海内外。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463563427655460.jpg\" title=\"1463563427655460.jpg\" alt=\"201511648682b1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　从中山桥进入正门牌楼，园区沿玉鉴湖西岸为界，将全园划分为东西两片。东边挖湖堆山，形成龟山、蛇山、五角星山等五个小岛，九曲桥、碧波桥、展虹桥、红桥、槐荫桥、玉带桥、浣花桥、丁水桥等8座大小造型各异的桥梁，跨水连丘，小桥流水，极富中国古典园林韵味，以游览、休憩功能为主；西面陆地面积较大，以道路分隔为若干绿化小区，种植观赏类乔灌林及花卉等，是园区活动、娱乐的主要场所。全园布局完整，中西合璧，动静结合，汇集古今中外造园之特色。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463563368640829.jpg\" title=\"1463563368640829.jpg\" alt=\"201511673960b1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公园中的牌楼、假山、九曲桥三处园林建筑被誉为园中&quot;三绝&quot;。牌楼高达18米，巨柱矗立，碧瓦覆盖，气势雄伟壮观，其正面镶有谭延闿手书&quot;中山公园&quot;四个浮雕金字，背面为孙中山先生手书&quot;天下为公&quot;四个浮雕金字。牌楼后面矗立着孙中山先生全身铜像。牌楼东面的三层假山占地1700平方米，以海石为主人工精砌而成，怪石嶙峋，曲径迂回，壁洞通幽，登高眺望，全园景色一览无遗。假山的底层保留了一幅展现汕头三十年代繁荣景象的&quot;汕头商埠图&quot;的浮雕壁画，已列入市级文物保护单位。九曲桥位于玉鉴湖北端，面积298平方米，是我国造园规模最大的一座九曲桥，桥有九折，中有双层主亭一座、副亭二座，桥、亭、栏有机融为一体，凭栏可览湖山秀色，湖光倒影，群鱼戏逐，情趣盎然。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公园历史悠久，古迹众多，有大小11座凉亭，其中最大及最重要的是浩然亭、绳芝亭。位于园区大操场北面的浩然亭，是一座长方形的凉亭，琉璃碧瓦，富有民族特色，此亭始建于1932年，1989年原址重建，是纪念筹建公园委员会常委林修雍建园业绩而修建的。2009年进行全面整修，并对景区环境进行全面改造和美化，营造桃花林景观。位于儿童游乐场的绳芝亭，建于1931年，是一座高7米，六角直简钢筋水泥的西式凉亭，为缅怀汕头水电创始人高绳芝先生支持辛亥革命爱国义举和兴办地方公益事业的功绩而兴建的。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　位于公园西南面的馆花宫（又称花展馆），1986年建成开放，至今已经过了三期的建设，景点设施日趋完善，是观赏盆景、名贵树木的理想去处。该馆采用我国古代庭园的设计手法，通廊迂回逶迤，连庭接院，馆内现存有粤东罕见的百年老榕、罗汉松及其他珍稀树木，集各类奇花异卉、名贵盆景供游客观赏。其中有由我园园艺师精心培植而成并多次荣获全国盆景大赛一、二、三等奖的盆景榆树、榕树、角花、罗汉松等盆景，是粤东最具规模的盆景园，也是接待国内外嘉宾的重要场所。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　位于公园西北部的忠烈祠（原少年宫）始建于1946年，坐东南方向，占地面积766平方米，建筑面积536平方米，其建筑风格是潮汕传统建筑&quot;四点金&quot;布局融入客家建筑的穿心廊的潮汕祠堂，以及西洋文化墙体白色门饰线等多种建筑元素修建而成，地板是花岗岩大理石。大门正上方有石牌匾&quot;忠烈祠&quot;和门柱石刻漆金对联，上联&quot;垂名炳海表&quot;下联&quot;柱国障天南&quot;都是出自当代著名政治家、教育家、篆书名家吴敬恒的手书。<span style=\"line-height: 22px; text-indent: 24px; font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 255);\">2013年，汕头市政协文史委员会提出关于“修复忠烈祠，铭记抗战历史”的提案，汕头市荣誉市民、马来西亚华人企业家庄坤平先生积极响应，捐款修复。2014年7月，修复告竣。12月，汕头市人民政府决定将修复后的忠烈祠开辟为“汕头市潮汕抗日战争纪念馆”，得到了社会各界热烈响应支持，踊跃捐献抗日文物史料。经多方努力，纪念馆于中国人民抗日战争暨世界反法西斯战争胜利70周年纪念日前夕正式开馆。<br/></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463563922723450.jpg\" title=\"1463563922723450.jpg\" alt=\"2015928959220.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"line-height: 22px; text-indent: 24px; font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 255);\"></span></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　位于公园东北面的龟山，始建于1934年，总面积 5400平方米 ，通过挖湖堆山用湖泥筑砌而成。龟山因其山形犹如一只灵龟在湖中畅游而得名，似&quot;神龟出水&quot;，与蛇山毗邻相望，同玉鉴湖山水一体，相得益彰，极富中国古典园林韵味。2009年秋，龟山景点进行全面整修和美化，使龟山更加精致典雅，环境更加怡人，并将龟文化融入其中，增设祈福石龟、连心锁、许愿树等景点，更好地渗透了中国传统文化祈福迎祥、安康长寿的意境，营造一个人与自然、景观与情感交汇融通的游览环境。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建于1954年的动物园位于园区假山旁，是粤东地区唯一的动物园。2007年进行全面改造，如今面貌焕然一新，现饲养有长颈鹿、白虎、河马、东北虎、狮、美洲豹、黑白天鹅、等珍稀动物，共四十多个品种一百多头（只），建成了猴山、小熊猫馆、观禽台等一批馆舍设施，并有大型动物表演，是市民特别是少年儿童最为喜爱的参观娱乐场所。2009年兴建的两栖馆位于动物园旁边，引进了鳄鱼、海龟、蟒蛇、蜥蜴等珍稀两栖动物，并有海狮表演,进一步丰富了我园的动物资源。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463563629246055.jpg\" title=\"1463563629246055.jpg\" alt=\"201511672027b4.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　园内的游乐服务配套设施完善。园区的游艇场位于龟山景区，玉鉴湖面积达6.3公顷。湖面碧波荡漾，湖水清澈可鉴，四周湖山秀色，树影婆娑，波光倒影，情趣盎然。现有红头船、老爷（车）船、激光战艇、脚踏艇、手划艇等各类型近百艘游艇，为你提供划艇赏景的服务。钓鱼场位于龟山景区内，垂钓亲水平台临湖而建，是休闲垂钓的绝佳去处。游乐场位于园区的西南面，2007年全面建成，引进激流勇进、旋风骑士等大型游乐设备，填补了粤东地区的空白。游乐场项目布局合理，有海盗船、碰碰车、太空漫步、金龙滑车、激战鲨鱼岛、豪华旋马等十几个游乐项目，老少皆宜，成为进园游客乐而忘返的地方。儿童嬉戏区建于游乐场旁边，是公园以前最主要的儿童免费活动嘻戏区域，2008年进行全面改造和翻新，现建有三座滑梯和一个大沙池，环境优美，绿树成荫，是小孩游玩嬉戏的最佳场所，也是汕头市民重拾儿时记忆的老地方。园区的灯谜台和象棋台常年举办灯谜会和棋类活动，是公园最主要的文化活动场所。月苑茶座位于中心园区工农兵舞台旁边，2009年全面改造建成，环境优美，绿树成荫，是休闲品茗、观赏歌舞表演的最佳地方。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中山公园泳园建于1991年，占地面积5400平方米，建有标准比赛泳池25×50米，共10条赛道，配套建有儿童游泳池，游泳配套设施完善。2007年进行全面改造和整修，引进澳大利亚水处理循环系统，配备专业救生人员和救生设备，开设游泳培训班，有完善的卫生监管制度，配套更衣室、寄衣室、小卖部等，是市民游泳避暑的理想去处。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公园有着丰富的植物资源，绿化已形成规模，园内绿树成荫，满目青葱，花团锦簇，种植有小叶榕、榆树、芒果、木棉、大王椰子、椰子、假槟榔、罗汉松、苏铁等各种观赏植物百余种，公园内的荷花池、桃花林，形成了&quot;冬赏桃花夏观荷&quot;的绿化美景，也是市民锻炼、游憩的理想场所。每年11月至12月的菊花展览，是我市最具影响的菊展，是市民传统赏菊的必游之地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为丰富市民的文化娱乐生活，我园积极举办丰富多彩的游园活动，每年的春节、国庆、元旦等节假日，公园游人如鲫，不仅吸引了市民的参观游览，潮州、揭阳、澄海等附近城市的市民也专程前来参观，如今的历史名园正在焕发出新的魅力。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中山公园集历史景观、人文景观及现代设施于一体，经过不断的完善，历史名园重焕新姿，我园将以创建&quot;四优一满意&quot;为目标，即创建&quot;优美的环境，优良的秩序，优质的服务，优秀的经营，使游客满意&quot;为目标，在发展中不断完善，广迎天下游客。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　温馨提示：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市区可乘坐2、3、5、7、10、13、14、17、18、20、22a、24、32、48、101路公共汽车到达；</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　开放时间：上午6：00——夜间23:00</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 17:11:42',0,NULL,2,NULL,736,8,'',0,0,0,'2016-05-18 17:32:13','2016-05-19 17:21:58');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (480,'【逛公园】金砂公园','9HyWWhv1RtLCl3P0.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463565334737349.jpg\" title=\"1463565334737349.jpg\" alt=\"4.. - 复件.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　金砂公园位于汕头市金平区金砂中路42号，上世纪八十年代筹建，1991年建成开放，面积约100亩，是改革开放后汕头建立的第一个公益性公园。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　金砂公园地处闹市区的金砂中路，交通便利，景色醉人，游人纷至沓来，是潮汕的一处游览胜地。<br/></span></p><p style=\"text-align:center\"><img src=\"http://olive.strtv.cn:7337/uploads/20160518/1463565807962276.jpg\" title=\"1463565807962276.jpg\" alt=\"4571435096758399752.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公园以绿化为主，园内有“远朋心悦”，“醉柳映红”，“晚亭问月”，“云竹朝露”、，“凤楼”，“南峰金幢”等主景区和“浸春湖”、“又一园”、“ 相思林”、“中日友谊坛 ”、“伏虎区”等一批景点；还有“顾鹊桥”、“绿杨桥”、“杏香楼”、“香月亭”等园林建筑。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463566356887014.jpg\" title=\"1463566356887014.jpg\" alt=\"67f7d284gc18f28b51ca9&amp;690.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　走进大门，胡耀邦同志手书的“金砂公园”四个金字，嵌砌在花岗石牌匾上，光彩熠熠。门前广场红翠相交，游客不绝。“有朋自远方来，不亦乐乎”。蜿蜒的园道穿连各景点，引导游客游览。在园中漫步，观香花灌木，观览疏林草坪，赏挺秀修竹，令人陶醉怡情。相思林区，绿荫浓郁；伏虎区富有野趣；中日友谊坛植有异国小叶冬青、红朱蕉各领风骚。还有廊亭系列组成的凤楼景区，各景点配置了“伏虎女郎”、“春”、“相思”、“捉小鸡”等一批雕塑小品，又为公园增添了无限情趣。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463566282577529.jpg\" title=\"1463566282577529.jpg\" alt=\"69a7f24fgbe2a84768e0e&amp;690.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　浸春湖水体面积约15亩，设有游艇区，游人可以泛舟湖上。西区有园中园“又一园”，假山瀑布，奇花异石，玲珑巧妙。东区有“嘉乐比游乐场”，开设探险城、海盗、丛林飞鼠、碰碰车、飓风飞椅等一批娱乐项目，是儿童娱乐的好场所。园中还设有溜冰场，金砂欢乐园，金凤茶座等服务项目。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　温馨提示：每日开放时间:早上6:00至晚上23:00。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公交线路:</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　金砂中路停靠金砂公园站</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2、4、6、11、26、33、37、38、47、53、101路公交车。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　金新北路停靠 金砂公园东站</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　9、16、21、32、48路公交车。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 17:39:05',0,NULL,2,NULL,277,8,'',0,0,0,'2016-05-18 18:12:52','2016-05-18 18:12:52');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (481,'【逛公园】海滨华侨公园','UVhY7gkfIyjAywb4.jpg','<p style=\"text-align: center\"><span style=\"font-family: 微软雅黑;\"></span><img src=\"/uploads/20160518/1463567651519909.jpg\" title=\"1463567651519909.jpg\" alt=\"3... - 复件.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　华侨公园位于汕头港湾北岸，南临大海，西接“观海长廊”，总面积17.8公顷，其中海域面积6.7公顷。1997年11月，第九届国际潮团联谊年会在汕头举行，汕头市委、市政府决定兴建华侨公园，以作纪念，故名“华侨公园”。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463567674728226.jpg\" title=\"1463567674728226.jpg\" alt=\"3768223_112034173000_2.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　华侨公园以华侨纪念、潮汕传统文化和海滨景观为主要特点，全园划分三个功能区：纪念区、观赏区、游览区。规划建设景区和景点有：鸿雁来宾、故乡明月、川流不息、侨林风韵、海滨邹鲁、锦上添花、鮀岛护神、大海慈航等。公园主入口以“扬帆”造型为建筑构图，雄伟壮观，景墙吸取潮汕传统民宅的“硬山墙”造型，富有地方特色。以鸿雁来宾、故乡明月、川流不息等景点作为主轴线，形成全园主体景区，布置有石碑、浮雕、跌水、喷泉等，内容丰富，情趣盈然，寓意深刻。侨林风韵荟萃了23个国家地区、54个潮团赠送的近千株特色花木，园内棕榈婆娑、繁花似锦、绿草如茵、碧波粼粼，一派海滨风光，植物配置简洁明快，体现现代化都市情调。沿园道可游览庄世平塑像广场、海滨茶座、儿童游乐园、热带雨林、4D5D电影院等景点设施，是一个集纪念、观赏、休闲的综合性公园。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　温馨提示：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　华侨公园开园时间：早晨6:00—晚上23:00正。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公交路线：46号。</span></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-05-18 18:22:05',0,NULL,2,NULL,283,8,'',0,0,0,'2016-05-18 18:35:58','2016-05-19 17:20:58');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (482,'【逛公园】石炮台公园','kIChECBHqmqxg960.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463567893125317.jpg\" title=\"1463567893125317.jpg\" alt=\"6. - 复件.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　石炮台公园是以崎碌砲台为主景点的纪念性公园，规划总面积为5.8公顷，目前，公园开放面积约3.4公顷，崎碌砲台是全国重点文物保护单位，汕头市爱国主义教育基地。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463568246113659.jpg\" title=\"1463568246113659.jpg\" alt=\"ilovegz_2269397_841325_l.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　崎碌砲台俗称石砲台，建于公元1874年（清同治13年），竣工于1879年（光绪5年），耗资八万余银元，是当时住潮州总兵方耀所建，至今已有一百多年的历史。当时在二次鸦片战争后，清政府被迫签订不平等条约《天津条约》（1858年）按条约规定汕头开放为十个通商口岸之一，帝国主义列强的兵舰随时游弋于我海域，伺机准备发动新的侵略战争，瓜分我神圣国土，广大人民群众强烈要求加强国防建设，方耀亲自勘查潮汕地区各港口后，便以“邻氛不净，潮海严防”为由，奏请清廷建筑了此砲台。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160518/1463568266436858.jpg\" title=\"1463568266436858.jpg\" alt=\"ba1aaeb51e4a4752ad8a47fcc0a0c622.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　石砲台面积19607平方米（包括护台河），其中城堡面积10568平方米，是广东省独立体炮台规模最大，并且保护得最为完好的古代海防军事砲台，砲台的建筑采用明清的建筑艺术风格，砲台的墙体以贝灰砂加煮烂的糯米和红糖浆调粘夯筑而成，再砌上规格不 一的花岗岩石。砲台的城堡结构呈环圆形，环绕城堡外围挖筑有一条水深3米，宽23米防御型的护台河，与外界隔离；大门依照明清时兴的券门款式建造，宽和高都是3米，门墙厚也是3米，显得结实、厚重而有分量。砲台分上、下两层，各置火炮18门，下层是一条石砌的拱顶隧道式砲巷，砲巷宽4.1米，全长292米，贯通于整个台堡，是放置炮弹和炮兵驻扎的营地；登台是一条27级呈波浪形的楼梯，易于运送炮械，设计十分独特实用，还有一道较为隐蔽的螺旋楼梯，是作战时的快速通道；上层台面有通风报话塔72座，每三个为一组，呈品字形分布，是砲巷的通风设备，也是用于砲台上、下传达信息；城堡中间有一个大型广场，是当年作为炮兵们日常操练的地方，广场东北角有一口水井，供驻防官兵饮用，当年这口井离大海只有几十米，取出的水却是淡的，非常奇妙。整座砲台的建筑风格很有特色给我们研究明清建筑留下了宝贵的实物资料，见证了当时人民勤劳勇敢，自强不息的民族精神，石砲台是我们祖先国防建设智慧的结晶，它体现了我国人民坚决抵御外敌侵略，保卫神圣国土的崇高爱国主义精神。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　温馨提示：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　联系电话：0754—88543120</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公交路线：可乘坐19路; 25路；103路; 105路直达石炮台公园</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　地址：汕头市金平区海滨路20号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公园开放时间：7：30—23：00（其中：炮台主景点开放时间：7：30—18：00）</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-18 18:36:26',0,NULL,2,NULL,398,8,'',0,0,0,'2016-05-18 18:45:34','2016-05-19 17:07:32');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (483,'【公示】汕头市树木迁移许可事项批前公示（汕城综管公示〔2016〕6号）','Js6VPza3qM6fAar0.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　依照《汕头经济特区城市绿化条例》的有关规定，我局将受理的树木迁移申请事项批前公示如下：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请单位：汕头市星光物流有限公司。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请事项：迁移树木9株。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目地址：汕头市泰山北路东侧（星光物流有限公司门口）分车绿带。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目规模：苹婆9株。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请理由：经规划、交警部门同意，需要拆除分车绿带开口，方便车辆出入大门。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示日期：2016年5月4日。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示地点：汕头市城市综合管理局网站和项目地址现场。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　凡与上述申请事项有重大利益关系的，依照《汕头经济特区城市绿化条例》第三十四条第（二）项、第三十五条的规定，可在公示之日起2日内向本局提出申述（申述采用书面形式。个人申述材料应亲笔签名并附身份证复印件；单位申述材料应由法人代表及单位签章）。逾期未提出的，视为放弃上述权利。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有关上述项目的详细信息，可到汕头市城市综合管理局行政许可管理科（中山路213号建委大楼805室）查询。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　联系电话：13068976077，联系人：郭同志。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　电子信箱：3262269374@qq.com。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　监督电话：88530138。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　特此公告。<br/><br/></span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2016年5月3日</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-03 22:35:56',0,NULL,2,NULL,150,10,'',0,0,0,'2016-05-18 22:53:06','2016-05-18 22:53:06');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (484,'【公示】关于汕头市澄海区协和园林景观有限公司申报城市园林绿化三级企业资质的公示（汕城综管公示〔2016〕7号）','iRuxkPoyrMqUQUx7.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　根据国家住建部《关于修订&lt;城市园林绿化企业资质标准&gt;的通知》（建城【2009】157号）规定，我局对汕头市澄海区协和园林景观有限公司（法人代表：唐伟然；地址：汕头市澄海区广益路唐两松住宅二层）申报延续城市园林绿化三级企业资质进行了审核。为保证审核工作的公正性和增加工作的透明度，现予以公示。公示时间：2016年5月6日—5月12日。如有异议，请于2016年5月12日前书面将有关情况寄（送）到汕头市城市综合管理局行政许可管理科，联系电话：88530138，电子邮箱：cgjxkk@126.com。单位反映情况要加盖公章，个人反映情况要签署真实姓名并留下联系电话、地址、邮编。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2016年5月6日</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-06 22:53:20',0,NULL,2,NULL,102,10,'',0,0,0,'2016-05-18 23:03:33','2016-05-18 23:03:33');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (485,'【公示】汕头市树木迁移许可事项批前公示（汕城综管公示〔2016〕8号）','jNfHFBjeHYZu2F0M.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　依照《汕头经济特区城市绿化条例》的有关规定，我局将受理的树木迁移申请事项批前公示如下：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请单位：汕头市联泰汇悦春天房地产有限公司</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请事项：迁移树木。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目地址：汕头市东厦路126号（市福利院至金墩园段）人行步道</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目规模：石栗21株。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请理由：该项目地址经市政府批准列入汕头市2015年度中心城区“三旧”改造，需要迁移树木并对步道进行改造，重新种植胸径25cm樟树24株。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示日期：2016年5月12日。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示地点：汕头市城市综合管理局网站和项目地址现场。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　凡与上述申请事项有重大利益关系的，依照《汕头经济特区城市绿化条例》第三十四条第（二）项、第三十五条的规定，可在公示之日起2日内向本局提出申述（申述采用书面形式。个人申述材料应亲笔签名并附身份证复印件；单位申述材料应由法人代表及单位签章）。逾期未提出的，视为放弃上述权利。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有关上述项目的详细信息，可到汕头市城市综合管理局行政许可管理科（中山路213号建委大楼805室）查询。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　联系电话：13068976077，联系人：郭同志。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　电子信箱：3262269374@qq.com。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　监督电话：88530138。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　特此公告。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2016年5月11日</span></p>','2016-05-11 23:03:56',0,NULL,2,NULL,81,10,'',0,0,0,'2016-05-18 23:05:17','2016-05-24 21:20:38');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (486,'【公示】汕头市户外广告许可事项批前公示（汕城综管公示〔2016〕9号）','nm6Si5QJ26F1rVtR.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　依照《广东省户外广告管理规定》、《汕头市人民政府办公室关于印发汕头市实施〈广东省户外广告管理规定〉有关问题的通知》（汕府办〔2015〕79号）的有关规定，我局将受理的户外广告设置申请事项批前公示如下：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请单位：汕头市龙湖苏宁电器有限公司。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请事项：设置户外LED屏广告牌1块。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目地址：长平路南侧苏宁广场北外立面。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　项目规模：长23.06米，高10.38米。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　申请理由：经市规划部门批准，在该公司苏宁广场项目北外立面设置户外LED广告牌，作为苏宁广场动态宣传阵地。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示日期：2016年5月19日。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　公示地点：汕头市城市综合管理局网站和公示栏。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　凡与上述申请事项有重大利益关系的，依照《广东省户外广告管理规定》第十一条及《汕头市人民政府办公室关于印发汕头市实施〈广东省户外广告管理规定〉有关问题的通知》第（五）的规定，可在公示之日起15日内向本局提出申述（申述采用书面形式。个人申述材料应亲笔签名并附身份证复印件；单位申述材料应由法人代表及单位签章）。逾期未提出的，视为放弃上述权利。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有关上述项目的详细信息，可到汕头市城市综合管理局行政许可管理科（中山路213号建委大楼805室）或汕头市城市综合管理局网站查询。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　联系电话：13068976077 &nbsp;联系人：郭同志。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　电子邮箱：3262269374@qq.com。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　监督电话：88530138</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　特此公告</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市城市综合管理局</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2016年5月19日</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-19 23:05:47',0,NULL,2,NULL,272,10,'',0,0,0,'2016-05-18 23:07:04','2016-05-18 23:08:45');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (487,'【动态】市城市综合管理局与金平区联合开展春季爱国卫生统一行动','Izh4Icj3Ybh0gufI.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463659292140791.jpg\" title=\"1463659292140791.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　近日，市城市综合管理局会同金平区有关单位在金新路（金砂路-金湖路）周边联合开展以“清死角、除积水，灭鼠蚊、保健康”为主题的春季爱国卫生统一行动。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　活动现场，市城市综合管理局由党组成员、副调研员陈锦辉同志带队，组织局机关及局属各单位共40余人开展环境卫生清洁、除四害行动。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463659367349762.jpg\" title=\"1463659367349762.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　活动现场还设立宣传咨询专场，结合入店入户宣传方式，发放《汕头市城市市容环境卫生管理条例》、《汕头市中心城区生活处理收费制度改革宣传小册子》等城市管理条例、规章以及《共建温馨家园 共享美好生活—致市民的一封信》、《包卫生包绿化包秩序、争当合格文明履行人—致沿街单位、门店及全体市民的倡议书》等宣传资料一批，为春季爱国卫生运动顺利推进积极宣传造势。</span></p><p><br/></p>','2016-05-19 19:58:44',0,NULL,2,NULL,112,9,'',0,0,0,'2016-05-19 21:41:17','2016-05-19 21:41:17');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (488,'【动态】深入开展理论知识学习，全面提升业务岗位水平——市城管局全面展开“业务大比拼、岗位大练兵”业务理论学习','m7vvkQsKFZgPmG4F.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">为进一步提高城管系统干部职工的综合素质和岗位业务技能，促进城市管理工作发展，市城市综合管理局从3月中旬至5月中旬，在局系统开展为期2个月的“业务大比拼、岗位大练兵”技能竞赛活动。活动分为业务理论考试、城管知识竞赛和现场实操比赛等3个阶段，旨在营造全员强化业务学习、提升综合素质的良好氛围，激发城管系统干部职工干事创业、奋发进取的工作热情和创造力，不断提高本职岗位业务技能，使城市管理工作水平上新台阶。目前，“业务理论考试”阶段已基本完成，其他项目正紧锣密鼓进行中。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463667443592246.jpg\" title=\"1463667443592246.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　一、落实部署，技能竞赛全面铺开</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　3月中旬，市城管局印发《市城管局“业务大比拼、岗位大练兵”技能竞赛活动实施方案》，对在局系统开展技能竞赛活动做出具体安排和部署，并成立了以局长许创生为组长的活动领导小组，加强对此次活动的组织指导。局属各单位纷纷响应，根据《实施方案》任务分工，制定相应工作方案，对业务学习、知识竞赛和现场演示等项目进行了细化分解，并对各单位干部职工进行动员，确保以良好的精神状态投入到技能竞赛活动中，力求高标准、高质量、高要求完成活动，达到全面提升岗位业务技能，提高城市管理水平的目的。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463667419110750.jpg\" title=\"1463667419110750.jpg\" alt=\"IMG_4.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 二、以学促用，理论学习扎实推进</span><br/><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 针对城市管理工作层面广、线条多的特点，市城管局系统各单位结合实际，以日常管养知识、专业技能、政策法规等应知、应会、应掌握的内容为重点，充分结合个人自学和集中学习，合理制订学习计划，将理论学习贯穿活动始终，并形成工作惯性，在日常穿插开展。通过业务骨干宣讲、现场操作指导、集中座谈讨论、个人自学等多种形式，对市政、园林、环卫、执法等各自线条的业务理论进行了充分学习，强化各板块干部职工对岗位知识的掌握和了解，提升局系统干部职工业务经办能力。截至目前，全系统共组织集中学习196场次。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463667475114292.jpg\" title=\"1463667475114292.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 三、以考促学，业务考试夯实基础</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　根据阶段学习安排，在各板块干部职工中组织开展针对性的业务理论考试，突出“以考促学”的作用。通过局对口业务科室出题以及各单位自行出题等方式，以考试的形式检验局系统各单位干部职工的学习成效，进一步夯实业务知识基础，深化岗位技能水平。截至目前，共组织业务理论考试21场。从业务理论考试情况来看，广大干部职工对业务理论知识的掌握有了很大提升，能够熟练掌握应知、应会、应懂的基本知识，为做好下来城市管理工作打下坚实基础。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463667493124955.jpg\" title=\"1463667493124955.jpg\" alt=\"IMG_2.jpg\"/></p>','2016-05-19 21:41:59',0,NULL,2,NULL,50,9,'',0,0,0,'2016-05-19 22:18:43','2016-05-19 22:18:43');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (489,'【动态】市城管局迅速贯彻落实陈良贤书记批示精神抓好城市管理工作','QA5JgnnZN1wv1boI.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>4月24日下午，市委陈良贤书记利用周日休息时间，专题听取市城管局的工作汇报。陈良贤书记详细了解汕头城市管理工作的基本情况、近期开展的工作、存在的主要问题以及下来的工作打算，并在市城管局汇报材料上作出批示：“有思路，想干事，关键在落实责任、上下联动，真抓实干，扎实推动，持之以恒，必有成效，让党委、政府满意，让百姓满意。” 陈书记批示下达后，市城管局迅速组织学习传达和贯彻落实，把城管系统干部职工的思想和认识统一到书记的批示精神上来，把城管系统各单位、各部门的工作和行动统一到市委、市政府的决策部署上来，以“地更绿、水更清、灯更亮、路更净，市容更整洁、夜景更璀璨、汕头更美丽”为工作目标，以提升城市投资环境、创造优美生活环境为城市管理工作的指挥棒，提振信心、鼓足干劲，攻坚克难、埋头苦干，以创新促发展，以实干抓落实，不断优化发展环境，服务我市加快振兴发展。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　一、认真学习贯彻，领会批示精神，在统一思想、提高认识上抓落实</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4月25日上午，市城管局召开局系统“两学一做”动员会，局主要负责同志在作“两学一做”动员部署的同时，传达了陈良贤书记对加强城市管理、提升城市环境工作的指示和期望。4月27日陈书记批示下达市城管局后，市城管局立即召开局长办公会，认真学习、传达和贯彻陈书记的批示精神，并将书记的批示精神转发局系统各单位、各科室认真组织学习贯彻，深刻领会精神实质，用以指导、谋划和推动城市管理的各项工作。市城管局班子全体成员认真学习了陈书记的批示精神，一致认为，陈书记来汕履职才20多天，高度关注城市管理工作并作出重要批示，批示有肯定、有要求、有期望、有目标，这既是对城市管理工作的重视，寄予厚望，也是对城管系统干部职工的鼓励和鞭策，极大地增强了城管系统干部职工克服困难、推进工作的信心。城管系统干部职工一致表示要坚决贯彻落实陈书记批示精神，做到落实责任、上下联动，真抓实干，扎实推动，持之以恒，全力以赴推进城市管理各项工作，努力把城市管好。市城管局主要负责同志还就贯彻落实陈书记的批示精神强调四点要求：一是要高度重视城市管理工作。当前城管任务十分繁重，存在问题不少，工作难度大，一定要按照市委市政府的工作部署，全力以赴抓好城市管理，努力创造优质的投资环境和人居环境，服务全市振兴发展大局；二是要按照陈书记的批示，做到“落实责任、上下联动，真抓实干，扎实推动，持之以恒”，有条不紊地推进工作、狠抓落实；三是要下大力气提升城市品质。市城管局把2016年定为“质量效益年”，并要求全体干部职工要鼓足干劲，攻坚克难，以创新促发展，以实干抓落实，一步一个脚印，一点一滴改变城市，不断提升城市品质；四是按照“四个要”（即要保平安、要干点事、要有血性、要有爱心）的要求，加强队伍管理和建设，推进工作高质量、高效率向前发展，使城管系统各级领导和党员干部保持良好的精神状态，使全局上下的思想统一到市委市政府的决策上来，把全局的行动统一到市委市政府的工作部署上来，以实实在在的工作成效，让市委市政府满意、让市民群众满意！</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　二、科学谋划工作，统筹协调推进，在强化措施、明确责任上抓落实</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在统一思想、提高认识的基础上，为及时将陈书记的批示精神转化为工作成效，化为实实在在的具体行动，市城管局系统按照市委、市政府有关创文和城市卫生整治的一系列部署，突出冲刺“一个目标”、抓好“两个板块”、整治“三个重点”。冲刺“一个目标”，即紧紧围绕营造优美环境服务汕头振兴发展，以“地更绿、水更清、灯更亮、路更净，市容更整洁、夜景更璀璨、汕头更美丽”为工作目标；抓好“两个板块”，一方面是继续高标准、严要求强化市城管局自身“一亩三分地”的管理，另一方面是依托市城管办的平台，加强对市城管委成员单位市容环境卫生管理责任落实情况的指导、检查和督办；整治“三个重点”，即强力推进环境卫生、市容“六乱”和生态环境等三个方面的整治和管理。还结合推进创文工作和城市环境提升行动，研究制订了城市环境提升行动实施方案。同时，全力以赴推进城市管理各项工作。一是推进重点环保工程项目。重点抓好雷打石环保工程项目建设和我局负责的污水处理设施建设。力争雷打石生活垃圾填埋场扩建工程今年底前完成库区建设；加快南区濠江污水处理厂沿江北路（濠江大桥－珠浦段）管线建设工作；加快新溪污水处理厂场外管网工程建设，促进新溪污水处理厂早日投产；启动新溪污水处理厂二期建设，同步论证和确定龙珠水质净化厂项目搬迁方案；同时指导、督促潮南区、潮阳区加快城乡生活垃圾处理设施建设进度。二是抓好重点市政设施项目。重点抓好韩江路（天山路-泰山路）三沟桥台及嵩山路积水点改造，珠江路（黄山路～泰山路）和华山路（金砂路～长平路）水浸街改造工程，龙湖沟、新河沟、星湖公园及港区排洪沟黑臭水体整治及水质提升项目等。三是抓好园林绿化项目。重点抓好西堤公园二期工程，儿童公园东园二期和石炮台公园首期简易绿化工程（一期）项目，确保西堤公园和儿童公园2016年内正式开放。四是抓好景观照明项目。重点抓好市政道路推广使用LED项目，对市区部分危损、老旧路灯设施改造，推进礐石大桥等19处景观照明修复改造项目的建设与管理等。力求通过抓好这些项目建设，加强城市扩容提质，提升城市承载力和环境品质。</span></p>','2016-05-19 22:19:22',0,NULL,2,NULL,176,9,'',0,0,0,'2016-05-19 22:28:47','2016-05-19 22:28:47');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (490,'【动态】比拼业务技能，赛出城管风采——市城管局举行“业务大比拼、岗位大练兵”技能实操比赛','fGSEFvAuRpychvKJ.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668413334184.jpg\" title=\"1463668413334184.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>5月13日上午，市城管局在市人民广场举行“业务大比拼、岗位大练兵”技能实操比赛，市园林处、市环卫局、市城照中心等3个单位共52名职工，分5个项目展开了技能比拼。市总工会副主席王述宗、市城管局局长许创生等有关领导现场观摩指导。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　比赛开始前，市城管局副调研员任振威主持了技能实操比赛启动仪式，明确此次技能实操比赛的总体要求和具体内容，并向各参赛单位和选手提出了希望。随后，参加当天技能实操比赛的3个单位迅速投入到比赛中，各项工作有序进行。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668442809685.jpg\" title=\"1463668442809685.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　多线开花，园林人展娴熟技艺</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　此次比赛中，市园林处共派出9支代表队，分别进行了割灌机操作、宽带绿篱剪操作和高空压条等3个项目的比赛，以及现场盆景制作演示。作为传统繁育花木果树技术，高空压条在园林培育方面发挥着非常重要的作用，在这次比赛中，园林参赛队现场对植株进行高空压条，充分考验了职工对枝条的选取、环割范围的控制以及包土的处理等程序的熟练程度；割灌机、宽带绿篱剪等机械操作的比拼同样精彩，作为园林管养的主要机械，参赛队伍从机油配比、安全防护、操作稳定性和修剪标准等四个方面进行了竞赛，在展现园林人高水准作业的同时，也提高了职工对园林管养技术的掌握。最终，高空压条、机械操作均决出团体名次，并进行颁奖。此外，人民广场、中山公园还派出技艺精湛的园艺专家，为在场人员现场演示了罗汉松盆景和山水盆景的制作。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668503660882.jpg\" title=\"1463668503660882.jpg\" alt=\"IMG_5.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　精准拆装，城照人露矫健身手</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　灯具是城市照明基础设施，对灯具的拆、装是城照职工的必备技能之一，市城照中心管养着全市3万多盏灯，对灯具的管养要在保证质量的情况下追求速度，才能提高管养效率，为市民群众的出行提供保障。因此，市城照中心选取了“高压钠灯灯具拆、装”作为此次技能实操比赛的项目，参赛选手在紧张的比赛节奏中，争分夺秒，对全新完好、亮灯正常的灯具进行拆卸和重新组装，并亮灯调试。最终，第一名选手仅用了2分29秒便完成了灯具的拆、装，比正常规定时间整整快了8分钟，充分展现了城照中心职工扎实的基础和过硬的技术。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668524106503.jpg\" title=\"1463668524106503.jpg\" alt=\"IMG_4.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　一气呵成，环卫人显精细水平</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　洒水作业是环卫日常作业内容，为城市道路降尘和清洁起到了重要作用。市环卫直属车队派出5名队员，轮流在指定路段进行洒水实操作业，从吸水栓装水、洒水车前喷水、左右冲洒、交叉对喷冲洗以及后洒水等环节，检验参赛队员对各流程和细节的把握和掌控，最终根据技术规范和操作准则，评出比赛名次。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668552799874.jpg\" title=\"1463668552799874.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　此次技能实操比赛是市城管局“业务大比拼、岗位大练兵”技能竞赛活动的一项重要内容，也是开展“两学一做”学习教育的一个亮点动作市园林处、环卫局、维修中心、城照中心和城绿中心等5个单位根据职责，分板块、分项目，共选出15个项目进行比赛和演示，通过参赛队伍和队员之间互相比拼，进一步强化一线职工在日常维修、养护等管养作业中的技术要求和要领，加强职工之间的技术交流，进一步提升一线人员日常作业技术规范和质量标准。下来，市维修中心、城绿中心还将针对各自项目组织技能实操比赛，以“两学一做”为引领，在全系统营造学习、比拼的浓烈氛围。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在市总工会的大力支持下，将从各单位参赛队伍中评选出“岗位能手”称号，在抓好理论知识学习、开展好技能实操比赛，给予城管人信心的肯定，激发城管系统干部职工干事创业、奋发进取的工作热情和创造力，建设作风硬、业务精、能力强的干部职工队伍，不断提高城管队伍形象，朝“地更绿、水更清、灯更亮、路更净，市容更整洁，夜景更璀璨，城市更美丽”的目标迈进！</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span></p>','2016-05-19 22:29:04',0,NULL,2,NULL,56,9,'',0,0,0,'2016-05-19 22:36:04','2016-05-19 22:36:04');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (491,'【动态】市城管局召开局系统创建全国文明城市强化城市管理工作会议','sLjAuhtTrSqOMufH.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　全市创建全国文明城市强化城市管理工作会议召开之后，今天下午，市城管局随即召开局系统创建全国文明城市强化城市管理工作会议。该局表示，将发挥城市管理主力军的作用，管好“一亩三分地”，实现精细化管理，进一步把城市管实管好。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　会议首先观看了当天上午市委市政府联合召开的汕头市创建全国文明城市、强化城市管理电视电话会议录像，传达会议精神。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668685121736.jpg\" title=\"1463668685121736.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　随后，许创生局长与局属各单位签订责任书并作重点工作部署讲话。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668702920449.jpg\" title=\"1463668702920449.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　讲话要求，要把思想、认识和行动统一到市委、市政府的要求上来，不折不扣、坚定不移地贯彻市委、市政府的工作部署，进一步提高思想认识，提振管好城市的信心，自觉投身于创建全国文明城市、强化城市管理的工作中。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668721754519.jpg\" title=\"1463668721754519.jpg\" alt=\"IMG_3.jpg\"/></p><p>　　<span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">作为城市管理主力军，城管系统要乘势而上，发挥城市管理主力军的作用。要从自身做起，抓实管好一亩三分地，要明确任务、强化责任、加强督查、严明奖罚、抓好保障，做到高标准、严要求，实现精细化管理；要发挥城管办和城管各职能部门的作用，加强对各区县和市直相关部门的协调、指导、检查、督促；要主动作为、主动协调、主动担当，支持、配合、参与市直和各区县开展城市管理工作。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span></p>','2016-05-19 22:36:29',0,NULL,2,NULL,126,9,'',0,0,0,'2016-05-19 22:39:49','2016-05-19 22:39:49');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (492,'【污水哪儿去了】中心城区污水处理厂','G54sDf5tfAoPOaXL.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">根据城市规划，我市中心城区共规划5座污水处理厂。目前，已建成并投入运行的污水处理厂有3座，总建设规模为48万吨/日。其中，汕头龙珠水质净化厂建设规模为26万吨/日、汕头市北轴污水处理厂建设规模为12万吨/日、汕头市南区污水处理项目濠江分厂建设规模为10万吨/日；在建项目为新溪污水处理厂，一期设计处理规模6万吨/日，预计2016年下半年建设投入使用；另外，在牛田洋片区还规划有西区污水处理厂（尚未启动建设）。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　一、汕头龙珠水质净化厂项目</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　汕头龙珠水质净化厂位于中山东路黄厝围，海湾大桥北引桥西侧，项目远期规划规模为34万吨/日，分期建设。由广东联泰环保股份有限公司进行建设和运营。污水处理服务范围为新津河以西、梅溪河以东区域。目前，项目处理规模达到26万吨/日，年处理污水量约9490万吨，污水处理后排放水质各项指标达到《城镇污水处理厂污染物排放标准》二级（GB18918-2002），大大改善汕头港湾水质环境。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668592432383.jpg\" title=\"1463668592432383.jpg\" alt=\"【龙珠】一期厂区.jpg\" width=\"607\" height=\"304\" style=\"width: 607px; height: 304px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　二、汕头市北轴污水处理厂项目</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　汕头市北轴污水处理厂位于汕头市金平区金凤路北侧、金园工业区南侧，占地面积103亩，项目建设规模为12万吨/日。由汕头市广业环保有限公司负责建设运营（BOT）。按城市规划，该厂服务范围为梅溪河以西、西港河以东区域，服务面积约18平方公里。由于岐山片区截污管网（护堤路系统）建设涉及大量拆迁导致目前无法启动建设，为解决该厂近期污水量问题，已将大学路截污管网延伸至大学路西尾桥附近，把规划上属于西区污水处理厂服务范围的鮀浦片区部分污水引入该厂处理。目前项目年处理水量可达4380万立方米，污水处理后排放水质各项指标达到《城镇污水处理厂污染物排放标准》二级（GB18918-2002），大大改善汕头内港湾水质环境。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668650105169.jpg\" title=\"1463668650105169.jpg\" alt=\"北轴.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　三、汕头市南区污水处理项目-濠江分厂项目</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　濠江分厂项目位于濠江区广澳街道河渡村，规划建设规模为41万吨/日，分期建设，污水处理服务范围濠江辖区，面积80多平方公里。项目一期建设规模为10万吨/日，是省产业转移园区重要环保配套设施，由汕头市南区广业环保有限公司负责建设运营（BOT），并于2014年8月正式投入运营。因配套管网尚不完善，目前年处理水量仅达1825万立方米。污水处理后排放水质各项指标达到《城镇污水处理厂污染物排放标准》二级（GB18918-2002），对改善濠江区水环境质量，推动社会经济发展具有重要意见。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668665107291.jpg\" title=\"1463668665107291.jpg\" alt=\"南区.jpg\" width=\"542\" height=\"342\" style=\"width: 542px; height: 342px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　四、新溪污水处理厂项目</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　汕头市新溪污水处理厂选址于新溪镇外砂河与中山东路（规划）交界处东南侧，服务范围为外砂镇、新溪镇行政辖区及东海岸新城，服务面积约65.37平方公里。项目规划规模为22万吨/日，分期建设。一期工程建设规模为6万吨/日，于2015年9月10日动工建设，计划于2016年下半年建成投产。项目的建设将填补外砂镇、新溪镇和东海岸新城生活污水集中处理的空白，是市委市政府为民办实事的重要举措，对改善外砂镇、新溪镇和东海岸新城水环境质量，推动区域社会经济发展具有极其重要的意见。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-19 22:19:00',0,NULL,2,NULL,394,7,'',0,0,0,'2016-05-19 22:40:27','2016-05-19 22:40:27');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (493,'【动态】市委副书记孙光辉带队到市城管局听取创建全国文明城市强化城市管理工作电视电话会议贯彻落实情况专题汇报','yf4bSaqD9WzbNRqI.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668922523266.jpg\" title=\"1463668922523266.jpg\" alt=\"IMG_4.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"><br/></span>　　今天下午，市委副书记、市创文领导小组常务副组长孙光辉带队到市城管局，专题听取市城管局创建全国文明城市强化城市管理工作电视电话会议贯彻落实情况汇报，重点听取创文和强化城市管理试点工作情况。孙光辉在讲话中强调，城管系统明确责任、增强信心，精细化管好“一亩三分地”，率先把每一项任务落实到位。市领导钟挥锷、邱伟出席会议并讲话。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668942342188.jpg\" title=\"1463668942342188.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　作为城市管理的主要部门和创文工作的主力军之一，许创生局长表示，市城管局将根据城市环境提升行动工作方案有关要求，在结合职责强化提升“一亩三分地”管养工作，协调督促各区各部门开展整治的同时，将进一步推进龙湖区玫瑰园绿化平台升级改造以及镇邦、金新、长平、利安、金湖等路段周边5片市政排水管网清淤清疏等2个试点项目；同时，采取疏堵结合措施，标本兼治解决占路为市、占道经营问题，开展路树、路灯和其他市政设施乱张贴、乱吊挂以及环境卫生、治污清淤、绿化美化等专项整治行动，坚持不懈的推进城市提升行动有序开展。</span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668957127847.jpg\" title=\"1463668957127847.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　孙光辉强调，城管系统在推进城市环境提升行动中行动快、态度坚决、措施有力，为城市环境打下好基础，创造好开头。他要求，市城管局要明确责任、增强信心，管好自身负责的“一亩三分地”，通过进一步精细化管理，率先把每一项任务落实到位，并建立内部督查问责机制，发现问题及时整改，努力让市城管局管理的“一亩三分地”成为全市的样板。作为业务主管部门，市城管局要通过制订规则目标，提供具体帮助指导及业务督查，在全市“两大行动”中负起牵头、指导、帮助、督查责任。要充分依靠各级党委政府和各部门，坚持集中力量对突出问题开展整治，根据实际推进试点工作，建立长效机制。要广泛动员广大市民群众，把工作做细、做实，为创建全国文明城市、强化城市管理做出应有的贡献，让市委、市政府和市民群众满意。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463668974125003.jpg\" title=\"1463668974125003.jpg\" alt=\"IMG_2.jpg\"/></p>','2016-05-19 22:39:56',0,NULL,2,NULL,153,9,'',0,0,0,'2016-05-19 22:43:32','2016-05-19 22:43:32');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (494,'【最美劳动者】寻找最美劳动者——专访“城市血管清道夫”','6jblabFubyACPB4K.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463669807117339.jpg\" title=\"1463669807117339.jpg\" alt=\"IMG_4.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>城市下水道是“城市的血管”，做好下水道清疏工作，是城市防洪排涝的重要保障。我台记者了解到，在今年汛期到来之前，市城管局早计划早安排，组织对市区28条主次干道下水道进行清疏。清疏队伍每天投入100多人，3台清洗车、3台吸泥车，分成4个班组，分片区作业。清淤工人每天从清早7点半开始清淤，一直干到晚上6点才收工。作业过程需要克服管道水位高、作业空间狭小、污水臭味刺鼻等种种困难，恶劣的作业环境，加大了清疏工人的作业难度和强度。但作为“城市血管清道夫”，清疏工人们任劳任怨，不退缩、不叫苦，不怕脏、不怕臭，为疏通下水道，减少道路内涝水浸默默奉献。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463669835513713.jpg\" title=\"1463669835513713.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　图为清疏工人作业现场。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463669849473005.jpg\" title=\"1463669849473005.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　清淤过程中，为避免产生的淤泥废土在运输过程中造成二次污染，打捞出来的淤泥废土需采用竹筐装乘，沥干水分后才能清离施工现场，并及时清洗路面，对交通造成一定的影响。对此，我台呼吁广大市民朋友给予支持理解。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463669871422055.jpg\" title=\"1463669871422055.jpg\" alt=\"IMG_1.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160519/1463669890600336.jpg\" title=\"1463669890600336.jpg\" alt=\"IMG_5.jpg\"/></p>','2016-05-19 22:52:09',0,NULL,2,NULL,290,8,'',0,0,0,'2016-05-19 22:59:56','2016-05-19 22:59:56');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (495,'【规章】《汕头市城市污水处理费征收管理办法》','sKMzQlGr9HX28e4T.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">广东省汕头市人民政府</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">第 104 号</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　《汕头市城市污水处理费征收管理办法》已经2008年8月8日市人民政府十二届第三十次常务会议审议通过,现予公布,自2008年10月1日起施行。<br/><br/></span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 市 长　　　　&nbsp;</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 二〇〇八年八月十三日&nbsp;<br/><br/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><strong>汕头市城市污水处理费征收管理办法</strong></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第一条 为规范城市污水处理费的征收管理,保障城市污水集中处理设施的建设和正常运行,根据国家和省的有关规定,结合本市实际,制定本办法。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二条 本市金平、龙湖、濠江区(以下称中心城区)范围内的城市污水处理费征收管理工作,适用本办法。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第三条 凡在中心城区范围内排放污水的单位和个人(以下称排污人),均应按本办法规定缴纳城市污水处理费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第四条 市环境保护行政主管部门(以下简称市环保部门)负责中心城区城市污水处理费的征收管理工作。市环保部门可以委托其下属的城市污水处理管理机构具体负责城市污水处理费的征收工作。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　建设、排水、税务、水利、民政等行政主管部门及镇人民政府(街道办事处)、社区村(居)民委员会应当按照各自职责,协助市环保部门做好城市污水处理费征收管理工作。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　物价、财政、审计等部门按照各自职责做好城市污水处理费征收使用的监督管理工作。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第五条 城市污水处理费的征收遵循合法、合理、保本微利的原则。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第六条 城市污水处理费征收标准由市环保部门提出,经市物价部门审核,报市人民政府批准后执行。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span>第七条 对排污人自行配套污水处理设施,经处理后的污水达到国家和地方排放标准且直接排入海洋、江河、湖泊,不使用中心城区市政排水设施的,不征收城市污水处理费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span>第八条 下列排污人暂免征收城市污水处理费:</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)大、中、小学校、幼儿园、托儿所(不含校办企业、职业学校和教职员工家庭生活用水);</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span>(二)非营利性的医疗机构;<br/></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)敬老院、福利院等社会公共福利机构;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(四)家庭人均收入低于中心城区居民最低生活保障标准的特困户。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第九条 在建成的污水集中处理设施纳管范围内,已自行配套污水处理设施的排污人,依法停用自有污水处理设施后,经市环保部门审核同意,可由城市污水集中处理设施负责处理其排放的污水,并按照低于自行处理的成本缴纳城市污水处理费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　前款规定缴纳城市污水处理费的具体办法由市环保部门会同物价部门制定,报市政府批准后执行。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十条 排污人应缴纳的城市污水处理费按以下规定征收:</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)排污人使用供水单位供水的,由市环保部门委托供水单位按月随自来水费一并征收,实行自来水费、城市污水处理费收费一票制;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)排污人使用自备水源的,由市环保部门直接征收或者委托水行政主管部门代征;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)建筑施工作业等临时性排水排入市政排水设施的,由市环保部门对其实际排水量进行核定后直接征收,具体核定办法由市环保部门会同物价部门制定;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(四)排污人属本办法第七条、第八条规定情形的,实行先征后返,具体实施办法由市环保部门会同财政部门制定;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(五)排污人属本办法第九条规定情形的,由市环保部门直接征收。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（供水公司）代征单位应当按月向市环保部门报送城市污水处理费征收情况明细表。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十一条 城市污水处理费按排污人的用水量征收: &nbsp;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(一)由供水单位供水的,按供水单位所提供的供水量征收;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(二)使用自备水源的,按水行政主管部门提供的采水量征收;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　(三)未安装用水、采水计量装置的,按供水设施日供水能力核定的月用水量征收。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十二条 排污人向城市污水集中处理设施排放污水,符合国家或者省规定的城市污水集中处理设施接纳标准,已缴纳城市污水处理费的,不再缴纳排污费;超过国家或者省规定的城市污水集中处理设施接纳标准的,应当依法缴纳超标准排污费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排污人缴纳污水处理费后,其在排水环节上应征收的城市排水设施使用费不再征收。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十三条 城市污水集中处理单位经营或者管理的城市污水集中处理设施排出的污水超过国家和地方排放标准的,应当依法缴纳超标准排污费。&nbsp;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十四条 任何单位和个人不得擅自减免城市污水处理费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　国有企业因严重亏损等原因,经市人民政府批准,可以缓缴城市污水处理费,但不得免交。城市污水处理费缓缴期限最长不得超过三个月。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十五条 城市污水处理费按行政事业性收费实施管理,实行收费许可证及收费综合年审制度。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十六条 城市污水处理费的收入全额上缴市财政专户,实行收支两条线管理。其中,代征单位代收的城市污水处理费,按月划入市财政设立的专户;市环保部门征收的城市污水处理费,直接缴入市财政设立的专户。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十七条 城市污水处理费的收入应当专款专用,专项用于城市污水处理厂、排污管网、排污泵站等相关设施的建设、运行和维护。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市环保部门应当会同排水行政主管部门于每年年初提出城市污水处理费收入的使用计划,经市财政部门审核,报市政府批准后,按规定用途使用。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　禁止任何单位和个人截留、挪用污水处理费。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十八条 市物价、财政、审计等部门应当加强对城市污水处理费收支情况的监督检查,保证专款专用。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市环保部门和排水行政主管部门应当定期向社会公布城市污水处理费的征收及使用情况。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第十九条 排污人未按本办法规定缴纳城市污水处理费的,由市环保部门责令十五日内缴纳;逾期不缴纳的,按日加收千分之二的滞纳金,并可处以罚款,但对个人最高不得超过一百元,对单位最高不得超过一千元。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　排污人对市环保部门作出的具体行政行为不服的,可以依法申请行政复议或者提起行政诉讼。排污人逾期拒不缴纳城市污水处理费或者履行行政处罚决定,也不申请行政复议,又不提起行政诉讼的,市环保部门可以申请人民法院强制执行。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十条 拒绝、妨碍行政执法部门及其工作人员依法执行公务的,由公安机关依照《中华人民共和国治安管理处罚法》予以处罚;构成犯罪的,依法追究刑事责任。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十一条 行政机关、社区村(居)民委员会及其工作人员违反本办法,玩忽职守、滥用职权、循私舞弊、索贿受贿的,由其所在单位或者上级主管部门给予行政处分;构成犯罪的,依法追究刑事责任。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十二条 潮阳、潮南、澄海区及南澳县城市污水处理费的征收管理,由当地人民政府结</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">合本地实际,参照本办法执行。&nbsp;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　第二十三条 本办法自2008年10月1日起施行。</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-05-19 22:42:27',0,NULL,2,NULL,98,11,'',0,0,0,'2016-05-19 23:01:36','2016-05-19 23:01:36');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (496,'【动态】汕头橄榄台“汕头城管”频道今起正式开通','ed0NXpYbGZG2xp0N.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160520/1463742888848887.jpg\" title=\"1463742888848887.jpg\" alt=\"IMG_9.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为贯彻落实汕头市创建全国文明城市、强化城市管理工作会议精神，真正把“创文牌”打成“民生牌”、“民心牌”，市城管局与市广播电视台密切合作，在汕头橄榄台APP联手打造移动互联网城市管理资讯发布及服务互动平台“汕头城管”频道。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160520/1463742906164983.jpg\" title=\"1463742906164983.jpg\" alt=\"IMG_8.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5月20日上午，汕头橄榄台“汕头城管”频道开通仪式在汕头市广播电视台大堂举行，市城市综合管理局、市广播电视台有关领导出席仪式。市城市综合管理局许创生局长、市广播电视台杜绍茂台长分别代表市城市综合管理局、市广播电视台签订《公共服务合作备忘录》，并按下启动球。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160520/1463742921976606.jpg\" title=\"1463742921976606.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　“汕头城管”频道的开通，将为城管部门加大城市管理宣传力度，营造浓烈创文氛围打造新的宣传平台，发挥更大作用。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160520/1463742937106176.jpg\" title=\"1463742937106176.jpg\" alt=\"IMG_1.jpg\"/></p>','2016-05-20 19:11:09',0,NULL,2,NULL,333,9,'',0,0,0,'2016-05-20 19:16:24','2016-05-20 19:18:08');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (497,'【动态】520暴雨将至，市政清疏队员在路上！','DziRlKW1tTCXpAh8.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px; font-family: 微软雅黑;\"><img src=\"http://olive.strtv.cn:7337/uploads/20160520/1463745453128183.jpg\" title=\"1463745453128183.jpg\" alt=\"IMG_3.jpg\" style=\"text-align: center; white-space: normal;\"/></span>　　根据市气象预报，20日夜间到22日我市将有大到暴雨，局部大暴雨的降水过程，小伙伴们脑海里又开始幻想威尼斯城的景象了么？手机上的“滴滴打船”安装了吗？美好的周末又该被水浸堵在家里了吧？今天还是520啊，说好的烛光晚餐……不说了，想想就心塞！！别着急，市城管局温馨呾你知：大雨还没来，我们市市政工程维修中心排水一所、二所、三所的队员们就已经上路了，为了大家的520，我们拼了！目前他们已经对市区广场周边、长平路交华山路、黄河路市公安局门口等易积水路段进行清疏，确保强降雨到来时，这些路段不会发生大面积积水，同时，清除沟井盖四周的垃圾杂物，确保沟井正常排水功能；还提前关闭鸥下关水闸及天山泵站引水闸，龙湖关、黄厝围电排站也利用低潮位全力排水，降低市区沟体水位，预留最大纳水容积。<br/><img src=\"http://olive.strtv.cn:7337/uploads/20160520/1463745465443141.jpg\" title=\"1463745465443141.jpg\" alt=\"IMG_5.jpg\" style=\"text-align: center; white-space: normal;\"/></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　怎么样，小伙伴们是不是有吃下定心丸的赶脚了？不过心里怎么还是有些怕怕的……没关系，我们的各支应急排涝队伍都已经做好防御强降雨的充分准备，必要时将启动应急预案进行排涝作业，尽最大努力让大家过一个安心的周末。<br/>　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">牺牲小我，造福百姓，城管人一直在路上！</span></p>','2016-05-20 19:44:58',0,NULL,2,NULL,410,9,'',0,0,0,'2016-05-20 19:58:57','2016-05-20 20:04:56');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (498,'【最美劳动者】寻找最美劳动者——冒雨铺鹅卵石的人','pFb7k7b5hBVIqb8R.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160521/1463837795113306.jpg\" title=\"1463837795113306.jpg\" alt=\"903874636.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当我们感觉到身边环境变美好了的时候，请别忘记这些冒雨铺鹅卵石的人……</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160521/1463837830537337.jpg\" title=\"1463837830537337.jpg\" alt=\"1819440392.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160521/1463837853876686.jpg\" title=\"1463837853876686.jpg\" alt=\"1409093748.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p>','2016-05-21 21:31:01',0,NULL,2,NULL,375,8,'',0,0,0,'2016-05-21 21:38:14','2016-05-21 21:38:14');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (499,'【温馨提示】中心城区部分路段路灯停电','J6PI3lVHvKN7MjTo.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160522/1463846737864938.jpg\" title=\"1463846737864938.jpg\" alt=\"867478915.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span><span style=\"font-size: 16px; line-height: 22.4px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">　　今夜，由于我市持续强降雨，中心城区部分路段积水严重。为安全起见，市城市照明管养中心立即启动防暴雨应急预案，派出工作人员全面加强路面巡查，及时对部分积水严重、已明显影响路灯设施安全运行的路段，通过采取中控和人工手动方式进行应急暂时停电，确保过往行人安全，候积水退后再恢复送电。暂时停电的具体路段有:安居小区 &nbsp;、杏花二桥慢车道、 &nbsp;护堤路与梅溪桥 &nbsp;、内马路与公园路、长江路与黄山路 、 长江路与嵩山路 &nbsp;、珠江路与嵩山路 、泰山路（珠江路至黄河路口）、杏花街、华坞路、中山路（近金环路口）、长平路与嵩山路、星湖公园港务局旁。</span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160522/1463846770254317.jpg\" title=\"1463846770254317.jpg\" alt=\"1529632603.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px; line-height: 22.4px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\"></span></span><br/></p>','2016-05-22 00:04:10',0,NULL,2,NULL,402,10,'',0,0,0,'2016-05-22 00:06:37','2016-05-22 00:06:37');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (500,'【动态】赞！昨夜，城管人快速反应战暴雨，恢复市容市貌显神威！','Mg2wZsmWcD5FdTH9.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160522/1463930601866444.jpg\" title=\"1463930601866444.jpg\" alt=\"1551573568.jpg\"/></p><pre class=\"js_message_plain ng-binding\" ng-bind-html=\"message.MMActualContent\" style=\"margin-top: 0px; margin-bottom: 0px; font-family: inherit; font-size: 14px; white-space: pre-wrap; word-break: initial; line-height: 22.4px; background-color: rgb(255, 255, 255);\">　　5月21日夜晚，我市持续强降雨，造成市区多处路段积水。市城管局未雨绸缪，主动作为，提前启动各涵闸强排、组织清淤，采取一切积极有效措施，确保排水顺畅，除个别积水点较严重需改造外，其他积水路段半小时至一小时基本排干！凌晨一点全城道路通畅、路灯全部恢复供电，正常亮灯！</pre><p><img src=\"/uploads/20160522/1463930630640743.jpg\" title=\"1463930630640743.jpg\" alt=\"367906829.jpg\"/><br/></p><pre class=\"js_message_plain ng-binding\" ng-bind-html=\"message.MMActualContent\" style=\"margin-top: 0px; margin-bottom: 0px; font-family: inherit; font-size: 14px; white-space: pre-wrap; word-break: initial; line-height: 22.4px; background-color: rgb(255, 255, 255);\">　　暴雨来临前，市市政工程维修中心龙湖关、黄厝围及东厦电排站提前启动各涵闸进行自排或强排降低水位并组织对部分易积水路段的下水道进行清淤。暴雨来临时，该中心全员出动，掀开各积水路段的沟井盖加强排水速度，同时对因下穿泵站水泵出现故障造成积水严重的金砂东下穿路段采取临时封路措施，确保过往车辆行人安全，并采取措施进行排水，多数积水路段的积水基本在半小时至一小时内排干，至凌晨一点，全城道路已恢复通畅。该中心还于5月22日凌晨4:30起再次对华山路、韩江路、黄河路等多处严重积水点进行巡查排涝，至5月22日上午8:30，市区积水全部排干。</pre><p style=\"text-align: center\"><img src=\"/uploads/20160522/1463930651662431.jpg\" title=\"1463930651662431.jpg\" alt=\"167329009396971081.jpg\"/></p><p><span style=\"font-family: 微软雅黑;\"></span></p><pre class=\"js_message_plain ng-binding\" ng-bind-html=\"message.MMActualContent\" style=\"margin-top: 0px; margin-bottom: 0px; font-family: inherit; font-size: 14px; white-space: pre-wrap; word-break: initial; line-height: 22.4px; background-color: rgb(255, 255, 255);\">　　市城市照明管养中心为应对强降雨对中心城区照明设施的影响，第一时间启动应急预案，组织干部职工22人、车辆12台次，全面加强路面巡查。为防止积水路段路灯线路漏电伤人，该中心临时对积水严重的护堤路、长江路、珠江路等路段实施停电处理，至22日凌晨一点，路灯全部恢复供电，正常亮灯。此外，21日夜间，该中心接到110来电称嵩山路下蓬中学对面路灯线路有冒火星和冒烟现象后，迅速赶往现场进行检查，发现该处是因大树倾斜导致路灯线路受损，该中心迅速落实安全措施，及时排除隐患。</pre><p style=\"text-align: center\"><img src=\"/uploads/20160522/1463930842138717.jpg\" title=\"1463930842138717.jpg\" alt=\"933698100.jpg\"/></p>','2016-05-22 23:22:08',0,NULL,2,NULL,854,9,'',0,0,0,'2016-05-22 23:31:22','2016-05-23 10:18:01');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (501,'【微曝光】弹！这些行为要不得！','YMnQmC5TFGczfbR0.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160523/1464000369880903.jpg\" title=\"1464000369880903.jpg\" alt=\"5038435252179951021.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　今年5月17日上午，我市召开创建全国文明城市暨强化城市管理工作会议，吹响了“创文”和强化城市管理的进军号角，力求通过创建全国文明城市，提高市民群众文明素质，营造一个宜居宜业的城市环境。市城管局作为落实“创文”和强化城市管理的主力军之一，围绕“地更绿、水更清、灯更亮、路更净、市容更整洁、夜景更璀璨、汕头更美丽”的目标，不断强化措施，落实责任，主动作为，千方百计提升城市管理水平。但是，近日我局在日常巡查中发现，一些市民群众的行为和我市当前“创文”的要求很不协调，如中山公园草地、雕像、洗手台等处不时可见市民群众踩踏草地或做出可能损坏设施设备的行为。在此，小编呼吁广大市民群众遵守公德，共同营造文明和谐的城市环境。<br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160523/1464000390394558.jpg\" title=\"1464000390394558.jpg\" alt=\"592226258417362028_副本1.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160523/1464000408110320.jpg\" title=\"1464000408110320.jpg\" alt=\"700863882201370695_副本1.jpg\"/></p><p><br/></p>','2016-05-23 18:39:32',0,NULL,2,NULL,340,9,'',0,0,0,'2016-05-23 18:48:35','2016-05-23 18:53:25');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (502,'【创文进行时】市城管局系统全面动员，全力推进创文工作！','mApgf1ZQXOU97wUd.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px; font-family: 微软雅黑;\"></span>　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">市城管局系统各单位认真贯彻落实市创建全国文明城市强化城市管理工作会议精神，围绕“地更绿、水更清、灯更亮、路更净、市容更整洁、夜景更璀璨、汕头更美丽”的目标，上下联动，全面动员，不断强化措施，落实责任，主动作为，扎实推动，在局系统内迅速掀起创建全国文明城市强化城市管理工作新高潮！&nbsp;<br/><br/></span></p><p style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 0);\"><strong><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"></span></strong></span></p><hr/><p style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 0);\"><strong><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市城市管理行政执法局</span></strong></span><br/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城市管理行政执法局组织各科室、各大队学习贯彻全市强化城市管理工作会议的有关精神，迅速部署，开展市容环境综合整治专项行动。充分发挥指挥协调作用，落实与各区执法局的联动机制，及时清理占道经营、乱吊乱挂等影响市容“六乱”现象，加强泥头车漏撒和生猪屠宰、家禽屠宰等专项整治工作。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079370649177.jpg\" style=\"\" title=\"1464079370649177.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079370128411.jpg\" style=\"\" title=\"1464079370128411.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079370387113.jpg\" style=\"\" title=\"1464079370387113.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079370121297.jpg\" style=\"\" title=\"1464079370121297.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079439118036.jpg\" style=\"\" title=\"1464079439118036.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079439112958.jpg\" style=\"\" title=\"1464079439112958.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><hr/><p style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 0);\"><strong style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市环境卫生管理局</span></strong></span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市环境卫生管理局积极督促中心城区各级环卫部门开展创建国家文明城市，强化城市环境卫生管理工作，落实环境卫生整治。各级环卫部门全面加强日常环卫作业和管理基础，并积极调查处置市民群众关注的环境卫生方面热点难点问题。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079531111430.jpg\" style=\"\" title=\"1464079531111430.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079531996486.jpg\" style=\"\" title=\"1464079531996486.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079531851728.jpg\" style=\"\" title=\"1464079531851728.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079531203583.jpg\" style=\"\" title=\"1464079531203583.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079531739716.jpg\" style=\"\" title=\"1464079531739716.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><hr/><p style=\"text-align: center;\"><strong style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市园林绿化管理处</span></strong></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市园林绿化管理处迅速将市创文强化城市管理工作的精神传达至处属各单位，统一认识，积极行动，全力做好绿化管养、园区卫生保洁、各项基础设施改造与维修工作。近日来，各管养单位共修剪草皮12万平方米，补种树木310株，修剪造型树240株，修剪绿化带、緑化色块7300平方米，清运绿化垃圾杂物135车。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623719190.jpg\" style=\"\" title=\"1464079623719190.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623232758.jpg\" style=\"\" title=\"1464079623232758.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623395677.jpg\" style=\"\" title=\"1464079623395677.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623786922.jpg\" style=\"\" title=\"1464079623786922.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623451876.jpg\" style=\"\" title=\"1464079623451876.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079623527440.jpg\" style=\"\" title=\"1464079623527440.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><hr/><p style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 0);\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市市政工程维修中心</span></strong></span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市市政工程维修中心重点做好排水清疏，及时防洪排涝；加强道路维修，确保道路安全；启动桥梁检测，维修病害桥梁；加强设施巡查，及时处理问题；强化联动执法，制止破坏市政设施等几项工作。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079664877331.jpg\" style=\"\" title=\"1464079664877331.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079664297736.jpg\" style=\"\" title=\"1464079664297736.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079664133570.jpg\" style=\"\" title=\"1464079664133570.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079664108358.jpg\" style=\"\" title=\"1464079664108358.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079664115402.jpg\" style=\"\" title=\"1464079664115402.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><hr/><p style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 0);\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市城市照明管养中心</span></strong></span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城市照明管养中心全力以赴加强巡查，完善城市照明设施管养工作，加强灯容灯貌保洁整治，强化责任担当。各照明所认真做好维修管养工作，同时对路灯设施上乱吊挂广告杂物现象及时进行清理，进一步加快“牛皮癣”整治节奏，提高清理效率。近日，共清理拆除路灯杆牛皮癣、非法悬挂物923处。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079703264129.jpg\" style=\"\" title=\"1464079703264129.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079703443485.jpg\" style=\"\" title=\"1464079703443485.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079703129904.jpg\" style=\"\" title=\"1464079703129904.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079703854439.jpg\" style=\"\" title=\"1464079703854439.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079703119292.jpg\" style=\"\" title=\"1464079703119292.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><hr/><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\"><br/>市城市绿化管理中心</span></strong></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城市绿化管理中心成立专门绿化巡查队，全面督查创文工作。对重点路段、重点部位做到精益求精，打造“样板路”，同时对2支清理小分队进行优化改革，及时清运绿化垃圾，确保常态化、全覆盖，做到垃圾“日产日清”。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737110027.jpg\" style=\"\" title=\"1464079737110027.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737469304.jpg\" style=\"\" title=\"1464079737469304.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737104524.jpg\" style=\"\" title=\"1464079737104524.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737493101.jpg\" style=\"\" title=\"1464079737493101.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737569052.jpg\" style=\"\" title=\"1464079737569052.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464079737114322.jpg\" style=\"\" title=\"1464079737114322.jpg\"/></p><p><br/></p>','2016-05-24 16:34:13',0,NULL,2,NULL,367,9,'',0,0,0,'2016-05-24 16:52:35','2016-05-24 16:57:34');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (503,'【动态】比拼业务技能，赛出城绿风采','pjfYYeKc3P9tTsib.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085693124215.jpg\" title=\"1464085693124215.jpg\" alt=\"IMG_6.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5月23日下午，市城市绿化管理中心在天山路二号绿化平台举行“业务大比拼、岗位大练兵”技能竞赛及现场实操演示活动。市城管局丁伟亮副局长、任振威副调研员及局人事科、园林科等有关负责同志到场观摩指导。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085726784985.jpg\" title=\"1464085726784985.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　此次比赛中，市城绿中心共组织8支代表队，分别进行了灌木球修剪、绿篱色块修剪和定点放线等3个项目的比赛。参赛队伍在紧张的比赛节奏中，争分夺秒，按照比赛规则各显身手，赛出水平、赛出风格、赛出形象，如修剪一株灌木球只花了40多秒。3个项目的比赛最终均决出一、二、三等奖，同时评出团体综合前三名，并颁发荣誉证书。赛后，2支路树队还在现场进行了大乔木高空修剪演示。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085751217740.jpg\" title=\"1464085751217740.jpg\" alt=\"IMG_10.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　植物整修修剪和定点放线是城市绿化管养工作的基本技能，市城绿中心管养着全市8万多株行道树、120万平方米的公共绿地，日常管理养护任务相当繁重，通过此次比拼活动，进一步强化了一线职工日常作业技术规范和质量标准，提高绿地管养效率，进一步激发了干部职工干事创业、奋发进取的工作热情，提升队伍整体形象，以饱满的精神面貌投入到“创文”工作中去，朝提升城市绿化档次和文化内涵、实现城市绿化精细化、规范化和节约型管养目标迈进。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797404369.jpg\" style=\"\" title=\"1464085797404369.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797785373.jpg\" style=\"\" title=\"1464085797785373.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797407424.jpg\" style=\"\" title=\"1464085797407424.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797741437.jpg\" style=\"\" title=\"1464085797741437.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797989817.jpg\" style=\"\" title=\"1464085797989817.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464085797125863.jpg\" style=\"\" title=\"1464085797125863.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span><br/></p>','2016-05-24 18:17:59',0,NULL,2,NULL,161,9,'',0,0,0,'2016-05-24 18:31:55','2016-05-24 18:31:55');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (504,'【逛公园】中山公园星山景区有望“六一”前开放','dbT5guKVugvpZdQg.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中山公园星山改造项目目前正在加紧推进中，现进入绿化种植阶段，该景区将力争在“六一”前开放。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464086351101030.jpg\" style=\"\" title=\"1464086351101030.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464086351348815.jpg\" style=\"\" title=\"1464086351348815.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160524/1464086351185553.jpg\" style=\"\" title=\"1464086351185553.jpg\"/></p><p><br/></p>','2016-05-24 18:31:59',0,NULL,2,NULL,377,8,'',0,0,0,'2016-05-24 18:47:23','2016-05-24 18:47:23');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (505,'【动态】追查源头  治理建筑垃圾污染','bRjX9P2hmECqf2hb.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464137003851383.jpg\" title=\"1464137003851383.jpg\" alt=\"IMG_2.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>针对“汕头泥头车很任性，想怎么装就怎么装，想怎么漏就怎么漏”的乱象，市余泥渣土管理所在前期摸查的基础上，于5月23日晚开展中心城区建筑垃圾处置情况全面清查，查清中心城区8个建筑垃圾污染黑点，并立即对处置建筑垃圾过程中污染环境卫生的行为予以制止，向相关责任方和泥头车司机进行宣传教育，要求责任单位立即清理污染路段。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464137024650511.jpg\" title=\"1464137024650511.jpg\" alt=\"IMG_3.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5月24日上午，市余泥渣土管理所再次对中心城区进行巡查，从巡查情况看，部分责任单位已经落实清理整治。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464137043361596.jpg\" title=\"1464137043361596.jpg\" alt=\"IMG_1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　近期，该所将采取四项措施强化建筑垃圾处置管理：一是规范处置管理。引导处置建筑垃圾的单位和泥头车按规定办理建筑垃圾准运证，从源头上规范建筑垃圾处置管理。二是加强路面巡查。及时制止建筑垃圾污染环境卫生行为，减少处置建筑垃圾对环境卫生的影响。三是上门检查宣传。组织人员分为两组对中心城区在建工地进行检查，督促工地出入车辆保持干净，严禁超载行为并落实防扬措施。四是加强执法联动。对不配合管理，处置建筑垃圾、运载建筑材料漏撒，严重污染城市环境的，将联合执法部门予以重点查处。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464137070135537.jpg\" title=\"1464137070135537.jpg\" alt=\"IMG_4.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464137087480751.jpg\" title=\"1464137087480751.jpg\" alt=\"IMG_5.jpg\"/></p>','2016-05-25 08:41:06',0,NULL,2,NULL,206,9,'',0,0,0,'2016-05-25 08:45:17','2016-05-25 08:45:17');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (506,'【温馨提醒】解放桥、同济桥要封闭检测了！','MI88UfF0fC4MB3fS.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464169352102604.jpg\" title=\"1464169352102604.jpg\" alt=\"1.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为确保市区桥梁安全使用，汕头市市政工程维修中心将对解放桥、同济桥进行荷载试验，为确保道路交通安全，根据《中华人民共和国道路交通安全法》第三十九条的规定，<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(146, 208, 80);\">2016年5月27日晚上22：00至28日凌晨6:00对解放桥实施全封闭临时交通管制，5月31日晚上21:30分至6月1日凌晨6时对同济桥实施全封闭临时交通管制</span>，请过往车辆及行人按现场批示标志、执勤交警的指挥绕道通行，不便之处敬请谅解。</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小伙伴们可根据现场交通疏导图指示通行，尽量绕开封闭区域。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464169596134250.jpg\" style=\"\" title=\"1464169596134250.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464169596135735.jpg\" style=\"\" title=\"1464169596135735.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464169596328633.jpg\" style=\"\" title=\"1464169596328633.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464169596336508.jpg\" style=\"\" title=\"1464169596336508.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span><br/></p>','2016-05-25 17:30:09',0,NULL,2,NULL,521,10,'',0,0,0,'2016-05-25 17:50:01','2016-05-25 17:50:01');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (507,'【动态】市城管局圆满完成“业务大比拼、岗位大练兵”技能竞赛活动','o8IptWr9vussf4Y9.jpg','<p style=\"text-align: left;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>5月25日上午，市城管局在局七楼会议厅举行“业务大比拼、岗位大练兵”知识竞赛决赛。此次知识竞赛分初赛、预赛和决赛，经过前两轮的激烈比拼，市园林处、市城绿中心和市城管局机关共4支队伍晋级决赛。决赛赛场上，各参赛代表队你追我赶、勇为人先，展现了出色的竞赛水平，最终市园林处代表队在决赛中脱颖而出，获得了知识竞赛第一名。至此，市城管局“业务大比拼、岗位大练兵”技能竞赛活动圆满完成。市总工会王述宗副主席、市直机关工委刘史芬副书记、市城管局许创生局长以及城管系统有关领导亲临现场观看了比赛，并为知识竞赛以及技能实操比赛获奖队伍颁奖。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174756262665.jpg\" title=\"1464174756262665.jpg\" alt=\"IMG_1.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174768860205.jpg\" title=\"1464174768860205.jpg\" alt=\"IMG_2.jpg\"/></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城管局以“两学一做”学习教育为契机，全面贯彻落实市创建国家文明城市强化城市管理工作会议精神，努力提升城管系统干部职工的综合素质和岗位业务技能，开展了为期2个月的“业务大比拼、岗位大练兵”技能竞赛活动。活动分为业务理论考试、城管知识竞赛和现场实操比赛等3个阶段，旨在营造全员强化业务学习、提升综合素质的良好氛围，激发城管系统干部职工干事创业、奋发进取的工作热情和创造力，不断提高本职岗位业务技能，使城市管理工作水平上新台阶。</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 一、以学促行，开展理论知识学习</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 针对城市管理工作层面广、线条多的特点，市城管局系统各单位结合实际，以日常管养知识、专业技能、政策法规等应知、应会、应掌握的内容为重点，灵活利用个人自学和集中学习，合理制订学习计划，将理论学习贯穿活动始终，并形成工作惯性，在日常穿插开展。通过业务骨干宣讲、现场操作指导、集中座谈讨论、个人自学等多种形式，对市政、园林、环卫、执法等各自线条的业务理论进行了充分学习，强化各板块干部职工对岗位知识的掌握和了解，提升局系统干部职工业务经办能力。同时，在局系统组织开展针对性的业务理论考试，以局对口业务科室出题以及各单位自行出题等方式，通过考试检验局系统各单位干部职工的学习成效，进一步夯实业务知识基础，深化岗位技能水平。自开展活动以来，全系统共组织集中学习200多场次，共组织业务理论考试21场，广大干部职工对业务理论知识的掌握有了很大提升，能够熟练掌握应知、应会、应懂的基本知识，在局系统营造了良好的学习氛围。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174816734122.jpg\" title=\"1464174816734122.jpg\" alt=\"IMG_3.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174826244764.jpg\" title=\"1464174826244764.jpg\" alt=\"IMG_4.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174837109567.jpg\" title=\"1464174837109567.jpg\" alt=\"IMG_5.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 二、以比促练，比拼业务岗位技能</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　技能实操比赛是市城管局“业务大比拼、岗位大练兵”技能竞赛活动的一项重要内容，也是开展“两学一做”学习教育的一个亮点动作。市园林处、环卫局、维修中心、城照中心和城绿中心等5个单位根据职责，分板块、分项目，共选出15个项目进行比赛和演示，通过参赛队伍和队员之间互相比拼，进一步强化一线职工在日常维修、养护等管养作业中的技术要求和要领，加强职工之间的技术交流，进一步提升一线人员日常作业技术规范和质量标准。经过各单位激烈比拼，决出团体和个人共9项第一名，在市总工会的大力支持下，将在其中评选“岗位能手”称号，在抓好理论知识学习、开展好技能实操比赛的同时，给予城管人信心的肯定，鼓励全体干部职工不断创先争优，培养造就一支作风硬、业务精、能力强的城管队伍，为创建国家文明城市、强化城市管理工作奠定坚实的基础。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174920552485.jpg\" title=\"1464174920552485.jpg\" alt=\"IMG_6.jpg\"/></p><p><img src=\"/uploads/20160525/1464174933335863.jpg\" title=\"1464174933335863.jpg\" alt=\"IMG_7.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464174999172987.jpg\" title=\"1464174999172987.jpg\" alt=\"IMG_8.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp;　 三、以赛促优，展现队伍精神风貌</span></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp; &nbsp; 　根据实施方案安排，市城管局在局系统开展了城管知识竞赛，进一步调动干部职工主动学习业务技能知识的自觉性，在局系统营造浓烈的“比学赶超”竞赛氛围，全面加深对城市管理知识的学习理解，切实提升解决问题的能力。经过初赛严格筛选，共有10支队伍进入预赛，通过预赛决出4支队伍进入决赛，并决出最终名次。本次知识竞赛题库内容涵盖了城管系统各线条的业务理论知识，各参赛代表队不仅要熟悉自身工作职责，更要学习了解兄弟单位业务岗位知识，是促进局系统各单位间形成良性互动、强化业务交流、树立“大城管”意识的一个良好契机，同时也展现了城管队伍斗志昂扬、勇攀高峰的精神面貌。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464175021201388.jpg\" title=\"1464175021201388.jpg\" alt=\"IMG_9.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464175030112390.jpg\" title=\"1464175030112390.jpg\" alt=\"IMG_10.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2016年是“十三五”的开局之年，是党中央以“两学一做”学习教育深化从严治党的一年，是我市开启创建文明城市、强化城市管理工作的第一年，也是城管系统的“质量效益年”。作为城市管理的主管部门和主力军之一，市城管局通过加强队伍建设、夯实业务基础，推进工作高质量、高效率向前发展，使城管系统各级领导和党员干部保持良好的精神状态，使全局上下的思想统一到市委市政府的决策上来，把全局的行动统一到市委市政府的工作部署上来，自觉投身于创建全国文明城市、强化城市管理的工作中，主动作为、真抓实干、攻坚克难，进一步提升城市管理效能，全面提升城市管理水平。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464175046812569.jpg\" title=\"1464175046812569.jpg\" alt=\"IMG_11.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160525/1464175057412803.jpg\" title=\"1464175057412803.jpg\" alt=\"IMG_12.jpg\"/></p>','2016-05-25 19:01:53',0,NULL,2,NULL,390,9,'',0,0,0,'2016-05-25 19:18:33','2016-05-25 19:18:33');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (508,'【动态】调整道路“洗澡”时间  开启强力降温模式','xoeWRS8dfjDesh8M.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"><span style=\"font-family: 微软雅黑;\"></span>　　汕头本周以来持续高温，平均最高气温达到30度左右，而且还在上升的趋势中！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258571873114.jpg\" title=\"1464258571873114.jpg\" alt=\"u=2840010242,504519187&amp;fm=21&amp;gp=0.jpg\" width=\"127\" height=\"111\" style=\"width: 127px; height: 111px;\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">很显然“桑拿天”已经开始渐渐爆发了，小伙伴们做好准备了吗？若没有，那就赶紧喝杯82年的雪碧压压惊吧……<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258593843422.jpg\" title=\"1464258593843422.jpg\" alt=\"20150622013922839.jpg\" width=\"120\" height=\"113\" style=\"width: 120px; height: 113px;\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">不过，不管大家做好准备了没，我们的城市“美容师”们已经准备好了，他们给城市道路“洗澡”的时间已悄然改变。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258638519996.jpg\" title=\"1464258638519996.jpg\" alt=\"u=145011033,29016386&amp;fm=56.jpg\" width=\"99\" height=\"98\" style=\"width: 99px; height: 98px;\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">随着炎炎夏季的到来，汕头市环卫直属车队原来实行的每天凌晨4:00至7:00对中心城区北岸主要道路进行道路洒水降尘作业模式，待到市民群众上班上学时，降尘降温效果已不明显。为提升道路洒水降尘降温效果，营造“清凉一夏”的最佳体验，提高市民群众的舒适度，该队从5月26日起尝试对道路洒水作业做如下调整：</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\">★调整范围：初定路段为海滨路、中山路、滨港路和时代广场周边。</span></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\">★调整作业时间：增加1个作业班次，作业时间由凌晨4:00至7:00调整为上午8:30至11:00、下午14:30至16:00。</span></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\">★调整作业模式：视路况和人流、车流情况，采取洒水与冲水相结合模式进行降尘降温作业。</span></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　这样的调整对于怕热的小伙伴们来说，是不是诚意满满呢？ 什么？为啥只有这几条路？别急，心急吃不了热豆腐，而且新事物总该给大家一个逐步习惯的过程嘛。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　目前环卫车队的蜀黍们正抓紧制定扩大道路洒水降尘作业覆盖面工作方案，争取进一步扩大洒水作业面，助力为市民群众营造清凉夏日，请大家稍安勿躁，搬好小板凳等着好戏上演吧！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258713647972.gif\" title=\"1464258713647972.gif\" alt=\"20111130181327.gif\" width=\"103\" height=\"109\" style=\"width: 103px; height: 109px;\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">好吧，惊喜归惊喜，重要的事情来了！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(255, 0, 0);\">注意避让！注意避让！注意避让！（我已经说了三遍！）</span></strong></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编必须提醒各位小伙伴，给道路“洗澡”的水压可不是闹着玩的哦，道路洒水作业模式启动后，洒水可达15米，可覆盖3条车道！道路冲水作业模式：洒水可达25米，可覆盖5条车道！！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258804139311.jpg\" title=\"1464258804139311.jpg\" alt=\"1443271189_500x500.jpg\" width=\"135\" height=\"148\" style=\"width: 135px; height: 148px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当然，我们的“美容师”蜀黍们作业时会尽力规避行人和车辆，但也需要请大家给予理解和支持，看到如下图的洒水车来时，注意避让，共同保障道路交通安全，小编在此先谢谢大家啦！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160526/1464258919316283.jpg\" title=\"1464258919316283.jpg\" alt=\"2739134185050440731.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p>','2016-05-26 18:21:35',0,NULL,2,NULL,314,9,'',0,0,0,'2016-05-26 18:37:41','2016-05-26 18:53:32');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (509,'【动态】全省《广东省城乡生活垃圾处理条例》粤东片区宣讲培训会在我市顺利举行','O5rqdHhEMZ99WIF2.jpg','<p><span style=\"font-family: 微软雅黑;\"></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464339778897083.jpg\" title=\"1464339778897083.jpg\" alt=\"320995707739480930.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5月27日，由广东省住房和城乡建设厅主办、广东省环境卫生协会承办的全省《广东省城乡生活垃圾处理条例》粤东片区宣讲培训会在我市顺利举行。本次会议是全省粤东片区的首场培训会，来自汕头、揭阳、潮州、汕尾市的市、区（县、市）级环卫主管部门及乡镇（街道）的分管领导和有关人员，共约350人参加了此次宣讲培训大会，大会由广东省环境卫生协会秘书长伍琳瑛主持。省住房和城乡建设厅城市建设处副处长卓云峰出席大会并作了动员讲话。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464339801738296.jpg\" title=\"1464339801738296.jpg\" alt=\"713624356981063616.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　卓云峰要求，各地要落实主体责任，切实增强垃圾管理工作的责任感和紧迫感；要切实推进垃圾源头分类减量，加快推进城市环卫保洁工作转型升级；要明确目标任务，深入推进农村生活垃圾管理工作；要切实贯彻落实《条例》，积极发挥《条例》对城乡垃圾管理工作指导作用。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　培训大会上，广东省环境卫生协会会长、高级工程师陈善坤对条例进行了详细解读，广东省环境卫生协会伍琳瑛秘书长对《广东省农村生活垃圾治理验收办法》进行了介绍说明。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464339814237320.jpg\" title=\"1464339814237320.jpg\" alt=\"666717127151219229.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　此次培训是市城管局为贯彻落实陈良贤书记重要讲话， 主动向省住建厅和环卫协会对接和争取，并在其支持下在汕召开。汕头市市级和6区1县环卫主管部门及乡镇（街道）分管领导，有关人员约100人参加培训。通过此次培训，将对当前我市创建全国文明城市，强化城市管理起到很好促进作用。</span><br/></p><p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464339830487550.jpg\" style=\"\" title=\"1464339830487550.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464339831133514.jpg\" style=\"\" title=\"1464339831133514.jpg\"/></p><p><br/></p>','2016-05-27 16:46:47',0,NULL,2,NULL,106,9,'',0,0,0,'2016-05-27 17:05:10','2016-05-27 17:06:02');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (510,'【动态】倡导文明　爱绿护绿','UVpI3QuOkARKCkOV.jpg','<p></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464343919421711.jpg\" title=\"1464343919421711.jpg\" alt=\"IMG_2169_看图王x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市道路分车绿化带不仅能分隔道路交通，还具有美化城市的作用。市区海滨路、潮汕路、金砂东路自改造提升后，道路中央都设立了分车绿化带，丰富了城市的色彩。近年来，市城绿中心创新思维，精益求精，投入大量人力物力，对市区重点路段、重点部位的绿化实行精细化管理，力争打造市区道路绿化“样板路”，提升管养标准和绿化档次。然而，因缺少硬防措施，分车绿化带经常被一些贪图方便的市民踩踏出不少缺口，虽经多次曝光、设栏，但效果甚微。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464343938863174.jpg\" title=\"1464343938863174.jpg\" alt=\"404728173113787260_看图王x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　据统计，仅上述三条路段因人为破坏已形成绿地缺口约44处，损坏绿化面积近500平方米。不仅影响了市绿化带整体美观效果，给日常管养工作造成困难，也带来一定的交通安全隐患。近日，结合我市创建全国文明城市、强化城市管理各项工作的深入开展，市城绿中心组织人力，再次对已破坏的绿地进行复绿、补绿，补种鸭脚木等植物。同时，在海滨路、潮汕路、金砂东路的分车绿化带增设总长度超过600米的隔离网，并在醒目位置挂上温馨提示标语。 &nbsp;</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464343958736575.jpg\" title=\"1464343958736575.jpg\" alt=\"IMG_2175_看图王x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　绿化是一座城市的外衣，市城绿中心在此呼吁每一位市民都能做到爱绿护绿，从自身做起，提高文明素质，用实际行动共同倡导文明汕头建设，呵护我们共同的绿色家园。</span><br/></p><p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464343975108969.jpg\" style=\"\" title=\"1464343975108969.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464343976286677.jpg\" style=\"\" title=\"1464343976286677.jpg\"/></p><p><br/></p>','2016-05-27 18:00:48',0,NULL,2,NULL,230,9,'',0,0,0,'2016-05-27 18:19:30','2016-05-27 18:19:30');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (511,'【公示】汕头市户外广告许可事项批前公示（汕城综管公示〔2016〕10号）','AKUX4UCuVMoR96C0.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　依照《广东省户外广告管理规定》、《汕头市人民政府办公室关于印发汕头市实施〈广东省户外广告管理规定〉有关问题的通知》（汕府办〔2015〕79号）的有关规定，我局将受理的户外广告设置申请事项批前公示如下：<p>　　申请单位：汕头市新天华文化传播有限公司。<br/>　　申请事项：设置路灯杆灯箱广告。<br/>　　项目地址：长平路（金环路口至华山路口、嵩山路口至黄山路口），黄山路（韩江路口至长平路口、金砂路口至珠池路口）。<br/>　　项目规模：126杆，每杆规格1.8米×1.1米。<br/>　　申请理由：该公司经拍卖竞得以上路段126杆路灯杆设置灯箱广告使用权，期限三年。<br/>　　公示日期：2016年5月27日。<br/>　　公示地点：汕头市城市综合管理局网站和公示栏。<br/>　　凡与上述申请事项有重大利益关系的，依照《广东省户外广告管理规定》第十一条及《汕头市人民政府办公室关于印发汕头市实施〈广东省户外广告管理规定〉有关问题的通知》第（五）的规定，可在公示之日起15日内向本局提出申述（申述采用书面形式。个人申述材料应亲笔签名并附身份证复印件；单位申述材料应由法人代表及单位签章）。逾期未提出的，视为放弃上述权利。<br/>　　有关上述项目的详细信息，可到汕头市城市综合管理局行政许可管理科（中山路213号建委大楼805室）或汕头市城市综合管理局网站查询。<br/>　　联系电话：13068976077&nbsp; \r\n联系人：郭同志。<br/>　　电子邮箱：<a style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\" href=\"mailto:3262269374@qq.com\">3262269374@qq.com</a>。<br/>　　监督电话：88530138<br/>　　特此公告</p><p><br/><br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n汕头市城市综合管理局<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2016年5月27日<br/></p></span><br/></p>','2016-05-27 18:37:44',0,NULL,2,NULL,241,10,'',0,0,0,'2016-05-27 18:39:37','2016-05-27 18:39:37');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (512,'【微曝光】路边的白色鹅卵石不要采！','16mjFKg5YTtr9cTJ.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464346852369083.jpg\" title=\"1464346852369083.jpg\" alt=\"580817654048140417.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编还记得，我们在本月21日发布过这样一组图：“寻找最美劳动者——冒雨铺鹅卵石的人”，讲述了这么一群在雨中美化树穴的劳动者。&nbsp;<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464346889951174.jpg\" style=\"\" title=\"1464346889951174.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464346889865842.jpg\" style=\"\" title=\"1464346889865842.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　铺上了白色鹅卵石的树穴整齐划一，使行道树整体景观档次进一步提升，成了一道新的风景线。这些鹅卵石既能改良行道树树穴营养面积，合理保水，为树木提供了较好的生长环境，又能保持树木周围地面土壤疏松、通气，防止板结，确保行道树的旺盛长势，同时与周围步道设施相协调，彰显行道树的整齐美观。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　但是，近期同事告诉小编，部分树穴中的鹅卵石总是莫名其妙减少，一些树穴又恢复了黄土露天的状态，本来优美的风景线开始变得残缺不堪。经过同事耐心观察，原来总有这么一些“不速之客”想把这些本属于大家的美好风景带回家中独自欣赏。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464346914574517.jpg\" style=\"\" title=\"1464346914574517.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160527/1464346914632395.jpg\" style=\"\" title=\"1464346914632395.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编非常理解“爱美之心人皆有之”，铺上鹅卵石后的美丽树穴能得到市民们的青睐，小编心里也是感觉“逼格”满满，但是小编在此不得不说，作为一名文明的市民，应该拥有分享的胸怀。把美景共享给大家的人，家中哪怕没有点缀也必然是美的。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　让我们共同做一个心灵美、行为美的文明市民！</span></p>','2016-05-27 18:59:23',0,NULL,2,NULL,572,9,'',0,0,0,'2016-05-27 19:03:47','2016-05-27 19:06:05');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (513,'【汕头好市民】热心市民栽柠檬，全民爱绿树新风','qBiYy9QLIR2LvgPh.jpg','<p><span style=\"font-family: 微软雅黑;\">　　今天小编看到有一位热心市民在星湖公园绿地栽种柠檬树，他每天用塑料袋装水给树苗浇灌！真的好有爱！好感动！太值得点赞了！<br/>　　小编相信，如果我们身边有越来越多这样的市民，我们的城市环境一定会变得更好！大家说是不是呢？<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160528/1464440998131311.jpg\" title=\"1464440998131311.jpg\" alt=\"819290204946574160_副本.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160528/1464440656240203.jpg\" title=\"1464440656240203.jpg\" alt=\"7895599769647336131_副本.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160528/1464441012445163.jpg\" title=\"1464441012445163.jpg\" alt=\"681022691953215852.jpg\"/></p><p><br/></p>','2016-05-28 20:47:52',0,NULL,2,NULL,282,12,'',0,0,0,'2016-05-28 21:11:28','2016-05-28 21:11:28');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (514,'【最美劳动者】抵挡威逼利诱 浴血英勇擒贼','X62RPjIVaBkiHhSf.jpg','<p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\">中山公园保卫陈聚成见义勇为，协助民警捉摩托车盗贼</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464501093911413.jpg\" title=\"1464501093911413.jpg\" alt=\"179862252512564601.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　面对摩托车盗贼的威逼利诱，市中山公园职工陈聚成正义凛然，不顾自身安危，协助同益派出所的民警英勇擒贼。直到偷车贼被成功制服抓走，陈聚成才发现自己的颈部被尖锐的开锁工具划伤，鲜血直流，略有偏差就伤及颈部大动脉。尽管见义勇为一度让生命安全面临危胁，陈聚成依然不改嫉恶如仇的耿直秉性，坚守公园大门，守护游客及财产的平安。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　事情发生在5月8日。当天上午9点多，中山公园里人山人海，一些居心不良的人也想伺机浑水摸鱼。正在公园正门轮值的保卫班长陈聚成接到群众举报：“有人在偷摩托车”。陈聚成发现，月眉路上一名四十多岁的男子形迹可疑，正蹲在地上准备开电动车的后锁，而前锁已经成功开启。陈聚成上前仔细端详，男子见状慌忙起身离开。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　难道眼睁睁看着小偷盗窃未遂安然离开？陈聚成心有不甘，他从公园正门“转战”到月眉门。果不其然，那名男子也碾转来到公园月眉门口一带，伺机作案。再次见到陈聚成，男子提高了警惕，晃悠到公园对面席坐静待时机，寻找作案目标。陈聚成也玩起“心理战”，站到僻静的角落，暂时在男子的眼前消失，放松他的戒备心理。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经过十几分钟的耐心等待，男子终于忍不住出手了。只见男子骑坐到路旁一辆摩托车上，顺势把手中的袋子挂在车上，使用“T”型工具，轻而易举地把摩托车锁拧开，动作娴熟得仿佛他就是这辆车的主人。陈聚成马上打电话向辖区派出所报警。眼看着盗贼就要得逞，陈聚成顾不上孤身一人，果断出手，把男子开锁的双手紧紧按住，并大喊“抓贼”。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　偷车男子被突如其来的袭击吓得不轻，一股脑儿地掏出身上红红绿绿的钞票，哀求陈聚成“放一条生路”。见陈聚成不为所动，男子“软的不行来硬的”，扬言要“捅死”陈聚成，得到的是陈聚成“不乖乖伏法只会让罪行越深重”的呵斥。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　二人扭打中，同益派出所民警也火速到场，周围群众听到声响也协力捉贼，大家合力把负隅顽抗的盗贼扭送公安部门。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　【后记】在偷车男子的身上，大家找到尖锐的开锁工具，还有螺丝刀、剪刀等作案工具。等众人散去，继续在公园值勤的陈聚成，在旁人提醒下，才发现自己的脖子在扭打过程中被尖锐的作案工具划伤，血流如注。去医院验伤时，陈聚成才知道，再偏点就伤到颈部大动脉，性命有虞。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　这已经不是陈聚成第一次抓贼。几年前，一名盗贼在公园里抢劫游客财物，被陈聚成逮了个正着。在中山公园工作了42个年头，陈聚成明年就要退休了，但他自始至终用满腔热情，尽职尽责地投入园区管理和保卫工作中。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　经过半个月时间，陈聚成脖子上的伤口早已结痂。一道三四厘米长的伤疤爬在他的颈部，仿佛是一道“军功章”，静静诉说着他洒血擒贼的英勇经历。（转载自<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">《汕头都市报》，记者 林彦恂 袁笙 摄影报道）<br/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">　　这起感人事件，汕头电视台《今日视线》“出彩汕头人”栏目也做了题为“威逼利诱不动摇，保安勇擒偷车贼”的报道。</span></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464501638780166.jpg\" style=\"\" title=\"1464501638780166.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464501638326094.jpg\" style=\"\" title=\"1464501638326094.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464501638328762.jpg\" style=\"\" title=\"1464501638328762.jpg\"/></p><p style=\"text-align: justify;\"><br/></p><p><br/></p>','2016-05-29 12:30:48',0,NULL,2,NULL,262,8,'',0,0,0,'2016-05-29 14:02:56','2016-05-29 14:06:41');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (515,'【花儿为什么这样美】赏花莫忘育花人','Wsz6F9om6DJhPBs3.jpg','<p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 255, 0);\">人民广场精心培育醉蝶花、长春花，花色宜人</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/><br/></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><hr/><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">赏花启事</span></strong></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编最近逛人民广场，发现广场内的草坪边上和条状花坛等处有了喜人的变化，多株时花竞相盛开，美不胜收，成为广场今年夏季的一大亮点。目前，栽种在广场内草坪边和广场内环游道长条状花坛上的5万株醉蝶花、2万株长春花已陆续开花，花期约有3个月，<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(255, 0, 0);\">6月份将是人民广场赏花的最佳时期</span></strong>，届时，人民广场内将有醉蝶花、长春花、硬枝黄婵、大红花、大叶紫薇可供市民朋友观赏。</span></p><hr/><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504820991538.jpg\" style=\"\" title=\"1464504820991538.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504931122188.jpg\" title=\"1464504931122188.jpg\" alt=\"人民广场2.jpg\" width=\"1\" height=\"1\" style=\"width: 1px; height: 1px;\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504820587297.jpg\" style=\"\" title=\"1464504820587297.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504820925656.jpg\" style=\"\" title=\"1464504820925656.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504820608899.jpg\" style=\"\" title=\"1464504820608899.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464504998824876.jpg\" title=\"1464504998824876.jpg\" alt=\"人民广场2.jpg\"/></p><p><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　不过，做完赏花预告，大家可曾知道，这些美艳鲜花的背后，是一群怎样的能工巧匠呢？而为了培育这些鲜花，他们又花费了多少辛勤汗水呢？</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　今年3月份，人民广场管理处为解决因资金紧缺，无法购买大量时花种植问题，另辟蹊径，发挥主观能动性，购买了醉蝶花等时花种子1公斤，约15万粒，尝试自行播种育苗。第一批取出约5万粒种子，播种醉蝶花、长春花两个品种。技术人员通过对种子进行浸种12小时，取出后放在自制的育种箱中催芽，每天按每12小时温差10摄氏度进行催芽，催芽4天长出胚根再进行播种，由于播种后长期受低温阴雨等天气影响，种苗成长缓慢，并出现病害，技术人员又耐心杀菌灭虫，精心培育，使种苗成长正常。整个培育过程十分讲究，也遇到了不少困难，广场管理处的技术人员都耐心克服，把广场当作自己的家，把广场花草树木都当作自己的孩子一般细心养护。该管理处还专门成立时花生产管理班组，落实专人负责，对本次栽培试验进行总结，对开花结出的后种子进行采摘播种栽种，同时，对生产过程中碰到的各种技术问题进行研究和解决。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046524600.jpg\" style=\"\" title=\"1464505046524600.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046107084.jpg\" style=\"\" title=\"1464505046107084.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046140726.jpg\" style=\"\" title=\"1464505046140726.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046756886.jpg\" style=\"\" title=\"1464505046756886.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046107773.jpg\" style=\"\" title=\"1464505046107773.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160529/1464505046978843.jpg\" style=\"\" title=\"1464505046978843.jpg\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编相信，在人民广场管理处这么多“爸爸妈妈”的精心照顾下，会有越来越多的美丽的“花精灵”在广场落户，给市民朋友提供一个赏心悦目的休闲去处。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-05-29 14:49:27',0,NULL,2,NULL,332,7,'',0,0,0,'2016-05-29 15:03:29','2016-05-29 15:03:29');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (516,'【动态】市城管局许创生局长带队到金平区指导交流城市环境提升行动试点小区和绿化平台管理工作','ymhD1dz2YAfvYLcl.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464603841705592.jpg\" title=\"1464603841705592.jpg\" alt=\"IMG_3929.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>5月30日上午，许创生局长带队到金平区，现场察看试点区域的东方园街区和葵花园绿化平台，并到广厦街道召开座谈交流会，倾听意见建议。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464603864535745.jpg\" title=\"1464603864535745.jpg\" alt=\"IMG_4067.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在座谈会交流中，许创生局长与金平区杨惠文副区长和相关街道就推进创文和强化城市管理工作进行了座谈交流。许局长提出五点意见：一是要强化责任意识；二是要整合社区资源；三是要带着感情、穷尽办法干事情；四是工作方法上要做到突破重点、难点、试点，推动面上工作，先治标后治本，及时反映情况，促使工作落实；五是市城管局将和各区一同努力，推动创建全国文明城市、强化城市管理工作落实。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464603908625983.jpg\" title=\"1464603908625983.jpg\" alt=\"IMG_4074.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　许创生局长一行还来到由市城管局负责建设的试点绿化平台——玫瑰园绿化平台，实地查看工程进展情况，并就相关工作提出要求。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464603923209779.jpg\" title=\"1464603923209779.jpg\" alt=\"IMG_4094.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城管局丁伟亮副局长、申健副调研员，市环卫局杨卫文局长，市城管局办公室、市容科、园林科、城管科相关工作人员也陪同参加活动。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604032682053.jpg\" title=\"1464604032682053.jpg\" alt=\"IMG_4101.jpg\"/></p>','2016-05-30 18:15:41',0,NULL,2,NULL,235,9,'',0,0,0,'2016-05-30 18:28:26','2016-05-30 18:28:26');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (517,'【动态】星湖公园水体整治就快完工啦！','iWMTVVKkzUfWBoe5.jpg','<p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\"></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604581842691.jpg\" title=\"1464604581842691.jpg\" alt=\"62567543870801136.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px;\">　　</span>星湖公园水体是我市中心城区的黑臭水体之一，为改善该水体的水环境，我局自去年10月开始实施的星湖公园水体整治，至今年5月底止，已基本完成施工图纸内的主要工作量。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604599101761.jpg\" title=\"1464604599101761.jpg\" alt=\"311270270081172791.jpg\"/></p><p><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">本次整治建设的主要内容有：1.更换龙湖沟天山泵站引水泵，将2台现状水泵更换为每台流量800m³/h，扬程18m，功率55kw潜水泵，以及电动葫芦1台，控制柜一组；2.各接入雨水管进行截污，在雨水排放口分别设置截流井、检查井以及铺设截流管；3.分别在中山东北侧和韩江路北侧设置涵闸与溢流墙兼顾蓄水和应急排涝，共设置7个闸门和溢流墙；4.湖底积泥清淤及修复砼路面及其他硬地以及绿化复绿修复；5.增加湖3至湖5底板撬开及设置种植水下睡莲,以提高水体水质和湖底纳污能力。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604615990075.jpg\" title=\"1464604615990075.jpg\" alt=\"213867361805521833.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　由于今年入春以来，我市遇到多次大雨暴雨的影响，造成星湖公园水体整治工作的进展，影响施工进度。在市城管局的高度重视下，经过施工单位努力及相关部门的密切配合，目前已完成图纸内的主要工作量，并于本月19日进行蓄水测试，蓄水水位达到珠标高程+0.22米，总体景观效果良好。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604630565867.jpg\" title=\"1464604630565867.jpg\" alt=\"248357590254350748.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">目前市城管局正针对蓄水效果存在的一些问题，如韩江闸溢流墙偏低和中山闸溢流墙偏高等问题，抓紧进行完善，同时对尚欠部分收尾工作，已落实施工单位加快进度，力争今年上半年全面完成整个项目的工作，尽快给周边市民一个良好的活动环境。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604655999499.jpg\" style=\"\" title=\"1464604655999499.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604655419904.jpg\" style=\"\" title=\"1464604655419904.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464604655680246.jpg\" style=\"\" title=\"1464604655680246.jpg\"/></p><p><br/></p>','2016-05-30 18:30:44',0,NULL,2,NULL,189,9,'',0,0,0,'2016-05-30 18:37:57','2016-05-30 18:37:57');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (518,'【汕头好市民】热心市民送水慰问  清疏工人士气更盛','9M9Wv1XtmQSiFgnG.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464605675119701.jpg\" title=\"1464605675119701.jpg\" alt=\"3.jpg\"/></p><p><span style=\"font-family: 微软雅黑;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　今天上午9点，市区烈日当空，天气异常闷热，户外温度高达32℃，市政工程维修中心的排水清疏工人在黄河路持续清疏作业了2个多小时，清理了积土300多筐。随着天气愈发炎热起来，东风日产汕佳专营店的王辉女士上班时看到我们的清疏工人在烈日下辛勤作业，汗流浃背，身上的衣物已经全部湿透，便立即安排该公司员工为我们可爱的清疏工人送上矿泉水进行慰问，并对我们的工作给予赞扬和感谢。市民这样举动，不正是“人间自有真情在”的最佳体现吗？<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464605698266366.jpg\" title=\"1464605698266366.jpg\" alt=\"1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　我们的城市管理工作需要大家的参与和支持，需要更多这样的正能量，需要更多这样的感人瞬间，小编相信，在热心市民的支持和鼓励下，我们的工作已经会做得更好，城市环境一定会更加整洁有序！</span><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160530/1464605716592719.jpg\" title=\"1464605716592719.jpg\" alt=\"2.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编在此再次衷心感谢东风日产汕佳专营店对我们城市管理工作的关心和支持。</span></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-05-30 18:52:31',0,NULL,2,NULL,310,12,'',0,0,0,'2016-05-30 18:57:01','2016-05-30 18:57:57');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (519,'【市政呾你知】龙湖沟等沟体缘何又发臭？','dkgHVCoLOEXou0HA.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464678805132150.jpg\" title=\"1464678805132150.jpg\" alt=\"316448223511893566.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　最近小编在长平路、韩江路散步，走到龙湖沟、星湖公园等处时，隐隐感觉到前方高能……看来是沟中的“臭水魔兽”又开始行动了，小编顿时就受到了10000点伤害，未免于继续受到攻击，只好赶紧护住呼吸器官，并忍痛抛出一只潮汕神兽“无变鹿”抵挡，然后快步通过“臭水魔兽”的地盘。<br/></span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464678258465130.jpg\" title=\"1464678258465130.jpg\" alt=\"92c143a98226cffc3a103215b9014a90f703eae7.jpg\" width=\"287\" height=\"295\" style=\"width: 287px; height: 295px;\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">　　真相只有一个！经过小编仔细了解，原来是市市政工程维修中心</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">的蜀黍们，为了确保市区沿途沟桥的安全使用，对长平路、韩江路沿途沟桥进行桥梁下部结构进行检测，检测时需要降低龙湖沟、新河沟、三脚关沟、黄厝围沟的沟渠水位，不想因此惊扰到了沟中的“臭水魔兽”，使其“兽性”大发。</span><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464678286637300.png\" title=\"1464678286637300.png\" alt=\"fa116e061d950a7bebe106680cd162d9f3d3c964.png\" width=\"268\" height=\"296\" style=\"width: 268px; height: 296px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464678866445809.jpg\" style=\"\" title=\"1464678866445809.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">　　不过市政维修中心的“英雄蜀黍”们正一边抵挡着“魔兽”的攻击，一边紧锣密鼓地对桥梁进行检测，预计6月2日就可以完成检测了，到时将会迅速恢复沟渠水位，将“臭水魔兽”打！入！冷！宫！”不再“惊扰”过往市民。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464678317809599.jpg\" title=\"1464678317809599.jpg\" alt=\"201604111008303262.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><img src=\"http://olive.strtv.cn:7337/uploads/20160531/1464678867351139.jpg\" title=\"1464678867351139.jpg\" style=\"text-align: center; white-space: normal;\"/><br/><img src=\"http://olive.strtv.cn:7337/uploads/20160531/1464678870921611.jpg\" title=\"1464678870921611.jpg\" style=\"text-align: center; white-space: normal;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　看在“英雄蜀黍”们是为了我们的桥梁安全的份上，小编也希望市民们给予他们谅解与支持，在此小编替他们谢谢各位大侠了！</span></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-05-31 15:03:01',0,NULL,2,NULL,302,7,'',0,0,0,'2016-05-31 15:17:07','2016-05-31 15:22:21');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (520,'【动态】创建全国文明城市 市园林处在行动','CxU5RdLxM6SGJWLQ.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464686721890198.jpg\" title=\"1464686721890198.jpg\" alt=\"华侨公园利用LED显示屏进行创文宣传x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　近期，市园林处各单位根据《汕头市园林绿化管理处创建全国文明城市强化城市管理工作方案》指导精神，对各公园、广场环境、卫生进行整治，全面清理卫生死角，对乔灌木进行修剪；同时各公园、广场通过户外LED显示屏和广播系统向广大市民游客进行创文宣传，并发放创文宣传资料，提高市民的文明意识，呼吁大家共同维护公共绿地、设施，提升城市环境。<br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686777121388.jpg\" style=\"\" title=\"1464686777121388.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为观海长廊加强卫生保洁<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"http://olive.strtv.cn:7337/uploads/20160531/1464686777318220.jpg\" title=\"1464686777318220.jpg\" style=\"text-align: center; white-space: normal;\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为观海长廊清理卫生死角<br/><br/></span><img src=\"/uploads/20160531/1464686777416590.jpg\" style=\"\" title=\"1464686777416590.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为华侨公园大门前景区改造<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686778138360.jpg\" style=\"\" title=\"1464686778138360.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为金砂公园清理卫生死角<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686781399291.jpg\" style=\"\" title=\"1464686781399291.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为时代广场利用多媒体广播进行创文宣传<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686782916706.jpg\" style=\"\" title=\"1464686782916706.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为时代广场创文宣传<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686783995109.jpg\" style=\"\" title=\"1464686783995109.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为中山公园利用LED显示屏进行创文宣传<br/><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464686784733562.jpg\" style=\"\" title=\"1464686784733562.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为金砂公园加强绿化管养</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><br/></p>','2016-05-31 17:20:24',0,NULL,2,NULL,166,9,'',0,0,0,'2016-05-31 17:36:48','2016-05-31 17:36:48');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (521,'【逛公园】“六一”儿童节不愁游园处','Bpig01tQlM1esY72.jpg','<p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688742126659.jpg\" title=\"1464688742126659.jpg\" alt=\"1671263786x.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　明天就是“六•一”儿童节了，作为一个严重超龄的“儿童”，小编早已没了过儿童节的兴奋劲儿，但是相信不少的“超龄儿童”都会碰到儿童节该带真正的适龄儿童去哪玩儿的问题，哼~你以为哀求我，小编我就会告诉你今年儿童节可以去中山公园五角星山景区吗？<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688954118741.jpg\" style=\"\" title=\"1464688954118741.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688954901472.jpg\" style=\"\" title=\"1464688954901472.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688956885077.jpg\" style=\"\" title=\"1464688956885077.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688957118105.jpg\" style=\"\" title=\"1464688957118105.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688959802585.jpg\" style=\"\" title=\"1464688959802585.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464688960135042.jpg\" style=\"\" title=\"1464688960135042.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　中山公园五角星山景区面积3143平方米，为中山公园东部中式园林景观跨水连丘的重要组成部分，北连榆树山，南交槐荫桥，由建园时挖湖堆山所得。五角星山二面临湖，风景秀丽，绿树成荫，既是游客游览中山公园的主要路线之一，又是市民休闲锻炼的重要场所。由于该区域树木高大、树荫密集，加上土地肥力下降、板结，地被植物难以存活，以至黄土露天、雨后泥泞。为解决该区域存在问题，提升公园景观档次，使其与已改造完成的榆树山区域自然对接，形成公园又一道崭新的靓丽风景线，公园班子积极准备，组织我园专业技术人员因地制宜进行规划设计，将景区的小径进行重新规划，设置沿湖观景平台；对原有小池进行清淤并连通玉鉴湖改善水质，对水池设计为堆石驳岸，在池中置石造景；对区域绿化进行全面调整改造，根据地形特点运用园林手法制作园林小景。景区于今年3月2日正式动工，目前该景区改造工程已全部完成，整个工程历时近2个月，共投入改造资金86万元，并于今天（5月31日）正式向公众开放。</span><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689076564576.jpg\" style=\"\" title=\"1464689076564576.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689078931293.jpg\" style=\"\" title=\"1464689078931293.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689078116370.jpg\" style=\"\" title=\"1464689078116370.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689083305611.jpg\" style=\"\" title=\"1464689083305611.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　五角星山景区改造项目共新增防腐木平台、沿湖栈道349平方米，敷设透水砖小径76平方米，石板观景平台、跌级平台272平方米，敷设石板园区道路260平方米，“五角星山”花岗岩景石1块，路灯21支，木栏杆座椅16米，木栏杆76米。新种风铃木、樱花、火焰木、造型榕、鸡蛋花、紫花玉兰、落羽杉、千层金等8个品种148株；新种桂花、山茶花、五凤、棕竹、旅人蕉、天堂鸟、硬枝黄蝉、大叶伞、金丝竹、红绒球、红继木球、非洲茉莉、黄金叶球、松梅、琴叶珊瑚、紫景等16个品种灌木329株；新种绿篱色块红继木、黄金叶、杜鹃、彩页竹芋、绿牡丹、鸢尾兰、春羽、龟背竹、红铁、黄素香、变叶木、红背桂、白蝴蝶、美人蕉、花叶良姜等13个品种约700平方米；新种银边草、蟛蜞菊等地420平方米。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689426376743.jpg\" title=\"1464689426376743.jpg\" alt=\"1478082977x.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464689155118879.jpg\" style=\"\" title=\"1464689155118879.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689157764803.jpg\" style=\"\" title=\"1464689157764803.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689158835505.jpg\" style=\"\" title=\"1464689158835505.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464689159324631.jpg\" style=\"\" title=\"1464689159324631.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　改造完成的五角星山景区与榆树山景区自然相连，形成公园的又一道靓丽的风景线。漫步于景区观景平台清风拂面，树影婆娑，身边景致随步轮换，眺望玉鉴湖波光粼粼，画舫游弋，远处的假山的红亭与嶙峋怪石在绿叶的映衬下若隐若现自成一景，置身其中令人心旷神怡，悠然自得。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　不说了，景区的湖光山色那么美，小编赶紧要找个适龄儿童带我去看看了。</span></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-05-31 17:50:05',0,NULL,2,NULL,176,8,'',0,0,0,'2016-05-31 18:11:08','2016-05-31 19:14:08');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (522,'【动态】市政道路有没“病”　“公路医生”来“体检”','0rMZ9n32QAGIrFcC.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692109108224.jpg\" title=\"1464692109108224.jpg\" alt=\"659861390795688249x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　城市道路是一个城市的筋脉，道路若“生病”，苦的可就是行走在道路上的车辆行人，对此我们市市政工程维修中心的“大夫”们可是不敢懈怠，为准确掌握市政道路的健康状况，更有针对性进行维护管养。近日，“大夫”们运用先进检测设备，对市区主要道路进行全面“体检”。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692135112041.jpg\" style=\"\" title=\"1464692135112041.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692135525805.jpg\" style=\"\" title=\"1464692135525805.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692136384575.jpg\" style=\"\" title=\"1464692136384575.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692136120859.jpg\" style=\"\" title=\"1464692136120859.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　此次检测范围包括中山东路、海滨路等7条铺设沥青路面的“主动脉”，总长度23.35公里，分二批进行。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为提高检测的精准度，“大夫”们委托专业公司动用了号称“公路医生”的多功能道路综合检测车、道路自动弯沉检测车等先进检测设备，对沥青路面破损、车辙、平整度和路面结构强度等进行检测，查清病害。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692175314648.jpg\" title=\"1464692175314648.jpg\" alt=\"395224521310684903x.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　根据“体检”结果和评价分析结论，“大夫”们将对不同道路或道路的不同路段做分类养护“治疗”，同时结合城市道路近期改造和远期规划，以预防为主的原则制定科学的养护规划，确定日常维修养护的工艺和方法。</span></p>','2016-05-31 18:51:22',0,NULL,2,NULL,157,9,'',0,0,0,'2016-05-31 18:56:53','2016-05-31 18:56:53');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (523,'【城管日记】城市桥梁的守护者 我们一直在努力','qQKD7rqUUJdKeDcE.jpg','<p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; background-color: rgb(255, 192, 0);\">&nbsp;——解放桥桥梁结构检测侧记</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">　　5月27日晚上，一场大雨过后，闷热依旧，人们走在户外,有如入了蒸笼一般,不一会便汗流浃背。就在今夜，位于市区梅溪下游厦岭港口的解放桥迎来一次桥梁结构检测。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692726289776.jpg\" style=\"\" title=\"1464692726289776.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692726605715.jpg\" style=\"\" title=\"1464692726605715.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　22时交通管制开始，桥面上，本次桥梁结构检测委托方的专业技术人员们正在安装各种传感器和仪器设备，为荷载试验做好准备工作；桥的两头，一个个闪动的光点宛如地上繁星，那是参与本次桥梁结构检测的市市政工程维修中心的工作人员们，他们身着反光衣，正迅速地对现场进行封闭处理，摆放安全围护栏、安插警示灯、拉起警示带等安全防护设施，与交警部门一道对过往车辆及行人进行疏导。随着时间流逝，此时的解放桥喧嚣不再，准备进行荷载试验的两辆自重为36吨的汽车缓缓驶上桥面，并排停靠。在整个试验过程中，桥梁检测的技术人员都在专心致志的做好实时观测，认真分析数据，直到凌晨4时许才结束全部的桥梁结构检测工作。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692784482816.jpg\" style=\"\" title=\"1464692784482816.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464692784120686.jpg\" style=\"\" title=\"1464692784120686.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　晨曦的微光驱走夜的最后一抹黑，辛劳的环卫工人已上岗开始了工作，零星的车辆开始从解放桥上驶过。参加检测的工作人员虽然满脸疲态，汗水浸透全身，却露出舒心的笑容，那是顺利完成夜间封闭检测任务的轻松和快乐！经过一夜的安静，解放桥又恢复如昔，随着一缕朝阳开始迎接它脊背上的繁忙。虽然时间的年轮在它的身上刻下了深深浅浅的印记，但相信有了我们定期的检测及维护，不仅确保了桥梁的使用，也更好的保障了市民的出行安全。同时也希望市民朋友们参与到爱护市政公共设施的行列中来，让我们这座共同的家园得到更多的保护与延续！<br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464692803954961.jpg\" title=\"1464692803954961.jpg\" alt=\"918385199882722360x.jpg\"/><br/><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（作者：汕头市市政工程维修中心　于莳翌）</span></p>','2016-05-31 18:57:00',0,NULL,2,NULL,259,8,'',0,0,0,'2016-05-31 19:12:19','2016-05-31 19:12:19');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (524,'【城绿呾你知】为何市树开花今年别样红？','bz8hdapaUnpmGoXc.jpg','<p><span style=\"font-family: 微软雅黑; \"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694232443184.jpg\" title=\"1464694232443184.jpg\" alt=\"IMG_2216x.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　五月鮀城，金凤花开。最近，有细心的市民发现，在市区金新路、海滨路、衡山平台等路段又看到了盛开的金凤花。金凤花迎风怒放，满树红艳艳，风起时，花瓣随风摆动，有的在枝头摇曳，有的飘落在地面，成为市区一道道亮丽的风景线。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694377757445.jpg\" style=\"\" title=\"1464694377757445.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694377120721.jpg\" style=\"\" title=\"1464694377120721.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694378130777.jpg\" style=\"\" title=\"1464694378130777.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694378138872.jpg\" style=\"\" title=\"1464694378138872.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694378760285.jpg\" style=\"\" title=\"1464694378760285.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编向市城绿中心了解到，金凤花又称凤凰木，取名于“叶如飞凰之羽，花若丹凤之冠”， 因鲜红或橙色的花朵配合鲜绿色的羽状复叶，被誉为世上最色彩鲜艳的树木之一，华南地区花期是每年的六、七月。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　相比往年，今年的金凤花好像特别红，特别艳丽。为什么“花儿今年别样红？”，市城绿中心给出了答案，低温和日照时数是影响金凤花开花的主要因素。今年由于经历春节前后汕头极端低温气候，导致部分冬梢冻坏，春节天气回暖，重新抽出新枝条（春梢），再加上今年4月份连续降雨，5月初天气回暖加快，光照条件大大改善，以上因素共同作用致使今年的金凤花更显炽热如火。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694422103056.jpg\" style=\"\" title=\"1464694422103056.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694422126040.jpg\" style=\"\" title=\"1464694422126040.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694422849705.jpg\" style=\"\" title=\"1464694422849705.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464694470198935.jpg\" title=\"1464694470198935.jpg\" alt=\"IMG_2231x.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　时下正值初夏，天气不会太热，空闲的时候，不妨带上家人、朋友，到户外赏一赏鲜艳夺目的金凤花，养眼而舒心。</span></p>','2016-05-31 19:29:34',0,NULL,2,NULL,347,7,'',0,0,0,'2016-05-31 19:35:04','2016-06-02 07:04:26');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (525,'【环卫呾你知】在中心城区发现环境卫生问题该找谁？','V08YqbIVZywIxs7Z.jpg','<p><span style=\"font-family: 微软雅黑;\"></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　发现卫生死角要找谁？袋装垃圾没清收向谁投诉？道路清扫不干净找谁负责？诸如此类环境卫生问题大家或许傻傻分不清楚吧。小编这就为你开启“十万个为什么”模式。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464701224630157.jpg\" title=\"1464701224630157.jpg\" alt=\"910235675709930442.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　要搞清楚该向什么部门反映和投诉环境卫生问题，首先要了解我市环境卫生管理体制。我市环境卫生实行<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 176, 80);\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px;\"><strong>“属地管理”原则和市、区、街道（镇）三级管理体制和市、区、街道（镇）、社区（物业管理单位）四级网络</strong></span>，</span>各区负责辖区内环境卫生的监督和管理。日常所见的道路清扫保洁、袋装垃圾收集、生活垃圾转运、果皮箱清收清洗、环卫所属公厕、垃圾转运站清洁管理等环卫作业和辖区环境卫生监管由各区负责。在此原则下，许多环境卫生问题的处理就简单了。<br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464701288557433.jpg\" style=\"\" title=\"1464701288557433.jpg\"/><br/>道路清扫<br/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464701294136221.jpg\" style=\"\" title=\"1464701294136221.jpg\"/><br/>垃圾转运<br/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464701348148029.jpg\" style=\"\" title=\"1464701348148029.jpg\"/><br/>果皮箱清洗<br/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160531/1464701357110742.jpg\" style=\"\" title=\"1464701357110742.jpg\"/><br/>公厕清洁管理<br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>我家袋装垃圾没有上门清收？</strong></span>不管是否实行市场化，居民袋装垃圾收集作业的监管部门都是当地街道，所以请直接向街道反映，要求落实袋装垃圾上门清收。沿街单位门店袋装垃圾清收问题则向区环卫部门或物业管理单位反映。</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>发现卫生死角怎么办？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">道路清洁不干净或者存在垃圾、废土等问题，根据所在地段不同：实行物业管理住宅区范围内的当然由物业管理部门负责，未实行物业管理住宅区和内街小巷、城中村找街道社区协调清理，在主次干道发现的问题则应向区环卫部门反映。如果是在公共活动场所，则应由场所管理单位负责整治。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>公厕提供服务不到位？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">发现公厕没有按时开放、不干净，管理人员服务态度不好等问题，请直接向市、区环卫局投诉，环卫部门将按照有关规定进行查处。另外，公共活动场所如广场、长廊、商场等设置的厕所则是由场所管理单位负责管理。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>路边的果皮箱脏了、丢了怎么办？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">果皮箱是设置在主次干道两侧人行步道，用于收集路人需要丢弃的果皮、纸屑等零星废弃物的容器。如果发现路边果皮箱内垃圾满溢、不干净，请直接向区环卫部门投诉；如果发现果皮箱破损、丢失的，请联系市环卫部门安排更新补装。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>发现生活垃圾转运问题？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">发现垃圾转运站点周边有人翻捡垃圾捡拾可回收物或者作业后站点周边没有清理、垃圾运输车辆滴漏渗沥液或者超高、超速，请直接向市、区环卫局投诉。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>我家垃圾去哪了？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">中心城区居民日产1300多吨生活垃圾统一在雷打石生活垃圾卫生填埋场进行无害化填埋处理。如果发现随意堆放、焚烧垃圾行为，可以向所在区投诉，各区将按职责落实有关单位进行处置。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>汕头港水域保洁由谁负责？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头内海湾是韩江、榕江、练江的三江出海口，其水域环境卫生状况受到上游的影响较大。每年4—8月份，潮汕地区进入台风雨季，汛期到来之时，练江、榕江、韩江等江河上游地区纷纷开闸泄洪，三江上游以及市区西片区的西港河、大港河、鮀济河等河流积聚的水浮莲顺流而下，对海湾水域环境卫生造成严重影响。目前环卫部门只负责内海湾北岸（人民广场至华侨公园北侧约6千米）从岸边往外延伸宽度200米范围内水域水上漂浮物打捞、清运工作，有关该水域保洁工作的问题可以直接向市环卫部门反映。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; background-color: rgb(255, 255, 0);\"><strong>道路洒水有什么作用？</strong></span></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">为了减少扬尘、降低路面温度，提升城市道路环境卫生，环卫部门在中层城区部分主干道进行道路洒水作业。如果发现洒水作业过程存在的问题，可以直接向市环卫部门反映。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464701511931508.jpg\" title=\"1464701511931508.jpg\" alt=\"857540528449932280.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px;\">另外，<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; color: rgb(255, 0, 0);\">非中心城区</span></strong>环境卫生作业和管理由各区负责，有关问题可以直接向各区反映。<br/></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160531/1464701894497619.jpg\" title=\"1464701894497619.jpg\" alt=\"807723031209711859.jpg\" width=\"93\" height=\"163\" style=\"width: 93px; height: 163px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px;\">什么？您觉得找有关部门太麻烦，或者无法确定应由哪个部门负责。那请您<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; color: rgb(0, 176, 80);\"><strong><span style=\"font-size: 20px;\">打开“汕头橄榄台”→服务→汕头城管</span></strong>，</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px;\">在</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 18px; color: rgb(0, 176, 80);\"><strong><span style=\"font-size: 20px;\">“政民互动”</span></strong></span>栏目中直接爆料，或者<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(0, 176, 80);\"><strong>拨打市政府<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(255, 0, 0);\">12345</span>服务热线</strong></span>。</span></p>','2016-05-31 21:23:15',0,NULL,2,NULL,452,7,'',0,0,0,'2016-05-31 21:40:01','2016-05-31 21:44:12');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (526,'【微曝光】我们身边竟然有这么多“小偷”，小伙伴们造吗？','0JksIEVD1Gh7xvYS.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　那个……小编在刚刚度过“六·一”节的深夜讲“小偷”会不会吓到小盆友……赶紧把手中的爱疯、爱派和糖葫芦、棉花糖藏好！</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797065116629.jpg\" title=\"1464797065116629.jpg\" alt=\"u=371730541,3685475750&amp;fm=21&amp;gp=0.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　藏好了吗？好，大家排排坐，小编要来讲故事了！不过小编想讲的小偷他并不偷东西，还“送”东西，并且！送出的东西很感人！这样的小偷在汕头还真不少哦，啥？不信？请往下看：</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797078451188.jpg\" title=\"1464797078451188.jpg\" alt=\"36206069727996797.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　▲上图是中山路绿化带旁已征未建用地，有木有感受到“小偷”满满的“爱”？别急，下面还有：</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797116140498.jpg\" style=\"\" title=\"1464797116140498.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797116931121.jpg\" style=\"\" title=\"1464797116931121.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　▲这里是护堤路堤坝旁，空间虽小，送出的“礼物”可一点也不含糊。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797131773883.jpg\" style=\"\" title=\"1464797131773883.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797131118591.jpg\" style=\"\" title=\"1464797131118591.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　▲这里是某工地旁的空地，小编已经被“感动”得说不出话了……<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797141113182.jpg\" title=\"1464797141113182.jpg\" alt=\"20150816234415_UCkWx.jpg\" width=\"251\" height=\"263\" style=\"width: 251px; height: 263px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　言归正传，我们这就来八一八这些会“送”东西的“小偷”。在月黑风高的夜晚，夜深人静之时，在城乡结合部、在灯光昏暗行人稀少的某个角落（此处应该有阴森的音乐），难免会潜伏着这些拥有大型“作案”工具的人。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797163102949.jpg\" title=\"1464797163102949.jpg\" alt=\"u=252489389,3983012037&amp;fm=21&amp;gp=0.jpg\" width=\"163\" height=\"167\" style=\"width: 163px; height: 167px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　作案工具有很多种，比较土豪的可能是人货车，手头宽裕的可能是手扶拖拉机，手机播着《最炫民族风》的也可能采用后三轮摩托车甚至人力三轮板车……</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797176562597.jpg\" title=\"1464797176562597.jpg\" alt=\"214615g955gm221119s22n_看图王.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　他们“送”完礼物第二天，我们便可以在“案发”现场一边发自内心地“感动”一边把他们家人都问候一遍了……时间一长，一堆堆的“礼物”会成为一座座小山，有些礼物比较“豪华”，上边还会围着一群小天（cang）使（ying）。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797199131548.gif\" title=\"1464797199131548.gif\" alt=\"46-120R1151248-51.gif\" width=\"101\" height=\"102\" style=\"width: 101px; height: 102px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　那么问题来了，请问这些“礼物”是要送给谁？答案自然是Nobody！下面请严肃一点！我们要开始总结了！</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797281863992.jpeg\" title=\"1464797281863992.jpeg\" alt=\"20150128210858_wfVf8.jpeg\" width=\"277\" height=\"275\" style=\"width: 277px; height: 275px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编在此奉劝“小偷”们，目前我们汕头正在创建全国文明城市，你们的“小偷”行为已经给我市的“创文”大计带来极其严重的不良影响，请您不要作为一粒老鼠屎，而坏了市民们的一锅好粥。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797296120747.jpg\" title=\"1464797296120747.jpg\" alt=\"u=3156790962,2870903946&amp;fm=21&amp;gp=0.jpg\" width=\"135\" height=\"174\" style=\"width: 135px; height: 174px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编也呼吁广大市民行动起来，让这样的“小偷”成为过街老鼠，一旦发现“小偷”作案请协助制止，或直接报告城管执法部门。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464797310892393.jpg\" title=\"1464797310892393.jpg\" alt=\"194822320716487128.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　同时，小编也希望</span><span style=\"font-family:微软雅黑, &#39;Microsoft YaHei&#39;\">园林、</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; \">城绿、国土、水务、住建等部门做到守土有责，学习“愚公”及时“移山”，再采取类似上图的措施做好围护，让“小偷”从此没有作案机会。</span></p><p style=\"text-align: right;\"><span style=\"font-family:微软雅黑\">（来源/市城管局 城管小编/阿敏 巧鹏 图/巧鹏）</span></p>','2016-06-02 00:02:28',0,NULL,2,NULL,333,8,'',0,0,0,'2016-06-02 00:17:23','2016-06-02 08:01:48');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (527,'【温馨提示】“搬家”启事','BP3fhtQB0auapRqM.jpg','<p><span style=\"font-family: 微软雅黑;\"></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　各位大小伙伴们，今天小编要隆重向您介绍我们市城管局在市行政服务中心的办事窗口！</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span><img src=\"/uploads/20160602/1464865285454415.gif\" title=\"1464865285454415.gif\" alt=\"QQ图片20160602184723.gif\" width=\"236\" height=\"187\" style=\"text-align: center; width: 236px; height: 187px;\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　各位客官请让小编把话讲完，我想说的是从6月1日起，申请污水处理费返拨审核办事事项已经划入市城管局了，想要办理这项手续的大小朋友今后请到我们的窗口来偶遇哈。</span><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本事项原来在服务中心3楼西厅市环保局的办事窗口办理：</span><span style=\"text-align: center; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">&nbsp;</span><img src=\"/uploads/20160602/1464865367761318.jpg\" title=\"1464865367761318.jpg\" alt=\"862261139483432181.jpg\" style=\"text-align: center;\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　不过现在，各位大小伙伴们可要留意了哦，我们的办事窗口在２楼西厅！２楼西厅！２楼西厅！（重要的事情说三遍）别走冤枉路了哈（想锻炼身体的除外）。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　那么……请认准我们的窗口，你觉得小编我会告诉你那里有绅士哥哥和美女姐姐为您办理业务吗？</span><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"></span><img src=\"http://olive.strtv.cn:7337/uploads/20160602/1464865392568880.jpg\" title=\"1464865392568880.jpg\" alt=\"293443267640452131.jpg\" style=\"text-align: center; white-space: normal;\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城管局 城管小编/阿敏 图/辉荣）</span><br/></p>','2016-06-02 18:58:40',0,NULL,2,NULL,171,10,'',0,0,0,'2016-06-02 19:05:28','2016-06-02 19:24:28');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (528,'【创文之路】“园林勇士”创文之路OL 之 卫生死角篇','hEXSq2WwbVIqrH42.jpg','<p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 微软雅黑;\"></span>出场人物<br/></span></strong></p><hr/><p style=\"text-align: center;\"><strong style=\"text-align: center;\"><img src=\"/uploads/20160602/1464873748112648.jpg\" title=\"1464873748112648.jpg\" alt=\"1.jpg\" width=\"133\" height=\"165\" style=\"width: 133px; height: 165px;\"/></strong></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></strong></p><hr/><p style=\"text-align: justify;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">BOSS：</span></strong><br/></p><p style=\"text-align: justify;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">中文名：</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">卫生死角<br/><strong>英文名：</strong>Gesundheitecken（度娘翻译的）</span></p><p style=\"text-align: justify;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">属性：<br/></span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">攻击力：C &nbsp;防御力：A+ &nbsp;力量：C- &nbsp;技能：隐蔽</span></p><p style=\"text-align: justify;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">描述：</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">美丽汕头“大陆”上的极不起眼但极具杀伤力的BOSS，可藏身于大陆上的任一角落，藏身越久实力越强大。技能发动后，攻击力和力量皆可瞬间升级A+。</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><hr/><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></strong></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464874410419559.jpg\" title=\"1464874410419559.jpg\" alt=\"2.jpg\" width=\"131\" height=\"181\" style=\"width: 131px; height: 181px;\"/></p><hr/><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">园林勇士：</span></strong><br/></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">中文名：</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">园林处职工<br/><strong>英文名：</strong>Staff and workers of various units in the garden（依旧是度娘翻译的，就是这么任性）；</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">属性：<br/></span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">攻击力：A &nbsp;防御力：A &nbsp;力量：B+ &nbsp;技能：封印、一切与植物相关的技能</span></p><p><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">描述：</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">美丽汕头“大陆”上的一群勤劳、善良的人类，肩负着守卫大陆环境的职责，是“卫生死角”的天敌，常年与之对抗，但由于敌人滋生能力过于强大且多变，一直很难将其彻底消灭。发动“封印”技能时，可让“卫生死角”的技能无效，进而将其消灭。<br/></span></p><hr/><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　最近，美丽的汕头“大陆”为了迎接“创文”使者的到来，为了让使者能在这片“大陆”上留下美好的回忆，“大陆”高层集结了“大陆”上各式各样的“勇士”，成立“创文”联盟，准备向“大陆”上所有具有不文明属性的“BOSS”发起最后的总攻，一举将其歼灭。随着“交警勇士”对“违反交通BOSS”、“城管执法勇士”对“占道经营BOSS”率先吹响了猛烈进攻的先锋号角后，众“园林勇士”也开始对各公园广场中的&nbsp;“卫生死角BOSS”发起攻击，场面十分激烈（米国“豪赖屋”对此IP表示非常感兴趣，正视图联系小编约稿。哼！我才没那么容易上当，本剧本不卖！）。<br/>　　多说无用，有图才有真相，<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">“战地小编”</span>立即为各位小伙伴送上新鲜热辣的“战场实图”。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464875219760801.jpg\" style=\"\" title=\"1464875219760801.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464875219160078.jpg\" style=\"\" title=\"1464875219160078.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">经过金砂公园、人民广场、观海长廊的三次大规模“战役”，“园林勇士”们无论是在战略上、士气上都远远胜于对手，“卫生死角”节节败退，无处遁形，当前已结束的战役中勇士们取得压倒性的胜利。（下面是“战地小编”第一时间传送回来的两张“卫生死角”堆积如山的“尸体”照，小编仿佛看到“战地小编”拍照时傲娇的神情）&nbsp;<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464875482844025.jpg\" style=\"\" title=\"1464875482844025.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464875482641905.jpg\" style=\"\" title=\"1464875482641905.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464875754128217.jpg\" title=\"1464875754128217.jpg\" alt=\"7.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">此次战役“园林勇士”们虽然取得了胜利，但仍不能掉以轻心，“卫生死角”这类的BOSS极易“复活”，并且还有不少“变种”和“帮凶”，只要稍微不注意，便会让它们有机可乘，所以我们勤劳的“园林勇士”们，将会继续追击，一直守护着大家美丽的汕头“大陆”。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　未完待续…</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市园林处 城管小编/陈辉&nbsp;图/陈辉）</span><br/></p>','2016-06-02 21:18:30',0,NULL,2,NULL,184,9,'',0,0,0,'2016-06-02 21:59:34','2016-06-02 22:05:43');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (529,'【城绿呾你知】时花要更新替换啦！','Mt2gQFaNWkHNLYXs.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　近日，有市民发觉，在市区海滨路、中山东路机动车道两侧的花箱、花碟中原来鲜艳的时花“不翼而飞”，只剩下空空的花器。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877309203368.jpg\" title=\"1464877309203368.jpg\" alt=\"1.jpg\"/></p><p>　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">带着这个疑问，小编随即向市城绿中心了解。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　原来，因近期天气较为炎热，原有种植的时花品种由于<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">不耐热</span>已经退化严重。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877463530280.jpg\" title=\"1464877463530280.jpg\" alt=\"u=3609914834,1480252754&amp;fm=21&amp;gp=0.jpg\"/></p><p>　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">好在，市城绿中心的“护花使者”们迅速反应，立即着手对时花进行更新替换，并对花箱、花碟内的土壤进行改良更换，填充混合配比更为合理的腐殖土。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877810115609.jpg\" style=\"\" title=\"1464877810115609.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877810137546.jpg\" style=\"\" title=\"1464877810137546.jpg\"/></p><p>　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">这种腐殖土透气性能好，具有较好的保水保肥能力。目前，市城绿中心已完成腐殖土的装填工作，新填充的腐殖土需要通过深翻日晒，达到杀菌消毒的效果，防止土壤板结，更有利于之后种植时花的生长。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877851135558.jpg\" style=\"\" title=\"1464877851135558.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464877851138966.jpg\" style=\"\" title=\"1464877851138966.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　接下来市城绿中心将选取颜色更为鲜艳，花期更长久、耐热性更好的时花品种进行种植，为市容市貌增光添彩，进一步强化美化城市绿化景观效果，小伙伴们拭目以待吧。</span><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城绿中心 城管小编/锐群 图/锐群）</span><br/></p>','2016-06-02 22:06:06',0,NULL,2,NULL,161,7,'',0,0,0,'2016-06-02 22:33:43','2016-06-02 22:33:43');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (530,'【城绿呾你知】行道树“理发”为哪般？','MxyGeSLaodmdhrdZ.jpg','<p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-size: 16px; font-family: 微软雅黑;\"></span>　　今天，市区录得入夏以来最高气温<strong><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(255, 0, 0);\">36.7℃</span></strong>！可是有细心的市民在经过市区金砂东路检验检疫局旁时，总感觉有哪里不对……抬头一看，oh no~这是在逗我吗……天然的“遮阳伞”居然不见了！太阳公公露出了“邪恶”的微笑。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464879008473435.jpg\" title=\"1464879008473435.jpg\" alt=\"u=2406862843,341473765&amp;fm=21&amp;gp=0.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　原来，今天上午市城市绿化管理中心的城市“理发师”们，对该路段的行道树进行了集体“剃头”。</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　我想，包括小编在内的小伙伴们这时候心里一定把这帮“添乱”的“理发师”都“问候”了一遍。<br/>　　如今正值盛夏，行道树茂盛正好可供市民乘凉避暑，为何要在此时进行修剪？<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464879583277985.jpg\" title=\"1464879583277985.jpg\" alt=\"修剪前2.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">修剪前（多好的发型~）<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464879631660968.jpg\" title=\"1464879631660968.jpg\" alt=\"修剪后2.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">修剪后（……）</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为了市民们夏天的幸福，小编斗胆上前询问。<br/>　　“理发师”们看到小编头上的“瀑布汗”，纷纷表示同情，并解释说：每年的夏、秋季是我市多台风、多强对流天气的季节，在考虑市民遮阳和做适当修剪之间必须取得一个平衡，而通过规范化、专业化的修剪，既可以提高行道树的抗风能力，避免行道树在台风到来时因“头重脚轻”被风刮倒，消除潜在的道路安全隐患，又能促进树木的更新复壮，重塑行道树树形树姿，使树形更加紧凑整齐美观，提升行道树的绿化景观效果。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464881060269637.jpg\" title=\"1464881060269637.jpg\" alt=\"修剪时1.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在得到城市“理发师”们的耐心解答后，小编不禁想到了当年大明湖畔……哦，不，当年的台风“天兔”给我市造成的破坏性影响，还记得那天晚上小编在路上因数不清的倒伏树木寸步难行，那感觉真是“酸爽”。想到这里，望着在烈日下湿透了衣服的城市“理发师”们，小编只能默默给他们点个赞！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160602/1464881099303761.jpg\" title=\"1464881099303761.jpg\" alt=\"修剪时2.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　并且，小编还要在此呼吁小伙伴们理解、支持这项工作，也希望大家在经过作业路段时听从作业人员的指挥，千万注意安全！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城绿中心 城管小编/阿敏 锐群&nbsp;文/庄泽华&nbsp;图/许志勇）</span><br/></p>','2016-06-02 22:40:52',0,NULL,2,NULL,265,7,'',0,0,0,'2016-06-02 23:29:32','2016-06-04 09:38:37');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (531,'【规范性文件】汕头市人民政府办公室关于印发汕头市实施《广东省户外广告管理规定》有关问题的通知','j5chtLb8etVkcUga.jpg','<p></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民政府办公室关于印发汕头市实施《广东省户外广告管理规定》有关问题的通知</span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕府办〔2015〕79号</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">各区县人民政府，市政府各部门、各直属机构：&nbsp;</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为贯彻实施《广东省户外广告管理规定》（广东省人民政府令第187号），规范我市户外广告管理秩序，经市政府同意，进一步做好户外广告的管理工作，现就有关问题通知如下：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　一、实施范围</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　特区范围内设置和发布户外广告的有关活动，按本通知的规定执行。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本通知所称户外广告，是指利用户外场所、空间、设施等发布的广告。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　本通知所称户外广告设施，是指利用建筑物、构筑物、场地等设置的，向户外空间发布广告的霓虹灯、展示牌、电子显示装置、灯箱、实物造型等设施。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　二、管理部门</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）市市容环境卫生行政主管部门是户外广告设施设置的主管部门，负责组织相关部门编制户外广告规划，负责全市户外广告设施设置的监督管理，负责以下户外广告设施的设置审批：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．负责利用市级管理范围国家和集体所有的建筑物、构筑物、场地等设置的户外广告设施的设置审批；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．负责在市级管理范围道路、人行步道（含工地围墙、书报亭等）及其沿线两侧建筑物、构筑物、市政设施、广场、空间的户外广告设施的设置审批。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）各区县市容环境卫生行政主管部门是辖区户外广告设施设置的主管部门，负责辖区内前款规定以外的户外广告设施的设置审批和监督管理。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）在金平区、龙湖区、濠江区（以下简称“中心城区”）范围内与道路交界处的公路两侧设置户外广告设施的，应当征求市市容环境卫生行政主管部门的意见。具体由户外广告规划明确。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　其它国省道等公路及公路两侧户外广告设施审批管理按相关法律法规的规定执行。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）工商行政管理部门是户外广告的监督管理机关，负责户外广告的登记管理工作。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）违反《广东省户外广告管理规定》和《汕头市城市市容环境卫生管理条例》规定的，由市容环境卫生行政主管部门依法查处，实施城市管理相对集中行政处罚权的区域，由城市管理行政执法部门依法实施行政处罚和行政强制。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）交通、公路、公安、海事、城乡规划、环境保护、住房建设、财政、发改、安全生产监督等部门按照各自职责，做好有关户外广告管理工作。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　三、规划管理</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）户外广告规划包括户外广告专项规划和户外广告设置规划。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）中心城区户外广告专项规划由市市容环境卫生行政主管部门结合城乡规划和交通运输发展规划的要求，会同同级城乡规划、工商、财政、公安、交通、公路等部门组织编制。中心城区户外广告设置规划由市市容环境卫生行政主管部门根据户外广告专项规划和城市容貌标准组织编制。中心城区户外广告专项规划和中心城区户外广告设置规划报市人民政府批准后公布实施。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）澄海区、潮阳区、潮南区、南澳县户外广告专项规划及其户外广告设置规划由辖区市容环境卫生行政主管部门，会同同级相关部门组织编制，经本级人民政府批准后公布实施，并报市市容环境卫生行政主管部门备案。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）举办文化、旅游、体育、会展、庆典、公益、促销等活动临时设置的户外广告设施，以及利用交通工具、移动设施设置的户外广告设施，不纳入户外广告规划。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）编制户外广告规划，应当征求相关部门和行业协会的意见，并采取座谈会、论证会等方式征求专家和公众的意见。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）户外广告规划草案应当向社会公示，公示时间不得少于30日。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）经批准的户外广告规划应当在政府网站、新闻媒体或者专门场所公告，并在政府网站长期公布，便于公众查询和监督。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）经批准公布实施的户外广告规划，不得擅自更改；确需调整的，应当按照原审批程序组织修订，报同级人民政府批准。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）户外广告设置规划应当明确户外广告设施的设置地点、位置、形式、规格等具体要求。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）有下列情形之一的，不得设置户外广告设施：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．利用或者遮挡指路牌、交通信号灯、电子监控等交通安全设施、交通标志及其支架的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．霓虹灯、LED等光源性广告设施与交通信号灯、电子监控设施距离过近，户外广告设施的形式与交通安全设施、交通标志相近，影响交通安全设施、交通标志正常使用的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3．影响道路交通安全的区域；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4．破坏建筑物、构筑物结构安全，影响市政公共设施、无障碍设施使用的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5．影响消防安全设施使用，或者妨碍消防车通行以及影响逃生、灭火救援和消防登高扑救，或者遮挡、堵塞、封闭现有外窗的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6．利用立交、高架道路桥梁、危房、违章建筑的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　7．利用行道树、绿化带，侵占、损毁绿地，占用机动车道、人行道路面或者阻碍机动车、行人通行的；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　8．国家机关、学校、医院、名胜风景点、文物保护单位的建筑控制地带；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　9．户外广告规划禁止设置户外广告的其他情形。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　四、设置审批</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）按照精简、高效、便民原则，市、区（县）市容环境卫生行政主管部门依照上述职责分工受理户外广告设施设置申请，并转告其他相关部门分别提出意见后统一办理。法律、法规另有规定的，从其规定。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）符合《中华人民共和国广告法》规定的广告主、广告经营者、广告发布者设置和发布户外广告应当向市容环境卫生行政主管部门申请办理《户外广告设施设置证》，并向工商行政管理部门申请办理《户外广告登记证》。未办理《户外广告设施设置证》，任何单位和个人不得设置户外广告，工商行政主管部门不予核发《户外广告登记证》。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）申请设置非临时性户外广告设施，应当提交下列材料：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．户外广告设施设置申请表；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．营业执照或者其他证明主体资格合法有效的证照；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3．设置户外广告设施所利用的场地、建筑物、构筑物、设施的所有权或者使用权证明；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4．户外广告设施位置关系图、正立面图及效果图；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5．涉及利害关系人的，应当提交其书面意见；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6．设置《城市户外广告设施技术规范》所指大型户外广告设施的，应当提交具备设置广告设施的相应资质设计单位设计的户外广告设施结构图纸及其资质证明；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　7．与户外广告设施相适应的安全措施证明材料；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　8．法律、法规、规章规定应当提交的其他材料。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　涉及新建、扩建建筑物、构筑物需在外立面预设户外广告设施的，应当提交城乡规划主管部门核发的建筑物、构筑物《建设工程规划许可证》复印件。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）举办文化、旅游、体育、会展、庆展、公益、促销等活动申请设置临时性户外广告设施，应当提前10个工作日向受理机关提交下列材料：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．临时户外广告设施设置申请表；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．营业执照或者其他证明主体资格合法有效的证照；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3．临时户外广告设施设置的形式、范围和示意图；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4．涉及利害关系人的，应当提交其书面意见；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5．有关主管部门批准举办活动的文件；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6．法律、法规、规章规定应当提交的其他材料。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）设置非临时性户外广告设施，许可或者审批机关在作出许可或者审批决定前，应当在政务网站或专门场所公示15日。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）市容环境卫生行政主管部门应当自受理非临时性户外广告设施设置申请之日起20个工作日内依法作出决定；自受理临时性户外广告设施设置申请之日起10个工作日内依法作出决定。需其他相关部门提出意见的，其他相关部门应当在收到市容环境卫生行政主管部门转来的设置申请后5个工作日内提出书面意见。对不符合设置要求的，应出具书面说明；对符合设置要求的，依申请核发《户外广告设施设置证》，并抄送同级工商行政管理部门。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）利用国家和集体所有的建筑物、构筑物、场地等设置的户外广告设施，应当通过招投标、拍卖或者其他公平竞争方式出让其使用权。招标或者拍卖应当委托具有相应资质的招标代理机构、拍卖机构进行。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）利用私人所有的建筑物、构筑物、场地等设置的户外广告设施，其所有权人或者用益物权人可以自由选择其使用权的出让方式，并依法享有因使用权出让而取得的收益。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）户外广告设施设置期限按照下列情况核定：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．非临时性户外广告设施的设置期限最长不超过3年，电子显示牌（屏）类户外广告设施可以适当延长，但最长不得超过6年；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．举办文化、旅游、体育、会展、庆展、公益、促销等活动设置临时性户外广告设施的，设置期限根据活动期限确定，最长不得超过3个月。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）《户外广告设施设置证》的设置期届满，需要继续设置的，户外广告设施产权人或使用权人应当于期限届满30日前按原程序申请延期，审批机关应当在期限届满前作出是否准予延续的决定；逾期未作出决定的，视为准予延续。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十一）确需变更经批准设置户外广告设施的地点、具体位置、形式、规格、数量、制作材质、灯饰配置、结构图、全景电脑设计图等事项的，应当按照原审批程序办理设施设置变更手续。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十二）户外广告设施设置审批机关应当在其办公场所、政务网站等公开户外广告规划、城市户外广告设施技术规范等资料，以及办事依据、条件、程序、期限、申请书示范文本、批准设置公告等。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　五、户外广告登记</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）发布户外广告应当依法向工商行政管理部门申请户外广告登记。法规、规章另有规定的除外。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　工商行政管理部门颁发《户外广告登记证》，应抄告户外广告设施设置批准机关。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）《户外广告登记证》有效期届满需要延续的，户外广告发布者应当在有效期届满30日前向原登记的工商行政管理部门申请延续。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）工商行政管理部门应当自受理延续申请之日起7个工作日内作出是否准予延续的决定；逾期未作决定的，视为准予延续。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　六、设置与维护</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）设置户外广告设施应当遵守以下规定：</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　1．符合户外广告专项规划和户外广告设置规划的范围和要求，按照批准文件载明的要求设置；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　2．使用安全、环保、清洁材料；符合国家建筑物和构筑物结构荷载、防雷、防风、抗震、消防、电气安全以及环境保护的要求；使用光源性装置的，避免对周边环境造成光污染；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　3．符合《城市户外广告设施技术规范》等安全技术规范和标准，由具有相应技术资质的单位进行设置、设计、施工、验收、维护及检测；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　4．户外广告画面右下角应当标明《户外广告设施设置证》文号和《户外广告登记证》登记证号；</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　5．《广东省户外广告管理规定》规定的其他事项。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）经批准设置的非临时性户外广告设施，自批准设置之日起60日内（LED户外电子显示屏自批准设置之日起90日内）设置完毕；逾期未设置完毕的，其《户外广告设施设置证》即行失效，并由证件核发部门予以注销。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（三）经批准设置的商业户外广告设施，其广告版面空置时间超过15日的，应按设置批准机关的规定发布公益广告。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（四）市、区（县）市容环境卫生行政主管部门应当根据户外广告设置规划，依照上述职责分工，在城市主要公共场所和主要道路设置公益广告设施。经批准设置的公益广告设施，不得发布商业广告。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（五）户外广告设施产权人或使用权人是户外广告设施安全管理责任人，应当定期对户外广告设施进行安全检测和检查，对检查不合格或发现户外广告设施存在安全隐患的，应当及时采取防范措施，立即整修或者拆除，及时排除安全隐患；隐患排除之前，户外广告设施产权人或使用权人应当在现场明显位置标设警示标志，必要时还应当派人值守，防止事故发生。气象部门发布台风、大风黄色以上和暴雨红色预警信号时，户外广告设施产权人或使用权人应当及时对户外广告设施进行安全检查，采取加固或者拆除等安全防范措施。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（六）户外广告设施设置期满2年的，户外广告设施产权人或使用权人应当在每年6月1日前，按照《城市户外广告设施技术规范》进行安全检测，对于有钢结构牌位的大型户外广告设施，户外广告设施产权人或使用权人应当向《户外广告设施设置证》审批机关提供具有结构安全资质的检测机构出具的户外广告设施结构安全检测报告；经检测不合格的，户外广告设施产权人或使用权人应当立即维修或者拆除。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（七）户外广告设施达到设计使用年限的，户外广告设施产权人或使用权人应当予以拆除或者更新。户外广告设施在建设、维修、更新或者拆除期间，应当遵守有关安全生产的规定，建立自检和防护制度，保证施工安全和设施牢固。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（八）户外广告设施产权人或使用权人应加强日常维护管理，保持户外广告的整洁、完好、美观。户外广告出现画面污损、严重褪色、字体残缺等影响市容市貌情形的，应及时维修、更新；配置夜间照明设施的，应保持照明设施功能完好；设置霓虹灯、电子显示装置、灯箱等设施的，应当保持画面显示完整；出现断亮、残损的，应及时维护、更换，并在修复前停止使用。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（九）户外广告设施产权人或使用权人应当在户外广告设施上公示户外广告设施日常维护责任人及其联系电话，接受社会监督。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（十）《户外广告设施设置证》被撤销、户外广告设施设置期届满后不再设置或者申请延期未获得批准的，其户外广告设施产权人或使用权人应当在被撤销或者设置期限届满之日起10日内自行拆除。其中，临时性户外广告设施应当在有效期届满之日起3日内予以拆除。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　七、监督检查</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（一）工商行政管理部门应当加强对户外广告的监督管理，依法查处违法户外广告。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　（二）户外广告设置依法收取的收入以及出让属于公共资源的户外广告设施载体使用权的收入，实行收支两条线管理，全额上缴财政，并按照我市公共资源有偿使用资金的有关规定进行管理。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　八、现有户外广告设施的清理整治工作，由市容环境卫生、城管执法部门牵头城乡规划、工商、财政、交通、公路、公安等部门组织研究，提出具体工作方案，报同级人民政府批准实施。</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　九、本通知自2016年1月1日起施行，有效期5年。</span><br/></p><p><br/></p><p><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民政府办公室</span></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">2015年12月23日</span></p><p><span style=\"font-family: 微软雅黑;\"><br/></span></p>','2016-06-03 08:31:37',0,NULL,2,NULL,40,11,'',0,0,0,'2016-06-03 08:39:53','2016-06-03 08:39:53');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (532,'【市政呾你知】“睡美人”将在星湖公园落户','53uflzgvhvXJKneW.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464949812701848.jpg\" title=\"1464949812701848.jpg\" alt=\"541917730349270123.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　还记得小编几日前跟各位小伙伴讲的“臭水魔兽”的故事吗？不记得或者不知道的小伙伴赶紧翻看5月31日《龙湖沟等沟体缘何又发臭？》一文哈。当时就提到因桥梁检测需要降低龙湖沟等沟渠水位，导致“臭水魔兽”“兽性”大发，而 6月2日完成检测后将恢复水位和消除水体发臭。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464949845689713.jpg\" title=\"1464949845689713.jpg\" alt=\"310699929065786494.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　可是今天小编是冒着被大家的口水淹没的危险出来跟大家道歉的┭┮﹏┭┮：星湖公园和港区排洪沟恢复水位跳票了！！！不过……不过……大家听我解释完再打我……<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464949878107007.jpg\" title=\"1464949878107007.jpg\" alt=\"477778423969792730.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　为美化星湖公园的景色，提升整体景观效果，星湖公园管理处将在湖中“邀请”美丽的“睡美人”——睡莲到湖中落户。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464949900375882.jpg\" title=\"1464949900375882.jpg\" alt=\"494118958765249676.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　“睡莲小姐”可不是那么容易请来的，先要降低湖水水位，给她“安家”——放置砼圈，只有这样，她才肯来哦！</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464949923122963.jpg\" title=\"1464949923122963.jpg\" alt=\"290534337590866223.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　所以，小编想说这几天星湖公园及港区排洪沟的水位将有所下降，有可能湖沟中的“臭水魔兽”会再度被“激怒”，小伙伴们就看在要请来“睡莲小姐”的份上，稍微忍耐几天，不出意外的话，6月7日我们就能迅速恢复水位了，把美丽的湖景还给大家。</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160603/1464950045318435.jpg\" title=\"1464950045318435.jpg\" alt=\"8.... - 复件.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市市政维修中心 城管小编/阿敏 文/谢祺 图/陈海龙）</span><br/></p>','2016-06-03 18:12:50',0,NULL,2,NULL,553,7,'',0,0,0,'2016-06-03 18:34:56','2016-06-04 09:40:33');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (533,'【创文进行时】儿童游玩新去处？NO！','H3UDwBPpKD04xz8G.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　正如各位小伙伴所见，我市“创文”工作已经“真刀真枪”干起来了，市区多处地方都呈现出往日难得一见的整洁景象，请允许小编用标准的广东话形容，那就是“阿妈都唔认得！”</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城绿中心也发挥主观能动性，见缝插绿，为我市创造更为优美的绿化环境。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010368100307.jpg\" title=\"1465010368100307.jpg\" alt=\"res07_attpic_brief.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　然而，本月2日晚，当小编来到我市重要门户——火车站前广场时，却发现一幅不和谐的画面，广场绿地上突然 “冒”出多家儿童游乐场，甚至一些直接建在绿化带里，让原本干净整洁的绿地变得面目全非。（请忽略晚上手机照片的渣像素）</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010405116439.jpg\" style=\"\" title=\"1465010405116439.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010405532044.jpg\" style=\"\" title=\"1465010405532044.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　面对如此破坏绿地、影响市容观瞻的行为，市城绿中心将2支巡查队的成员调集起来，连夜赶到站前广场，对在站前广场绿地摆放的娱乐设施和小摊贩进行劝离。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010422771602.jpg\" style=\"\" title=\"1465010422771602.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010422122283.jpg\" style=\"\" title=\"1465010422122283.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　到晚上9时30分，绿地上不和谐元素的制造者们均在队员们的耐心劝说后自行清理完毕，绿地恢复干净整洁。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010444294294.jpg\" style=\"\" title=\"1465010444294294.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010445103999.jpg\" style=\"\" title=\"1465010445103999.jpg\"/></p><p>　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">在此，市城绿中心也委托小编再次呼吁市民群众一起爱绿护绿，提高自身文明素质，呵护我们共同的绿色家园。</span></p><p><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465010471209306.jpg\" title=\"1465010471209306.jpg\" alt=\"火车站前广场绿化改造工程.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城绿中心 城管小编/阿敏 文/周梓斌 图/城绿巡查队）</span></p>','2016-06-04 11:17:48',0,NULL,2,NULL,135,9,'',0,0,0,'2016-06-04 11:24:50','2016-06-04 11:24:50');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (534,'【创文进行时】新品种、强措施，再对缺口说“不！”','tXSB0c3exDZknDCO.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　还记得小编在5月27日发过一篇名为《倡导文明　爱绿护绿》的文章，呼吁大家不要践踏绿化带，保护身边的绿，尊重绿化工人们的劳动成果。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465011529100505.jpg\" title=\"1465011529100505.jpg\" alt=\"1464343976286677.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\"></span>　　当前，泰山路沿线中间分车绿化带也由于反复遭人为踩踏、横跨形成多处缺口，不仅影响绿化带的整体美观效果，也带来一定的交通安全隐患。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465012086164381.jpg\" title=\"1465012086164381.jpg\" alt=\"5.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6月3日下午，市城绿中心绿化队再次组织人力，顶着36.7℃的烈日高温（小编认为地表温度远远不止这个温度），选取新的品种，对被破坏的14处缺口和沿线缺株短线处进行补种，此次共补种金边龙舌兰80株，黄心榕400平方米约 12000株，红继木300平方米约9000株，鸭脚木300平方米约9000株。<br/></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465012133130330.jpg\" style=\"\" title=\"1465012133130330.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465012133115927.jpg\" style=\"\" title=\"1465012133115927.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465012133408802.jpg\" style=\"\" title=\"1465012133408802.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在此，小编再次呼吁各位要过马路的小伙伴，为了您的生命安全和家庭的幸福，为了我们身边这一抹抹绿，请移步至路口人行横道或通过天桥过马路，勿横跨道路中心分车绿带，共同维护我们的“绿色生命线”。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465012158280782.jpg\" title=\"1465012158280782.jpg\" alt=\"1.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城绿中心 城管小编/阿敏 文、图/庄泽华）<br/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(255, 0, 0); font-size: 18px;\"><strong>相关阅读：</strong></span><br/><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=510\" target=\"_self\">【动态】倡导文明 爱绿护绿</a><br/></span><br/></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-06-04 11:29:09',0,NULL,2,NULL,166,9,'',0,0,0,'2016-06-04 11:56:48','2016-06-04 12:13:50');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (535,'【动态】省住建厅督办我市“一县一场”项目建设进展情况','xlnA5vz2OhulPkuX.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">　　6月3日上午，省住建厅副厅长杜梃带队督办我市“一县一场”项目建设进展情况。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465014254470638.jpg\" title=\"1465014254470638.jpg\" alt=\"270051486740611030.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　督查组一行先后到潮南区生活垃圾焚烧发电厂建设项目、潮阳区棉北垃圾填埋场升级改造项目进行现场督查。代市长刘小涛、副市长李耿坚陪同督查活动。&nbsp;<br/></span></p><p style=\"text-align: center\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465014363327256.jpg\" title=\"1465014363327256.jpg\" style=\"text-align: center; white-space: normal;\"/></p><p style=\"text-align: center;\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465014363112773.jpg\" title=\"1465014363112773.jpg\" style=\"text-align: center; white-space: normal;\"/><br/><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465014363437676.jpg\" title=\"1465014363437676.jpg\" style=\"text-align: center; white-space: normal;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　据了解，潮南区生活垃圾焚烧发电厂项目采用BOT模式，即“建设-运营-移交”，项目占地300亩，首期用地面积约140亩；规划建设规模为焚烧处理生活垃圾1000吨/日，且具备掺烧处理80吨/日（含水80%）污泥的能力，项目于2014年10月29日顺利开工建设，目前，累计平整土地约120亩，回填区已有30多亩达到设计标高，开挖土石方约80万立方米，投入资金约6822多万元。施工方正抓紧推进厂平土石方施工平整，今年6月份进行护坡、道路施工，8月份进行桩基施工，10月份进行主厂房基础开挖，力争在年底完成主体框架工程建设。&nbsp;<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465014459658644.jpg\" style=\"\" title=\"1465014459658644.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465014459356303.jpg\" style=\"\" title=\"1465014459356303.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　潮阳区则着力破解区生活垃圾焚烧发电厂项目推进过程中存在的“邻避效应”，扎实推进项目建设，同步推动现有生活垃圾终端处理设施改造升级工作，力争尽快实现潮阳生活垃圾无害化处理。区生活垃圾简易填埋场无害化改造升级项目占地约267亩，预计投入7000万元，工程分为二期建设，一期工程将力争于2016年9月底前建成投入使用。预计项目全部建成后，可满足未来10年潮阳区生活垃圾无害化处理的需要。&nbsp;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　省督查组要求，各相关部门和单位要做好项目环评等手续和防护措施，减少对周边居民生产生活的影响，进一步完善应急预案，以应对台风等极端天气带来的影响，在确保生产安全的前提下加快推进各“一县一场”项目的建设。&nbsp;</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/汕头特区晚报 城管小编/阿敏 文/黄鸿华 图/施工）</span></p>','2016-06-04 12:11:03',0,NULL,2,NULL,58,9,'',0,0,0,'2016-06-04 12:30:51','2016-06-04 12:30:51');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (536,'【创文进行时】三部门联手整治建筑废土污染！','jvWdkt9Yph5rvPkd.jpg','<p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　天了噜！</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465016702907325.jpg\" title=\"1465016702907325.jpg\" alt=\"773260816432265096.jpg\" width=\"278\" height=\"268\" style=\"width: 278px; height: 268px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编和朋友们在泰星路、中山东路等建筑工地集中的地段仍见运输建筑垃圾污染道路的现象。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465016782920966.jpg\" style=\"\" title=\"1465016782920966.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465016782130201.jpg\" style=\"\" title=\"1465016782130201.jpg\"/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 112, 192);\">图为：泥头车漏撒污染中山东路</span></strong></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编还记得在5月25日发过《追查源头 治理建筑垃圾污染》一文，说汕头市余泥渣土管理所开展专项整治，又是上门检查宣传、引导建筑工程施工单位办理建筑垃圾处置证件，又是制止无证处置建筑垃圾、运载建筑垃圾污染环境等行为，还并督令责任人组织清扫道路，消除对城市环境卫生的影响。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017035907970.jpg\" style=\"\" title=\"1465017035907970.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017486997304.jpg\" title=\"1465017486997304.jpg\" alt=\"150927816037924982.jpg\"/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 112, 192);\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465017035114516.jpg\" title=\"1465017035114516.jpg\" style=\"text-align: center; white-space: normal;\"/><br/>图为市余泥渣土管理所工作人员主动上门</span></strong></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　可是！说好的干净道路呢？难道汕头市余泥渣土管理所开展的专项整治“然并卵”？</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017089641173.png\" title=\"1465017089641173.png\" alt=\"16370692698347942.png\" width=\"188\" height=\"181\" style=\"width: 188px; height: 181px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当然不是！经过连日来的督查及宣传教育，中心城区多家未办理证件的建筑工地停止处置建筑垃圾，并按要求配套固定冲洗池和监控等设施，其中有5家施工企业已具备申办处置许可资格，另有2家运输企业约30部运输车辆正在申办运输许可。但是，仍有部分建筑工地和泥头车玩起了“猫捉老鼠”游戏，千方百计寻觅空挡，无证运输、超高超载建筑垃圾等“任性”行为对城市道路卫生造成严重影响的现象仍然存在。不过，市余泥渣土管理所工作人员不会轻易选择“狗带”！</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017171855127.jpg\" title=\"1465017171855127.jpg\" alt=\"294642428788849698.jpg\" width=\"260\" height=\"231\" style=\"width: 260px; height: 231px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6月3日晚，市交通运输执法、城管执法和市余泥渣土管理所三部门联合行动，在泰星路和泰星一街交界处检查发现一部车牌号为“豫R·21321”的泥头车正在运输品质较好的建筑废土，车身和车轮沾满泥土，车辆驾驶员无法出示建筑垃圾运输许可等有效证件，而且车辆没有采取密封措施，运输漏撒严重污染道路卫生环境。执法人员立即勒令其停运，同时要求责任人清理受污染路段并接受相应处理。随后，联合执法队伍在黄厝围、中山东路对过往建筑垃圾、建筑材料运输车辆进行检查，并进行普法宣传，要求车辆驾驶员按照法规从事运输作业，共同维护城市环境卫生，共建文明城市。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017275826425.jpg\" style=\"\" title=\"1465017275826425.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017275103448.jpg\" style=\"\" title=\"1465017275103448.jpg\"/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 112, 192);\">图为：联合执法人员查处无证、超载、漏撒的泥头车</span></strong></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　下来，市余泥渣土管理所还将继续加强与各有关部门的协调，沟通完善联动机制，加大对“任性”建筑工地和泥头车的查处力度，并积极向上级申请增设建筑废土临时受纳场地，解决中心城区废土出路问题，采取堵疏结合的方式，规范中心城区建筑垃圾处置秩序，维护城市道路卫生环境。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017328139681.png\" title=\"1465017328139681.png\" alt=\"483125966809216944.png\" width=\"230\" height=\"178\" style=\"width: 230px; height: 178px;\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小样！不信治不了违法违规处置建筑垃圾行为！</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465017350617118.jpg\" title=\"1465017350617118.jpg\" alt=\"239344505962560948.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　然而，城市环境和文明需要城市的每一分子共同建设和维护！希望所有建筑工程建设和管理者能够遵章守法，共同维护城市卫生环境，共建文明美丽汕头！</span></p><p><span style=\"font-family: 微软雅黑;\">（来源/市环卫局 城管小编/阿敏 文、图/九月九）<br/><br/><span style=\"font-family: 微软雅黑; font-size: 20px; color: rgb(255, 0, 0);\">相关阅读：</span><br/><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=505\" target=\"_self\">追查源头 治理建筑垃圾污染</a></span></p>','2016-06-04 13:01:29',0,NULL,2,NULL,246,9,'',0,0,0,'2016-06-04 13:23:07','2016-06-04 13:25:54');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (537,'【精准扶贫】市城管局、人防办实施精准扶贫工作取得初步成效！','f9iIqjdbrw2UE7Wy.jpg','<p><span style=\"font-family: 微软雅黑;\"><span style=\"font-family: 微软雅黑;\"></span>　　根据市委市政府的统一部署，市城管局和市人防办对口帮扶潮南区峡山街道上家村，开展为期三年的脱贫攻坚工作。<br/>　　上家村地处潮南区峡山街道最南边，全村总人口4120人，共738户，其中贫困户48户，包含低保户13户，五保户1户，贫困人口212人，属涉农村居，村集体经济薄弱，交通不便，基础设施落后。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465018704408371.jpg\" style=\"\" title=\"1465018704408371.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465018704185036.jpg\" style=\"\" title=\"1465018704185036.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160604/1465018704216708.jpg\" style=\"\" title=\"1465018704216708.jpg\"/><br/><span style=\"color: rgb(0, 112, 192);\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">图为 改造前村委面貌</span></strong></span><br/></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑;\">　　日前，市城管局陈锦辉副调研员带队到上家村进行实地走访，了解到该村原有政务公开栏残旧、村委大门瓷砖脱落存在安全隐患及办公设备匮乏等实际情况后，市城管局决定拨给上家村5万元用于改善村政务公开栏和办公设施设备等。目前，村中新的政务公开栏已基本完成，并将村各项政务公开内容张贴上墙，同时制作“两学一做”宣传栏。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465018814833955.jpg\" style=\"\" title=\"1465018814833955.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465018814694963.jpg\" style=\"\" title=\"1465018814694963.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465018814592117.jpg\" style=\"\" title=\"1465018814592117.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160604/1465018814119537.jpg\" style=\"\" title=\"1465018814119537.jpg\"/><br/><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 112, 192);\">图为 改造后村委面貌</span></strong><br/></p><p style=\"text-align: left;\"><span style=\"font-family: 微软雅黑;\">　　村委大门维修及办公设备配套已经完成，村委面貌焕然一新。<br/>（来源/市城管局 市人防办 城管小编/阿敏 文、图/潘宗晖）<br/></span><br/></p>','2016-06-04 13:30:46',0,NULL,2,NULL,107,9,'',0,0,0,'2016-06-04 13:44:17','2016-06-04 13:46:19');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (538,'【图说绿化】炎炎夏日，请您到“草原”一游','YK5DbcmTPxOKYBbK.jpg','<p><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465022240260256.jpg\" title=\"1465022240260256.jpg\" alt=\"777115349757254269_缩小大小.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family:微软雅黑, Microsoft YaHei\">小编知道，小伙伴看了这张图一定已经准备了不下一万条弹幕吐槽准备把小编淹没：<br/>　　“好端端的发什么桌面壁纸！！”<br/>　　“度娘一下到处都是，这不是存心浪费大家流量吗？”<br/>　　“小编你过来，保证不打死你！”<br/>　　“小样！这货是准备送流量吗？”<br/>　　“楼主，你闲着没事儿是吧，过来给大爷揉揉肩。”<br/>　　……<br/>　　在感受到小伙伴们满满的“恶意”之后，小编我只能很负责任地告诉你们，这并非壁纸！这是咱们大汕头天山路二号平台！<br/>　　怎么？不信？有诗为证：<br/></span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 176, 80);\">热浪熏蒸仲夏天，</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 176, 80);\">绿荫蔽日任流连。</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 176, 80);\">鮀城更有清凉景，</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(0, 176, 80);\">漫步“天山”看草原！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　炎炎夏日，相信小伙伴们看到如此的“壁纸”一定会心旷神怡，神清气爽，那么既然小编已经清楚明白告诉你们“草原”就在咱们身边，大家是不是该带上长枪短炮出发了呢？再不济也得带上部能拍照的手机吧~小编难道会告诉你们那里360°无死角么╮(╯▽╰)╭<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465021074588416.jpg\" title=\"1465021074588416.jpg\" alt=\"QQ截图20160604141736.jpg\" width=\"349\" height=\"274\" style=\"width: 349px; height: 274px;\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">“</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">小编你有种别跑！”<br/>　　“把相机留下！”<br/>　　“我要莱卡，请汇款至*****”<br/>　　“我的小米1跟你换爱疯6爱死普拉斯”<br/>　　咳咳~大家好，我是小编的弟弟小小编，鉴于小编已经潜逃，剩下的工作由我来完成，大家肯定等不及想知道具体地址了，那么小小编我这就郑重告诉大家，天山路二号平台当然就是在天山路啦（有其哥必有其弟啊。。。）那个。。。其实我是想多卖弄几张无死角的照片，地址在照片后，请大家往下翻~<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465022271132499.jpg\" title=\"1465022271132499.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465022271497017.jpg\" title=\"1465022271497017.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465022271370288.jpg\" style=\"\" title=\"1465022271370288.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">天山路二号绿化平台地址：天山路（中山路至长平路中间第二自然段），即丹霞小学碧霞校区正对面。<br/><strong>（来源/市城绿中心 城管小编/阿敏 诗、图/刘明逸）</strong><br/><br/>　　若想了解更多我市更多城市绿化资讯，敬请关注“汕头城绿中心”微信公众号，请自行搜索微信号：stclzx或直接扫描下方二维码关注哦。<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465021872106929.jpg\" title=\"1465021872106929.jpg\" alt=\"547708997325524167.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span><br/></p>','2016-06-04 13:52:54',0,NULL,2,NULL,324,8,'',0,0,0,'2016-06-04 14:33:28','2016-06-04 14:40:49');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (539,'【创文进行时】老干部的关心！感动！鼓舞！砥砺前行！','n8cCKMLHI7JSjAWF.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"><span style=\"font-family: 微软雅黑;\"></span>　　创文任务很艰巨、很辛苦，不分昼夜，不管风吹、日晒、雨淋，我们城管人都永远在路上。<br/></span></p><p style=\"white-space: normal; text-align: justify;\"><span style=\"font-family: 微软雅黑;\">　　正是因为有这帮勤劳、可爱的城管人，城管局系统各项创文工作才得以顺利推进，而这一切，我们城管系统的离退休老干部一直关注着、关心着、支持着，并不时为大家鼓劲、点赞。<br/>　　老干部的关心、关怀、支持和鼓励，仿佛是炎炎夏日中的一汪清泉，是前行途中蓦然回首时慈母的微笑，是漫漫长夜中的一盏明灯，给了我们勇往直前的动力，激励我们风雨兼程。<br/>　　近日，老干部陈泽生同志给许创生局长发来这么一段文字和诗篇，小编从中体会到了老干部对我们深深的爱：<br/>　　<strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">许局长，你好！近观局所属各部干群在局统一领导下，冒高温酷暑奋战在创文一线，尤其看到路灯和绿化中心二组现场照片，深感欣喜。这是以往难见的。如能坚持不懈，创文必有硕果。随作劣诗二首，以资激励。<br/><br/></span></strong></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><strong><span style=\"font-size: 18px;\">“黑人”</span></strong></span><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\"></span><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\"></span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">昼夜守护奔路中，</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">风吹日晒劲不松。</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">纵然肤色疑“黑人”</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">灯光辉煌心轻松。</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\">——赞汕头城管路灯人</span></strong></p><p style=\"white-space: normal; text-align: center;\"><strong><span style=\"font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px;\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465026350125622.jpg\" title=\"1465026350125622.jpg\" style=\"text-align: center; white-space: normal;\"/></span></strong></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><strong><span style=\"font-size: 18px;\">无题</span></strong></span><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\"></span><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\"></span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\">天山路上见翠园，</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\">疑是草原天上来。</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\">绿化员工显身手，</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\">造福鮀城功德载。</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size: 18px; font-family: 楷体, 楷体_GB2312, SimKai;\">——赞汕头城管绿化中心干群</span></strong></p><p style=\"white-space: normal; text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465029334725989.jpg\" title=\"1465029334725989.jpg\" alt=\"777115349757254269_缩小大小.jpg\"/></p><p style=\"white-space: normal; text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　读完老干部的诗，小编热泪盈眶，离退休老干部尚且如此关心城管事业，我们有何理由不把工作做好呢？汕头是我们的家，正如许创生局长所说：“谁人不想家乡美”，相信每一个有良知的汕头人，都希望自己的家乡变得更加美丽！而我们正是美丽家园的守护者，小编相信，只要我们城管人齐心协力，“地更绿、水更清、灯更亮、路更净、市容更整洁、夜景更璀璨、汕头更美丽”的目标肯定将很快实现！<br/>　　我们是有担当的城管人，我们一直在路上！</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026350493488.jpg\" title=\"1465026350493488.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026350773152.jpg\" style=\"\" title=\"1465026350773152.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026350100242.jpg\" style=\"\" title=\"1465026350100242.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026351416074.jpg\" title=\"1465026351416074.jpg\"/><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160604/1465026351871178.jpg\" style=\"\" title=\"1465026351871178.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026431139151.jpg\" title=\"1465026431139151.jpg\" alt=\"1463669871422055.jpg\"/></p><p style=\"white-space: normal; text-align: justify;\">　　<span style=\"font-family:微软雅黑, Microsoft YaHei\">最后，请允许小编代表城管系统全体干部职工向离退休老干部表示感谢，在你们的关心鼓励下，我们的工作一定会做得越来越好！我们的家乡一定会变得越来越美丽！<br/>　　下面这幅城绿中心浇水作业的图片，作为本文的结尾，图片中那道彩虹不正是我们工作成果的美丽写照吗？<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465026623349375.jpg\" title=\"1465026623349375.jpg\" alt=\"16330961273224877_缩小大小.jpg\"/></p><p style=\"white-space: normal; text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市城管局 鸣谢/陈泽生<span style=\"text-align: justify;\">&nbsp;城管小编/阿敏）</span></span></p>','2016-06-04 16:30:00',0,NULL,2,NULL,757,9,'',0,0,0,'2016-06-04 16:16:15','2016-06-04 16:56:07');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (540,'【创文进行时】这背影，美！','w7LqQFasboREm3JL.jpg','<p><span style=\"font-family: 微软雅黑;\"></span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465041716270669.jpg\" title=\"1465041716270669.jpg\" alt=\"280851139346228455_缩小大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　有那么一群人，每天都在大马路上出现，可是，照片里的他们常常只有背影！</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465041757104859.jpg\" title=\"1465041757104859.jpg\" alt=\"381404040602918437_缩小大小.jpg\"/></p><p style=\"text-align: center;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(84, 141, 212);\">巡查队帅哥内心独白：你把俺们的树包成这样是想闹哪样？</span></strong></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465041942976649.jpg\" title=\"1465041942976649.jpg\" alt=\"682966870466205784_缩小大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　原来，自<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">“</span>创建全国文明城市、强化城市管理”的号角”吹响之后，市城绿中心为贯彻落实市委、市政府的指示精神，配合全市环境卫生提升行动，加大清理市区行道树上乱吊挂、乱张贴等“六乱”行为的整治力度，有效提升了城市的市容市貌环境。</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465042002102329.jpg\" title=\"1465042002102329.jpg\" alt=\"884396385121244270_缩小大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　每天，城绿中心的两支绿化巡查队就兵分两路，对行道树上的“六乱”行为进行清理，即使烈日炎炎，他们依旧努力着，哪怕汗流浃背，一身“臭汗”，因为，他们有自己的工作准则：还市民一个真正的“绿”！</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465042177118586.jpg\" title=\"1465042177118586.jpg\" alt=\"452719842058525498_缩小大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　据小编了解，到目前为止，短短一个多月，两支巡查队共清理布条126副，各类大小广告牌280块，树包金112处，彩旗314支。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465042217760974.jpg\" style=\"\" title=\"1465042217760974.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465042217110733.jpg\" style=\"\" title=\"1465042217110733.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160604/1465042217442323.jpg\" style=\"\" title=\"1465042217442323.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　在此，小编希望广大市民群众一起加入到此次的“创文”浪潮中，从自身做起，不要在行道树上乱粘贴、乱吊挂，一起抵制各类侵绿、毁绿、占绿和影响市容市貌的行为，一起爱绿护绿，呵护我们的绿色家园。<br/>（来源/市城绿中心 城管小编/阿敏 文、图/锐群）<br/><br/></span></p><p style=\"white-space: normal; text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　若想了解更多我市更多城市绿化资讯，敬请关注“汕头城绿中心”微信公众号，请自行搜索微信号：stclzx或直接扫描下方二维码关注哦。<br/></span></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465021872106929.jpg\" title=\"1465021872106929.jpg\" alt=\"547708997325524167.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br/></span><br/></p>','2016-06-04 19:46:56',0,NULL,2,NULL,432,9,'',0,0,0,'2016-06-04 20:32:13','2016-06-04 20:34:39');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (541,'【动态】美哉！路边时花恢复斑斓！','nd0hFBKvr7rO5ZOL.jpg','<p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178279757316.jpg\" title=\"1465178279757316.jpg\" alt=\"1464877810137546.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　6月3日，小编在《<span style=\"line-height: 29.7px; font-size: 16px;\">时花要更新替换啦！》一文中预告<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; text-align: justify;\">市城绿中心将对市区中山东路和海滨路沿线花箱、花碟的时花进行更新替换，并且正在做各项前期准备工作！还说要小伙伴们拭目以待，可是已经拭了好久了，眼泪都拭出来了，说好的花呢？难道要等到猴年马月？</span></span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178295515908.gif\" title=\"1465178295515908.gif\" alt=\"QQ图片20160606095217.gif\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　大家别急，小编其实想说的是……请看下图：</span><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178463907927.jpg\" title=\"1465178463907927.jpg\" alt=\"339523406249447370_调整大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　没错，我们万众期待的猴年马月已经到了，所以，我们市城绿中心的“城市园丁”们，顶着烈日和暴雨（画面感好强！），努力兑现郑重许下的承诺，对中山东路、海滨路的花箱、花碟中的市花实施更新替换。小编不会告诉你市城绿中心主任都上阵了哦~</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178771127894.jpg\" style=\"\" title=\"1465178771127894.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178771612686.jpg\" style=\"\" title=\"1465178771612686.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178772691219.jpg\" title=\"1465178772691219.jpg\"/><br/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178777125910.jpg\" style=\"\" title=\"1465178777125910.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">据小编了解，市城绿中心此次选取种植了约3.6万株长春花、白日草、香彩雀、黄帝菊等颜色更鲜艳，花期也将更长久，耐热性会更好的时花。快带你的小伙伴们来一起欣赏新一期的“一路一花景”吧！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465178971126500.jpg\" title=\"1465178971126500.jpg\" alt=\"326321661574501333_调整大小.jpg\"/></p><p style=\"text-align: justify;\">　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">不过，小伙伴们请允许小编我多唠叨一句，时花虽美，但它属于公共财产，喜欢“顺手牵羊”的大小伙伴们，我们已经准备好许多“无懈可击”等着了哦，而且这也是对“城市园丁”辛苦劳动的尊重嘛，请做文明市民！<br/><br/>（来源/市城绿中心 城管小编/阿敏 文、图/锐群）<br/><br/>相关阅读：<br/></span><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=529\" target=\"_self\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">时花要更新替换啦！</span></a><br/><br/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; white-space: normal; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　若想了解更多我市更多城市绿化资讯，敬请关注“汕头城绿中心”微信公众号，请自行搜索微信号：stclzx或直接扫描下方二维码关注哦。</span><span style=\"box-sizing: border-box; font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><br style=\"box-sizing: border-box;\"/></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"http://olive.strtv.cn:7337/uploads/20160604/1465021872106929.jpg\" title=\"1465021872106929.jpg\" alt=\"547708997325524167.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle;\"/></p><p style=\"text-align: justify;\"><br/></p>','2016-06-06 08:20:25',0,NULL,2,NULL,131,9,'',0,0,0,'2016-06-06 10:50:12','2016-06-06 10:50:12');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (542,'【一封信】致市民朋友们的一封信','sCcp7C6zN8PLV3a1.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　【编者语】长期以来，我们的城市在以“十乱”为代表的病毒侵染下久病难愈，这些现象看在眼里，痛在“贤哥”“涛哥”、各级领导、广大市民及小编的心里。日前，汕头市市政工程维修中心的战士们正在发动一场联合剿灭“私设地锁、地桩、斜坡”病毒的运动战，全力守护汕头文明之花。</span></p><p><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">亲爱的市民朋友们：</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　汕头是我们共同的家园，我们每个汕头人都希望自己的家环境优美、秩序优良、生活优雅。新任市领导响应汕头人民的心声，决定在汕头这片热土上重新播撒文明的种子，并且刮起了创建全国文明城市的春风，在各个职能部门各司其职、通力合作的浇灌中，文明之花终于含苞待放！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　然而，私建地锁、地桩、斜坡等不文明现象欺凌了我们辛勤栽培的文明之花，破坏了我们的生活环境，影响了我们汕头母亲的形象。为了能给我们的文明之花营造一个健康的成长环境，小编呼吁市民朋友们：</span></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190652702278.jpg\" style=\"\" title=\"1465190652702278.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190658127640.jpg\" style=\"\" title=\"1465190658127640.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当名为“私设地锁”的病毒破坏我们的步道设施，妨碍我们的通行的时候，我们要携手消灭它！</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190759122631.jpg\" title=\"1465190759122631.jpg\" alt=\"436600013597535221_调整大小.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当名为“私立地桩”的病毒侵占我们的公共空间，损害我们的利益的时候，我们要一起打败它！</span><br/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190769210683.jpg\" style=\"\" title=\"1465190769210683.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190776513541.jpg\" style=\"\" title=\"1465190776513541.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465190776149750.jpg\" style=\"\" title=\"1465190776149750.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　当名为“私建斜坡”的病毒阻碍我们的排水通道，影响我们的安全的时候，我们要共同战胜它！</span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　君何时才见文明之花遍地开，百花争艳斗芬芳？待到全民参与文明行动，争做文明使者时，文明之花便在城中笑！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　您若参与传播文明之光，汕头母亲将用灿烂的笑脸迎接晴朗的明天；您若按耐不住心中不文明的魔火，有关部门将对您进行查处，广大市民会为您不齿，汕头母亲也会因您蒙羞。为了汕头的未来，做一个文明守法的好市民，何乐而不为呢！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　文明之花大家养，花园之美大家赏！我们是城市的主人，既是城市文明的建设者和创造者，更是城市文明的享有者和受益者。给城市一份美丽，还家园一份和谐，这其中有你我一份义务，更有你我一份责任！希望大家携手共建文明汕头！</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　最后，祝广大市民朋友身体健康，工作顺利，家庭幸福，万事如意!<br/></span></p><p style=\"text-indent:37px\"><br/></p><p style=\"text-align: right;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">汕头市市政工程维修中心　　</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（来源/市市政维修中心 城管小编/阿敏 文/吴少毅 图/谢祺）<br/><br/>　　<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; line-height: 20px; text-align: justify;\">若想了解更多我市更多市政工程维修资讯，敬请关注“汕头市政”微信公众号，请自行搜索微信号：stszgcwxzx或直接扫描下方二维码关注哦。</span></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465191258644142.jpg\" title=\"1465191258644142.jpg\" alt=\"72175393597745948.jpg\"/><span style=\"text-align: justify;\"></span></p>','2016-06-06 13:15:56',0,NULL,2,NULL,116,12,'',0,0,0,'2016-06-06 13:35:51','2016-06-06 13:59:07');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (543,'【市政呾你知】红领巾路为啥老积水？','WcGC00cvzpp4fSXy.jpg','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"><span style=\"font-family: 微软雅黑;\"></span>　　一场大雨过后，市区红领巾路（金陵路-饶平路）水漫金山，给行人和车辆带来诸多不便，小伙伴们调侃：不用移步海滨路也能“看海”啦！<br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160606/1465191809107811.jpg\" title=\"1465191809107811.jpg\" alt=\"1.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">（红领巾路内心独白：宝宝心里苦！为什么总是我？！）</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　那么红领巾路为啥那么苦呢？小编这就告诉小伙伴们：红领巾路（新陵路-饶平路）下水道修建于上世纪60年代（哎呀妈呀，这老久了~都能进博物馆了吧），因排水管道较小、老化严重、集水面积大，现已无法满足大雨级别以上的降雨排涝需求。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465192052644142.png\" title=\"1465192052644142.png\" alt=\"2.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　市城管局为全力解决群众关心关注的民生问题，已将该处情况向市政府汇报，把红领巾路全段列入2016年度民生改造工程。改造后，周边居民就可以正式告别逢雨必浸的日子啦。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　同时，小编也想告知小伙伴们，造成该处雨后积水的另一原因：每次雨后，市市政工程维修中心都会在第一时间派员到现场打开沟井盖，加快排涝速度。但由于部分沟井盖被停放在路边的车辆压住，无法打开，造成边沟井集水功能暂时性丧失，积水无法及时排走，大大延长排涝时间。</span></p><p style=\"text-align: center;\"><img src=\"/uploads/20160606/1465192192443442.jpg\" title=\"1465192192443442.jpg\" alt=\"3_调整大小.jpg\"/><br/><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">有图有真相！<br/></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　小编在这里想提醒将车辆停放在市政沟井盖上的车主，您为图一时方便，不仅影响我们的排涝工作，造成其他市民朋友的不方便，也有可能因为雨水无法及时排走导致路段严重内涝而淹及您的爱车。因此，小编也在此呼吁：为人为己，请勿随意乱停车辆。</span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465192279101481.jpg\" title=\"1465192279101481.jpg\" alt=\"4.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　对于车辆碾压造成的沟井损坏的情况，据《汕头市市政设施管理条例》第二十五条：在城市排水设施管理范围内，禁止行为包括，其他侵占，损害城市排水设施的行为。违者责令限期改正，并可处五百元以上二千元以下罚款。朋友们，小编相信你们不会跟自己的钱包过不去吧！<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465192419130571.jpg\" title=\"1465192419130571.jpg\" alt=\"5_调整大小.jpg\"/></p><p><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\"></span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">（来源/市市政维修中心 城管小编/阿敏 文、图/唐飞鹏）<br/><br/></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; white-space: normal; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　若想了解更多我市更多市政工程维修资讯，敬请关注“汕头市政”微信公众号，请自行搜索微信号：stszgcwxzx或直接扫描下方二维码关注哦。</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; box-sizing: border-box;\"><br style=\"box-sizing: border-box;\"/></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"http://olive.strtv.cn:7337/uploads/20160606/1465191258644142.jpg\" title=\"1465191258644142.jpg\" alt=\"72175393597745948.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle;\"/></p><p><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-06-06 13:38:14',0,NULL,2,NULL,97,7,'',0,0,0,'2016-06-06 13:54:02','2016-06-06 14:01:12');
insert  into `chaosky`(`tipid`,`tiptitle`,`tipimg1`,`tipcontent`,`stime`,`postflag`,`posttime`,`userid`,`post_user`,`readnum`,`proid`,`tipvideo`,`commentflag`,`delflag`,`draftflag`,`created_at`,`updated_at`) values (544,'【动态】私设广告牌？拆！！','BPSZRtdSxoucXZO4.png','<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"><span style=\"font-family: 微软雅黑;\"></span>　　小伙伴们有没有留意到，市区不少地方存在私设大小广告牌的现象，这些广告牌不仅影响市容观瞻，且由于没有报批，还存在不小的安全隐患。<br/>　　</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">针对中心城区违规设置的大型户外广告牌，市城管执法局事前摸查，并责令整改。但从目前看来，违规广告牌的所有者并未自行拆除或提交申请。大家熟悉的“最强特种部队”终于忍无可忍无需再忍！（此处该有劲酷的出场乐）<br/>　　2016年6月6日，这是个无比666的日子，市城管执法局联合市政、城绿部门开展专项拆除行动，共出动</span><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">20多名工作人员，6部执法车辆以及大型长板车和吊车。对在泰山路、乐山路违规设置的四处大型户外广告牌进行<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(255, 0, 0);\">强制拆除</span></strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px;\"><strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(255, 0, 0);\">！</span></strong></span></span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 16px;\">　　下面请跟随小编的镜头，进入到广告牌的拆除现场：<br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465214921139870.png\" title=\"1465214921139870.png\" alt=\"image001_缩小大小.png\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">这是在泰山路绿地擅自设置的大型户外广告牌，<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; color: rgb(255, 0, 0); font-size: 20px;\">拆！</span></strong><br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465215252414439.png\" title=\"1465215252414439.png\" alt=\"image003_缩小大小.png\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">这是在乐山路与汕樟路交界处西南角的人行步道擅自设置的户外广告牌，<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(255, 0, 0);\">拆！</span></strong><br/></span></p><p style=\"text-align: center\"><img src=\"/uploads/20160606/1465215229119297.png\" title=\"1465215229119297.png\" alt=\"image005_缩小大小.png\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/20160606/1465215200159346.jpg\" title=\"1465215200159346.jpg\" alt=\"image007_缩小大小.jpg\"/><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">这两处都是在乐山路公共场地擅自设置的户外广告牌，<strong><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 20px; color: rgb(255, 0, 0);\">拆！</span></strong></span><br/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">　　文明的城市，离开不开安全整齐的城市环境，此次拆除行动，事前预案准备充分，经过一天的辛勤努力，最终顺利安全拆除。<span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">看到这些违规的大型广告牌被强制拆除，小编心里觉得特别解气！</span><br/>　　在此，小编也要呼吁沿路商家们要按章办事，需要设置广告牌的，请自行前往行政服务中心市城管局办事窗口进行申请，经过审批的广告牌，才能让您真正高枕无忧哇！<br/><br/>（来源/市城管执法局 城管小编/阿敏 文、图/谢福东）<br/><br/>相关阅读：<br/></span></p><p style=\"text-align: justify;\"><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=477\" target=\"_self\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">市城管局办事问答</span></a><br/><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=471\" target=\"_self\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">广东省户外广告管理规定</span></a><br/><a href=\"http://olive.strtv.cn:7337/cg/ifo.php?tipid=531\" target=\"_self\"><span style=\"font-family: 微软雅黑, &#39;Microsoft YaHei&#39;;\">汕头市人民政府办公室关于印发汕头市实施《广东省户外广告管理规定》有关问题的通知</span></a></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑;\"></span><br/></p>','2016-06-06 19:56:22',0,NULL,2,NULL,86,9,'',0,0,0,'2016-06-06 20:24:51','2016-06-06 20:27:16');
