insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`) values (68,'陈兴财','','13592833116','虽说现在城管到处整顿店铺的占用问题，怎么就没有来整顿我这边的，在月季园的7栋这里一到早上五六点开始到中午1点就变成早菜市场，非常拥挤，垃圾也很多，非常占用周边居民的地方，乱搭建好多，居委会也不管，有没有人来管管制制，不要说来管一两天，过几天又这样，那就等于没做，常年累月这样子没法在这生活的，时间已经最少有数十年之久！','2016-05-31 12:35:09',0,1,'14.158.227.86',NULL);
