insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`) values (74,'郑（龙眼南城管与商家在步道上和睦喝茶聊天）','','18675401664','龙眼南夜市11点前虽已暂停经营，但是步道及道路依然被占，严重影响交通。北方煲及烤鱼等摊贩11点前用多辆自家摩托车霸占路道；步道上天天坐着各店家的服务员，更甚有多名城管人员在步道上与商家喝茶聊天玩手机，其电瓶车停放路边；11点后各商家纷纷摆出阵势，第二天道路上依旧积满油啧和垃圾，天气高温，不断散发恶臭；市场周边虽已清理，却有不少乱停车占道现象，小摊贩持续经营。以上问题根深蒂固，希电视台暗访比予以曝光，相关部门予以解决，涉及违规城管监督人员予以处罚！一般时间为晚上6至10点。望重视！周边居民有苦难言！','2016-06-02 00:59:35',0,1,'163.204.9.86',NULL);
