insert  into `jrsx`(`id`,`username`,`pic`,`dh`,`comments`,`postdate`,`delflag`,`f1`,`ip`,`localrecord`) values (90,'寄居人','','13413413889','珠池路与星湖公园交叉处〈火车站对面不远处)，有两集装房长期占用公共人行道，从事餐飲和烧烤，随处挖开人行道砖作为排水，臭气熏天。在此斜对面有一商户长期在行道树旁圈养十几只鸡脏臭不堪！','2016-06-04 22:00:34',0,1,'125.91.81.240',NULL);
